// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

/**
 * @title Compound's InterestRateModel Interface
 * @author Compound
 */
abstract contract InterestRateModel {
  /// @notice Indicator that this is an InterestRateModel contract (for inspection)
  bool public constant isInterestRateModel = true;

  /**
   * @notice Calculates the current borrow interest rate per block
   * @param cash The total amount of cash the market has
   * @param borrows The total amount of borrows the market has outstanding
   * @param reserves The total amount of reserves the market has
   * @return The borrow rate per block (as a percentage, and scaled by 1e18)
   */
  function getBorrowRate(
    uint256 cash,
    uint256 borrows,
    uint256 reserves
  ) public view virtual returns (uint256);

  /**
   * @notice Calculates the current supply interest rate per block
   * @param cash The total amount of cash the market has
   * @param borrows The total amount of borrows the market has outstanding
   * @param reserves The total amount of reserves the market has
   * @param reserveFactorMantissa The current reserve factor the market has
   * @return The supply rate per block (as a percentage, and scaled by 1e18)
   */
  function getSupplyRate(
    uint256 cash,
    uint256 borrows,
    uint256 reserves,
    uint256 reserveFactorMantissa
  ) public view virtual returns (uint256);
}

contracts/compound/Unitroller.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "./ErrorReporter.sol";
import "./ComptrollerStorage.sol";
import "./Comptroller.sol";
import { DiamondExtension, DiamondBase, LibDiamond } from "../ionic/DiamondExtension.sol";

/**
 * @title Unitroller
 * @dev Storage for the comptroller is at this address, while execution is delegated via the Diamond Extensions
 * CTokens should reference this contract as their comptroller.
 */
contract Unitroller is ComptrollerV3Storage, ComptrollerErrorReporter, DiamondBase {
  /**
   * @notice Event emitted when the admin rights are changed
   */
  event AdminRightsToggled(bool hasRights);

  /**
   * @notice Emitted when pendingAdmin is changed
   */
  event NewPendingAdmin(address oldPendingAdmin, address newPendingAdmin);

  /**
   * @notice Emitted when pendingAdmin is accepted, which means admin is updated
   */
  event NewAdmin(address oldAdmin, address newAdmin);

  constructor(address payable _ionicAdmin) {
    admin = msg.sender;
    ionicAdmin = _ionicAdmin;
  }

  /*** Admin Functions ***/

  /**
   * @notice Toggles admin rights.
   * @param hasRights Boolean indicating if the admin is to have rights.
   * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
   */
  function _toggleAdminRights(bool hasRights) external returns (uint256) {
    if (!hasAdminRights()) {
      return fail(Error.UNAUTHORIZED, FailureInfo.TOGGLE_ADMIN_RIGHTS_OWNER_CHECK);
    }

    // Check that rights have not already been set to the desired value
    if (adminHasRights == hasRights) return uint256(Error.NO_ERROR);

    adminHasRights = hasRights;
    emit AdminRightsToggled(hasRights);

    return uint256(Error.NO_ERROR);
  }

  /**
   * @notice Begins transfer of admin rights. The newPendingAdmin must call `_acceptAdmin` to finalize the transfer.
   * @dev Admin function to begin change of admin. The newPendingAdmin must call `_acceptAdmin` to finalize the transfer.
   * @param newPendingAdmin New pending admin.
   * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
   */
  function _setPendingAdmin(address newPendingAdmin) public returns (uint256) {
    if (!hasAdminRights()) {
      return fail(Error.UNAUTHORIZED, FailureInfo.SET_PENDING_ADMIN_OWNER_CHECK);
    }

    address oldPendingAdmin = pendingAdmin;
    pendingAdmin = newPendingAdmin;
    emit NewPendingAdmin(oldPendingAdmin, newPendingAdmin);

    return uint256(Error.NO_ERROR);
  }

  /**
   * @notice Accepts transfer of admin rights. msg.sender must be pendingAdmin
   * @dev Admin function for pending admin to accept role and update admin
   * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
   */
  function _acceptAdmin() public returns (uint256) {
    // Check caller is pendingAdmin and pendingAdmin  address(0)
    if (msg.sender != pendingAdmin || msg.sender == address(0)) {
      return fail(Error.UNAUTHORIZED, FailureInfo.ACCEPT_ADMIN_PENDING_ADMIN_CHECK);
    }

    // Save current values for inclusion in log
    address oldAdmin = admin;
    address oldPendingAdmin = pendingAdmin;

    admin = pendingAdmin;
    pendingAdmin = address(0);

    emit NewAdmin(oldAdmin, admin);
    emit NewPendingAdmin(oldPendingAdmin, pendingAdmin);

    return uint256(Error.NO_ERROR);
  }

  function comptrollerImplementation() public view returns (address) {
    return LibDiamond.getExtensionForFunction(bytes4(keccak256(bytes("_deployMarket(uint8,bytes,bytes,uint256)"))));
  }

  /**
   * @dev upgrades the implementation if necessary
   */
  function _upgrade() external {
    require(msg.sender == address(this) || hasAdminRights(), "!self || !admin");

    address currentImplementation = comptrollerImplementation();
    address latestComptrollerImplementation = IFeeDistributor(ionicAdmin).latestComptrollerImplementation(
      currentImplementation
    );

    _updateExtensions(latestComptrollerImplementation);

    if (currentImplementation != latestComptrollerImplementation) {
      // reinitialize
      _functionCall(address(this), abi.encodeWithSignature("_becomeImplementation()"), "!become impl");
    }
  }

  function _functionCall(
    address target,
    bytes memory data,
    string memory errorMessage
  ) internal returns (bytes memory) {
    (bool success, bytes memory returndata) = target.call(data);

    if (!success) {
      // Look for revert reason and bubble it up if present
      if (returndata.length > 0) {
        // The easiest way to bubble the revert reason is using memory via assembly

        // solhint-disable-next-line no-inline-assembly
        assembly {
          let returndata_size := mload(returndata)
          revert(add(32, returndata), returndata_size)
        }
      } else {
        revert(errorMessage);
      }
    }

    return returndata;
  }

  function _updateExtensions(address currentComptroller) internal {
    address[] memory latestExtensions = IFeeDistributor(ionicAdmin).getComptrollerExtensions(currentComptroller);
    address[] memory currentExtensions = LibDiamond.listExtensions();

    // removed the current (old) extensions
    for (uint256 i = 0; i < currentExtensions.length; i++) {
      LibDiamond.removeExtension(DiamondExtension(currentExtensions[i]));
    }
    // add the new extensions
    for (uint256 i = 0; i < latestExtensions.length; i++) {
      LibDiamond.addExtension(DiamondExtension(latestExtensions[i]));
    }
  }

  /**
   * @dev register a logic extension
   * @param extensionToAdd the extension whose functions are to be added
   * @param extensionToReplace the extension whose functions are to be removed/replaced
   */
  function _registerExtension(DiamondExtension extensionToAdd, DiamondExtension extensionToReplace) external override {
    require(hasAdminRights(), "!unauthorized");
    LibDiamond.registerExtension(extensionToAdd, extensionToReplace);
  }
}

contracts/EmissionsManager.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import { ERC20 } from "solmate/tokens/ERC20.sol";
import { SafeTransferLib } from "solmate/utils/SafeTransferLib.sol";

import { Ownable2StepUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/access/Ownable2StepUpgradeable.sol";
import { IonicFlywheelCore } from "./ionic/strategies/flywheel/IonicFlywheelCore.sol";
import { IonicComptroller } from "./compound/ComptrollerInterface.sol";
import { BasePriceOracle } from "./oracles/BasePriceOracle.sol";
import { ICErc20 } from "./compound/CTokenInterfaces.sol";
import { PoolDirectory } from "./PoolDirectory.sol";
import { IEmissionsManager } from "./IEmissionsManager.sol";
import { IveION } from "./veION/interfaces/IveION.sol";

interface Oracle {
  function getUnderlyingPrice(address) external returns (uint256);
}

contract EmissionsManager is IEmissionsManager, Ownable2StepUpgradeable {
  using SafeTransferLib for ERC20;

  address public protocolAddress;
  uint256 public collateralBp;
  PoolDirectory public fpd;
  ERC20 public rewardToken;
  address public veION;

  bytes public nonBlacklistableTargetBytecode;
  mapping(address => bool) public isBlacklisted;
  mapping(address => bool) public nonBlacklistable;

  uint256 public constant MAXIMUM_BASIS_POINTS = 10_000;
  uint256 public constant BLACKLISTER_SHARE = 80;
  uint256 public constant PROTOCOL_SHARE = 20;

  modifier onlyBlacklistableBytecode(address _addr) {
    bytes memory code = _addr.code;
    require(keccak256(code) != keccak256(nonBlacklistableTargetBytecode), "Non-blacklistable bytecode");
    _;
  }

  constructor() {
    _disableInitializers(); // Locks the implementation contract from being initialized
  }

  function initialize(
    PoolDirectory _fpd,
    address _protocolAddress,
    ERC20 _rewardToken,
    uint256 _collateralBp,
    bytes memory _nonBlacklistableTargetBytecode
  ) public initializer {
    if (address(_fpd) == address(0)) revert InvalidPoolDirectoryAaddress();
    if (_protocolAddress == address(0)) revert InvalidProtocolAddress();
    if (address(_rewardToken) == address(0)) revert InvalidRewardTokenAddress();
    if (_collateralBp >= MAXIMUM_BASIS_POINTS) revert CollateralBasisPointsExceedMaximum();

    __Ownable2Step_init();
    protocolAddress = _protocolAddress;
    fpd = _fpd;
    rewardToken = _rewardToken;
    collateralBp = _collateralBp;
    nonBlacklistableTargetBytecode = _nonBlacklistableTargetBytecode;

    emit Initialized(_protocolAddress, address(_rewardToken), _collateralBp, _nonBlacklistableTargetBytecode);
  }

  function setVeIon(IveION _veIon) external onlyOwner {
    if (address(_veIon) == address(0)) revert InvalidVeIONAddress();
    veION = address(_veIon);
    emit VeIonSet(address(_veIon));
  }

  function setCollateralBp(uint256 _collateralBp) external onlyOwner {
    if (_collateralBp >= MAXIMUM_BASIS_POINTS) revert MaximumLimitExceeded();
    collateralBp = _collateralBp;
    emit CollateralBpSet(_collateralBp);
  }

  function setNonBlacklistableAddress(address _user, bool _isNonBlacklistable) external onlyOwner {
    nonBlacklistable[_user] = _isNonBlacklistable;
    emit NonBlacklistableAddressSet(_user, _isNonBlacklistable);
  }

  function setNonBlacklistableTargetBytecode(bytes memory _newBytecode) external onlyOwner {
    nonBlacklistableTargetBytecode = _newBytecode;
    emit NonBlacklistableTargetBytecodeSet(_newBytecode);
  }

  function setProtocolAddress(address _newProtocolAddress) external onlyOwner {
    require(_newProtocolAddress != address(0), "Invalid address");
    protocolAddress = _newProtocolAddress;
  }

  function _getUserTotalCollateral(address _user) internal view returns (uint256) {
    uint256 totalColateralInETH = 0;
    (, PoolDirectory.Pool[] memory pools) = fpd.getActivePools();
    uint256 poolsLength = pools.length;
    for (uint256 i = 0; i < poolsLength; i++) {
      IonicComptroller comptroller = IonicComptroller(pools[i].comptroller);
      BasePriceOracle oracle = comptroller.oracle();
      ICErc20[] memory cTokens = comptroller.getAllMarkets();
      uint256 cTokensLength = cTokens.length;
      for (uint256 j = 0; j < cTokensLength; j++) {
        uint256 supplyBalance = cTokens[j].balanceOfUnderlying(_user);
        if (supplyBalance > 0) {
          uint256 collateralInETH = (supplyBalance * oracle.getUnderlyingPrice(cTokens[j])) / 1e18;
          totalColateralInETH += collateralInETH;
        }
      }
    }
    return totalColateralInETH;
  }

  function getUserTotalCollateral(address _user) external view returns (uint256) {
    return _getUserTotalCollateral(_user);
  }

  function _checkCollateralRatio(address _user) internal view returns (bool) {
    uint256 userCollateralValue = _getUserTotalCollateral(_user);
    if (userCollateralValue == 0) return true;
    uint256 userLPValue = IveION(veION).getTotalEthValueOfTokens(_user);
    if ((userLPValue * MAXIMUM_BASIS_POINTS) / userCollateralValue >= collateralBp) {
      return true;
    } else return false;
  }

  function reportUser(address _user) external onlyBlacklistableBytecode(_user) {
    require(!nonBlacklistable[_user], "Non-blacklistable user");
    require(!isBlacklisted[_user], "Already blacklisted");
    require(!_checkCollateralRatio(_user), "LP balance above threshold");
    isBlacklisted[_user] = true;
    _blacklistUserAndClaimEmissions(_user);
  }

  function whitelistUser(address _user) external {
    require(isBlacklisted[_user], "Already whitelisted");
    require(_checkCollateralRatio(_user), "LP balance below threshold");
    isBlacklisted[_user] = false;
    (, PoolDirectory.Pool[] memory pools) = fpd.getActivePools();
    uint256 poolsLength = pools.length;
    for (uint256 i = 0; i < poolsLength; i++) {
      IonicComptroller comptroller = IonicComptroller(pools[i].comptroller);
      ICErc20[] memory cTokens = comptroller.getAllMarkets();
      uint256 cTokensLength = cTokens.length;
      for (uint256 j = 0; j < cTokensLength; j++) {
        address[] memory flywheelAddresses = comptroller.getAccruingFlywheels();
        uint256 flywheelAddressesLength = flywheelAddresses.length;
        for (uint256 k = 0; k < flywheelAddressesLength; k++) {
          IonicFlywheelCore flywheel = IonicFlywheelCore(flywheelAddresses[k]);
          if (address(flywheel.rewardToken()) == address(rewardToken)) {
            flywheel.whitelistUser(ERC20(address(cTokens[j])), _user);
            flywheel.accrue(ERC20(address(cTokens[j])), _user);
          }
        }
      }
    }
  }

  function isUserBlacklisted(address _user) external view returns (bool) {
    return isBlacklisted[_user];
  }

  function isUserBlacklistable(address _user) external view returns (bool) {
    if (nonBlacklistable[_user] || keccak256(_user.code) == keccak256(nonBlacklistableTargetBytecode)) {
      return false;
    }
    return !_checkCollateralRatio(_user) && !isBlacklisted[_user];
  }

  function _blacklistUserAndClaimEmissions(address user) internal {
    uint256 balanceBefore = ERC20(rewardToken).balanceOf(address(this));
    (, PoolDirectory.Pool[] memory pools) = fpd.getActivePools();
    uint256 poolsLength = pools.length;
    for (uint256 i = 0; i < poolsLength; i++) {
      IonicComptroller comptroller = IonicComptroller(pools[i].comptroller);
      ERC20[] memory markets;
      {
        ICErc20[] memory cerc20s = comptroller.getAllMarkets();
        uint256 cerc20sLength = cerc20s.length;
        markets = new ERC20[](cerc20sLength);
        for (uint256 j = 0; j < cerc20sLength; j++) {
          markets[j] = ERC20(address(cerc20s[j]));
        }
      }

      address[] memory flywheelAddresses = comptroller.getAccruingFlywheels();
      uint256 flywheelAddressesLength = flywheelAddresses.length;
      for (uint256 k = 0; k < flywheelAddressesLength; k++) {
        IonicFlywheelCore flywheel = IonicFlywheelCore(flywheelAddresses[k]);
        if (address(flywheel.rewardToken()) == address(rewardToken)) {
          uint256 marketsLength = markets.length;
          for (uint256 m = 0; m < marketsLength; m++) {
            flywheel.accrue(markets[m], user);
            flywheel.updateBlacklistBalances(markets[m], user);
          }
          flywheel.takeRewardsFromUser(user, address(this));
        }
      }
    }

    uint256 balanceAfter = ERC20(rewardToken).balanceOf(address(this));
    uint256 totalClaimed = balanceAfter - balanceBefore;
    if (totalClaimed > 0) {
      rewardToken.safeTransfer(msg.sender, (totalClaimed * BLACKLISTER_SHARE) / 100);
      rewardToken.safeTransfer(protocolAddress, (totalClaimed * PROTOCOL_SHARE) / 100);
    }
  }
}

