contracts/ionic/strategies/flywheel/Ioni.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.10;

import { ERC20 } from "solmate/tokens/ERC20.sol";
import { SafeTransferLib } from "solmate/utils/SafeTransferLib.sol";
import { SafeCastLib } from "solmate/utils/SafeCastLib.sol";

import { IFlywheelRewards } from "./rewards/IFlywheelRewards.sol";
import { IFlywheelBooster } from "./IFlywheelBooster.sol";
import { IEmissionsManager } from "../../../IEmissionsManager.sol";
import { Ownable2StepUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/access/Ownable2StepUpgradeable.sol";

contract IonicFlywheelCore is Ownable2StepUpgradeable {
  using SafeTransferLib for ERC20;
  using SafeCastLib for uint256;

  /// @notice How much rewardsToken will be send to treasury
  uint256 public performanceFee;

  /// @notice Address that gets rewardsToken accrued by performanceFee
  address public feeRecipient;

  /// @notice The token to reward
  ERC20 public rewardToken;

  /// @notice append-only list of strategies added
  ERC20[] public allStrategies;

  /// @notice the rewards contract for managing streams
  IFlywheelRewards public flywheelRewards;

  /// @notice optional booster module for calculating virtual balances on strategies
  IFlywheelBooster public flywheelBooster;

  IEmissionsManager public emissionsManager;

  /// @notice The accrued but not yet transferred rewards for each user
  mapping(address => uint256) internal _rewardsAccrued;

  /// @notice The strategy index and last updated per strategy
  mapping(ERC20 => RewardsState) internal _strategyState;

  /// @notice user index per strategy
  mapping(ERC20 => mapping(address => uint224)) internal _userIndex;

  /// @notice user blacklisted supply per strategy
  mapping(ERC20 => mapping(address => uint256)) public userBlacklistedSupply;

  /// @notice blacklisted supply per strategy
  mapping(ERC20 => uint256) public blacklistedSupply;

  modifier onlyEmissionsManager() {
    require(address(emissionsManager) == msg.sender, "!emissionsManager");
    _;
  }

  constructor() {
    _disableInitializers(); // Locks the implementation contract from being initialized
  }

  function initialize(
    ERC20 _rewardToken,
    IFlywheelRewards _flywheelRewards,
    IFlywheelBooster _flywheelBooster,
    address _owner
  ) public initializer {
    __Ownable2Step_init();

    rewardToken = _rewardToken;
    flywheelRewards = _flywheelRewards;
    flywheelBooster = _flywheelBooster;

    performanceFee = 10e16; // 10%
    feeRecipient = _owner;
  }

  /*----------------------------------------------------------------
                        ACCRUE/CLAIM LOGIC
    ----------------------------------------------------------------*/

  /** 
      @notice Emitted when a user's rewards accrue to a given strategy.
      @param strategy the updated rewards strategy
      @param user the user of the rewards
      @param rewardsDelta how many new rewards accrued to the user
      @param rewardsIndex the market index for rewards per token accrued
    */
  event AccrueRewards(ERC20 indexed strategy, address indexed user, uint256 rewardsDelta, uint256 rewardsIndex);

  /** 
      @notice Emitted when a user claims accrued rewards.
      @param user the user of the rewards
      @param amount the amount of rewards claimed
    */
  event ClaimRewards(address indexed user, uint256 amount);

  /** 
      @notice accrue rewards for a single user on a strategy
      @param strategy the strategy to accrue a user's rewards on
      @param user the user to be accrued
      @return the cumulative amount of rewards accrued to user (including prior)
    */
  function accrue(ERC20 strategy, address user) public returns (uint256) {
    (uint224 index, uint32 ts) = strategyState(strategy);
    RewardsState memory state = RewardsState(index, ts);

    if (state.index == 0) return 0;

    state = accrueStrategy(strategy, state);
    return accrueUser(strategy, user, state);
  }

  /** 
      @notice accrue rewards for a two users on a strategy
      @param strategy the strategy to accrue a user's rewards on
      @param user the first user to be accrued
      @param user the second user to be accrued
      @return the cumulative amount of rewards accrued to the first user (including prior)
      @return the cumulative amount of rewards accrued to the second user (including prior)
    */
  function accrue(ERC20 strategy, address user, address secondUser) public returns (uint256, uint256) {
    (uint224 index, uint32 ts) = strategyState(strategy);
    RewardsState memory state = RewardsState(index, ts);

    if (state.index == 0) return (0, 0);

    state = accrueStrategy(strategy, state);
    return (accrueUser(strategy, user, state), accrueUser(strategy, secondUser, state));
  }

  /** 
      @notice claim rewards for a given user
      @param user the user claiming rewards
      @dev this function is public, and all rewards transfer to the user
    */
  function claimRewards(address user) external {
    require(!emissionsManager.isUserBlacklisted(user), "blacklisted");
    require(!emissionsManager.isUserBlacklistable(user), "blacklistable");
    uint256 accrued = rewardsAccrued(user);

    if (accrued != 0) {
      _rewardsAccrued[user] = 0;

      rewardToken.safeTransferFrom(address(flywheelRewards), user, accrued);

      emit ClaimRewards(user, accrued);
    }
  }

  /** 
      @notice take rewards for a given user
      @param user the user claiming rewards
      @param receiver the address that receives the rewards
      @dev this function is public, and all rewards transfer to the receiver
    */
  function takeRewardsFromUser(address user, address receiver) external onlyEmissionsManager {
    uint256 accrued = rewardsAccrued(user);

    if (accrued != 0) {
      _rewardsAccrued[user] = 0;

      rewardToken.safeTransferFrom(address(flywheelRewards), receiver, accrued);

      emit ClaimRewards(user, accrued);
    }
  }

  /** 
      @notice set user balances to zero
      @param strategy strategy to whitelist user for
      @param user the user to be whitelisted
      @dev this function is public, and all user and strategy blacklisted supplies are reset
    */
  function whitelistUser(ERC20 strategy, address user) external onlyEmissionsManager {
    blacklistedSupply[strategy] -= userBlacklistedSupply[strategy][user];
    userBlacklistedSupply[strategy][user] = 0;
  }

  /** 
      @notice update user blacklisted balances
      @param strategy strategy to update blacklisted balances
      @param user the user to be blacklisted
      @dev this function is public
    */
  function updateBlacklistBalances(ERC20 strategy, address user) external onlyEmissionsManager {
    _updateBlacklistBalances(strategy, user);
  }

  /** 
      @notice update user blacklisted balances
      @param strategy strategy to update blacklisted balances
      @param user the user to be blacklisted
      @dev this function is private
    */
  function _updateBlacklistBalances(ERC20 strategy, address user) internal {
    if (emissionsManager.isUserBlacklisted(user)) {
      uint256 _oldUserBlacklistedSupply = userBlacklistedSupply[strategy][user];
      uint256 supplierTokens = address(flywheelBooster) != address(0)
        ? flywheelBooster.boostedBalanceOf(ERC20(strategy), user)
        : ERC20(strategy).balanceOf(user);

      if (supplierTokens >= _oldUserBlacklistedSupply) {
        blacklistedSupply[strategy] += supplierTokens - _oldUserBlacklistedSupply;
        userBlacklistedSupply[strategy][user] = supplierTokens;
      } else {
        blacklistedSupply[strategy] -= _oldUserBlacklistedSupply - supplierTokens;
        userBlacklistedSupply[strategy][user] = supplierTokens;
      }
    }
  }
  /*----------------------------------------------------------------
                          ADMIN LOGIC
    ----------------------------------------------------------------*/

  /** 
      @notice Emitted when a new strategy is added to flywheel by the admin
      @param newStrategy the new added strategy
    */
  event AddStrategy(address indexed newStrategy);

  /// @notice initialize a new strategy
  function setEmissionsManager(IEmissionsManager _emissionsManager) external onlyOwner {
    emissionsManager = _emissionsManager;
  }

  /// @notice initialize a new strategy
  function addStrategyForRewards(ERC20 strategy) external onlyOwner {
    _addStrategyForRewards(strategy);
  }

  function _addStrategyForRewards(ERC20 strategy) internal {
    (uint224 index, ) = strategyState(strategy);
    require(index == 0, "strategy");
    _strategyState[strategy] = RewardsState({
      index: (10 ** rewardToken.decimals()).safeCastTo224(),
      lastUpdatedTimestamp: block.timestamp.safeCastTo32()
    });

    allStrategies.push(strategy);
    emit AddStrategy(address(strategy));
  }

  function getAllStrategies() external view returns (ERC20[] memory) {
    return allStrategies;
  }

  /** 
      @notice Emitted when the rewards module changes
      @param newFlywheelRewards the new rewards module
    */
  event FlywheelRewardsUpdate(address indexed newFlywheelRewards);

  /// @notice swap out the flywheel rewards contract
  function setFlywheelRewards(IFlywheelRewards newFlywheelRewards) external onlyOwner {
    if (address(flywheelRewards) != address(0)) {
      uint256 oldRewardBalance = rewardToken.balanceOf(address(flywheelRewards));
      if (oldRewardBalance > 0) {
        rewardToken.safeTransferFrom(address(flywheelRewards), address(newFlywheelRewards), oldRewardBalance);
      }
    }

    flywheelRewards = newFlywheelRewards;

    emit FlywheelRewardsUpdate(address(newFlywheelRewards));
  }

  /** 
      @notice Emitted when the booster module changes
      @param newBooster the new booster module
    */
  event FlywheelBoosterUpdate(address indexed newBooster);

  /// @notice swap out the flywheel booster contract
  function setBooster(IFlywheelBooster newBooster) external onlyOwner {
    flywheelBooster = newBooster;

    emit FlywheelBoosterUpdate(address(newBooster));
  }

  event UpdatedFeeSettings(
    uint256 oldPerformanceFee,
    uint256 newPerformanceFee,
    address oldFeeRecipient,
    address newFeeRecipient
  );

  /**
   * @notice Update performanceFee and/or feeRecipient
   * @dev Claim rewards first from the previous feeRecipient before changing it
   */
  function updateFeeSettings(uint256 _performanceFee, address _feeRecipient) external onlyOwner {
    _updateFeeSettings(_performanceFee, _feeRecipient);
  }

  function _updateFeeSettings(uint256 _performanceFee, address _feeRecipient) internal {
    emit UpdatedFeeSettings(performanceFee, _performanceFee, feeRecipient, _feeRecipient);

    if (feeRecipient != _feeRecipient) {
      _rewardsAccrued[_feeRecipient] += rewardsAccrued(feeRecipient);
      _rewardsAccrued[feeRecipient] = 0;
    }
    performanceFee = _performanceFee;
    feeRecipient = _feeRecipient;
  }

  /*----------------------------------------------------------------
                    INTERNAL ACCOUNTING LOGIC
    ----------------------------------------------------------------*/

  struct RewardsState {
    /// @notice The strategy's last updated index
    uint224 index;
    /// @notice The timestamp the index was last updated at
    uint32 lastUpdatedTimestamp;
  }

  /// @notice accumulate global rewards on a strategy
  function accrueStrategy(
    ERC20 strategy,
    RewardsState memory state
  ) private returns (RewardsState memory rewardsState) {
    // calculate accrued rewards through module
    uint256 strategyRewardsAccrued = flywheelRewards.getAccruedRewards(strategy, state.lastUpdatedTimestamp);

    rewardsState = state;

    if (strategyRewardsAccrued > 0) {
      // use the booster or token supply to calculate reward index denominator
      uint256 supplyTokens = address(flywheelBooster) != address(0)
        ? flywheelBooster.boostedTotalSupply(strategy) - blacklistedSupply[strategy]
        : strategy.totalSupply() - blacklistedSupply[strategy];

      // 100% = 100e16
      uint256 accruedFees = (strategyRewardsAccrued * performanceFee) / uint224(100e16);

      _rewardsAccrued[feeRecipient] += accruedFees;
      strategyRewardsAccrued -= accruedFees;

      uint224 deltaIndex;

      if (supplyTokens != 0)
        deltaIndex = ((strategyRewardsAccrued * (10 ** strategy.decimals())) / supplyTokens).safeCastTo224();

      // accumulate rewards per token onto the index, multiplied by fixed-point factor
      rewardsState = RewardsState({
        index: state.index + deltaIndex,
        lastUpdatedTimestamp: block.timestamp.safeCastTo32()
      });
      _strategyState[strategy] = rewardsState;
    }
  }

  /// @notice accumulate rewards on a strategy for a specific user
  function accrueUser(ERC20 strategy, address user, RewardsState memory state) private returns (uint256) {
    // load indices
    uint224 strategyIndex = state.index;
    uint224 supplierIndex = userIndex(strategy, user);

    // sync user index to global
    _userIndex[strategy][user] = strategyIndex;

    // if user hasn't yet accrued rewards, grant them interest from the strategy beginning if they have a balance
    // zero balances will have no effect other than syncing to global index
    if (supplierIndex == 0) {
      supplierIndex = (10 ** rewardToken.decimals()).safeCastTo224();
    }

    uint224 deltaIndex = strategyIndex - supplierIndex;
    // use the booster or token balance to calculate reward balance multiplier
    uint256 supplierTokens = address(flywheelBooster) != address(0)
      ? flywheelBooster.boostedBalanceOf(strategy, user) - userBlacklistedSupply[strategy][user]
      : strategy.balanceOf(user) - userBlacklistedSupply[strategy][user];

    // accumulate rewards by multiplying user tokens by rewardsPerToken index and adding on unclaimed
    uint256 supplierDelta = (deltaIndex * supplierTokens) / (10 ** strategy.decimals());
    uint256 supplierAccrued = rewardsAccrued(user) + supplierDelta;

    _rewardsAccrued[user] = supplierAccrued;

    emit AccrueRewards(strategy, user, supplierDelta, strategyIndex);

    return supplierAccrued;
  }

  function rewardsAccrued(address user) public virtual returns (uint256) {
    return _rewardsAccrued[user];
  }

  function userIndex(ERC20 strategy, address user) public virtual returns (uint224) {
    return _userIndex[strategy][user];
  }

  function strategyState(ERC20 strategy) public virtual returns (uint224 index, uint32 lastUpdatedTimestamp) {
    return (_strategyState[strategy].index, _strategyState[strategy].lastUpdatedTimestamp);
  }
}

contracts/ionic/strategies/flywheel/rewa.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.10;

import {SafeTransferLib, ERC20} from "solmate/utils/SafeTransferLib.sol";
import {IFlywheelRewards} from "./IFlywheelRewards.sol";
import {IonicFlywheelCore} from "../IonicFlywheelCore.sol";

/** 
 @title Flywheel Reward Module
 @notice Determines how many rewards accrue to each strategy globally over a given time period.
 @dev approves the flywheel core for the reward token to allow balances to be managed by the module but claimed from core.
*/
abstract contract BaseFlywheelRewards is IFlywheelRewards {
    using SafeTransferLib for ERC20;

    /// @notice thrown when caller is not the flywheel
    error FlywheelError();

    /// @notice the reward token paid
    ERC20 public immutable override rewardToken;

    /// @notice the flywheel core contract
    IonicFlywheelCore public immutable override flywheel;

    constructor(IonicFlywheelCore _flywheel) {
        flywheel = _flywheel;
        ERC20 _rewardToken = _flywheel.rewardToken();
        rewardToken = _rewardToken;

        _rewardToken.safeApprove(address(_flywheel), type(uint256).max);
    }

    modifier onlyFlywheel() {
        if (msg.sender != address(flywheel)) revert FlywheelError();
        _;
    }
}

// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.10;

import { Auth, Authority } from "solmate/auth/Auth.sol";
import { BaseFlywheelRewards } from "./BaseFlywheelRewards.sol";
import { SafeTransferLib, ERC20 } from "solmate/utils/SafeTransferLib.sol";
import { IonicFlywheelCore } from "../IonicFlywheelCore.sol";

/**
 * @title FlywheelInstantRewards
 * @author Jourdan DUnkley <jourdan@ionic.money> (https://github.com/jourdanDunkley)
 * @notice This contract handles the instant reward distribution for strategies.
 * It allows setting and transferring rewards for specific strategies.
 */
contract FlywheelInstantRewards is Auth, BaseFlywheelRewards {
  using SafeTransferLib for ERC20;

  event RewardsSet(ERC20 indexed strategy, uint256 amount);

  /// @notice Mapping to store the reward amount for each strategy.
  mapping(ERC20 => uint256) rewardAmount;
  /// @notice Mapping to track if a strategy has new rewards to be distributed.
  mapping(ERC20 => bool) rewardsNew;

  /**
   * @notice Constructor to initialize the FlywheelInstantRewards contract.
   * @param _flywheel The flywheel core contract.
   * @param _owner The owner of the contract.
   * @param _authority The authority contract for authentication.
   */
  constructor(
    IonicFlywheelCore _flywheel,
    address _owner,
    Authority _authority
  ) Auth(_owner, _authority) BaseFlywheelRewards(_flywheel) {}

  /**
   * @notice Set the reward amount for a specific strategy.
   * @param _amount The amount of rewards to be set for the strategy.
   * @param _strategy The strategy for which the rewards are being set.
   * @dev Requires authentication to execute.
   */
  function setRewards(ERC20 _strategy, uint256 _amount) external requiresAuth {
    rewardAmount[_strategy] = _amount;
    rewardsNew[_strategy] = true;
    rewardToken.safeTransferFrom(msg.sender, address(this), _amount);
    emit RewardsSet(_strategy, _amount);
  }

  /**
   * @notice Calculate and transfer accrued rewards to the flywheel core.
   * @param strategy The strategy to accrue rewards for.
   * @return amount The amount of tokens accrued and transferred.
   * @dev This function can only be called by the flywheel core.
   */
  function getAccruedRewards(
    ERC20 strategy,
    uint32 /* lastUpdatedTimestamp */
  ) external override onlyFlywheel returns (uint256 amount) {
    if (rewardsNew[strategy]) {
      amount = rewardAmount[strategy];
      rewardsNew[strategy] = false;
    } else {
      amount = 0;
    }
  }
}

// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.10;

import {ERC20} from "solmate/tokens/ERC20.sol";
import {IonicFlywheelCore} from "../IonicFlywheelCore.sol";

/**
 @title Rewards Module for Flywheel
 @notice Flywheel is a general framework for managing token incentives.
         It takes reward streams to various *strategies* such as staking LP tokens and divides them among *users* of those strategies.

         The Rewards module is responsible for:
         * determining the ongoing reward amounts to entire strategies (core handles the logic for dividing among users)
         * actually holding rewards that are yet to be claimed

         The reward stream can follow arbitrary logic as long as the amount of rewards passed to flywheel core has been sent to this contract.

         Different module strategies include:
         * a static reward rate per second
         * a decaying reward rate
         * a dynamic just-in-time reward stream
         * liquid governance reward delegation (Curve Gauge style)

         SECURITY NOTE: The rewards strategy should be smooth and continuous, to prevent gaming the reward distribution by frontrunning.
 */
interface IFlywheelRewards {
    /**
     @notice calculate the rewards amount accrued to a strategy since the last update.
     @param strategy the strategy to accrue rewards for.
     @param lastUpdatedTimestamp the last time rewards were accrued for the strategy.
     @return rewards the amount of rewards accrued to the market
    */
    function getAccruedRewards(ERC20 strategy, uint32 lastUpdatedTimestamp) external returns (uint256 rewards);

    /// @notice return the flywheel core address
    function flywheel() external view returns (IonicFlywheelCore);

    /// @notice return the reward token associated with flywheel core.
    function rewardToken() external view returns (ERC20);
}

contracts/ionic/strategies/IonicERC4626..content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import { FixedPointMathLib } from "solmate/utils/FixedPointMathLib.sol";

import { PausableUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/security/PausableUpgradeable.sol";
import { ERC4626Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/extensions/ERC4626Upgradeable.sol";
import { ERC20Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";
import { SafeERC20Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/utils/SafeERC20Upgradeable.sol";

import { SafeOwnableUpgradeable } from "../../ionic/SafeOwnableUpgradeable.sol";

abstract contract IonicERC4626 is SafeOwnableUpgradeable, PausableUpgradeable, ERC4626Upgradeable {
  using FixedPointMathLib for uint256;
  using SafeERC20Upgradeable for ERC20Upgradeable;

  /* ========== STATE VARIABLES ========== */

  uint256 public vaultShareHWM;
  uint256 public performanceFee;
  address public feeRecipient;

  /* ========== EVENTS ========== */

  event UpdatedFeeSettings(
    uint256 oldPerformanceFee,
    uint256 newPerformanceFee,
    address oldFeeRecipient,
    address newFeeRecipient
  );

  event UpdatedRewardsRecipient(address oldRewardsRecipient, address newRewardsRecipient);

  /* ========== INITIALIZER ========== */

  function __IonicER4626_init(ERC20Upgradeable asset_) internal onlyInitializing {
    __SafeOwnable_init(msg.sender);
    __Pausable_init();
    __Context_init();
    __ERC20_init(
      string(abi.encodePacked("Ionic ", asset_.name(), " Vault")),
      string(abi.encodePacked("iv", asset_.symbol()))
    );
    __ERC4626_init(asset_);

    vaultShareHWM = 10 ** asset_.decimals();
    feeRecipient = msg.sender;
  }

  function _asset() internal view returns (ERC20Upgradeable) {
    return ERC20Upgradeable(super.asset());
  }

  /* ========== DEPOSIT/WITHDRAW FUNCTIONS ========== */

  function deposit(uint256 assets, address receiver) public override whenNotPaused returns (uint256 shares) {
    // Check for rounding error since we round down in previewDeposit.
    require((shares = previewDeposit(assets)) != 0, "ZERO_SHARES");

    // Need to transfer before minting or ERC777s could reenter.
    _asset().safeTransferFrom(msg.sender, address(this), assets);

    _mint(receiver, shares);

    emit Deposit(msg.sender, receiver, assets, shares);

    afterDeposit(assets, shares);
  }

  function mint(uint256 shares, address receiver) public override whenNotPaused returns (uint256 assets) {
    assets = previewMint(shares); // No need to check for rounding error, previewMint rounds up.

    // Need to transfer before minting or ERC777s could reenter.
    _asset().safeTransferFrom(msg.sender, address(this), assets);

    _mint(receiver, shares);

    emit Deposit(msg.sender, receiver, assets, shares);

    afterDeposit(assets, shares);
  }

  function withdraw(uint256 assets, address receiver, address owner) public override returns (uint256 shares) {
    shares = previewWithdraw(assets); // No need to check for rounding error, previewWithdraw rounds up.

    if (msg.sender != owner) {
      uint256 allowed = allowance(owner, msg.sender); // Saves gas for limited approvals.

      if (allowed != type(uint256).max) _approve(owner, msg.sender, allowed - shares);
    }

    if (!paused()) {
      uint256 balanceBeforeWithdraw = _asset().balanceOf(address(this));

      beforeWithdraw(assets, shares);

      assets = _asset().balanceOf(address(this)) - balanceBeforeWithdraw;
    }

    _burn(owner, shares);

    emit Withdraw(msg.sender, receiver, owner, assets, shares);

    _asset().safeTransfer(receiver, assets);
  }

  function redeem(uint256 shares, address receiver, address owner) public override returns (uint256 assets) {
    if (msg.sender != owner) {
      uint256 allowed = allowance(owner, msg.sender); // Saves gas for limited approvals.

      if (allowed != type(uint256).max) _approve(owner, msg.sender, allowed - shares);
    }

    // Check for rounding error since we round down in previewRedeem.
    require((assets = previewRedeem(shares)) != 0, "ZERO_ASSETS");

    if (!paused()) {
      uint256 balanceBeforeWithdraw = _asset().balanceOf(address(this));

      beforeWithdraw(assets, shares);

      assets = _asset().balanceOf(address(this)) - balanceBeforeWithdraw;
    }

    _burn(owner, shares);

    emit Withdraw(msg.sender, receiver, owner, assets, shares);

    _asset().safeTransfer(receiver, assets);
  }

  /* ========== FEE FUNCTIONS ========== */

  /**
   * @notice Take the performance fee that has accrued since last fee harvest.
   * @dev Performance fee is based on a vault share high water mark value. If vault share value has increased above the
   *   HWM in a fee period, issue fee shares to the vault equal to the performance fee.
   */
  function takePerformanceFee() external onlyOwner {
    require(feeRecipient != address(0), "fee recipient not initialized");

    uint256 currentAssets = totalAssets();
    uint256 shareValue = convertToAssets(10 ** _asset().decimals());

    require(shareValue > vaultShareHWM, "shareValue !> vaultShareHWM");
    // cache value
    uint256 supply = totalSupply();

    uint256 accruedPerformanceFee = (performanceFee * (shareValue - vaultShareHWM) * supply) / 1e36;
    _mint(feeRecipient, accruedPerformanceFee.mulDivDown(supply, (currentAssets - accruedPerformanceFee)));

    vaultShareHWM = convertToAssets(10 ** _asset().decimals());
  }

  /**
   * @notice Transfer accrued fees to rewards manager contract. Caller must be a registered keeper.
   * @dev We must make sure that feeRecipient is not address(0) before withdrawing fees
   */
  function withdrawAccruedFees() external onlyOwner {
    redeem(balanceOf(feeRecipient), feeRecipient, feeRecipient);
  }

  /**
   * @notice Update performanceFee and/or feeRecipient
   */
  function updateFeeSettings(uint256 newPerformanceFee, address newFeeRecipient) external onlyOwner {
    emit UpdatedFeeSettings(performanceFee, newPerformanceFee, feeRecipient, newFeeRecipient);

    performanceFee = newPerformanceFee;

    if (newFeeRecipient != feeRecipient) {
      if (feeRecipient != address(0)) {
        uint256 oldFees = balanceOf(feeRecipient);

        _burn(feeRecipient, oldFees);
        _approve(feeRecipient, owner(), 0);
        _mint(newFeeRecipient, oldFees);
      }

      _approve(newFeeRecipient, owner(), type(uint256).max);
    }

    feeRecipient = newFeeRecipient;
  }

  /* ========== EMERGENCY FUNCTIONS ========== */

  // Should withdraw all funds from the strategy and pause the contract
  function emergencyWithdrawAndPause() external virtual;

  function unpause() external virtual;

  function shutdown(address market) external onlyOwner whenPaused returns (uint256) {
    ERC20Upgradeable theAsset = _asset();
    uint256 endBalance = theAsset.balanceOf(address(this));
    theAsset.transfer(market, endBalance);
    return endBalance;
  }

  /* ========== INTERNAL HOOKS LOGIC ========== */

  function beforeWithdraw(uint256 assets, uint256 shares) internal virtual;

  function afterDeposit(uint256 assets, uint256 shares) internal virtual;
}

contracts/ionic/strategies/IonicMarketER.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;
import { IonicERC4626, ERC20Upgradeable } from "./IonicERC4626.sol";
import { SafeERC20Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/utils/SafeERC20Upgradeable.sol";
import { ICErc20 } from "../../compound/CTokenInterfaces.sol";
import { ComptrollerErrorReporter } from "../../compound/ErrorReporter.sol";

interface IonicFlywheelLensRouter_4626 {
  function claimAllRewardTokens(address user) external returns (address[] memory, uint256[] memory);
}

contract IonicMarketERC4626 is IonicERC4626 {
  using SafeERC20Upgradeable for ERC20Upgradeable;

  error IonicMarketERC4626__CompoundError(ComptrollerErrorReporter.Error);

  // STATE VARIABLES
  address public rewardsRecipient;
  IonicFlywheelLensRouter_4626 public flywheelLensRouter;
  ICErc20 public cToken;

  function initialize(
    ERC20Upgradeable asset_,
    ICErc20 cToken_,
    address flywheelLensRouter_,
    address rewardsRecipient_
  ) public initializer {
    __IonicER4626_init(asset_);
    cToken = cToken_;
    rewardsRecipient = rewardsRecipient_;
    flywheelLensRouter = IonicFlywheelLensRouter_4626(flywheelLensRouter_);
  }

  /* ========== VIEW FUNCTIONS ========== */
  function totalAssets() public view virtual override returns (uint256) {
    return cToken.balanceOfUnderlying(address(this));
  }

  /// @notice maximum amount of underlying tokens that can be deposited into the underlying protocol
  function maxDeposit(address) public view override returns (uint256) {
    if (cToken.comptroller().mintGuardianPaused(address(cToken))) {
      return 0;
    }

    uint256 supplyCap = cToken.comptroller().supplyCaps(address(cToken));
    if (supplyCap != 0) {
      uint256 currentExchangeRate = cToken.exchangeRateCurrent();
      uint256 _totalSupply = cToken.totalSupply();
      uint256 totalSupplies = (_totalSupply * currentExchangeRate) / 1e18; /// exchange rate is scaled up by 1e18, so needs to be divided off to get accurate total supply

      // uint256 totalCash = MToken(address(mToken)).getCash();
      // uint256 totalBorrows = MToken(address(mToken)).totalBorrows();
      // uint256 totalReserves = MToken(address(mToken)).totalReserves();

      // // (Pseudocode) totalSupplies = totalCash + totalBorrows - totalReserves
      // uint256 totalSupplies = (totalCash + totalBorrows) - totalReserves;

      // supply cap is      3
      // total supplies is  1
      /// no room for additional supplies

      // supply cap is      3
      // total supplies is  0
      /// room for 1 additional supplies

      // supply cap is      4
      // total supplies is  1
      /// room for 1 additional supplies

      /// total supplies could exceed supply cap as interest accrues, need to handle this edge case
      /// going to subtract 2 from supply cap to account for rounding errors
      if (totalSupplies + 2 >= supplyCap) {
        return 0;
      }

      return supplyCap - totalSupplies - 2;
    }

    return type(uint256).max;
  }

  /// @notice Returns the maximum amount of tokens that can be supplied
  /// no way for this function to ever revert unless comptroller or mToken is broken
  /// @dev accrue interest must be called before this function is called, otherwise
  /// an outdated value will be fetched, and the returned value will be incorrect
  /// (greater than actual amount available to be minted will be returned)
  function maxMint(address) public view override returns (uint256) {
    uint256 mintAmount = maxDeposit(address(0));

    return mintAmount == type(uint256).max ? mintAmount : convertToShares(mintAmount);
  }

  /// @notice maximum amount of underlying tokens that can be withdrawn
  /// @param owner The address that owns the shares
  function maxWithdraw(address owner) public view override returns (uint256) {
    uint256 cash = cToken.getCash();
    uint256 assetsBalance = convertToAssets(balanceOf(owner));
    return cash < assetsBalance ? cash : assetsBalance;
  }

  /// @notice maximum amount of shares that can be withdrawn
  /// @param owner The address that owns the shares
  function maxRedeem(address owner) public view override returns (uint256) {
    uint256 cash = cToken.getCash();
    uint256 cashInShares = convertToShares(cash);
    uint256 shareBalance = balanceOf(owner);
    return cashInShares < shareBalance ? cashInShares : shareBalance;
  }

  /* ========== REWARDS FUNCTIONS ========== */

  function updateRewardsRecipient(address newRewardsRecipient) external onlyOwner {
    emit UpdatedRewardsRecipient(rewardsRecipient, newRewardsRecipient);
    rewardsRecipient = newRewardsRecipient;
  }

  function claimRewards() external {
    (address[] memory tokens, uint256[] memory amounts) = flywheelLensRouter.claimAllRewardTokens(address(this));
    for (uint256 i = 0; i < tokens.length; i++) {
      _asset().safeTransfer(rewardsRecipient, amounts[i]);
    }
  }

  /* ========== EMERGENCY FUNCTIONS ========== */

  // Should withdraw all funds from the strategy and pause the contract
  function emergencyWithdrawAndPause() external override onlyOwner {
    _pause();
  }

  function unpause() external override onlyOwner {
    _unpause();
  }

  /* ========== INTERNAL HOOKS LOGIC ========== */

  function beforeWithdraw(uint256 assets, uint256 /*shares*/) internal override {
    /// -----------------------------------------------------------------------
    /// Withdraw assets from Ionic
    /// -----------------------------------------------------------------------

    uint256 errorCode = cToken.redeemUnderlying(assets);
    if (errorCode != uint256(ComptrollerErrorReporter.Error.NO_ERROR)) {
      revert IonicMarketERC4626__CompoundError(ComptrollerErrorReporter.Error(errorCode));
    }
  }

  function afterDeposit(uint256 assets, uint256 /*shares*/) internal override {
    /// -----------------------------------------------------------------------
    /// Deposit assets into Ionic
    /// -----------------------------------------------------------------------

    // approve to cToken
    _asset().safeApprove(address(cToken), assets);

    // deposit into cToken
    uint256 errorCode = cToken.mint(assets);
    if (errorCode != uint256(ComptrollerErrorReporter.Error.NO_ERROR)) {
      revert IonicMarketERC4626__CompoundError(ComptrollerErrorReporter.Error(errorCode));
    }
  }
}

contracts/IonicUniV3Liquidator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/access/OwnableUpgradeable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/utils/AddressUpgradeable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/utils/SafeERC20Upgradeable.sol";

import "./liquidators/IRedemptionStrategy.sol";
import "./liquidators/IFundsConversionStrategy.sol";
import "./ILiquidator.sol";

import "./external/uniswap/IUniswapV3FlashCallback.sol";
import "./external/uniswap/IUniswapV3Pool.sol";
import "./external/pyth/IExpressRelay.sol";
import "./external/pyth/IExpressRelayFeeReceiver.sol";
import { IUniswapV3Quoter } from "./external/uniswap/quoter/interfaces/IUniswapV3Quoter.sol";
import { IFlashLoanReceiver } from "./ionic/IFlashLoanReceiver.sol";

import { ICErc20 } from "./compound/CTokenInterfaces.sol";

import "./PoolLens.sol";

/**
 * @title IonicUniV3Liquidator
 * @author Veliko Minkov <v.minkov@dcvx.io> (https://github.com/vminkov)
 * @notice IonicUniV3Liquidator liquidates unhealthy borrowers with flashloan support.
 */
contract IonicUniV3Liquidator is
  OwnableUpgradeable,
  ILiquidator,
  IUniswapV3FlashCallback,
  IExpressRelayFeeReceiver,
  IFlashLoanReceiver
{
  using AddressUpgradeable for address payable;
  using SafeERC20Upgradeable for IERC20Upgradeable;

  event VaultReceivedETH(address sender, uint256 amount, bytes permissionKey);
  /**
   * @dev Cached liquidator profit exchange source.
   * ERC20 token address or the zero address for NATIVE.
   * For use in `safeLiquidateToTokensWithFlashLoan` after it is set by `postFlashLoanTokens`.
   */
  address private _liquidatorProfitExchangeSource;

  /**
   * @dev Cached flash swap amount.
   * For use in `repayTokenFlashLoan` after it is set by `safeLiquidateToTokensWithFlashLoan`.
   */
  uint256 private _flashSwapAmount;

  /**
   * @dev Cached flash swap token.
   * For use in `repayTokenFlashLoan` after it is set by `safeLiquidateToTokensWithFlashLoan`.
   */
  address private _flashSwapToken;

  address public W_NATIVE_ADDRESS;
  mapping(address => bool) public redemptionStrategiesWhitelist;
  IUniswapV3Quoter public quoter;

  /**
   * @dev Addres of Pyth Express Relay for preventing value leakage in liquidations.
   */
  IExpressRelay public expressRelay;
  /**
   * @dev Pool Lens.
   */
  PoolLens public lens;
  /**
   * @dev Health Factor below which PER permissioning is bypassed.
   */
  uint256 public healthFactorThreshold;

  modifier onlyLowHF(address borrower, ICErc20 cToken) {
    uint256 currentHealthFactor = lens.getHealthFactor(borrower, cToken.comptroller());
    require(currentHealthFactor < healthFactorThreshold, "HF not low enough, reserving for PYTH");
    _;
  }

  function initialize(address _wtoken, address _quoter) external initializer {
    __Ownable_init();
    W_NATIVE_ADDRESS = _wtoken;
    quoter = IUniswapV3Quoter(_quoter);
  }

  /**
   * @notice Safely liquidate an unhealthy loan (using capital from the sender), confirming that at least `minOutputAmount` in collateral is seized (or outputted by exchange if applicable).
   * @param borrower The borrower's Ethereum address.
   * @param repayAmount The amount to repay to liquidate the unhealthy loan.
   * @param cErc20 The borrowed cErc20 to repay.
   * @param cTokenCollateral The cToken collateral to be liquidated.
   * @param minOutputAmount The minimum amount of collateral to seize (or the minimum exchange output if applicable) required for execution. Reverts if this condition is not met.
   */
  function _safeLiquidate(
    address borrower,
    uint256 repayAmount,
    ICErc20 cErc20,
    ICErc20 cTokenCollateral,
    uint256 minOutputAmount
  ) internal returns (uint256) {
    // Transfer tokens in, approve to cErc20, and liquidate borrow
    require(repayAmount > 0, "Repay amount (transaction value) must be greater than 0.");
    IERC20Upgradeable underlying = IERC20Upgradeable(cErc20.underlying());
    underlying.safeTransferFrom(msg.sender, address(this), repayAmount);
    underlying.approve(address(cErc20), repayAmount);
    require(cErc20.liquidateBorrow(borrower, repayAmount, address(cTokenCollateral)) == 0, "Liquidation failed.");

    // Redeem seized cTokens for underlying asset
    uint256 seizedCTokenAmount = cTokenCollateral.balanceOf(address(this));
    require(seizedCTokenAmount > 0, "No cTokens seized.");
    uint256 redeemResult = cTokenCollateral.redeem(seizedCTokenAmount);
    require(redeemResult == 0, "Error calling redeeming seized cToken: error code not equal to 0");

    return transferSeizedFunds(address(cTokenCollateral.underlying()), minOutputAmount);
  }

  function safeLiquidate(
    address borrower,
    uint256 repayAmount,
    ICErc20 cErc20,
    ICErc20 cTokenCollateral,
    uint256 minOutputAmount
  ) external onlyLowHF(borrower, cTokenCollateral) returns (uint256) {
    return _safeLiquidate(borrower, repayAmount, cErc20, cTokenCollateral, minOutputAmount);
  }

  function safeLiquidatePyth(
    address borrower,
    uint256 repayAmount,
    ICErc20 cErc20,
    ICErc20 cTokenCollateral,
    uint256 minOutputAmount
  ) external returns (uint256) {
    require(expressRelay.isPermissioned(address(this), abi.encode(borrower)), "invalid liquidation");
    return _safeLiquidate(borrower, repayAmount, cErc20, cTokenCollateral, minOutputAmount);
  }

  function safeLiquidateWithAggregator(
    address borrower,
    uint256 repayAmount,
    ICErc20 cErc20,
    ICErc20 cTokenCollateral,
    address aggregatorTarget,
    bytes memory aggregatorData
  ) external {
    // Transfer tokens in, approve to cErc20, and liquidate borrow
    require(repayAmount > 0, "Repay amount (transaction value) must be greater than 0.");
    cErc20.flash(
      repayAmount,
      abi.encode(borrower, cErc20, cTokenCollateral, aggregatorTarget, aggregatorData, msg.sender)
    );
  }

  function receiveFlashLoan(address _underlyingBorrow, uint256 amount, bytes calldata data) external {
    (
      address borrower,
      ICErc20 cErc20,
      ICErc20 cTokenCollateral,
      address aggregatorTarget,
      bytes memory aggregatorData,
      address liquidator
    ) = abi.decode(data, (address, ICErc20, ICErc20, address, bytes, address));
    IERC20Upgradeable underlyingBorrow = IERC20Upgradeable(_underlyingBorrow);
    underlyingBorrow.approve(address(cErc20), amount);
    require(cErc20.liquidateBorrow(borrower, amount, address(cTokenCollateral)) == 0, "Liquidation failed.");

    // Redeem seized cTokens for underlying asset
    IERC20Upgradeable underlyingCollateral = IERC20Upgradeable(cTokenCollateral.underlying());
    {
      uint256 seizedCTokenAmount = cTokenCollateral.balanceOf(address(this));
      require(seizedCTokenAmount > 0, "No cTokens seized.");
      uint256 redeemResult = cTokenCollateral.redeem(seizedCTokenAmount);
      require(redeemResult == 0, "Error calling redeeming seized cToken: error code not equal to 0");
      uint256 underlyingCollateralRedeemed = underlyingCollateral.balanceOf(address(this));

      // Call the aggregator
      underlyingCollateral.approve(aggregatorTarget, underlyingCollateralRedeemed);
      (bool success, ) = aggregatorTarget.call(aggregatorData);
      require(success, "Aggregator call failed");
    }

    // receive profits
    {
      uint256 receivedAmount = underlyingBorrow.balanceOf(address(this));
      require(receivedAmount >= amount, "Not received enough collateral after swap.");
      uint256 profitBorrow = receivedAmount - amount;
      if (profitBorrow > 0) {
        underlyingBorrow.safeTransfer(liquidator, profitBorrow);
      }

      uint256 profitCollateral = underlyingCollateral.balanceOf(address(this));
      if (profitCollateral > 0) {
        underlyingCollateral.safeTransfer(liquidator, profitCollateral);
      }
    }

    // pay back flashloan
    underlyingBorrow.approve(address(cErc20), amount);
  }

  /**
   * @dev Transfers seized funds to the sender.
   * @param erc20Contract The address of the token to transfer.
   * @param minOutputAmount The minimum amount to transfer.
   */
  function transferSeizedFunds(address erc20Contract, uint256 minOutputAmount) internal returns (uint256) {
    IERC20Upgradeable token = IERC20Upgradeable(erc20Contract);
    uint256 seizedOutputAmount = token.balanceOf(address(this));
    require(seizedOutputAmount >= minOutputAmount, "Minimum token output amount not satified.");
    if (seizedOutputAmount > 0) token.safeTransfer(msg.sender, seizedOutputAmount);

    return seizedOutputAmount;
  }

  function safeLiquidateToTokensWithFlashLoan(
    LiquidateToTokensWithFlashSwapVars calldata vars
  ) external onlyLowHF(vars.borrower, vars.cTokenCollateral) returns (uint256) {
    // Input validation
    require(vars.repayAmount > 0, "Repay amount must be greater than 0.");

    // we want to calculate the needed flashSwapAmount on-chain to
    // avoid errors due to changing market conditions
    // between the time of calculating and including the tx in a block
    uint256 fundingAmount = vars.repayAmount;
    IERC20Upgradeable fundingToken;
    if (vars.debtFundingStrategies.length > 0) {
      require(
        vars.debtFundingStrategies.length == vars.debtFundingStrategiesData.length,
        "Funding IFundsConversionStrategy contract array and strategy data bytes array must be the same length."
      );
      // estimate the initial (flash-swapped token) input from the expected output (debt token)
      for (uint256 i = 0; i < vars.debtFundingStrategies.length; i++) {
        bytes memory strategyData = vars.debtFundingStrategiesData[i];
        IFundsConversionStrategy fcs = vars.debtFundingStrategies[i];
        (fundingToken, fundingAmount) = fcs.estimateInputAmount(fundingAmount, strategyData);
      }
    } else {
      fundingToken = IERC20Upgradeable(ICErc20(address(vars.cErc20)).underlying());
    }

    // the last outputs from estimateInputAmount are the ones to be flash-swapped
    _flashSwapAmount = fundingAmount;
    _flashSwapToken = address(fundingToken);

    IUniswapV3Pool flashSwapPool = IUniswapV3Pool(vars.flashSwapContract);
    bool token0IsFlashSwapFundingToken = flashSwapPool.token0() == address(fundingToken);
    flashSwapPool.flash(
      address(this),
      token0IsFlashSwapFundingToken ? fundingAmount : 0,
      !token0IsFlashSwapFundingToken ? fundingAmount : 0,
      msg.data
    );

    return transferSeizedFunds(_liquidatorProfitExchangeSource, vars.minProfitAmount);
  }

  /**
   * @dev Receives NATIVE from liquidations and flashloans.
   * Requires that `msg.sender` is W_NATIVE, a CToken, or a Uniswap V2 Router, or another contract.
   */
  receive() external payable {
    require(payable(msg.sender).isContract(), "Sender is not a contract.");
  }

  /**
   * @notice receiveAuctionProceedings function - receives native token from the express relay
   * You can use permission key to distribute the received funds to users who got liquidated, LPs, etc...
   */
  function receiveAuctionProceedings(bytes calldata permissionKey) external payable {
    emit VaultReceivedETH(msg.sender, msg.value, permissionKey);
  }

  function withdrawAll() external onlyOwner {
    uint256 balance = address(this).balance;
    require(balance > 0, "No Ether left to withdraw");

    // Transfer all Ether to the owner
    (bool sent, ) = msg.sender.call{ value: balance }("");
    require(sent, "Failed to send Ether");
  }

  /**
   * @dev Callback function for Uniswap flashloans.
   */

  function supV3FlashCallback(uint256 fee0, uint256 fee1, bytes calldata data) external {
    uniswapV3FlashCallback(fee0, fee1, data);
  }

  function algebraFlashCallback(uint256 fee0, uint256 fee1, bytes calldata data) external {
    uniswapV3FlashCallback(fee0, fee1, data);
  }

  function uniswapV3FlashCallback(uint256 fee0, uint256 fee1, bytes calldata data) public {
    // Liquidate unhealthy borrow, exchange seized collateral, return flashloaned funds, and exchange profit
    // Decode params
    LiquidateToTokensWithFlashSwapVars memory vars = abi.decode(data[4:], (LiquidateToTokensWithFlashSwapVars));

    // Post token flashloan
    // Cache liquidation profit token (or the zero address for NATIVE) for use as source for exchange later
    _liquidatorProfitExchangeSource = postFlashLoanTokens(vars, fee0, fee1);
  }

  /**
   * @dev Liquidate unhealthy token borrow, exchange seized collateral, return flashloaned funds, and exchange profit.
   */
  function postFlashLoanTokens(
    LiquidateToTokensWithFlashSwapVars memory vars,
    uint256 fee0,
    uint256 fee1
  ) private returns (address) {
    IERC20Upgradeable debtRepaymentToken = IERC20Upgradeable(_flashSwapToken);
    uint256 debtRepaymentAmount = _flashSwapAmount;

    if (vars.debtFundingStrategies.length > 0) {
      // loop backwards to convert the initial (flash-swapped token) input to the final expected output (debt token)
      for (uint256 i = vars.debtFundingStrategies.length; i > 0; i--) {
        (debtRepaymentToken, debtRepaymentAmount) = convertCustomFunds(
          debtRepaymentToken,
          debtRepaymentAmount,
          vars.debtFundingStrategies[i - 1],
          vars.debtFundingStrategiesData[i - 1]
        );
      }
    }

    // Approve the debt repayment transfer, liquidate and redeem the seized collateral
    {
      address underlyingBorrow = vars.cErc20.underlying();
      require(
        address(debtRepaymentToken) == underlyingBorrow,
        "the debt repayment funds should be converted to the underlying debt token"
      );
      require(debtRepaymentAmount >= vars.repayAmount, "debt repayment amount not enough");
      // Approve repayAmount to cErc20
      IERC20Upgradeable(underlyingBorrow).approve(address(vars.cErc20), vars.repayAmount);

      // Liquidate borrow
      require(
        vars.cErc20.liquidateBorrow(vars.borrower, vars.repayAmount, address(vars.cTokenCollateral)) == 0,
        "Liquidation failed."
      );

      // Redeem seized cTokens for underlying asset
      uint256 seizedCTokenAmount = vars.cTokenCollateral.balanceOf(address(this));
      require(seizedCTokenAmount > 0, "No cTokens seized.");
      uint256 redeemResult = vars.cTokenCollateral.redeem(seizedCTokenAmount);
      require(redeemResult == 0, "Error calling redeeming seized cToken: error code not equal to 0");
    }

    // Repay flashloan
    return repayTokenFlashLoan(vars.cTokenCollateral, vars.redemptionStrategies, vars.strategyData, fee0, fee1);
  }

  /**
   * @dev Repays token flashloans.
   */
  function repayTokenFlashLoan(
    ICErc20 cTokenCollateral,
    IRedemptionStrategy[] memory redemptionStrategies,
    bytes[] memory strategyData,
    uint256 fee0,
    uint256 fee1
  ) private returns (address) {
    IUniswapV3Pool pool = IUniswapV3Pool(msg.sender);
    uint256 flashSwapReturnAmount = _flashSwapAmount;
    if (IUniswapV3Pool(msg.sender).token0() == _flashSwapToken) {
      flashSwapReturnAmount += fee0;
    } else if (IUniswapV3Pool(msg.sender).token1() == _flashSwapToken) {
      flashSwapReturnAmount += fee1;
    } else {
      revert("wrong pool or _flashSwapToken");
    }

    // Swap cTokenCollateral for cErc20 via Uniswap
    // Check underlying collateral seized
    IERC20Upgradeable underlyingCollateral = IERC20Upgradeable(ICErc20(address(cTokenCollateral)).underlying());
    uint256 underlyingCollateralSeized = underlyingCollateral.balanceOf(address(this));

    // Redeem custom collateral if liquidation strategy is set
    if (redemptionStrategies.length > 0) {
      require(
        redemptionStrategies.length == strategyData.length,
        "IRedemptionStrategy contract array and strategy data bytes array mnust the the same length."
      );
      for (uint256 i = 0; i < redemptionStrategies.length; i++)
        (underlyingCollateral, underlyingCollateralSeized) = redeemCustomCollateral(
          underlyingCollateral,
          underlyingCollateralSeized,
          redemptionStrategies[i],
          strategyData[i]
        );
    }

    // Check if we can repay directly one of the sides with collateral
    if (address(underlyingCollateral) == pool.token0() || address(underlyingCollateral) == pool.token1()) {
      // Repay flashloan directly with collateral
      uint256 collateralRequired;
      if (address(underlyingCollateral) == _flashSwapToken) {
        // repay the borrowed asset directly
        collateralRequired = flashSwapReturnAmount;

        // Repay flashloan
        IERC20Upgradeable(_flashSwapToken).transfer(address(pool), flashSwapReturnAmount);
      } else {
        // TODO swap within the same pool and then repay the FL to the pool
        bool zeroForOne = address(underlyingCollateral) == pool.token0();

        {
          collateralRequired = quoter.quoteExactOutputSingle(
            zeroForOne ? pool.token0() : pool.token1(),
            zeroForOne ? pool.token1() : pool.token0(),
            pool.fee(),
            _flashSwapAmount,
            0 // sqrtPriceLimitX96
          );
        }
        require(
          collateralRequired <= underlyingCollateralSeized,
          "Token flashloan return amount greater than seized collateral."
        );

        // Repay flashloan
        pool.swap(
          address(pool),
          zeroForOne,
          int256(collateralRequired),
          0, // sqrtPriceLimitX96
          ""
        );
      }

      return address(underlyingCollateral);
    } else {
      revert("the redemptions strategy did not swap to the flash swapped pool assets");
    }
  }

  /**
   * @dev for security reasons only whitelisted redemption strategies may be used.
   * Each whitelisted redemption strategy has to be checked to not be able to
   * call `selfdestruct` with the `delegatecall` call in `redeemCustomCollateral`
   */
  function _whitelistRedemptionStrategy(IRedemptionStrategy strategy, bool whitelisted) external onlyOwner {
    redemptionStrategiesWhitelist[address(strategy)] = whitelisted;
  }

  /**
   * @dev for security reasons only whitelisted redemption strategies may be used.
   * Each whitelisted redemption strategy has to be checked to not be able to
   * call `selfdestruct` with the `delegatecall` call in `redeemCustomCollateral`
   */
  function _whitelistRedemptionStrategies(
    IRedemptionStrategy[] calldata strategies,
    bool[] calldata whitelisted
  ) external onlyOwner {
    require(
      strategies.length > 0 && strategies.length == whitelisted.length,
      "list of strategies empty or whitelist does not match its length"
    );

    for (uint256 i = 0; i < strategies.length; i++) {
      redemptionStrategiesWhitelist[address(strategies[i])] = whitelisted[i];
    }
  }

  function setExpressRelay(address _expressRelay) external onlyOwner {
    expressRelay = IExpressRelay(_expressRelay);
  }

  function setPoolLens(address _poolLens) external onlyOwner {
    lens = PoolLens(_poolLens);
  }

  function setHealthFactorThreshold(uint256 _healthFactorThreshold) external onlyOwner {
    require(_healthFactorThreshold <= 1e18, "Invalid Health Factor Threshold");
    healthFactorThreshold = _healthFactorThreshold;
  }

  /**
   * @dev Redeem "special" collateral tokens (before swapping the output for borrowed tokens to be repaid via Uniswap).
   * Public visibility because we have to call this function externally if called from a payable IonicLiquidator function (for some reason delegatecall fails when called with msg.value > 0).
   */
  function redeemCustomCollateral(
    IERC20Upgradeable underlyingCollateral,
    uint256 underlyingCollateralSeized,
    IRedemptionStrategy strategy,
    bytes memory strategyData
  ) private returns (IERC20Upgradeable, uint256) {
    require(redemptionStrategiesWhitelist[address(strategy)], "only whitelisted redemption strategies can be used");

    bytes memory returndata = _functionDelegateCall(
      address(strategy),
      abi.encodeWithSelector(strategy.redeem.selector, underlyingCollateral, underlyingCollateralSeized, strategyData)
    );
    return abi.decode(returndata, (IERC20Upgradeable, uint256));
  }

  function convertCustomFunds(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    IFundsConversionStrategy strategy,
    bytes memory strategyData
  ) private returns (IERC20Upgradeable, uint256) {
    require(redemptionStrategiesWhitelist[address(strategy)], "only whitelisted redemption strategies can be used");

    bytes memory returndata = _functionDelegateCall(
      address(strategy),
      abi.encodeWithSelector(strategy.convert.selector, inputToken, inputAmount, strategyData)
    );
    return abi.decode(returndata, (IERC20Upgradeable, uint256));
  }

  /**
   * @dev Same as {xref-Address-functionCall-address-bytes-string-}[`functionCall`], but performing a delegate call.
   * Copied from https://github.com/OpenZeppelin/openzeppelin-contracts-upgradeable/contracts/blob/cb4774ace1cb84f2662fa47c573780aab937628b/contracts/utils/MulticallUpgradeable.sol#L37
   */
  function _functionDelegateCall(address target, bytes memory data) private returns (bytes memory) {
    require(AddressUpgradeable.isContract(target), "Address: delegate call to non-contract");

    // solhint-disable-next-line avoid-low-level-calls
    (bool success, bytes memory returndata) = target.delegatecall(data);
    return _verifyCallResult(success, returndata, "Address: low-level delegate call failed");
  }

  /**
   * @dev Used by `_functionDelegateCall` to verify the result of a delegate call.
   * Copied from https://github.com/OpenZeppelin/openzeppelin-contracts-upgradeable/contracts/blob/cb4774ace1cb84f2662fa47c573780aab937628b/contracts/utils/MulticallUpgradeable.sol#L45
   */
  function _verifyCallResult(
    bool success,
    bytes memory returndata,
    string memory errorMessage
  ) private pure returns (bytes memory) {
    if (success) {
      return returndata;
    } else {
      // Look for revert reason and bubble it up if present
      if (returndata.length > 0) {
        // The easiest way to bubble the revert reason is using memory via assembly

        // solhint-disable-next-line no-inline-assembly
        assembly {
          let returndata_size := mload(returndata)
          revert(add(32, returndata), returndata_size)
        }
      } else {
        revert(errorMessage);
      }
    }
  }
}

contracts/liquidators/IFundsConversionSt.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "./IRedemptionStrategy.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";

interface IFundsConversionStrategy is IRedemptionStrategy {
  function convert(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    bytes memory strategyData
  ) external returns (IERC20Upgradeable outputToken, uint256 outputAmount);

  function estimateInputAmount(
    uint256 outputAmount,
    bytes memory strategyData
  ) external view returns (IERC20Upgradeable inputToken, uint256 inputAmount);
}

contracts/liquidators/IRedemptionStrateg.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";

/**
 * @title IRedemptionStrategy
 * @notice Redeems seized wrapped token collateral for an underlying token for use as a step in a liquidation.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
interface IRedemptionStrategy {
  /**
   * @notice Redeems custom collateral `token` for an underlying token.
   * @param inputToken The input wrapped token to be redeemed for an underlying token.
   * @param inputAmount The amount of the input wrapped token to be redeemed for an underlying token.
   * @param strategyData The ABI-encoded data to be used in the redemption strategy logic.
   * @return outputToken The underlying ERC20 token outputted.
   * @return outputAmount The quantity of underlying tokens outputted.
   */
  function redeem(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    bytes memory strategyData
  ) external returns (IERC20Upgradeable outputToken, uint256 outputAmount);

  function name() external view returns (string memory);
}

contracts/oracles/BasePriceOracle.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "../compound/CTokenInterfaces.sol";

/**
 * @title BasePriceOracle
 * @notice Returns prices of underlying tokens directly without the caller having to specify a cToken address.
 * @dev Implements the `PriceOracle` interface.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
interface BasePriceOracle {
  /**
   * @notice Get the price of an underlying asset.
   * @param underlying The underlying asset to get the price of.
   * @return The underlying asset price in ETH as a mantissa (scaled by 1e18).
   * Zero means the price is unavailable.
   */
  function price(address underlying) external view returns (uint256);

  /**
   * @notice Get the underlying price of a cToken asset
   * @param cToken The cToken to get the underlying price of
   * @return The underlying asset price mantissa (scaled by 1e18).
   *  Zero means the price is unavailable.
   */
  function getUnderlyingPrice(ICErc20 cToken) external view returns (uint256);
}

contracts/oracles/MasterPriceOracle.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";

import { ICErc20 } from "../compound/CTokenInterfaces.sol";

import { BasePriceOracle } from "./BasePriceOracle.sol";

/**
 * @title MasterPriceOracle
 * @notice Use a combination of price oracles.
 * @dev Implements `PriceOracle`.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
contract MasterPriceOracle is Initializable, BasePriceOracle {
  /**
   * @dev Maps underlying token addresses to `PriceOracle` contracts (can be `BasePriceOracle` contracts too).
   */
  mapping(address => BasePriceOracle) public oracles;

  /**
   * @dev Default/fallback `PriceOracle`.
   */
  BasePriceOracle public defaultOracle;

  /**
   * @dev The administrator of this `MasterPriceOracle`.
   */
  address public admin;

  /**
   * @dev Controls if `admin` can overwrite existing assignments of oracles to underlying tokens.
   */
  bool internal noAdminOverwrite;

  /**
   * @dev The Wrapped native asset address.
   */
  address public wtoken;

  /**
   * @dev Maps underlying token addresses to `PriceOracle` contracts (can be `BasePriceOracle` contracts too).
   */
  mapping(address => BasePriceOracle) public fallbackOracles;

  /**
   * @dev Returns a boolean indicating if `admin` can overwrite existing assignments of oracles to underlying tokens.
   */
  function canAdminOverwrite() external view returns (bool) {
    return !noAdminOverwrite;
  }

  /**
   * @dev Event emitted when `admin` is changed.
   */
  event NewAdmin(address oldAdmin, address newAdmin);

  /**
   * @dev Event emitted when the default oracle is changed.
   */
  event NewDefaultOracle(address oldOracle, address newOracle);

  /**
   * @dev Event emitted when an underlying token's oracle is changed.
   */
  event NewOracle(address underlying, address oldOracle, address newOracle);

  /**
   * @dev  Initialize state variables.
   * @param underlyings The underlying ERC20 token addresses to link to `_oracles`.
   * @param _oracles The `PriceOracle` contracts to be assigned to `underlyings`.
   * @param _defaultOracle The default `PriceOracle` contract to use.
   * @param _admin The admin who can assign oracles to underlying tokens.
   * @param _canAdminOverwrite Controls if `admin` can overwrite existing assignments of oracles to underlying tokens.
   * @param _wtoken The Wrapped native asset address
   */
  function initialize(
    address[] memory underlyings,
    BasePriceOracle[] memory _oracles,
    BasePriceOracle _defaultOracle,
    address _admin,
    bool _canAdminOverwrite,
    address _wtoken
  ) external initializer {
    // Input validation
    require(underlyings.length == _oracles.length, "Lengths of both arrays must be equal.");

    // Initialize state variables
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];
      BasePriceOracle newOracle = _oracles[i];
      oracles[underlying] = newOracle;
      emit NewOracle(underlying, address(0), address(newOracle));
    }

    defaultOracle = _defaultOracle;
    admin = _admin;
    noAdminOverwrite = !_canAdminOverwrite;
    wtoken = _wtoken;
  }

  /**
   * @dev Sets `_oracles` for `underlyings`.
   */
  function add(address[] calldata underlyings, BasePriceOracle[] calldata _oracles) external onlyAdmin {
    // Input validation
    require(
      underlyings.length > 0 && underlyings.length == _oracles.length,
      "Lengths of both arrays must be equal and greater than 0."
    );

    // Assign oracles to underlying tokens
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];
      address oldOracle = address(oracles[underlying]);
      if (noAdminOverwrite)
        require(
          oldOracle == address(0),
          "Admin cannot overwrite existing assignments of oracles to underlying tokens."
        );
      BasePriceOracle newOracle = _oracles[i];
      oracles[underlying] = newOracle;
      emit NewOracle(underlying, oldOracle, address(newOracle));
    }
  }

  /**
   * @dev Sets `_oracles` for `underlyings`.
   */
  function addFallbacks(address[] calldata underlyings, BasePriceOracle[] calldata _oracles) external onlyAdmin {
    // Input validation
    require(
      underlyings.length > 0 && underlyings.length == _oracles.length,
      "Lengths of both arrays must be equal and greater than 0."
    );

    // Assign oracles to underlying tokens
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];
      address oldOracle = address(fallbackOracles[underlying]);
      if (noAdminOverwrite)
        require(
          oldOracle == address(0),
          "Admin cannot overwrite existing assignments of oracles to underlying tokens."
        );
      BasePriceOracle newOracle = _oracles[i];
      fallbackOracles[underlying] = newOracle;
      emit NewOracle(underlying, oldOracle, address(newOracle));
    }
  }

  /**
   * @dev Changes the default price oracle
   */
  function setDefaultOracle(BasePriceOracle newOracle) external onlyAdmin {
    BasePriceOracle oldOracle = defaultOracle;
    defaultOracle = newOracle;
    emit NewDefaultOracle(address(oldOracle), address(newOracle));
  }

  /**
   * @dev Changes the admin and emits an event.
   */
  function changeAdmin(address newAdmin) external onlyAdmin {
    address oldAdmin = admin;
    admin = newAdmin;
    emit NewAdmin(oldAdmin, newAdmin);
  }

  /**
   * @dev Modifier that checks if `msg.sender == admin`.
   */
  modifier onlyAdmin() {
    require(msg.sender == admin, "Sender is not the admin.");
    _;
  }

  /**
   * @notice Returns the price in ETH of the token underlying `cToken`.
   * @dev Implements the `PriceOracle` interface for Ionic pools (and Compound v2).
   * @return Price in ETH of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICErc20 cToken) external view override returns (uint256) {
    // Get underlying ERC20 token address
    address underlying = address(ICErc20(address(cToken)).underlying());

    if (underlying == wtoken) return 1e18;

    BasePriceOracle oracle = oracles[underlying];
    BasePriceOracle fallbackOracle = fallbackOracles[underlying];

    if (address(oracle) != address(0)) {
      try oracle.getUnderlyingPrice(cToken) returns (uint256 underlyingPrice) {
        if (underlyingPrice == 0) {
          if (address(fallbackOracle) != address(0)) return fallbackOracle.getUnderlyingPrice(cToken);
        } else {
          return underlyingPrice;
        }
      } catch {
        if (address(fallbackOracle) != address(0)) return fallbackOracle.getUnderlyingPrice(cToken);
      }
    } else {
      if (address(fallbackOracle) != address(0)) return fallbackOracle.getUnderlyingPrice(cToken);
    }
    revert("Price oracle not found for this underlying token address.");
  }

  /**
   * @dev Attempts to return the price in ETH of `underlying` (implements `BasePriceOracle`).
   */
  function price(address underlying) public view override returns (uint256) {
    // Return 1e18 for WETH
    if (underlying == wtoken) return 1e18;

    // Get underlying price from assigned oracle
    BasePriceOracle oracle = oracles[underlying];
    BasePriceOracle fallbackOracle = fallbackOracles[underlying];

    if (address(oracle) != address(0)) {
      try oracle.price(underlying) returns (uint256 underlyingPrice) {
        if (underlyingPrice == 0) {
          if (address(fallbackOracle) != address(0)) return fallbackOracle.price(underlying);
        } else {
          return underlyingPrice;
        }
      } catch {
        if (address(fallbackOracle) != address(0)) return fallbackOracle.price(underlying);
      }
    } else {
      if (address(fallbackOracle) != address(0)) return fallbackOracle.price(underlying);
    }
    revert("Price oracle not found for this underlying token address.");
  }
}

contracts/PoolDirectory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/utils/Create2Upgradeable.sol";

import { IonicComptroller } from "./compound/ComptrollerInterface.sol";
import { BasePriceOracle } from "./oracles/BasePriceOracle.sol";
import { Unitroller } from "./compound/Unitroller.sol";
import "./ionic/SafeOwnableUpgradeable.sol";
import "./ionic/DiamondExtension.sol";

/**
 * @title PoolDirectory
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 * @notice PoolDirectory is a directory for Ionic interest rate pools.
 */
contract PoolDirectory is SafeOwnableUpgradeable {
  /**
   * @dev Initializes a deployer whitelist if desired.
   * @param _enforceDeployerWhitelist Boolean indicating if the deployer whitelist is to be enforced.
   * @param _deployerWhitelist Array of Ethereum accounts to be whitelisted.
   */
  function initialize(bool _enforceDeployerWhitelist, address[] memory _deployerWhitelist) public initializer {
    __SafeOwnable_init(msg.sender);
    enforceDeployerWhitelist = _enforceDeployerWhitelist;
    for (uint256 i = 0; i < _deployerWhitelist.length; i++) deployerWhitelist[_deployerWhitelist[i]] = true;
  }

  /**
   * @dev Struct for a Ionic interest rate pool.
   */
  struct Pool {
    string name;
    address creator;
    address comptroller;
    uint256 blockPosted;
    uint256 timestampPosted;
  }

  /**
   * @dev Array of Ionic interest rate pools.
   */
  Pool[] public pools;

  /**
   * @dev Maps Ethereum accounts to arrays of Ionic pool indexes.
   */
  mapping(address => uint256[]) private _poolsByAccount;

  /**
   * @dev Maps Ionic pool Comptroller addresses to bools indicating if they have been registered via the directory.
   */
  mapping(address => bool) public poolExists;

  /**
   * @dev Emitted when a new Ionic pool is added to the directory.
   */
  event PoolRegistered(uint256 index, Pool pool);

  /**
   * @dev Booleans indicating if the deployer whitelist is enforced.
   */
  bool public enforceDeployerWhitelist;

  /**
   * @dev Maps Ethereum accounts to booleans indicating if they are allowed to deploy pools.
   */
  mapping(address => bool) public deployerWhitelist;

  /**
   * @dev Controls if the deployer whitelist is to be enforced.
   * @param enforce Boolean indicating if the deployer whitelist is to be enforced.
   */
  function _setDeployerWhitelistEnforcement(bool enforce) external onlyOwner {
    enforceDeployerWhitelist = enforce;
  }

  /**
   * @dev Adds/removes Ethereum accounts to the deployer whitelist.
   * @param deployers Array of Ethereum accounts to be whitelisted.
   * @param status Whether to add or remove the accounts.
   */
  function _editDeployerWhitelist(address[] calldata deployers, bool status) external onlyOwner {
    require(deployers.length > 0, "No deployers supplied.");
    for (uint256 i = 0; i < deployers.length; i++) deployerWhitelist[deployers[i]] = status;
  }

  /**
   * @dev Adds a new Ionic pool to the directory (without checking msg.sender).
   * @param name The name of the pool.
   * @param comptroller The pool's Comptroller proxy contract address.
   * @return The index of the registered Ionic pool.
   */
  function _registerPool(string memory name, address comptroller) internal returns (uint256) {
    require(!poolExists[comptroller], "Pool already exists in the directory.");
    require(!enforceDeployerWhitelist || deployerWhitelist[msg.sender], "Sender is not on deployer whitelist.");
    require(bytes(name).length <= 100, "No pool name supplied.");
    Pool memory pool = Pool(name, msg.sender, comptroller, block.number, block.timestamp);
    pools.push(pool);
    _poolsByAccount[msg.sender].push(pools.length - 1);
    poolExists[comptroller] = true;
    emit PoolRegistered(pools.length - 1, pool);
    return pools.length - 1;
  }

  function _deprecatePool(address comptroller) external onlyOwner {
    for (uint256 i = 0; i < pools.length; i++) {
      if (pools[i].comptroller == comptroller) {
        _deprecatePool(i);
        break;
      }
    }
  }

  function _deprecatePool(uint256 index) public onlyOwner {
    Pool storage ionicPool = pools[index];

    require(ionicPool.comptroller != address(0), "pool already deprecated");

    // swap with the last pool of the creator and delete
    uint256[] storage creatorPools = _poolsByAccount[ionicPool.creator];
    for (uint256 i = 0; i < creatorPools.length; i++) {
      if (creatorPools[i] == index) {
        creatorPools[i] = creatorPools[creatorPools.length - 1];
        creatorPools.pop();
        break;
      }
    }

    // leave it to true to deny the re-registering of the same pool
    poolExists[ionicPool.comptroller] = true;

    // nullify the storage
    ionicPool.comptroller = address(0);
    ionicPool.creator = address(0);
    ionicPool.name = "";
    ionicPool.blockPosted = 0;
    ionicPool.timestampPosted = 0;
  }

  /**
   * @dev Deploys a new Ionic pool and adds to the directory.
   * @param name The name of the pool.
   * @param implementation The Comptroller implementation contract address.
   * @param constructorData Encoded construction data for `Unitroller constructor()`
   * @param enforceWhitelist Boolean indicating if the pool's supplier/borrower whitelist is to be enforced.
   * @param closeFactor The pool's close factor (scaled by 1e18).
   * @param liquidationIncentive The pool's liquidation incentive (scaled by 1e18).
   * @param priceOracle The pool's PriceOracle contract address.
   * @return Index of the registered Ionic pool and the Unitroller proxy address.
   */
  function deployPool(
    string memory name,
    address implementation,
    bytes calldata constructorData,
    bool enforceWhitelist,
    uint256 closeFactor,
    uint256 liquidationIncentive,
    address priceOracle
  ) external returns (uint256, address) {
    // Input validation
    require(implementation != address(0), "No Comptroller implementation contract address specified.");
    require(priceOracle != address(0), "No PriceOracle contract address specified.");

    // Deploy Unitroller using msg.sender, name, and block.number as a salt
    bytes memory unitrollerCreationCode = abi.encodePacked(type(Unitroller).creationCode, constructorData);
    address proxy = Create2Upgradeable.deploy(
      0,
      keccak256(abi.encodePacked(msg.sender, name, ++poolsCounter)),
      unitrollerCreationCode
    );

    // Setup the pool
    IonicComptroller comptrollerProxy = IonicComptroller(proxy);
    // Set up the extensions
    comptrollerProxy._upgrade();

    // Set pool parameters
    require(comptrollerProxy._setCloseFactor(closeFactor) == 0, "Failed to set pool close factor.");
    require(
      comptrollerProxy._setLiquidationIncentive(liquidationIncentive) == 0,
      "Failed to set pool liquidation incentive."
    );
    require(comptrollerProxy._setPriceOracle(BasePriceOracle(priceOracle)) == 0, "Failed to set pool price oracle.");

    // Whitelist
    if (enforceWhitelist)
      require(comptrollerProxy._setWhitelistEnforcement(true) == 0, "Failed to enforce supplier/borrower whitelist.");

    // Make msg.sender the admin
    require(comptrollerProxy._setPendingAdmin(msg.sender) == 0, "Failed to set pending admin on Unitroller.");

    // Register the pool with this PoolDirectory
    return (_registerPool(name, proxy), proxy);
  }

  /**
   * @notice Returns `ids` and directory information of all non-deprecated Ionic pools.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getActivePools() public view returns (uint256[] memory, Pool[] memory) {
    uint256 count = 0;
    for (uint256 i = 0; i < pools.length; i++) {
      if (pools[i].comptroller != address(0)) count++;
    }

    Pool[] memory activePools = new Pool[](count);
    uint256[] memory poolIds = new uint256[](count);

    uint256 index = 0;
    for (uint256 i = 0; i < pools.length; i++) {
      if (pools[i].comptroller != address(0)) {
        poolIds[index] = i;
        activePools[index] = pools[i];
        index++;
      }
    }

    return (poolIds, activePools);
  }

  /**
   * @notice Returns arrays of all Ionic pools' data.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getAllPools() public view returns (Pool[] memory) {
    uint256 count = 0;
    for (uint256 i = 0; i < pools.length; i++) {
      if (pools[i].comptroller != address(0)) count++;
    }

    Pool[] memory result = new Pool[](count);

    uint256 index = 0;
    for (uint256 i = 0; i < pools.length; i++) {
      if (pools[i].comptroller != address(0)) {
        result[index++] = pools[i];
      }
    }

    return result;
  }

  /**
   * @notice Returns arrays of all public Ionic pool indexes and data.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getPublicPools() external view returns (uint256[] memory, Pool[] memory) {
    uint256 arrayLength = 0;

    (, Pool[] memory activePools) = getActivePools();
    for (uint256 i = 0; i < activePools.length; i++) {
      try IonicComptroller(activePools[i].comptroller).enforceWhitelist() returns (bool enforceWhitelist) {
        if (enforceWhitelist) continue;
      } catch {}

      arrayLength++;
    }

    uint256[] memory indexes = new uint256[](arrayLength);
    Pool[] memory publicPools = new Pool[](arrayLength);
    uint256 index = 0;

    for (uint256 i = 0; i < activePools.length; i++) {
      try IonicComptroller(activePools[i].comptroller).enforceWhitelist() returns (bool enforceWhitelist) {
        if (enforceWhitelist) continue;
      } catch {}

      indexes[index] = i;
      publicPools[index] = activePools[i];
      index++;
    }

    return (indexes, publicPools);
  }

  /**
   * @notice Returns arrays of all public Ionic pool indexes and data.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getPoolsOfUser(address user) external view returns (uint256[] memory, Pool[] memory) {
    uint256 arrayLength = 0;

    (, Pool[] memory activePools) = getActivePools();
    for (uint256 i = 0; i < activePools.length; i++) {
      try IonicComptroller(activePools[i].comptroller).isUserOfPool(user) returns (bool isUsing) {
        if (!isUsing) continue;
      } catch {}

      arrayLength++;
    }

    uint256[] memory indexes = new uint256[](arrayLength);
    Pool[] memory poolsOfUser = new Pool[](arrayLength);
    uint256 index = 0;

    for (uint256 i = 0; i < activePools.length; i++) {
      try IonicComptroller(activePools[i].comptroller).isUserOfPool(user) returns (bool isUsing) {
        if (!isUsing) continue;
      } catch {}

      indexes[index] = i;
      poolsOfUser[index] = activePools[i];
      index++;
    }

    return (indexes, poolsOfUser);
  }

  /**
   * @notice Returns arrays of Ionic pool indexes and data created by `account`.
   */
  function getPoolsByAccount(address account) external view returns (uint256[] memory, Pool[] memory) {
    uint256[] memory indexes = new uint256[](_poolsByAccount[account].length);
    Pool[] memory accountPools = new Pool[](_poolsByAccount[account].length);
    (, Pool[] memory activePools) = getActivePools();

    for (uint256 i = 0; i < _poolsByAccount[account].length; i++) {
      indexes[i] = _poolsByAccount[account][i];
      accountPools[i] = activePools[_poolsByAccount[account][i]];
    }

    return (indexes, accountPools);
  }

  /**
   * @notice Modify existing Ionic pool name.
   */
  function setPoolName(uint256 index, string calldata name) external {
    IonicComptroller _comptroller = IonicComptroller(pools[index].comptroller);
    require(
      (msg.sender == _comptroller.admin() && _comptroller.adminHasRights()) || msg.sender == owner(),
      "!permission"
    );
    pools[index].name = name;
  }

  /**
   * @dev Maps Ethereum accounts to booleans indicating if they are a whitelisted admin.
   */
  mapping(address => bool) public adminWhitelist;

  /**
   * @dev used as salt for the creation of new pools
   */
  uint256 public poolsCounter;

  /**
   * @dev Event emitted when the admin whitelist is updated.
   */
  event AdminWhitelistUpdated(address[] admins, bool status);

  /**
   * @dev Adds/removes Ethereum accounts to the admin whitelist.
   * @param admins Array of Ethereum accounts to be whitelisted.
   * @param status Whether to add or remove the accounts.
   */
  function _editAdminWhitelist(address[] calldata admins, bool status) external onlyOwner {
    require(admins.length > 0, "No admins supplied.");
    for (uint256 i = 0; i < admins.length; i++) adminWhitelist[admins[i]] = status;
    emit AdminWhitelistUpdated(admins, status);
  }

  /**
   * @notice Returns arrays of all Ionic pool indexes and data with whitelisted admins.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getPublicPoolsByVerification(bool whitelistedAdmin) external view returns (uint256[] memory, Pool[] memory) {
    uint256 arrayLength = 0;

    (, Pool[] memory activePools) = getActivePools();
    for (uint256 i = 0; i < activePools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(activePools[i].comptroller);

      try comptroller.admin() returns (address admin) {
        if (whitelistedAdmin != adminWhitelist[admin]) continue;
      } catch {}

      arrayLength++;
    }

    uint256[] memory indexes = new uint256[](arrayLength);
    Pool[] memory publicPools = new Pool[](arrayLength);
    uint256 index = 0;

    for (uint256 i = 0; i < activePools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(activePools[i].comptroller);

      try comptroller.admin() returns (address admin) {
        if (whitelistedAdmin != adminWhitelist[admin]) continue;
      } catch {}

      indexes[index] = i;
      publicPools[index] = activePools[i];
      index++;
    }

    return (indexes, publicPools);
  }

  /**
   * @notice Returns arrays of all verified Ionic pool indexes and data for which the account is whitelisted
   * @param account who is whitelisted in the returned verified whitelist-enabled pools.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getVerifiedPoolsOfWhitelistedAccount(
    address account
  ) external view returns (uint256[] memory, Pool[] memory) {
    uint256 arrayLength = 0;
    (, Pool[] memory activePools) = getActivePools();
    for (uint256 i = 0; i < activePools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(activePools[i].comptroller);

      try comptroller.enforceWhitelist() returns (bool enforceWhitelist) {
        if (!enforceWhitelist || !comptroller.whitelist(account)) continue;
      } catch {}

      arrayLength++;
    }

    uint256[] memory indexes = new uint256[](arrayLength);
    Pool[] memory accountWhitelistedPools = new Pool[](arrayLength);
    uint256 index = 0;

    for (uint256 i = 0; i < activePools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(activePools[i].comptroller);
      try comptroller.enforceWhitelist() returns (bool enforceWhitelist) {
        if (!enforceWhitelist || !comptroller.whitelist(account)) continue;
      } catch {}

      indexes[index] = i;
      accountWhitelistedPools[index] = activePools[i];
      index++;
    }

    return (indexes, accountWhitelistedPools);
  }
}

contracts/PoolLens.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";

import { IonicComptroller } from "./compound/ComptrollerInterface.sol";
import { BasePriceOracle } from "./oracles/BasePriceOracle.sol";
import { ICErc20 } from "./compound/CTokenInterfaces.sol";

import { PoolDirectory } from "./PoolDirectory.sol";
import { MasterPriceOracle } from "./oracles/MasterPriceOracle.sol";

/**
 * @title PoolLens
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 * @notice PoolLens returns data on Ionic interest rate pools in mass for viewing by dApps, bots, etc.
 */
contract PoolLens is Initializable {
  error ComptrollerError(uint256 errCode);

  /**
   * @notice Initialize the `PoolDirectory` contract object.
   * @param _directory The PoolDirectory
   * @param _name Name for the nativeToken
   * @param _symbol Symbol for the nativeToken
   * @param _hardcodedAddresses Underlying token addresses for a token like maker which are DSToken and/or use bytes32 for `symbol`
   * @param _hardcodedNames Harcoded name for these tokens
   * @param _hardcodedSymbols Harcoded symbol for these tokens
   * @param _uniswapLPTokenNames Harcoded names for underlying uniswap LpToken
   * @param _uniswapLPTokenSymbols Harcoded symbols for underlying uniswap LpToken
   * @param _uniswapLPTokenDisplayNames Harcoded display names for underlying uniswap LpToken
   */
  function initialize(
    PoolDirectory _directory,
    string memory _name,
    string memory _symbol,
    address[] memory _hardcodedAddresses,
    string[] memory _hardcodedNames,
    string[] memory _hardcodedSymbols,
    string[] memory _uniswapLPTokenNames,
    string[] memory _uniswapLPTokenSymbols,
    string[] memory _uniswapLPTokenDisplayNames
  ) public initializer {
    require(address(_directory) != address(0), "PoolDirectory instance cannot be the zero address.");
    require(
      _hardcodedAddresses.length == _hardcodedNames.length && _hardcodedAddresses.length == _hardcodedSymbols.length,
      "Hardcoded addresses lengths not equal."
    );
    require(
      _uniswapLPTokenNames.length == _uniswapLPTokenSymbols.length &&
        _uniswapLPTokenNames.length == _uniswapLPTokenDisplayNames.length,
      "Uniswap LP token names lengths not equal."
    );

    directory = _directory;
    name = _name;
    symbol = _symbol;
    for (uint256 i = 0; i < _hardcodedAddresses.length; i++) {
      hardcoded[_hardcodedAddresses[i]] = TokenData({ name: _hardcodedNames[i], symbol: _hardcodedSymbols[i] });
    }

    for (uint256 i = 0; i < _uniswapLPTokenNames.length; i++) {
      uniswapData.push(
        UniswapData({
          name: _uniswapLPTokenNames[i],
          symbol: _uniswapLPTokenSymbols[i],
          displayName: _uniswapLPTokenDisplayNames[i]
        })
      );
    }
  }

  string public name;
  string public symbol;

  struct TokenData {
    string name;
    string symbol;
  }
  mapping(address => TokenData) hardcoded;

  struct UniswapData {
    string name; // ie "Uniswap V2" or "SushiSwap LP Token"
    string symbol; // ie "UNI-V2" or "SLP"
    string displayName; // ie "SushiSwap" or "Uniswap"
  }
  UniswapData[] uniswapData;

  /**
   * @notice `PoolDirectory` contract object.
   */
  PoolDirectory public directory;

  /**
   * @dev Struct for Ionic pool summary data.
   */
  struct IonicPoolData {
    uint256 totalSupply;
    uint256 totalBorrow;
    address[] underlyingTokens;
    string[] underlyingSymbols;
    bool whitelistedAdmin;
  }

  /**
   * @notice Returns arrays of all public Ionic pool indexes, data, total supply balances (in ETH), total borrow balances (in ETH), arrays of underlying token addresses, arrays of underlying asset symbols, and booleans indicating if retrieving each pool's data failed.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getPublicPoolsWithData()
    external
    returns (uint256[] memory, PoolDirectory.Pool[] memory, IonicPoolData[] memory, bool[] memory)
  {
    (uint256[] memory indexes, PoolDirectory.Pool[] memory publicPools) = directory.getPublicPools();
    (IonicPoolData[] memory data, bool[] memory errored) = getPoolsData(publicPools);
    return (indexes, publicPools, data, errored);
  }

  /**
   * @notice Returns arrays of all whitelisted public Ionic pool indexes, data, total supply balances (in ETH), total borrow balances (in ETH), arrays of underlying token addresses, arrays of underlying asset symbols, and booleans indicating if retrieving each pool's data failed.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getPublicPoolsByVerificationWithData(
    bool whitelistedAdmin
  ) external returns (uint256[] memory, PoolDirectory.Pool[] memory, IonicPoolData[] memory, bool[] memory) {
    (uint256[] memory indexes, PoolDirectory.Pool[] memory publicPools) = directory.getPublicPoolsByVerification(
      whitelistedAdmin
    );
    (IonicPoolData[] memory data, bool[] memory errored) = getPoolsData(publicPools);
    return (indexes, publicPools, data, errored);
  }

  /**
   * @notice Returns arrays of the indexes of Ionic pools created by `account`, data, total supply balances (in ETH), total borrow balances (in ETH), arrays of underlying token addresses, arrays of underlying asset symbols, and booleans indicating if retrieving each pool's data failed.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getPoolsByAccountWithData(
    address account
  ) external returns (uint256[] memory, PoolDirectory.Pool[] memory, IonicPoolData[] memory, bool[] memory) {
    (uint256[] memory indexes, PoolDirectory.Pool[] memory accountPools) = directory.getPoolsByAccount(account);
    (IonicPoolData[] memory data, bool[] memory errored) = getPoolsData(accountPools);
    return (indexes, accountPools, data, errored);
  }

  /**
   * @notice Returns arrays of the indexes of Ionic pools used by `user`, data, total supply balances (in ETH), total borrow balances (in ETH), arrays of underlying token addresses, arrays of underlying asset symbols, and booleans indicating if retrieving each pool's data failed.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getPoolsOIonicrWithData(
    address user
  ) external returns (uint256[] memory, PoolDirectory.Pool[] memory, IonicPoolData[] memory, bool[] memory) {
    (uint256[] memory indexes, PoolDirectory.Pool[] memory userPools) = directory.getPoolsOfUser(user);
    (IonicPoolData[] memory data, bool[] memory errored) = getPoolsData(userPools);
    return (indexes, userPools, data, errored);
  }

  /**
   * @notice Internal function returning arrays of requested Ionic pool indexes, data, total supply balances (in ETH), total borrow balances (in ETH), arrays of underlying token addresses, arrays of underlying asset symbols, and booleans indicating if retrieving each pool's data failed.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getPoolsData(PoolDirectory.Pool[] memory pools) internal returns (IonicPoolData[] memory, bool[] memory) {
    IonicPoolData[] memory data = new IonicPoolData[](pools.length);
    bool[] memory errored = new bool[](pools.length);

    for (uint256 i = 0; i < pools.length; i++) {
      try this.getPoolSummary(IonicComptroller(pools[i].comptroller)) returns (
        uint256 _totalSupply,
        uint256 _totalBorrow,
        address[] memory _underlyingTokens,
        string[] memory _underlyingSymbols,
        bool _whitelistedAdmin
      ) {
        data[i] = IonicPoolData(_totalSupply, _totalBorrow, _underlyingTokens, _underlyingSymbols, _whitelistedAdmin);
      } catch {
        errored[i] = true;
      }
    }

    return (data, errored);
  }

  /**
   * @notice Returns total supply balance (in ETH), total borrow balance (in ETH), underlying token addresses, and underlying token symbols of a Ionic pool.
   */
  function getPoolSummary(
    IonicComptroller comptroller
  ) external returns (uint256, uint256, address[] memory, string[] memory, bool) {
    uint256 totalBorrow = 0;
    uint256 totalSupply = 0;
    ICErc20[] memory cTokens = comptroller.getAllMarkets();
    address[] memory underlyingTokens = new address[](cTokens.length);
    string[] memory underlyingSymbols = new string[](cTokens.length);
    BasePriceOracle oracle = comptroller.oracle();

    for (uint256 i = 0; i < cTokens.length; i++) {
      ICErc20 cToken = cTokens[i];
      (bool isListed, ) = comptroller.markets(address(cToken));
      if (!isListed) continue;
      cToken.accrueInterest();
      uint256 assetTotalBorrow = cToken.totalBorrowsCurrent();
      uint256 assetTotalSupply = cToken.getCash() +
        assetTotalBorrow -
        (cToken.totalReserves() + cToken.totalAdminFees() + cToken.totalIonicFees());
      uint256 underlyingPrice = oracle.getUnderlyingPrice(cToken);
      totalBorrow = totalBorrow + (assetTotalBorrow * underlyingPrice) / 1e18;
      totalSupply = totalSupply + (assetTotalSupply * underlyingPrice) / 1e18;

      underlyingTokens[i] = ICErc20(address(cToken)).underlying();
      (, underlyingSymbols[i]) = getTokenNameAndSymbol(underlyingTokens[i]);
    }

    bool whitelistedAdmin = directory.adminWhitelist(comptroller.admin());
    return (totalSupply, totalBorrow, underlyingTokens, underlyingSymbols, whitelistedAdmin);
  }

  /**
   * @dev Struct for a Ionic pool asset.
   */
  struct PoolAsset {
    address cToken;
    address underlyingToken;
    string underlyingName;
    string underlyingSymbol;
    uint256 underlyingDecimals;
    uint256 underlyingBalance;
    uint256 supplyRatePerBlock;
    uint256 borrowRatePerBlock;
    uint256 totalSupply;
    uint256 totalBorrow;
    uint256 supplyBalance;
    uint256 borrowBalance;
    uint256 liquidity;
    bool membership;
    uint256 exchangeRate; // Price of cTokens in terms of underlying tokens
    uint256 underlyingPrice; // Price of underlying tokens in ETH (scaled by 1e18)
    address oracle;
    uint256 collateralFactor;
    uint256 reserveFactor;
    uint256 adminFee;
    uint256 ionicFee;
    bool borrowGuardianPaused;
    bool mintGuardianPaused;
  }

  /**
   * @notice Returns data on the specified assets of the specified Ionic pool.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   * @param comptroller The Comptroller proxy contract address of the Ionic pool.
   * @param cTokens The cToken contract addresses of the assets to query.
   * @param user The user for which to get account data.
   * @return An array of Ionic pool assets.
   */
  function getPoolAssetsWithData(
    IonicComptroller comptroller,
    ICErc20[] memory cTokens,
    address user
  ) internal returns (PoolAsset[] memory) {
    uint256 arrayLength = 0;

    for (uint256 i = 0; i < cTokens.length; i++) {
      (bool isListed, ) = comptroller.markets(address(cTokens[i]));
      if (isListed) arrayLength++;
    }

    PoolAsset[] memory detailedAssets = new PoolAsset[](arrayLength);
    uint256 index = 0;
    BasePriceOracle oracle = BasePriceOracle(address(comptroller.oracle()));

    for (uint256 i = 0; i < cTokens.length; i++) {
      // Check if market is listed and get collateral factor
      (bool isListed, uint256 collateralFactorMantissa) = comptroller.markets(address(cTokens[i]));
      if (!isListed) continue;

      // Start adding data to PoolAsset
      PoolAsset memory asset;
      ICErc20 cToken = cTokens[i];
      asset.cToken = address(cToken);

      cToken.accrueInterest();

      // Get underlying asset data
      asset.underlyingToken = ICErc20(address(cToken)).underlying();
      ERC20Upgradeable underlying = ERC20Upgradeable(asset.underlyingToken);
      (asset.underlyingName, asset.underlyingSymbol) = getTokenNameAndSymbol(asset.underlyingToken);
      asset.underlyingDecimals = underlying.decimals();
      asset.underlyingBalance = underlying.balanceOf(user);

      // Get cToken data
      asset.supplyRatePerBlock = cToken.supplyRatePerBlock();
      asset.borrowRatePerBlock = cToken.borrowRatePerBlock();
      asset.liquidity = cToken.getCash();
      asset.totalBorrow = cToken.totalBorrowsCurrent();
      asset.totalSupply =
        asset.liquidity +
        asset.totalBorrow -
        (cToken.totalReserves() + cToken.totalAdminFees() + cToken.totalIonicFees());
      asset.supplyBalance = cToken.balanceOfUnderlying(user);
      asset.borrowBalance = cToken.borrowBalanceCurrent(user);
      asset.membership = comptroller.checkMembership(user, cToken);
      asset.exchangeRate = cToken.exchangeRateCurrent(); // We would use exchangeRateCurrent but we already accrue interest above
      asset.underlyingPrice = oracle.price(asset.underlyingToken);

      // Get oracle for this cToken
      asset.oracle = address(oracle);

      try MasterPriceOracle(asset.oracle).oracles(asset.underlyingToken) returns (BasePriceOracle _oracle) {
        asset.oracle = address(_oracle);
      } catch {}

      // More cToken data
      asset.collateralFactor = collateralFactorMantissa;
      asset.reserveFactor = cToken.reserveFactorMantissa();
      asset.adminFee = cToken.adminFeeMantissa();
      asset.ionicFee = cToken.ionicFeeMantissa();
      asset.borrowGuardianPaused = comptroller.borrowGuardianPaused(address(cToken));
      asset.mintGuardianPaused = comptroller.mintGuardianPaused(address(cToken));

      // Add to assets array and increment index
      detailedAssets[index] = asset;
      index++;
    }

    return (detailedAssets);
  }

  function getBorrowCapsPerCollateral(
    ICErc20 borrowedAsset,
    IonicComptroller comptroller
  )
    internal
    view
    returns (
      address[] memory collateral,
      uint256[] memory borrowCapsAgainstCollateral,
      bool[] memory borrowingBlacklistedAgainstCollateral
    )
  {
    ICErc20[] memory poolMarkets = comptroller.getAllMarkets();

    collateral = new address[](poolMarkets.length);
    borrowCapsAgainstCollateral = new uint256[](poolMarkets.length);
    borrowingBlacklistedAgainstCollateral = new bool[](poolMarkets.length);

    for (uint256 i = 0; i < poolMarkets.length; i++) {
      address collateralAddress = address(poolMarkets[i]);
      if (collateralAddress != address(borrowedAsset)) {
        collateral[i] = collateralAddress;
        borrowCapsAgainstCollateral[i] = comptroller.borrowCapForCollateral(address(borrowedAsset), collateralAddress);
        borrowingBlacklistedAgainstCollateral[i] = comptroller.borrowingAgainstCollateralBlacklist(
          address(borrowedAsset),
          collateralAddress
        );
      }
    }
  }

  /**
   * @notice Returns the `name` and `symbol` of `token`.
   * Supports Uniswap V2 and SushiSwap LP tokens as well as MKR.
   * @param token An ERC20 token contract object.
   * @return The `name` and `symbol`.
   */
  function getTokenNameAndSymbol(address token) internal view returns (string memory, string memory) {
    // i.e. MKR is a DSToken and uses bytes32
    if (bytes(hardcoded[token].symbol).length != 0) {
      return (hardcoded[token].name, hardcoded[token].symbol);
    }

    // Get name and symbol from token contract
    ERC20Upgradeable tokenContract = ERC20Upgradeable(token);
    string memory _name = tokenContract.name();
    string memory _symbol = tokenContract.symbol();

    return (_name, _symbol);
  }

  /**
   * @notice Returns the assets of the specified Ionic pool.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   * @param comptroller The Comptroller proxy contract of the Ionic pool.
   * @return An array of Ionic pool assets.
   */
  function getPoolAssetsWithData(IonicComptroller comptroller) external returns (PoolAsset[] memory) {
    return getPoolAssetsWithData(comptroller, comptroller.getAllMarkets(), msg.sender);
  }

  /**
   * @dev Struct for a Ionic pool user.
   */
  struct IonicPoolUser {
    address account;
    uint256 totalBorrow;
    uint256 totalCollateral;
    uint256 health;
  }

  /**
   * @notice Returns arrays of PoolAsset for a specific user
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getPoolAssetsByUser(IonicComptroller comptroller, address user) public returns (PoolAsset[] memory) {
    PoolAsset[] memory assets = getPoolAssetsWithData(comptroller, comptroller.getAssetsIn(user), user);
    return assets;
  }

  /**
   * @notice returns the total supply cap for each asset in the pool
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getSupplyCapsForPool(IonicComptroller comptroller) public view returns (address[] memory, uint256[] memory) {
    ICErc20[] memory poolMarkets = comptroller.getAllMarkets();

    address[] memory assets = new address[](poolMarkets.length);
    uint256[] memory supplyCapsPerAsset = new uint256[](poolMarkets.length);
    for (uint256 i = 0; i < poolMarkets.length; i++) {
      assets[i] = address(poolMarkets[i]);
      supplyCapsPerAsset[i] = comptroller.effectiveSupplyCaps(assets[i]);
    }

    return (assets, supplyCapsPerAsset);
  }

  /**
   * @notice returns the total supply cap for each asset in the pool and the total non-whitelist supplied assets
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getSupplyCapsDataForPool(
    IonicComptroller comptroller
  ) public view returns (address[] memory, uint256[] memory, uint256[] memory) {
    ICErc20[] memory poolMarkets = comptroller.getAllMarkets();

    address[] memory assets = new address[](poolMarkets.length);
    uint256[] memory supplyCapsPerAsset = new uint256[](poolMarkets.length);
    uint256[] memory nonWhitelistedTotalSupply = new uint256[](poolMarkets.length);
    for (uint256 i = 0; i < poolMarkets.length; i++) {
      assets[i] = address(poolMarkets[i]);
      supplyCapsPerAsset[i] = comptroller.effectiveSupplyCaps(assets[i]);
      uint256 assetTotalSupplied = poolMarkets[i].getTotalUnderlyingSupplied();
      uint256 whitelistedSuppliersSupply = comptroller.getWhitelistedSuppliersSupply(assets[i]);
      if (whitelistedSuppliersSupply >= assetTotalSupplied) nonWhitelistedTotalSupply[i] = 0;
      else nonWhitelistedTotalSupply[i] = assetTotalSupplied - whitelistedSuppliersSupply;
    }

    return (assets, supplyCapsPerAsset, nonWhitelistedTotalSupply);
  }

  /**
   * @notice returns the total borrow cap and the per collateral borrowing cap/blacklist for the asset
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getBorrowCapsForAsset(
    ICErc20 asset
  )
    public
    view
    returns (
      address[] memory collateral,
      uint256[] memory borrowCapsPerCollateral,
      bool[] memory collateralBlacklisted,
      uint256 totalBorrowCap
    )
  {
    IonicComptroller comptroller = IonicComptroller(asset.comptroller());
    (collateral, borrowCapsPerCollateral, collateralBlacklisted) = getBorrowCapsPerCollateral(asset, comptroller);
    totalBorrowCap = comptroller.effectiveBorrowCaps(address(asset));
  }

  /**
   * @notice returns the total borrow cap, the per collateral borrowing cap/blacklist for the asset and the total non-whitelist borrows
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getBorrowCapsDataForAsset(
    ICErc20 asset
  )
    public
    view
    returns (
      address[] memory collateral,
      uint256[] memory borrowCapsPerCollateral,
      bool[] memory collateralBlacklisted,
      uint256 totalBorrowCap,
      uint256 nonWhitelistedTotalBorrows
    )
  {
    IonicComptroller comptroller = IonicComptroller(asset.comptroller());
    (collateral, borrowCapsPerCollateral, collateralBlacklisted) = getBorrowCapsPerCollateral(asset, comptroller);
    totalBorrowCap = comptroller.effectiveBorrowCaps(address(asset));
    uint256 totalBorrows = asset.totalBorrowsCurrent();
    uint256 whitelistedBorrowersBorrows = comptroller.getWhitelistedBorrowersBorrows(address(asset));
    if (whitelistedBorrowersBorrows >= totalBorrows) nonWhitelistedTotalBorrows = 0;
    else nonWhitelistedTotalBorrows = totalBorrows - whitelistedBorrowersBorrows;
  }

  /**
   * @notice Returns arrays of Ionic pool indexes and data with a whitelist containing `account`.
   * Note that the whitelist does not have to be enforced.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getWhitelistedPoolsByAccount(
    address account
  ) public view returns (uint256[] memory, PoolDirectory.Pool[] memory) {
    (, PoolDirectory.Pool[] memory pools) = directory.getActivePools();
    uint256 arrayLength = 0;

    for (uint256 i = 0; i < pools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(pools[i].comptroller);

      if (comptroller.whitelist(account)) arrayLength++;
    }

    uint256[] memory indexes = new uint256[](arrayLength);
    PoolDirectory.Pool[] memory accountPools = new PoolDirectory.Pool[](arrayLength);
    uint256 index = 0;

    for (uint256 i = 0; i < pools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(pools[i].comptroller);

      if (comptroller.whitelist(account)) {
        indexes[index] = i;
        accountPools[index] = pools[i];
        index++;
        break;
      }
    }

    return (indexes, accountPools);
  }

  /**
   * @notice Returns arrays of the indexes of Ionic pools with a whitelist containing `account`, data, total supply balances (in ETH), total borrow balances (in ETH), arrays of underlying token addresses, arrays of underlying asset symbols, and booleans indicating if retrieving each pool's data failed.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getWhitelistedPoolsByAccountWithData(
    address account
  ) external returns (uint256[] memory, PoolDirectory.Pool[] memory, IonicPoolData[] memory, bool[] memory) {
    (uint256[] memory indexes, PoolDirectory.Pool[] memory accountPools) = getWhitelistedPoolsByAccount(account);
    (IonicPoolData[] memory data, bool[] memory errored) = getPoolsData(accountPools);
    return (indexes, accountPools, data, errored);
  }

  function getHealthFactor(address user, IonicComptroller pool) external view returns (uint256) {
    return getHealthFactorHypothetical(pool, user, address(0), 0, 0, 0);
  }

  function getHealthFactorHypothetical(
    IonicComptroller pool,
    address account,
    address cTokenModify,
    uint256 redeemTokens,
    uint256 borrowAmount,
    uint256 repayAmount
  ) public view returns (uint256) {
    (uint256 err, uint256 collateralValue, uint256 liquidity, uint256 shortfall) = pool.getHypotheticalAccountLiquidity(
      account,
      cTokenModify,
      redeemTokens,
      borrowAmount,
      repayAmount
    );

    if (err != 0) revert ComptrollerError(err);

    if (shortfall > 0) {
      // HF < 1.0
      return (collateralValue * 1e18) / (collateralValue + shortfall);
    } else {
      // HF >= 1.0
      if (collateralValue <= liquidity) return type(uint256).max;
      else return (collateralValue * 1e18) / (collateralValue - liquidity);
    }
  }
}

contracts/veION/interfaces/IBribeRewards.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

interface IBribeRewards {
  /// @notice A checkpoint for marking balance
  struct Checkpoint {
    uint256 timestamp;
    uint256 balanceOf;
  }

  /// @notice A checkpoint for marking supply
  struct SupplyCheckpoint {
    uint256 timestamp;
    uint256 supply;
  }

  struct EarnedVars {
    uint256 totalReward;
    uint256 reward;
    uint256 supplyValue;
    uint256 epochBalanceValue;
    uint256 currTs;
    uint256 index;
    uint256 numEpochs;
    uint256 overallBalance;
    uint256 overallSupply;
    uint256 historicalPrice;
  }

  error InvalidReward();
  error Unauthorized();
  error InvalidGauge();
  error InvalidEscrowToken();
  error SingleTokenExpected();
  error InvalidVotingEscrow();
  error TokenNotWhitelisted();
  error AmountCannotBeZero();
  error HistoricalPriceNotSet(address lpToken, uint256 epochStart);

  event Deposit(address indexed user, uint256 indexed tokenId, uint256 amount);
  event Withdraw(address indexed user, uint256 indexed tokenId, uint256 amount);
  event RewardNotification(address indexed user, address indexed rewardToken, uint256 indexed epoch, uint256 amount);
  event RewardsClaimed(address indexed user, address indexed rewardToken, uint256 amount);
  event HistoricalPriceSet(uint256 indexed epochTimestamp, address indexed lpToken, uint256 price);

  /// @notice Deposit an amount into the bribe rewards contract for a specific veNFT
  /// @dev Can only be called internally by authorized entities.
  /// @param lpToken  Address of the liquidity pool token
  /// @param amount   Amount to be deposited for the veNFT
  /// @param tokenId  Unique identifier of the veNFT
  function deposit(address lpToken, uint256 amount, uint256 tokenId) external;

  /// @notice Withdraw an amount from the bribe rewards contract for a specific veNFT
  /// @dev Can only be called internally by authorized entities.
  /// @param lpToken  Address of the liquidity pool token
  /// @param amount   Amount to be withdrawn for the veNFT
  /// @param tokenId  Unique identifier of the veNFT
  function withdraw(address lpToken, uint256 amount, uint256 tokenId) external;

  /// @notice Claim the rewards earned by a veNFT holder
  /// @param tokenId  Unique identifier of the veNFT
  /// @param tokens   Array of tokens to claim rewards for
  function getReward(uint256 tokenId, address[] memory tokens) external;

  /// @notice Notify the contract about new rewards for stakers
  /// @param token    Address of the reward token
  /// @param amount   Amount of the reward token to be added
  function notifyRewardAmount(address token, uint256 amount) external;

  /// @notice Get the prior balance index for a veNFT at a specific timestamp
  /// @dev Timestamp must be in the past or present.
  /// @param tokenId      The veNFT token ID to check
  /// @param lpToken      Address of the liquidity pool token
  /// @param timestamp    The timestamp to get the balance at
  /// @return The balance index at the given timestamp
  function getPriorBalanceIndex(uint256 tokenId, address lpToken, uint256 timestamp) external view returns (uint256);

  /// @notice Get the prior supply index at a specific timestamp
  /// @dev Timestamp must be in the past or present.
  /// @param timestamp The timestamp to get the supply index at
  /// @return The supply index at the given timestamp
  function getPriorSupplyIndex(uint256 timestamp, address lpToken) external view returns (uint256);

  /// @notice Calculate the rewards earned for a specific token and veNFT
  /// @param token Address of the reward token
  /// @param tokenId Unique identifier of the veNFT
  /// @return Amount of the reward token earned
  function earned(address token, uint256 tokenId) external view returns (uint256);
}

solmate/auth/Auth.sol.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.8.0;

/// @notice Provides a flexible and updatable auth pattern which is completely separate from application logic.
/// @author Solmate (https://github.com/transmissions11/solmate/blob/main/src/auth/Auth.sol)
/// @author Modified from Dappsys (https://github.com/dapphub/ds-auth/blob/master/src/auth.sol)
abstract contract Auth {
    event OwnerUpdated(address indexed user, address indexed newOwner);

    event AuthorityUpdated(address indexed user, Authority indexed newAuthority);

    address public owner;

    Authority public authority;

    constructor(address _owner, Authority _authority) {
        owner = _owner;
        authority = _authority;

        emit OwnerUpdated(msg.sender, _owner);
        emit AuthorityUpdated(msg.sender, _authority);
    }

    modifier requiresAuth() virtual {
        require(isAuthorized(msg.sender, msg.sig), "UNAUTHORIZED");

        _;
    }

    function isAuthorized(address user, bytes4 functionSig) internal view virtual returns (bool) {
        Authority auth = authority; // Memoizing authority saves us a warm SLOAD, around 100 gas.

        // Checking if the caller is the owner only after calling the authority saves gas in most cases, but be
        // aware that this makes protected functions uncallable even to the owner if the authority is out of order.
        return (address(auth) != address(0) && auth.canCall(user, address(this), functionSig)) || user == owner;
    }

    function setAuthority(Authority newAuthority) public virtual {
        // We check if the caller is the owner first because we want to ensure they can
        // always swap out the authority even if it's reverting or using up a lot of gas.
        require(msg.sender == owner || authority.canCall(msg.sender, address(this), msg.sig));

        authority = newAuthority;

        emit AuthorityUpdated(msg.sender, newAuthority);
    }

    function setOwner(address newOwner) public virtual requiresAuth {
        owner = newOwner;

        emit OwnerUpdated(msg.sender, newOwner);
    }
}

/// @notice A generic interface for a contract which provides authorization data to an Auth instance.
/// @author Solmate (https://github.com/transmissions11/solmate/blob/main/src/auth/Auth.sol)
/// @author Modified from Dappsys (https://github.com/dapphub/ds-auth/blob/master/src/auth.sol)
interface Authority {
    function canCall(
        address user,
        address target,
        bytes4 functionSig
    ) external view returns (bool);
}

