contracts/compound/CarefulMath.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

/**
 * @title Careful Math
 * @author Compound
 * @notice Derived from OpenZeppelin's SafeMath library
 *         https://github.com/OpenZeppelin/openzeppelin-solidity/blob/master/contracts/math/SafeMath.sol
 */
contract CarefulMath {
  /**
   * @dev Possible error codes that we can return
   */
  enum MathError {
    NO_ERROR,
    DIVISION_BY_ZERO,
    INTEGER_OVERFLOW,
    INTEGER_UNDERFLOW
  }

  /**
   * @dev Multiplies two numbers, returns an error on overflow.
   */
  function mulUInt(uint256 a, uint256 b) internal pure returns (MathError, uint256) {
    if (a == 0) {
      return (MathError.NO_ERROR, 0);
    }

    uint256 c;
    unchecked {
      c = a * b;
    }

    if (c / a != b) {
      return (MathError.INTEGER_OVERFLOW, 0);
    } else {
      return (MathError.NO_ERROR, c);
    }
  }

  /**
   * @dev Integer division of two numbers, truncating the quotient.
   */
  function divUInt(uint256 a, uint256 b) internal pure returns (MathError, uint256) {
    if (b == 0) {
      return (MathError.DIVISION_BY_ZERO, 0);
    }

    return (MathError.NO_ERROR, a / b);
  }

  /**
   * @dev Subtracts two numbers, returns an error on overflow (i.e. if subtrahend is greater than minuend).
   */
  function subUInt(uint256 a, uint256 b) internal pure returns (MathError, uint256) {
    if (b <= a) {
      return (MathError.NO_ERROR, a - b);
    } else {
      return (MathError.INTEGER_UNDERFLOW, 0);
    }
  }

  /**
   * @dev Adds two numbers, returns an error on overflow.
   */
  function addUInt(uint256 a, uint256 b) internal pure returns (MathError, uint256) {
    uint256 c;
    unchecked {
      c = a + b;
    }

    if (c >= a) {
      return (MathError.NO_ERROR, c);
    } else {
      return (MathError.INTEGER_OVERFLOW, 0);
    }
  }

  /**
   * @dev add a and b and then subtract c
   */
  function addThenSubUInt(
    uint256 a,
    uint256 b,
    uint256 c
  ) internal pure returns (MathError, uint256) {
    (MathError err0, uint256 sum) = addUInt(a, b);

    if (err0 != MathError.NO_ERROR) {
      return (err0, 0);
    }

    return subUInt(sum, c);
  }
}

contracts/compound/CErc20Delegate.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "./CToken.sol";

/**
 * @title Compound's CErc20Delegate Contract
 * @notice CTokens which wrap an EIP-20 underlying and are delegated to
 * @author Compound
 */
contract CErc20Delegate is CErc20 {
  function _getExtensionFunctions() public pure virtual override returns (bytes4[] memory functionSelectors) {
    uint8 fnsCount = 3;

    bytes4[] memory superFunctionSelectors = super._getExtensionFunctions();
    functionSelectors = new bytes4[](superFunctionSelectors.length + fnsCount);

    for (uint256 i = 0; i < superFunctionSelectors.length; i++) {
      functionSelectors[i] = superFunctionSelectors[i];
    }

    functionSelectors[--fnsCount + superFunctionSelectors.length] = this.contractType.selector;
    functionSelectors[--fnsCount + superFunctionSelectors.length] = this.delegateType.selector;
    functionSelectors[--fnsCount + superFunctionSelectors.length] = this._becomeImplementation.selector;

    require(fnsCount == 0, "use the correct array length");
  }

  /**
   * @notice Called by the delegator on a delegate to initialize it for duty
   */
  function _becomeImplementation(bytes memory) public virtual override {
    require(msg.sender == address(this) || hasAdminRights(), "!self || !admin");
  }

  function delegateType() public pure virtual override returns (uint8) {
    return 1;
  }

  function contractType() external pure virtual override returns (string memory) {
    return "CErc20Delegate";
  }
}

contracts/compound/CErc20Delegator.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "./ComptrollerInterface.sol";
import "./InterestRateModel.sol";
import "../ionic/DiamondExtension.sol";
import { CErc20DelegatorBase, CDelegateInterface } from "./CTokenInterfaces.sol";
import { IFeeDistributor } from "./IFeeDistributor.sol";
import { EIP20Interface } from "./EIP20Interface.sol";

/**
 * @title Compound's CErc20Delegator Contract
 * @notice CTokens which wrap an EIP-20 underlying and delegate to an implementation
 * @author Compound
 */
contract CErc20Delegator is CErc20DelegatorBase, DiamondBase {
  /**
   * @notice Emitted when implementation is changed
   */
  event NewImplementation(address oldImplementation, address newImplementation);

  /**
   * @notice Initialize the new money market
   * @param underlying_ The address of the underlying asset
   * @param comptroller_ The address of the Comptroller
   * @param ionicAdmin_ The FeeDistributor contract address.
   * @param interestRateModel_ The address of the interest rate model
   * @param name_ ERC-20 name of this token
   * @param symbol_ ERC-20 symbol of this token
   */
  constructor(
    address underlying_,
    IonicComptroller comptroller_,
    address payable ionicAdmin_,
    InterestRateModel interestRateModel_,
    string memory name_,
    string memory symbol_,
    uint256 reserveFactorMantissa_,
    uint256 adminFeeMantissa_
  ) {
    require(msg.sender == ionicAdmin_, "!admin");
    uint8 decimals_ = EIP20Interface(underlying_).decimals();
    {
      ionicAdmin = ionicAdmin_;

      // Set initial exchange rate
      initialExchangeRateMantissa = 0.2e18;

      // Set the comptroller
      comptroller = comptroller_;

      // Initialize block number and borrow index (block number mocks depend on comptroller being set)
      accrualBlockNumber = block.number;
      borrowIndex = 1e18;

      // Set the interest rate model (depends on block number / borrow index)
      require(interestRateModel_.isInterestRateModel(), "!notIrm");
      interestRateModel = interestRateModel_;
      emit NewMarketInterestRateModel(InterestRateModel(address(0)), interestRateModel_);

      name = name_;
      symbol = symbol_;
      decimals = decimals_;

      // Set reserve factor
      // Check newReserveFactor ≤ maxReserveFactor
      require(
        reserveFactorMantissa_ + adminFeeMantissa + ionicFeeMantissa <= reserveFactorPlusFeesMaxMantissa,
        "!rf:set"
      );
      reserveFactorMantissa = reserveFactorMantissa_;
      emit NewReserveFactor(0, reserveFactorMantissa_);

      // Set admin fee
      // Sanitize adminFeeMantissa_
      if (adminFeeMantissa_ == type(uint256).max) adminFeeMantissa_ = adminFeeMantissa;
      // Get latest Ionic fee
      uint256 newIonicFeeMantissa = IFeeDistributor(ionicAdmin).interestFeeRate();
      require(
        reserveFactorMantissa + adminFeeMantissa_ + newIonicFeeMantissa <= reserveFactorPlusFeesMaxMantissa,
        "!adminFee:set"
      );
      adminFeeMantissa = adminFeeMantissa_;
      emit NewAdminFee(0, adminFeeMantissa_);
      ionicFeeMantissa = newIonicFeeMantissa;
      emit NewIonicFee(0, newIonicFeeMantissa);

      // The counter starts true to prevent changing it from zero to non-zero (i.e. smaller cost/refund)
      _notEntered = true;
    }

    // Set underlying and sanity check it
    underlying = underlying_;
    EIP20Interface(underlying).totalSupply();
  }

  function implementation() public view returns (address) {
    return LibDiamond.getExtensionForFunction(bytes4(keccak256(bytes("delegateType()"))));
  }

  /**
   * @notice Called by the admin to update the implementation of the delegator
   * @param implementation_ The address of the new implementation for delegation
   * @param becomeImplementationData The encoded bytes data to be passed to _becomeImplementation
   */
  function _setImplementationSafe(address implementation_, bytes calldata becomeImplementationData) external override {
    // Check admin rights
    require(hasAdminRights(), "!admin");

    // Set implementation
    _setImplementationInternal(implementation_, becomeImplementationData);
  }

  /**
   * @dev upgrades the implementation if necessary
   */
  function _upgrade() external override {
    require(msg.sender == address(this) || hasAdminRights(), "!self or admin");

    (bool success, bytes memory data) = address(this).staticcall(abi.encodeWithSignature("delegateType()"));
    require(success, "no delegate type");

    uint8 currentDelegateType = abi.decode(data, (uint8));
    (address latestCErc20Delegate, bytes memory becomeImplementationData) = IFeeDistributor(ionicAdmin)
      .latestCErc20Delegate(currentDelegateType);

    address currentDelegate = implementation();
    if (currentDelegate != latestCErc20Delegate) {
      _setImplementationInternal(latestCErc20Delegate, becomeImplementationData);
    } else {
      // only update the extensions without reinitializing with becomeImplementationData
      _updateExtensions(currentDelegate);
    }
  }

  /**
   * @dev register a logic extension
   * @param extensionToAdd the extension whose functions are to be added
   * @param extensionToReplace the extension whose functions are to be removed/replaced
   */
  function _registerExtension(DiamondExtension extensionToAdd, DiamondExtension extensionToReplace) external override {
    require(msg.sender == address(ionicAdmin), "!unauthorized");
    LibDiamond.registerExtension(extensionToAdd, extensionToReplace);
  }

  /**
   * @dev Internal function to update the implementation of the delegator
   * @param implementation_ The address of the new implementation for delegation
   * @param becomeImplementationData The encoded bytes data to be passed to _becomeImplementation
   */
  function _setImplementationInternal(address implementation_, bytes memory becomeImplementationData) internal {
    address delegateBefore = implementation();
    _updateExtensions(implementation_);

    _functionCall(
      address(this),
      abi.encodeWithSelector(CDelegateInterface._becomeImplementation.selector, becomeImplementationData),
      "!become impl"
    );

    emit NewImplementation(delegateBefore, implementation_);
  }

  function _updateExtensions(address newDelegate) internal {
    address[] memory latestExtensions = IFeeDistributor(ionicAdmin).getCErc20DelegateExtensions(newDelegate);
    address[] memory currentExtensions = LibDiamond.listExtensions();

    // removed the current (old) extensions
    for (uint256 i = 0; i < currentExtensions.length; i++) {
      LibDiamond.removeExtension(DiamondExtension(currentExtensions[i]));
    }
    // add the new extensions
    for (uint256 i = 0; i < latestExtensions.length; i++) {
      LibDiamond.addExtension(DiamondExtension(latestExtensions[i]));
    }
  }

  function _functionCall(
    address target,
    bytes memory data,
    string memory errorMessage
  ) internal returns (bytes memory) {
    (bool success, bytes memory returndata) = target.call(data);

    if (!success) {
      // Look for revert reason and bubble it up if present
      if (returndata.length > 0) {
        // The easiest way to bubble the revert reason is using memory via assembly

        // solhint-disable-next-line no-inline-assembly
        assembly {
          let returndata_size := mload(returndata)
          revert(add(32, returndata), returndata_size)
        }
      } else {
        revert(errorMessage);
      }
    }

    return returndata;
  }
}

contracts/compound/CErc20PluginDelegate..content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "./CErc20Delegate.sol";
import "./EIP20Interface.sol";
import "./IERC4626.sol";
import "../external/uniswap/IUniswapV2Pair.sol";

/**
 * @title Rari's CErc20Plugin's Contract
 * @notice CToken which outsources token logic to a plugin
 * @author Joey Santoro
 *
 * CErc20PluginDelegate deposits and withdraws from a plugin contract
 * It is also capable of delegating reward functionality to a PluginRewardsDistributor
 */
contract CErc20PluginDelegate is CErc20Delegate {
  event NewPluginImplementation(address oldImpl, address newImpl);

  /**
   * @notice Plugin address
   */
  IERC4626 public plugin;

  function _getExtensionFunctions() public pure virtual override returns (bytes4[] memory functionSelectors) {
    uint8 fnsCount = 2;

    bytes4[] memory superFunctionSelectors = super._getExtensionFunctions();
    functionSelectors = new bytes4[](superFunctionSelectors.length + fnsCount);

    for (uint256 i = 0; i < superFunctionSelectors.length; i++) {
      functionSelectors[i] = superFunctionSelectors[i];
    }

    functionSelectors[--fnsCount + superFunctionSelectors.length] = this.plugin.selector;
    functionSelectors[--fnsCount + superFunctionSelectors.length] = this._updatePlugin.selector;

    require(fnsCount == 0, "use the correct array length");
  }

  /**
   * @notice Delegate interface to become the implementation
   * @param data The encoded arguments for becoming
   */
  function _becomeImplementation(bytes memory data) public virtual override {
    require(msg.sender == address(this) || hasAdminRights(), "only self and admins can call _becomeImplementation");

    address _plugin = abi.decode(data, (address));

    if (_plugin == address(0) && address(plugin) != address(0)) {
      // if no new plugin address is given, use the latest implementation
      _plugin = IFeeDistributor(ionicAdmin).latestPluginImplementation(address(plugin));
    }

    if (_plugin != address(0) && _plugin != address(plugin)) {
      _updatePlugin(_plugin);
    }
  }

  /**
   * @notice Update the plugin implementation to a whitelisted implementation
   * @param _plugin The address of the plugin implementation to use
   */
  function _updatePlugin(address _plugin) public {
    require(msg.sender == address(this) || hasAdminRights(), "only self and admins can call _updatePlugin");

    address oldImplementation = address(plugin) != address(0) ? address(plugin) : _plugin;

    if (address(plugin) != address(0) && plugin.balanceOf(address(this)) != 0) {
      plugin.redeem(plugin.balanceOf(address(this)), address(this), address(this));
    }

    plugin = IERC4626(_plugin);

    EIP20Interface(underlying).approve(_plugin, type(uint256).max);

    uint256 amount = EIP20Interface(underlying).balanceOf(address(this));
    if (amount != 0) {
      deposit(amount);
    }

    emit NewPluginImplementation(oldImplementation, _plugin);
  }

  /*** CToken Overrides ***/

  /*** Safe Token ***/

  /**
   * @notice Gets balance of the plugin in terms of the underlying
   * @return The quantity of underlying tokens owned by this contract
   */
  function getCashInternal() internal view override returns (uint256) {
    return plugin.previewRedeem(plugin.balanceOf(address(this)));
  }

  /**
   * @notice Transfer the underlying to the cToken and trigger a deposit
   * @param from Address to transfer funds from
   * @param amount Amount of underlying to transfer
   * @return The actual amount that is transferred
   */
  function doTransferIn(address from, uint256 amount) internal override returns (uint256) {
    // Perform the EIP-20 transfer in
    require(EIP20Interface(underlying).transferFrom(from, address(this), amount), "send");

    deposit(amount);
    return amount;
  }

  function deposit(uint256 amount) internal {
    plugin.deposit(amount, address(this));
  }

  /**
   * @notice Transfer the underlying from plugin to destination
   * @param to Address to transfer funds to
   * @param amount Amount of underlying to transfer
   */
  function doTransferOut(address to, uint256 amount) internal override {
    plugin.withdraw(amount, to, address(this));
  }

  function delegateType() public pure virtual override returns (uint8) {
    return 2;
  }

  function contractType() external pure virtual override returns (string memory) {
    return "CErc20PluginDelegate";
  }
}

