pragma solidity >=0.8.0;

import { ICErc20 } from "./CTokenInterfaces.sol";

abstract contract PriceOracle {
  /// @notice Indicator that this is a PriceOracle contract (for inspection)
  bool public constant isPriceOracle = true;

  /**
   * @notice Get the underlying price of a cToken asset
   * @param cToken The cToken to get the underlying price of
   * @return The underlying asset price mantissa (scaled by 1e18).
   *  Zero means the price is unavailable.
   */
  function getUnderlyingPrice(ICErc20 cToken) external view virtual returns (uint256);
}

contracts/compound/Unitroller.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "./ErrorReporter.sol";
import "./ComptrollerStorage.sol";
import "./Comptroller.sol";
import { DiamondExtension, DiamondBase, LibDiamond } from "../ionic/DiamondExtension.sol";

/**
 * @title Unitroller
 * @dev Storage for the comptroller is at this address, while execution is delegated via the Diamond Extensions
 * CTokens should reference this contract as their comptroller.
 */
contract Unitroller is ComptrollerV3Storage, ComptrollerErrorReporter, DiamondBase {
  /**
   * @notice Event emitted when the admin rights are changed
   */
  event AdminRightsToggled(bool hasRights);

  /**
   * @notice Emitted when pendingAdmin is changed
   */
  event NewPendingAdmin(address oldPendingAdmin, address newPendingAdmin);

  /**
   * @notice Emitted when pendingAdmin is accepted, which means admin is updated
   */
  event NewAdmin(address oldAdmin, address newAdmin);

  constructor(address payable _ionicAdmin) {
    admin = msg.sender;
    ionicAdmin = _ionicAdmin;
  }

  /*** Admin Functions ***/

  /**
   * @notice Toggles admin rights.
   * @param hasRights Boolean indicating if the admin is to have rights.
   * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
   */
  function _toggleAdminRights(bool hasRights) external returns (uint256) {
    if (!hasAdminRights()) {
      return fail(Error.UNAUTHORIZED, FailureInfo.TOGGLE_ADMIN_RIGHTS_OWNER_CHECK);
    }

    // Check that rights have not already been set to the desired value
    if (adminHasRights == hasRights) return uint256(Error.NO_ERROR);

    adminHasRights = hasRights;
    emit AdminRightsToggled(hasRights);

    return uint256(Error.NO_ERROR);
  }

  /**
   * @notice Begins transfer of admin rights. The newPendingAdmin must call `_acceptAdmin` to finalize the transfer.
   * @dev Admin function to begin change of admin. The newPendingAdmin must call `_acceptAdmin` to finalize the transfer.
   * @param newPendingAdmin New pending admin.
   * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
   */
  function _setPendingAdmin(address newPendingAdmin) public returns (uint256) {
    if (!hasAdminRights()) {
      return fail(Error.UNAUTHORIZED, FailureInfo.SET_PENDING_ADMIN_OWNER_CHECK);
    }

    address oldPendingAdmin = pendingAdmin;
    pendingAdmin = newPendingAdmin;
    emit NewPendingAdmin(oldPendingAdmin, newPendingAdmin);

    return uint256(Error.NO_ERROR);
  }

  /**
   * @notice Accepts transfer of admin rights. msg.sender must be pendingAdmin
   * @dev Admin function for pending admin to accept role and update admin
   * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
   */
  function _acceptAdmin() public returns (uint256) {
    // Check caller is pendingAdmin and pendingAdmin ≠ address(0)
    if (msg.sender != pendingAdmin || msg.sender == address(0)) {
      return fail(Error.UNAUTHORIZED, FailureInfo.ACCEPT_ADMIN_PENDING_ADMIN_CHECK);
    }

    // Save current values for inclusion in log
    address oldAdmin = admin;
    address oldPendingAdmin = pendingAdmin;

    admin = pendingAdmin;
    pendingAdmin = address(0);

    emit NewAdmin(oldAdmin, admin);
    emit NewPendingAdmin(oldPendingAdmin, pendingAdmin);

    return uint256(Error.NO_ERROR);
  }

  function comptrollerImplementation() public view returns (address) {
    return LibDiamond.getExtensionForFunction(bytes4(keccak256(bytes("_deployMarket(uint8,bytes,bytes,uint256)"))));
  }

  /**
   * @dev upgrades the implementation if necessary
   */
  function _upgrade() external {
    require(msg.sender == address(this) || hasAdminRights(), "!self || !admin");

    address currentImplementation = comptrollerImplementation();
    address latestComptrollerImplementation = IFeeDistributor(ionicAdmin).latestComptrollerImplementation(
      currentImplementation
    );

    _updateExtensions(latestComptrollerImplementation);

    if (currentImplementation != latestComptrollerImplementation) {
      // reinitialize
      _functionCall(address(this), abi.encodeWithSignature("_becomeImplementation()"), "!become impl");
    }
  }

  function _functionCall(
    address target,
    bytes memory data,
    string memory errorMessage
  ) internal returns (bytes memory) {
    (bool success, bytes memory returndata) = target.call(data);

    if (!success) {
      // Look for revert reason and bubble it up if present
      if (returndata.length > 0) {
        // The easiest way to bubble the revert reason is using memory via assembly

        // solhint-disable-next-line no-inline-assembly
        assembly {
          let returndata_size := mload(returndata)
          revert(add(32, returndata), returndata_size)
        }
      } else {
        revert(errorMessage);
      }
    }

    return returndata;
  }

  function _updateExtensions(address currentComptroller) internal {
    address[] memory latestExtensions = IFeeDistributor(ionicAdmin).getComptrollerExtensions(currentComptroller);
    address[] memory currentExtensions = LibDiamond.listExtensions();

    // removed the current (old) extensions
    for (uint256 i = 0; i < currentExtensions.length; i++) {
      LibDiamond.removeExtension(DiamondExtension(currentExtensions[i]));
    }
    // add the new extensions
    for (uint256 i = 0; i < latestExtensions.length; i++) {
      LibDiamond.addExtension(DiamondExtension(latestExtensions[i]));
    }
  }

  /**
   * @dev register a logic extension
   * @param extensionToAdd the extension whose functions are to be added
   * @param extensionToReplace the extension whose functions are to be removed/replaced
   */
  function _registerExtension(DiamondExtension extensionToAdd, DiamondExtension extensionToReplace) external override {
    require(hasAdminRights(), "!unauthorized");
    LibDiamond.registerExtension(extensionToAdd, extensionToReplace);
  }
}

contracts/EmissionsManager.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import { ERC20 } from "solmate/tokens/ERC20.sol";
import { SafeTransferLib } from "solmate/utils/SafeTransferLib.sol";

import { Ownable2StepUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/access/Ownable2StepUpgradeable.sol";
import { IonicFlywheelCore } from "./ionic/strategies/flywheel/IonicFlywheelCore.sol";
import { IonicComptroller } from "./compound/ComptrollerInterface.sol";
import { BasePriceOracle } from "./oracles/BasePriceOracle.sol";
import { ICErc20 } from "./compound/CTokenInterfaces.sol";
import { PoolDirectory } from "./PoolDirectory.sol";
import { IEmissionsManager } from "./IEmissionsManager.sol";
import { IveION } from "./veION/interfaces/IveION.sol";

interface Oracle {
  function getUnderlyingPrice(address) external returns (uint256);
}

contract EmissionsManager is IEmissionsManager, Ownable2StepUpgradeable {
  using SafeTransferLib for ERC20;

  address public protocolAddress;
  uint256 public collateralBp;
  PoolDirectory public fpd;
  ERC20 public rewardToken;
  address public veION;

  bytes public nonBlacklistableTargetBytecode;
  mapping(address => bool) public isBlacklisted;
  mapping(address => bool) public nonBlacklistable;

  uint256 public constant MAXIMUM_BASIS_POINTS = 10_000;
  uint256 public constant BLACKLISTER_SHARE = 80;
  uint256 public constant PROTOCOL_SHARE = 20;

  modifier onlyBlacklistableBytecode(address _addr) {
    bytes memory code = _addr.code;
    require(keccak256(code) != keccak256(nonBlacklistableTargetBytecode), "Non-blacklistable bytecode");
    _;
  }

  constructor() {
    _disableInitializers(); // Locks the implementation contract from being initialized
  }

  function initialize(
    PoolDirectory _fpd,
    address _protocolAddress,
    ERC20 _rewardToken,
    uint256 _collateralBp,
    bytes memory _nonBlacklistableTargetBytecode
  ) public initializer {
    if (address(_fpd) == address(0)) revert InvalidPoolDirectoryAaddress();
    if (_protocolAddress == address(0)) revert InvalidProtocolAddress();
    if (address(_rewardToken) == address(0)) revert InvalidRewardTokenAddress();
    if (_collateralBp >= MAXIMUM_BASIS_POINTS) revert CollateralBasisPointsExceedMaximum();

    __Ownable2Step_init();
    protocolAddress = _protocolAddress;
    fpd = _fpd;
    rewardToken = _rewardToken;
    collateralBp = _collateralBp;
    nonBlacklistableTargetBytecode = _nonBlacklistableTargetBytecode;

    emit Initialized(_protocolAddress, address(_rewardToken), _collateralBp, _nonBlacklistableTargetBytecode);
  }

  function setVeIon(IveION _veIon) external onlyOwner {
    if (address(_veIon) == address(0)) revert InvalidVeIONAddress();
    veION = address(_veIon);
    emit VeIonSet(address(_veIon));
  }

  function setCollateralBp(uint256 _collateralBp) external onlyOwner {
    if (_collateralBp >= MAXIMUM_BASIS_POINTS) revert MaximumLimitExceeded();
    collateralBp = _collateralBp;
    emit CollateralBpSet(_collateralBp);
  }

  function setNonBlacklistableAddress(address _user, bool _isNonBlacklistable) external onlyOwner {
    nonBlacklistable[_user] = _isNonBlacklistable;
    emit NonBlacklistableAddressSet(_user, _isNonBlacklistable);
  }

  function setNonBlacklistableTargetBytecode(bytes memory _newBytecode) external onlyOwner {
    nonBlacklistableTargetBytecode = _newBytecode;
    emit NonBlacklistableTargetBytecodeSet(_newBytecode);
  }

  function setProtocolAddress(address _newProtocolAddress) external onlyOwner {
    require(_newProtocolAddress != address(0), "Invalid address");
    protocolAddress = _newProtocolAddress;
  }

  function _getUserTotalCollateral(address _user) internal view returns (uint256) {
    uint256 totalColateralInETH = 0;
    (, PoolDirectory.Pool[] memory pools) = fpd.getActivePools();
    uint256 poolsLength = pools.length;
    for (uint256 i = 0; i < poolsLength; i++) {
      IonicComptroller comptroller = IonicComptroller(pools[i].comptroller);
      BasePriceOracle oracle = comptroller.oracle();
      ICErc20[] memory cTokens = comptroller.getAssetsIn(_user);
      uint256 cTokensLength = cTokens.length;
      for (uint256 j = 0; j < cTokensLength; j++) {
        uint256 supplyBalance = cTokens[j].balanceOfUnderlying(_user);
        uint256 collateralInETH = (supplyBalance * oracle.getUnderlyingPrice(cTokens[j])) / 1e18;
        totalColateralInETH += collateralInETH;
      }
    }
    return totalColateralInETH;
  }

  function getUserTotalCollateral(address _user) external view returns (uint256) {
    return _getUserTotalCollateral(_user);
  }

  function _checkCollateralRatio(address _user) internal view returns (bool) {
    uint256 userCollateralValue = _getUserTotalCollateral(_user);
    if (userCollateralValue == 0) return true;
    uint256 userLPValue = IveION(veION).getTotalEthValueOfTokens(_user);
    if ((userLPValue * MAXIMUM_BASIS_POINTS) / userCollateralValue >= collateralBp) {
      return true;
    } else return false;
  }

  function reportUser(address _user) external onlyBlacklistableBytecode(_user) {
    require(!nonBlacklistable[_user], "Non-blacklistable user");
    require(!isBlacklisted[_user], "Already blacklisted");
    require(!_checkCollateralRatio(_user), "LP balance above threshold");
    isBlacklisted[_user] = true;
    _blacklistUserAndClaimEmissions(_user);
  }

  function whitelistUser(address _user) external {
    require(isBlacklisted[_user], "Already whitelisted");
    require(_checkCollateralRatio(_user), "LP balance below threshold");
    isBlacklisted[_user] = false;
    (, PoolDirectory.Pool[] memory pools) = fpd.getActivePools();
    uint256 poolsLength = pools.length;
    for (uint256 i = 0; i < poolsLength; i++) {
      IonicComptroller comptroller = IonicComptroller(pools[i].comptroller);
      ICErc20[] memory cTokens = comptroller.getAssetsIn(_user);
      uint256 cTokensLength = cTokens.length;
      for (uint256 j = 0; j < cTokensLength; j++) {
        address[] memory flywheelAddresses = comptroller.getAccruingFlywheels();
        uint256 flywheelAddressesLength = flywheelAddresses.length;
        for (uint256 k = 0; k < flywheelAddressesLength; k++) {
          IonicFlywheelCore flywheel = IonicFlywheelCore(flywheelAddresses[k]);
          if (address(flywheel.rewardToken()) == address(rewardToken)) {
            flywheel.whitelistUser(ERC20(address(cTokens[j])), _user);
            flywheel.accrue(ERC20(address(cTokens[j])), _user);
          }
        }
      }
    }
  }

  function isUserBlacklisted(address _user) external view returns (bool) {
    return isBlacklisted[_user];
  }

  function isUserBlacklistable(address _user) external view returns (bool) {
    if (nonBlacklistable[_user] || keccak256(_user.code) == keccak256(nonBlacklistableTargetBytecode)) {
      return false;
    }
    return !_checkCollateralRatio(_user) && !isBlacklisted[_user];
  }

  function _blacklistUserAndClaimEmissions(address user) internal {
    uint256 balanceBefore = ERC20(rewardToken).balanceOf(address(this));
    (, PoolDirectory.Pool[] memory pools) = fpd.getActivePools();
    uint256 poolsLength = pools.length;
    for (uint256 i = 0; i < poolsLength; i++) {
      IonicComptroller comptroller = IonicComptroller(pools[i].comptroller);
      ERC20[] memory markets;
      {
        ICErc20[] memory cerc20s = comptroller.getAllMarkets();
        uint256 cerc20sLength = cerc20s.length;
        markets = new ERC20[](cerc20sLength);
        for (uint256 j = 0; j < cerc20sLength; j++) {
          markets[j] = ERC20(address(cerc20s[j]));
        }
      }

      address[] memory flywheelAddresses = comptroller.getAccruingFlywheels();
      uint256 flywheelAddressesLength = flywheelAddresses.length;
      for (uint256 k = 0; k < flywheelAddressesLength; k++) {
        IonicFlywheelCore flywheel = IonicFlywheelCore(flywheelAddresses[k]);
        if (address(flywheel.rewardToken()) == address(rewardToken)) {
          uint256 marketsLength = markets.length;
          for (uint256 m = 0; m < marketsLength; m++) {
            flywheel.accrue(markets[m], user);
            flywheel.updateBlacklistBalances(markets[m], user);
          }
          flywheel.takeRewardsFromUser(user, address(this));
        }
      }
    }

    uint256 balanceAfter = ERC20(rewardToken).balanceOf(address(this));
    uint256 totalClaimed = balanceAfter - balanceBefore;
    if (totalClaimed > 0) {
      rewardToken.safeTransfer(msg.sender, (totalClaimed * BLACKLISTER_SHARE) / 100);
      rewardToken.safeTransfer(protocolAddress, (totalClaimed * PROTOCOL_SHARE) / 100);
    }
  }
}

contracts/external/aerodrome/IAerodromeR.content:
// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.10;

/**
 * @dev Interface of the ERC20 standard as defined in the EIP.
 */
interface IERC20_Router {
  /**
   * @dev Emitted when `value` tokens are moved from one account (`from`) to
   * another (`to`).
   *
   * Note that `value` may be zero.
   */
  event Transfer(address indexed from, address indexed to, uint256 value);

  /**
   * @dev Emitted when the allowance of a `spender` for an `owner` is set by
   * a call to {approve}. `value` is the new allowance.
   */
  event Approval(address indexed owner, address indexed spender, uint256 value);

  /**
   * @dev Returns the amount of tokens in existence.
   */
  function totalSupply() external view returns (uint256);

  /**
   * @dev Returns the amount of tokens owned by `account`.
   */
  function balanceOf(address account) external view returns (uint256);

  /**
   * @dev Moves `amount` tokens from the caller's account to `to`.
   *
   * Returns a boolean value indicating whether the operation succeeded.
   *
   * Emits a {Transfer} event.
   */
  function transfer(address to, uint256 amount) external returns (bool);

  /**
   * @dev Returns the remaining number of tokens that `spender` will be
   * allowed to spend on behalf of `owner` through {transferFrom}. This is
   * zero by default.
   *
   * This value changes when {approve} or {transferFrom} are called.
   */
  function allowance(address owner, address spender) external view returns (uint256);

  /**
   * @dev Sets `amount` as the allowance of `spender` over the caller's tokens.
   *
   * Returns a boolean value indicating whether the operation succeeded.
   *
   * IMPORTANT: Beware that changing an allowance with this method brings the risk
   * that someone may use both the old and the new allowance by unfortunate
   * transaction ordering. One possible solution to mitigate this race
   * condition is to first reduce the spender's allowance to 0 and set the
   * desired value afterwards:
   * https://github.com/ethereum/EIPs/issues/20#issuecomment-263524729
   *
   * Emits an {Approval} event.
   */
  function approve(address spender, uint256 amount) external returns (bool);

  /**
   * @dev Moves `amount` tokens from `from` to `to` using the
   * allowance mechanism. `amount` is then deducted from the caller's
   * allowance.
   *
   * Returns a boolean value indicating whether the operation succeeded.
   *
   * Emits a {Transfer} event.
   */
  function transferFrom(address from, address to, uint256 amount) external returns (bool);
}

interface IWETH is IERC20_Router {
  function deposit() external payable;

  function withdraw(uint256) external;
}

interface IRouter_Aerodrome {
  struct Route {
    address from;
    address to;
    bool stable;
    address factory;
  }

  error ETHTransferFailed();
  error Expired();
  error InsufficientAmount();
  error InsufficientAmountA();
  error InsufficientAmountB();
  error InsufficientAmountADesired();
  error InsufficientAmountBDesired();
  error InsufficientAmountAOptimal();
  error InsufficientLiquidity();
  error InsufficientOutputAmount();
  error InvalidAmountInForETHDeposit();
  error InvalidTokenInForETHDeposit();
  error InvalidPath();
  error InvalidRouteA();
  error InvalidRouteB();
  error OnlyWETH();
  error PoolDoesNotExist();
  error PoolFactoryDoesNotExist();
  error SameAddresses();
  error ZeroAddress();

  /// @notice Address of FactoryRegistry.sol
  function factoryRegistry() external view returns (address);

  /// @notice Address of Protocol PoolFactory.sol
  function defaultFactory() external view returns (address);

  /// @notice Address of Voter.sol
  function voter() external view returns (address);

  /// @notice Interface of WETH contract used for WETH => ETH wrapping/unwrapping
  function weth() external view returns (IWETH);

  /// @dev Represents Ether. Used by zapper to determine whether to return assets as ETH/WETH.
  function ETHER() external view returns (address);

  /// @dev Struct containing information necessary to zap in and out of pools
  /// @param tokenA           .
  /// @param tokenB           .
  /// @param stable           Stable or volatile pool
  /// @param factory          factory of pool
  /// @param amountOutMinA    Minimum amount expected from swap leg of zap via routesA
  /// @param amountOutMinB    Minimum amount expected from swap leg of zap via routesB
  /// @param amountAMin       Minimum amount of tokenA expected from liquidity leg of zap
  /// @param amountBMin       Minimum amount of tokenB expected from liquidity leg of zap
  struct Zap {
    address tokenA;
    address tokenB;
    bool stable;
    address factory;
    uint256 amountOutMinA;
    uint256 amountOutMinB;
    uint256 amountAMin;
    uint256 amountBMin;
  }

  /// @notice Sort two tokens by which address value is less than the other
  /// @param tokenA   Address of token to sort
  /// @param tokenB   Address of token to sort
  /// @return token0  Lower address value between tokenA and tokenB
  /// @return token1  Higher address value between tokenA and tokenB
  function sortTokens(address tokenA, address tokenB) external pure returns (address token0, address token1);

  /// @notice Calculate the address of a pool by its' factory.
  ///         Used by all Router functions containing a `Route[]` or `_factory` argument.
  ///         Reverts if _factory is not approved by the FactoryRegistry
  /// @dev Returns a randomly generated address for a nonexistent pool
  /// @param tokenA   Address of token to query
  /// @param tokenB   Address of token to query
  /// @param stable   True if pool is stable, false if volatile
  /// @param _factory Address of factory which created the pool
  function poolFor(address tokenA, address tokenB, bool stable, address _factory) external view returns (address pool);

  /// @notice Fetch and sort the reserves for a pool
  /// @param tokenA       .
  /// @param tokenB       .
  /// @param stable       True if pool is stable, false if volatile
  /// @param _factory     Address of PoolFactory for tokenA and tokenB
  /// @return reserveA    Amount of reserves of the sorted token A
  /// @return reserveB    Amount of reserves of the sorted token B
  function getReserves(
    address tokenA,
    address tokenB,
    bool stable,
    address _factory
  ) external view returns (uint256 reserveA, uint256 reserveB);

  /// @notice Perform chained getAmountOut calculations on any number of pools
  function getAmountsOut(uint256 amountIn, Route[] memory routes) external view returns (uint256[] memory amounts);

  // **** ADD LIQUIDITY ****

  /// @notice Quote the amount deposited into a Pool
  /// @param tokenA           .
  /// @param tokenB           .
  /// @param stable           True if pool is stable, false if volatile
  /// @param _factory         Address of PoolFactory for tokenA and tokenB
  /// @param amountADesired   Amount of tokenA desired to deposit
  /// @param amountBDesired   Amount of tokenB desired to deposit
  /// @return amountA         Amount of tokenA to actually deposit
  /// @return amountB         Amount of tokenB to actually deposit
  /// @return liquidity       Amount of liquidity token returned from deposit
  function quoteAddLiquidity(
    address tokenA,
    address tokenB,
    bool stable,
    address _factory,
    uint256 amountADesired,
    uint256 amountBDesired
  ) external view returns (uint256 amountA, uint256 amountB, uint256 liquidity);

  /// @notice Quote the amount of liquidity removed from a Pool
  /// @param tokenA       .
  /// @param tokenB       .
  /// @param stable       True if pool is stable, false if volatile
  /// @param _factory     Address of PoolFactory for tokenA and tokenB
  /// @param liquidity    Amount of liquidity to remove
  /// @return amountA     Amount of tokenA received
  /// @return amountB     Amount of tokenB received
  function quoteRemoveLiquidity(
    address tokenA,
    address tokenB,
    bool stable,
    address _factory,
    uint256 liquidity
  ) external view returns (uint256 amountA, uint256 amountB);

  /// @notice Add liquidity of two tokens to a Pool
  /// @param tokenA           .
  /// @param tokenB           .
  /// @param stable           True if pool is stable, false if volatile
  /// @param amountADesired   Amount of tokenA desired to deposit
  /// @param amountBDesired   Amount of tokenB desired to deposit
  /// @param amountAMin       Minimum amount of tokenA to deposit
  /// @param amountBMin       Minimum amount of tokenB to deposit
  /// @param to               Recipient of liquidity token
  /// @param deadline         Deadline to receive liquidity
  /// @return amountA         Amount of tokenA to actually deposit
  /// @return amountB         Amount of tokenB to actually deposit
  /// @return liquidity       Amount of liquidity token returned from deposit
  function addLiquidity(
    address tokenA,
    address tokenB,
    bool stable,
    uint256 amountADesired,
    uint256 amountBDesired,
    uint256 amountAMin,
    uint256 amountBMin,
    address to,
    uint256 deadline
  ) external returns (uint256 amountA, uint256 amountB, uint256 liquidity);

  /// @notice Add liquidity of a token and WETH (transferred as ETH) to a Pool
  /// @param token                .
  /// @param stable               True if pool is stable, false if volatile
  /// @param amountTokenDesired   Amount of token desired to deposit
  /// @param amountTokenMin       Minimum amount of token to deposit
  /// @param amountETHMin         Minimum amount of ETH to deposit
  /// @param to                   Recipient of liquidity token
  /// @param deadline             Deadline to add liquidity
  /// @return amountToken         Amount of token to actually deposit
  /// @return amountETH           Amount of tokenETH to actually deposit
  /// @return liquidity           Amount of liquidity token returned from deposit
  function addLiquidityETH(
    address token,
    bool stable,
    uint256 amountTokenDesired,
    uint256 amountTokenMin,
    uint256 amountETHMin,
    address to,
    uint256 deadline
  ) external payable returns (uint256 amountToken, uint256 amountETH, uint256 liquidity);

  // **** REMOVE LIQUIDITY ****

  /// @notice Remove liquidity of two tokens from a Pool
  /// @param tokenA       .
  /// @param tokenB       .
  /// @param stable       True if pool is stable, false if volatile
  /// @param liquidity    Amount of liquidity to remove
  /// @param amountAMin   Minimum amount of tokenA to receive
  /// @param amountBMin   Minimum amount of tokenB to receive
  /// @param to           Recipient of tokens received
  /// @param deadline     Deadline to remove liquidity
  /// @return amountA     Amount of tokenA received
  /// @return amountB     Amount of tokenB received
  function removeLiquidity(
    address tokenA,
    address tokenB,
    bool stable,
    uint256 liquidity,
    uint256 amountAMin,
    uint256 amountBMin,
    address to,
    uint256 deadline
  ) external returns (uint256 amountA, uint256 amountB);

  /// @notice Remove liquidity of a token and WETH (returned as ETH) from a Pool
  /// @param token            .
  /// @param stable           True if pool is stable, false if volatile
  /// @param liquidity        Amount of liquidity to remove
  /// @param amountTokenMin   Minimum amount of token to receive
  /// @param amountETHMin     Minimum amount of ETH to receive
  /// @param to               Recipient of liquidity token
  /// @param deadline         Deadline to receive liquidity
  /// @return amountToken     Amount of token received
  /// @return amountETH       Amount of ETH received
  function removeLiquidityETH(
    address token,
    bool stable,
    uint256 liquidity,
    uint256 amountTokenMin,
    uint256 amountETHMin,
    address to,
    uint256 deadline
  ) external returns (uint256 amountToken, uint256 amountETH);

  /// @notice Remove liquidity of a fee-on-transfer token and WETH (returned as ETH) from a Pool
  /// @param token            .
  /// @param stable           True if pool is stable, false if volatile
  /// @param liquidity        Amount of liquidity to remove
  /// @param amountTokenMin   Minimum amount of token to receive
  /// @param amountETHMin     Minimum amount of ETH to receive
  /// @param to               Recipient of liquidity token
  /// @param deadline         Deadline to receive liquidity
  /// @return amountETH       Amount of ETH received
  function removeLiquidityETHSupportingFeeOnTransferTokens(
    address token,
    bool stable,
    uint256 liquidity,
    uint256 amountTokenMin,
    uint256 amountETHMin,
    address to,
    uint256 deadline
  ) external returns (uint256 amountETH);

  // **** SWAP ****

  /// @notice Swap one token for another
  /// @param amountIn     Amount of token in
  /// @param amountOutMin Minimum amount of desired token received
  /// @param routes       Array of trade routes used in the swap
  /// @param to           Recipient of the tokens received
  /// @param deadline     Deadline to receive tokens
  /// @return amounts     Array of amounts returned per route
  function swapExactTokensForTokens(
    uint256 amountIn,
    uint256 amountOutMin,
    Route[] calldata routes,
    address to,
    uint256 deadline
  ) external returns (uint256[] memory amounts);

  /// @notice Swap ETH for a token
  /// @param amountOutMin Minimum amount of desired token received
  /// @param routes       Array of trade routes used in the swap
  /// @param to           Recipient of the tokens received
  /// @param deadline     Deadline to receive tokens
  /// @return amounts     Array of amounts returned per route
  function swapExactETHForTokens(
    uint256 amountOutMin,
    Route[] calldata routes,
    address to,
    uint256 deadline
  ) external payable returns (uint256[] memory amounts);

  /// @notice Swap a token for WETH (returned as ETH)
  /// @param amountIn     Amount of token in
  /// @param amountOutMin Minimum amount of desired ETH
  /// @param routes       Array of trade routes used in the swap
  /// @param to           Recipient of the tokens received
  /// @param deadline     Deadline to receive tokens
  /// @return amounts     Array of amounts returned per route
  function swapExactTokensForETH(
    uint256 amountIn,
    uint256 amountOutMin,
    Route[] calldata routes,
    address to,
    uint256 deadline
  ) external returns (uint256[] memory amounts);

  /// @notice Swap one token for another without slippage protection
  /// @return amounts     Array of amounts to swap  per route
  /// @param routes       Array of trade routes used in the swap
  /// @param to           Recipient of the tokens received
  /// @param deadline     Deadline to receive tokens
  function UNSAFE_swapExactTokensForTokens(
    uint256[] memory amounts,
    Route[] calldata routes,
    address to,
    uint256 deadline
  ) external returns (uint256[] memory);

  // **** SWAP (supporting fee-on-transfer tokens) ****

  /// @notice Swap one token for another supporting fee-on-transfer tokens
  /// @param amountIn     Amount of token in
  /// @param amountOutMin Minimum amount of desired token received
  /// @param routes       Array of trade routes used in the swap
  /// @param to           Recipient of the tokens received
  /// @param deadline     Deadline to receive tokens
  function swapExactTokensForTokensSupportingFeeOnTransferTokens(
    uint256 amountIn,
    uint256 amountOutMin,
    Route[] calldata routes,
    address to,
    uint256 deadline
  ) external;

  /// @notice Swap ETH for a token supporting fee-on-transfer tokens
  /// @param amountOutMin Minimum amount of desired token received
  /// @param routes       Array of trade routes used in the swap
  /// @param to           Recipient of the tokens received
  /// @param deadline     Deadline to receive tokens
  function swapExactETHForTokensSupportingFeeOnTransferTokens(
    uint256 amountOutMin,
    Route[] calldata routes,
    address to,
    uint256 deadline
  ) external payable;

  /// @notice Swap a token for WETH (returned as ETH) supporting fee-on-transfer tokens
  /// @param amountIn     Amount of token in
  /// @param amountOutMin Minimum amount of desired ETH
  /// @param routes       Array of trade routes used in the swap
  /// @param to           Recipient of the tokens received
  /// @param deadline     Deadline to receive tokens
  function swapExactTokensForETHSupportingFeeOnTransferTokens(
    uint256 amountIn,
    uint256 amountOutMin,
    Route[] calldata routes,
    address to,
    uint256 deadline
  ) external;

  /// @notice Zap a token A into a pool (B, C). (A can be equal to B or C).
  ///         Supports standard ERC20 tokens only (i.e. not fee-on-transfer tokens etc).
  ///         Slippage is required for the initial swap.
  ///         Additional slippage may be required when adding liquidity as the
  ///         price of the token may have changed.
  /// @param tokenIn      Token you are zapping in from (i.e. input token).
  /// @param amountInA    Amount of input token you wish to send down routesA
  /// @param amountInB    Amount of input token you wish to send down routesB
  /// @param zapInPool    Contains zap struct information. See Zap struct.
  /// @param routesA      Route used to convert input token to tokenA
  /// @param routesB      Route used to convert input token to tokenB
  /// @param to           Address you wish to mint liquidity to.
  /// @param stake        Auto-stake liquidity in corresponding gauge.
  /// @return liquidity   Amount of LP tokens created from zapping in.
  function zapIn(
    address tokenIn,
    uint256 amountInA,
    uint256 amountInB,
    Zap calldata zapInPool,
    Route[] calldata routesA,
    Route[] calldata routesB,
    address to,
    bool stake
  ) external payable returns (uint256 liquidity);

  /// @notice Zap out a pool (B, C) into A.
  ///         Supports standard ERC20 tokens only (i.e. not fee-on-transfer tokens etc).
  ///         Slippage is required for the removal of liquidity.
  ///         Additional slippage may be required on the swap as the
  ///         price of the token may have changed.
  /// @param tokenOut     Token you are zapping out to (i.e. output token).
  /// @param liquidity    Amount of liquidity you wish to remove.
  /// @param zapOutPool   Contains zap struct information. See Zap struct.
  /// @param routesA      Route used to convert tokenA into output token.
  /// @param routesB      Route used to convert tokenB into output token.
  function zapOut(
    address tokenOut,
    uint256 liquidity,
    Zap calldata zapOutPool,
    Route[] calldata routesA,
    Route[] calldata routesB
  ) external;

  /// @notice Used to generate params required for zapping in.
  ///         Zap in => remove liquidity then swap.
  ///         Apply slippage to expected swap values to account for changes in reserves in between.
  /// @dev Output token refers to the token you want to zap in from.
  /// @param tokenA           .
  /// @param tokenB           .
  /// @param stable           .
  /// @param _factory         .
  /// @param amountInA        Amount of input token you wish to send down routesA
  /// @param amountInB        Amount of input token you wish to send down routesB
  /// @param routesA          Route used to convert input token to tokenA
  /// @param routesB          Route used to convert input token to tokenB
  /// @return amountOutMinA   Minimum output expected from swapping input token to tokenA.
  /// @return amountOutMinB   Minimum output expected from swapping input token to tokenB.
  /// @return amountAMin      Minimum amount of tokenA expected from depositing liquidity.
  /// @return amountBMin      Minimum amount of tokenB expected from depositing liquidity.
  function generateZapInParams(
    address tokenA,
    address tokenB,
    bool stable,
    address _factory,
    uint256 amountInA,
    uint256 amountInB,
    Route[] calldata routesA,
    Route[] calldata routesB
  ) external view returns (uint256 amountOutMinA, uint256 amountOutMinB, uint256 amountAMin, uint256 amountBMin);

  /// @notice Used to generate params required for zapping out.
  ///         Zap out => swap then add liquidity.
  ///         Apply slippage to expected liquidity values to account for changes in reserves in between.
  /// @dev Output token refers to the token you want to zap out of.
  /// @param tokenA           .
  /// @param tokenB           .
  /// @param stable           .
  /// @param _factory         .
  /// @param liquidity        Amount of liquidity being zapped out of into a given output token.
  /// @param routesA          Route used to convert tokenA into output token.
  /// @param routesB          Route used to convert tokenB into output token.
  /// @return amountOutMinA   Minimum output expected from swapping tokenA into output token.
  /// @return amountOutMinB   Minimum output expected from swapping tokenB into output token.
  /// @return amountAMin      Minimum amount of tokenA expected from withdrawing liquidity.
  /// @return amountBMin      Minimum amount of tokenB expected from withdrawing liquidity.
  function generateZapOutParams(
    address tokenA,
    address tokenB,
    bool stable,
    address _factory,
    uint256 liquidity,
    Route[] calldata routesA,
    Route[] calldata routesB
  ) external view returns (uint256 amountOutMinA, uint256 amountOutMinB, uint256 amountAMin, uint256 amountBMin);

  /// @notice Used by zapper to determine appropriate ratio of A to B to deposit liquidity. Assumes stable pool.
  /// @dev Returns stable liquidity ratio of B to (A + B).
  ///      E.g. if ratio is 0.4, it means there is more of A than there is of B.
  ///      Therefore you should deposit more of token A than B.
  /// @param tokenA   tokenA of stable pool you are zapping into.
  /// @param tokenB   tokenB of stable pool you are zapping into.
  /// @param factory  Factory that created stable pool.
  /// @return ratio   Ratio of token0 to token1 required to deposit into zap.
  function quoteStableLiquidityRatio(
    address tokenA,
    address tokenB,
    address factory
  ) external view returns (uint256 ratio);
}

