// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.10;

import { IFeeDistributor } from "../../compound/IFeeDistributor.sol";
import { ILiquidatorsRegistry } from "../../liquidators/registry/ILiquidatorsRegistry.sol";
import { IonicComptroller } from "../../compound/ComptrollerInterface.sol";
import { BasePriceOracle } from "../../oracles/BasePriceOracle.sol";
import { IRedemptionStrategy } from "../../liquidators/IRedemptionStrategy.sol";
import { ICErc20 } from "../../compound/CTokenInterfaces.sol";
import { LeveredPositionFactoryStorage } from "./LeveredPositionFactoryStorage.sol";
import { DiamondBase, DiamondExtension, LibDiamond } from "../../ionic/DiamondExtension.sol";

import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

contract LeveredPositionFactory is LeveredPositionFactoryStorage, DiamondBase {
  using EnumerableSet for EnumerableSet.AddressSet;

  /*----------------------------------------------------------------
                            Constructor
  ----------------------------------------------------------------*/

  constructor(IFeeDistributor _feeDistributor, ILiquidatorsRegistry _registry, uint256 _blocksPerYear) {
    feeDistributor = _feeDistributor;
    liquidatorsRegistry = _registry;
    blocksPerYear = _blocksPerYear;
  }

  /*----------------------------------------------------------------
                            Admin Functions
  ----------------------------------------------------------------*/

  function _setPairWhitelisted(ICErc20 _collateralMarket, ICErc20 _stableMarket, bool _whitelisted) external onlyOwner {
    require(_collateralMarket.comptroller() == _stableMarket.comptroller(), "markets not of the same pool");

    if (_whitelisted) {
      collateralMarkets.add(address(_collateralMarket));
      borrowableMarketsByCollateral[_collateralMarket].add(address(_stableMarket));
    } else {
      borrowableMarketsByCollateral[_collateralMarket].remove(address(_stableMarket));
      if (borrowableMarketsByCollateral[_collateralMarket].length() == 0)
        collateralMarkets.remove(address(_collateralMarket));
    }
  }

  function _setLiquidatorsRegistry(ILiquidatorsRegistry _liquidatorsRegistry) external onlyOwner {
    liquidatorsRegistry = _liquidatorsRegistry;
  }

  function _registerExtension(
    DiamondExtension extensionToAdd,
    DiamondExtension extensionToReplace
  ) public override onlyOwner {
    LibDiamond.registerExtension(extensionToAdd, extensionToReplace);
  }
}

// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.10;

import "../../ionic/DiamondExtension.sol";
import { LeveredPositionFactoryStorage } from "./LeveredPositionFactoryStorage.sol";
import { ILeveredPositionFactoryFirstExtension } from "./ILeveredPositionFactory.sol";
import { ICErc20 } from "../../compound/CTokenInterfaces.sol";
import { IRedemptionStrategy } from "../../liquidators/IRedemptionStrategy.sol";
import { LeveredPosition } from "./LeveredPosition.sol";
import { IComptroller, IPriceOracle } from "../../external/compound/IComptroller.sol";
import { ILiquidatorsRegistry } from "../../liquidators/registry/ILiquidatorsRegistry.sol";
import { AuthoritiesRegistry } from "../AuthoritiesRegistry.sol";
import { PoolRolesAuthority } from "../PoolRolesAuthority.sol";

import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

contract LeveredPositionFactoryFirstExtension is
  LeveredPositionFactoryStorage,
  DiamondExtension,
  ILeveredPositionFactoryFirstExtension
{
  using SafeERC20Upgradeable for IERC20Upgradeable;
  using EnumerableSet for EnumerableSet.AddressSet;

  error PairNotWhitelisted();
  error NoSuchPosition();
  error PositionNotClosed();

  function _getExtensionFunctions() external pure override returns (bytes4[] memory) {
    uint8 fnsCount = 10;
    bytes4[] memory functionSelectors = new bytes4[](fnsCount);
    functionSelectors[--fnsCount] = this.removeClosedPosition.selector;
    functionSelectors[--fnsCount] = this.closeAndRemoveUserPosition.selector;
    functionSelectors[--fnsCount] = this.getMinBorrowNative.selector;
    functionSelectors[--fnsCount] = this.getRedemptionStrategies.selector;
    functionSelectors[--fnsCount] = this.getBorrowableMarketsByCollateral.selector;
    functionSelectors[--fnsCount] = this.getWhitelistedCollateralMarkets.selector;
    functionSelectors[--fnsCount] = this.getAccountsWithOpenPositions.selector;
    functionSelectors[--fnsCount] = this.getPositionsByAccount.selector;
    functionSelectors[--fnsCount] = this.getPositionsExtension.selector;
    functionSelectors[--fnsCount] = this._setPositionsExtension.selector;

    require(fnsCount == 0, "use the correct array length");
    return functionSelectors;
  }

  /*----------------------------------------------------------------
                          Mutable Functions
  ----------------------------------------------------------------*/

  // @return true if removed, otherwise false
  function removeClosedPosition(address closedPosition) external returns (bool) {
    return _removeClosedPosition(closedPosition, msg.sender);
  }

  function closeAndRemoveUserPosition(LeveredPosition position) external onlyOwner returns (bool) {
    address positionOwner = position.positionOwner();
    position.closePosition(positionOwner);
    return _removeClosedPosition(address(position), positionOwner);
  }

  function _removeClosedPosition(address closedPosition, address positionOwner) internal returns (bool removed) {
    EnumerableSet.AddressSet storage userPositions = positionsByAccount[positionOwner];
    if (!userPositions.contains(closedPosition)) revert NoSuchPosition();
    if (!LeveredPosition(closedPosition).isPositionClosed()) revert PositionNotClosed();

    removed = userPositions.remove(closedPosition);
    if (userPositions.length() == 0) accountsWithOpenPositions.remove(positionOwner);
  }

  function _setPositionsExtension(bytes4 msgSig, address extension) external onlyOwner {
    _positionsExtensions[msgSig] = extension;
  }

  /*----------------------------------------------------------------
                            View Functions
  ----------------------------------------------------------------*/

  function getMinBorrowNative() external view returns (uint256) {
    return feeDistributor.minBorrowEth();
  }

  function getRedemptionStrategies(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) external view returns (IRedemptionStrategy[] memory strategies, bytes[] memory strategiesData) {
    return liquidatorsRegistry.getRedemptionStrategies(inputToken, outputToken);
  }

  function getPositionsByAccount(
    address account
  ) external view returns (address[] memory positions, bool[] memory closed) {
    positions = positionsByAccount[account].values();
    closed = new bool[](positions.length);
    for (uint256 i = 0; i < positions.length; i++) {
      closed[i] = LeveredPosition(positions[i]).isPositionClosed();
    }
  }

  function getAccountsWithOpenPositions() external view returns (address[] memory) {
    return accountsWithOpenPositions.values();
  }

  function getWhitelistedCollateralMarkets() external view returns (address[] memory) {
    return collateralMarkets.values();
  }

  function getBorrowableMarketsByCollateral(ICErc20 _collateralMarket) external view returns (address[] memory) {
    return borrowableMarketsByCollateral[_collateralMarket].values();
  }

  function getPositionsExtension(bytes4 msgSig) external view returns (address) {
    return _positionsExtensions[msgSig];
  }
}

// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.10;

import "../../ionic/DiamondExtension.sol";
import { LeveredPositionFactoryStorage } from "./LeveredPositionFactoryStorage.sol";
import { ILeveredPositionFactorySecondExtension } from "./ILeveredPositionFactory.sol";
import { ICErc20 } from "../../compound/CTokenInterfaces.sol";
import { IRedemptionStrategy } from "../../liquidators/IRedemptionStrategy.sol";
import { LeveredPosition } from "./LeveredPosition.sol";
import { IComptroller, IPriceOracle } from "../../external/compound/IComptroller.sol";
import { ILiquidatorsRegistry } from "../../liquidators/registry/ILiquidatorsRegistry.sol";
import { AuthoritiesRegistry } from "../AuthoritiesRegistry.sol";
import { PoolRolesAuthority } from "../PoolRolesAuthority.sol";

import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

contract LeveredPositionFactorySecondExtension is
  LeveredPositionFactoryStorage,
  DiamondExtension,
  ILeveredPositionFactorySecondExtension
{
  using SafeERC20Upgradeable for IERC20Upgradeable;
  using EnumerableSet for EnumerableSet.AddressSet;

  error PairNotWhitelisted();

  function _getExtensionFunctions() external pure override returns (bytes4[] memory) {
    uint8 fnsCount = 3;
    bytes4[] memory functionSelectors = new bytes4[](fnsCount);
    functionSelectors[--fnsCount] = this.createPosition.selector;
    functionSelectors[--fnsCount] = this.createAndFundPosition.selector;
    functionSelectors[--fnsCount] = this.createAndFundPositionAtRatio.selector;
    require(fnsCount == 0, "use the correct array length");
    return functionSelectors;
  }

  /*----------------------------------------------------------------
                          Mutable Functions
  ----------------------------------------------------------------*/

  function createPosition(ICErc20 _collateralMarket, ICErc20 _stableMarket) public returns (LeveredPosition) {
    if (!borrowableMarketsByCollateral[_collateralMarket].contains(address(_stableMarket))) revert PairNotWhitelisted();

    LeveredPosition position = new LeveredPosition(msg.sender, _collateralMarket, _stableMarket);

    accountsWithOpenPositions.add(msg.sender);
    positionsByAccount[msg.sender].add(address(position));

    AuthoritiesRegistry authoritiesRegistry = feeDistributor.authoritiesRegistry();
    address poolAddress = address(_collateralMarket.comptroller());
    PoolRolesAuthority poolAuth = authoritiesRegistry.poolsAuthorities(poolAddress);
    if (address(poolAuth) != address(0)) {
      authoritiesRegistry.setUserRole(poolAddress, address(position), poolAuth.LEVERED_POSITION_ROLE(), true);
    }

    return position;
  }

  function createAndFundPosition(
    ICErc20 _collateralMarket,
    ICErc20 _stableMarket,
    IERC20Upgradeable _fundingAsset,
    uint256 _fundingAmount
  ) public returns (LeveredPosition) {
    LeveredPosition position = createPosition(_collateralMarket, _stableMarket);
    _fundingAsset.safeTransferFrom(msg.sender, address(this), _fundingAmount);
    _fundingAsset.approve(address(position), _fundingAmount);
    position.fundPosition(_fundingAsset, _fundingAmount);
    return position;
  }

  function createAndFundPositionAtRatio(
    ICErc20 _collateralMarket,
    ICErc20 _stableMarket,
    IERC20Upgradeable _fundingAsset,
    uint256 _fundingAmount,
    uint256 _leverageRatio
  ) external returns (LeveredPosition) {
    LeveredPosition position = createAndFundPosition(_collateralMarket, _stableMarket, _fundingAsset, _fundingAmount);
    if (_leverageRatio > 1e18) {
      position.adjustLeverageRatio(_leverageRatio);
    }
    return position;
  }
}

// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.10;

import { SafeOwnable } from "../../ionic/SafeOwnable.sol";
import { IFeeDistributor } from "../../compound/IFeeDistributor.sol";
import { ILiquidatorsRegistry } from "../../liquidators/registry/ILiquidatorsRegistry.sol";
import { ICErc20 } from "../../compound/CTokenInterfaces.sol";

import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

abstract contract LeveredPositionFactoryStorage is SafeOwnable {
  EnumerableSet.AddressSet internal accountsWithOpenPositions;
  mapping(address => EnumerableSet.AddressSet) internal positionsByAccount;
  EnumerableSet.AddressSet internal collateralMarkets;
  mapping(ICErc20 => EnumerableSet.AddressSet) internal borrowableMarketsByCollateral;

  mapping(IERC20Upgradeable => mapping(IERC20Upgradeable => uint256)) private __unused;

  IFeeDistributor public feeDistributor;
  ILiquidatorsRegistry public liquidatorsRegistry;
  uint256 public blocksPerYear;

  mapping(bytes4 => address) internal _positionsExtensions;
}

contracts/ionic/levered/LeveredPositions.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.10;

import { ILeveredPositionFactory } from "./ILeveredPositionFactory.sol";
import { LeveredPosition } from "./LeveredPosition.sol";
import { ICErc20 } from "../../compound/CTokenInterfaces.sol";
import { IonicComptroller } from "../../compound/ComptrollerInterface.sol";
import { BasePriceOracle } from "../../oracles/BasePriceOracle.sol";
import { Initializable } from "@openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";

import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";

contract LeveredPositionsLens is Initializable {
  ILeveredPositionFactory public factory;

  function initialize(ILeveredPositionFactory _factory) external initializer {
    factory = _factory;
  }

  function reinitialize(ILeveredPositionFactory _factory) external reinitializer(2) {
    factory = _factory;
  }

  /// @notice this is a lens fn, it is not intended to be used on-chain
  /// @dev returns lists of the market addresses, names and symbols of the underlying assets of those collateral markets that are whitelisted
  function getCollateralMarkets()
    external
    view
    returns (
      address[] memory markets,
      IonicComptroller[] memory poolOfMarket,
      address[] memory underlyings,
      uint256[] memory underlyingPrices,
      string[] memory names,
      string[] memory symbols,
      uint8[] memory decimals,
      uint256[] memory totalUnderlyingSupplied,
      uint256[] memory ratesPerBlock
    )
  {
    markets = factory.getWhitelistedCollateralMarkets();
    poolOfMarket = new IonicComptroller[](markets.length);
    underlyings = new address[](markets.length);
    underlyingPrices = new uint256[](markets.length);
    names = new string[](markets.length);
    symbols = new string[](markets.length);
    totalUnderlyingSupplied = new uint256[](markets.length);
    decimals = new uint8[](markets.length);
    ratesPerBlock = new uint256[](markets.length);
    for (uint256 i = 0; i < markets.length; i++) {
      ICErc20 market = ICErc20(markets[i]);
      poolOfMarket[i] = market.comptroller();
      underlyingPrices[i] = BasePriceOracle(poolOfMarket[i].oracle()).getUnderlyingPrice(market);
      underlyings[i] = market.underlying();
      ERC20Upgradeable underlying = ERC20Upgradeable(underlyings[i]);
      names[i] = underlying.name();
      symbols[i] = underlying.symbol();
      decimals[i] = underlying.decimals();
      totalUnderlyingSupplied[i] = market.getTotalUnderlyingSupplied();
      ratesPerBlock[i] = market.supplyRatePerBlock();
    }
  }

  /// @notice this is a lens fn, it is not intended to be used on-chain
  /// @dev returns the Rate for the chosen borrowable at the specified leverage ratio and supply amount
  function getBorrowRateAtRatio(
    ICErc20 _collateralMarket,
    ICErc20 _stableMarket,
    uint256 _equityAmount,
    uint256 _targetLeverageRatio
  ) external view returns (uint256) {
    IonicComptroller pool = IonicComptroller(_stableMarket.comptroller());
    BasePriceOracle oracle = pool.oracle();
    uint256 stableAssetPrice = oracle.getUnderlyingPrice(_stableMarket);
    uint256 collateralAssetPrice = oracle.getUnderlyingPrice(_collateralMarket);

    uint256 borrowAmount = ((_targetLeverageRatio - 1e18) * _equityAmount * collateralAssetPrice) /
      (stableAssetPrice * 1e18);
    return _stableMarket.borrowRatePerBlockAfterBorrow(borrowAmount) * factory.blocksPerYear();
  }

  /// @notice this is a lens fn, it is not intended to be used on-chain
  /// @dev returns lists of the market addresses, names, symbols and the current Rate for each Borrowable asset
  function getBorrowableMarketsAndRates(
    ICErc20 _collateralMarket
  )
    external
    view
    returns (
      address[] memory markets,
      address[] memory underlyings,
      uint256[] memory underlyingsPrices,
      string[] memory names,
      string[] memory symbols,
      uint256[] memory rates,
      uint8[] memory decimals
    )
  {
    markets = factory.getBorrowableMarketsByCollateral(_collateralMarket);
    underlyings = new address[](markets.length);
    names = new string[](markets.length);
    symbols = new string[](markets.length);
    rates = new uint256[](markets.length);
    decimals = new uint8[](markets.length);
    underlyingsPrices = new uint256[](markets.length);
    for (uint256 i = 0; i < markets.length; i++) {
      ICErc20 market = ICErc20(markets[i]);
      address underlyingAddress = market.underlying();
      underlyings[i] = underlyingAddress;
      ERC20Upgradeable underlying = ERC20Upgradeable(underlyingAddress);
      names[i] = underlying.name();
      symbols[i] = underlying.symbol();
      rates[i] = market.borrowRatePerBlock();
      decimals[i] = underlying.decimals();
      underlyingsPrices[i] = market.comptroller().oracle().getUnderlyingPrice(market);
    }
  }

  /// @notice this is a lens fn, it is not intended to be used on-chain
  function getNetAPY(
    uint256 _supplyAPY,
    uint256 _supplyAmount,
    ICErc20 _collateralMarket,
    ICErc20 _stableMarket,
    uint256 _targetLeverageRatio
  ) public view returns (int256 netAPY) {
    if (_supplyAmount == 0 || _targetLeverageRatio <= 1e18) return 0;

    IonicComptroller pool = IonicComptroller(_collateralMarket.comptroller());
    BasePriceOracle oracle = pool.oracle();
    // TODO the calcs can be implemented without using collateralAssetPrice
    uint256 collateralAssetPrice = oracle.getUnderlyingPrice(_collateralMarket);

    // total collateral = base collateral + levered collateral
    uint256 totalCollateral = (_supplyAmount * _targetLeverageRatio) / 1e18;
    uint256 yieldFromTotalSupplyScaled = _supplyAPY * totalCollateral;
    int256 yieldValueScaled = int256((yieldFromTotalSupplyScaled * collateralAssetPrice) / 1e18);

    uint256 borrowedValueScaled = (totalCollateral - _supplyAmount) * collateralAssetPrice;
    uint256 _borrowRate = _stableMarket.borrowRatePerBlock() * factory.blocksPerYear();
    int256 borrowInterestValueScaled = int256((_borrowRate * borrowedValueScaled) / 1e18);

    int256 netValueDiffScaled = yieldValueScaled - borrowInterestValueScaled;

    netAPY = ((netValueDiffScaled / int256(collateralAssetPrice)) * 1e18) / int256(_supplyAmount);
  }

  function getPositionsInfo(
    LeveredPosition[] calldata positions,
    uint256[] calldata supplyApys
  ) external view returns (PositionInfo[] memory infos) {
    infos = new PositionInfo[](positions.length);
    for (uint256 i = 0; i < positions.length; i++) {
      infos[i] = getPositionInfo(positions[i], supplyApys[i]);
    }
  }

  function getLeverageRatioAfterFunding(LeveredPosition pos, uint256 newFunding) public view returns (uint256) {
    uint256 equityAmount = pos.getEquityAmount();
    if (equityAmount == 0 && newFunding == 0) return 0;

    uint256 suppliedCollateralCurrent = pos.collateralMarket().balanceOfUnderlying(address(pos));
    return ((suppliedCollateralCurrent + newFunding) * 1e18) / (equityAmount + newFunding);
  }

  function getNetApyForPositionAfterFunding(
    LeveredPosition pos,
    uint256 supplyAPY,
    uint256 newFunding
  ) public view returns (int256) {
    return
      getNetAPY(
        supplyAPY,
        pos.getEquityAmount() + newFunding,
        pos.collateralMarket(),
        pos.stableMarket(),
        getLeverageRatioAfterFunding(pos, newFunding)
      );
  }

  function getNetApyForPosition(LeveredPosition pos, uint256 supplyAPY) public view returns (int256) {
    return getNetApyForPositionAfterFunding(pos, supplyAPY, 0);
  }

  struct PositionInfo {
    uint256 collateralAssetPrice;
    uint256 borrowedAssetPrice;
    uint256 positionSupplyAmount;
    uint256 positionValue;
    uint256 debtAmount;
    uint256 debtValue;
    uint256 equityAmount;
    uint256 equityValue;
    int256 currentApy;
    uint256 debtRatio;
    uint256 liquidationThreshold;
    uint256 safetyBuffer;
  }

  function getPositionInfo(LeveredPosition pos, uint256 supplyApy) public view returns (PositionInfo memory info) {
    ICErc20 collateralMarket = pos.collateralMarket();
    IonicComptroller pool = pos.pool();
    info.collateralAssetPrice = pool.oracle().getUnderlyingPrice(collateralMarket);
    {
      info.positionSupplyAmount = collateralMarket.balanceOfUnderlying(address(pos));
      info.positionValue = (info.collateralAssetPrice * info.positionSupplyAmount) / 1e18;
      info.currentApy = getNetApyForPosition(pos, supplyApy);
    }

    {
      ICErc20 stableMarket = pos.stableMarket();
      info.borrowedAssetPrice = pool.oracle().getUnderlyingPrice(stableMarket);
      info.debtAmount = stableMarket.borrowBalanceCurrent(address(pos));
      info.debtValue = (info.borrowedAssetPrice * info.debtAmount) / 1e18;
      info.equityValue = info.positionValue - info.debtValue;
      info.debtRatio = info.positionValue == 0 ? 0 : (info.debtValue * 1e18) / info.positionValue;
      info.equityAmount = (info.equityValue * 1e18) / info.collateralAssetPrice;
    }

    {
      (, uint256 collateralFactor) = pool.markets(address(collateralMarket));
      info.liquidationThreshold = collateralFactor;
      info.safetyBuffer = collateralFactor - info.debtRatio;
    }
  }
}

