pragma solidity ^0.8.10;

import "../../ionic/DiamondExtension.sol";
import "./LiquidatorsRegistryStorage.sol";
import "./LiquidatorsRegistryExtension.sol";

contract LiquidatorsRegistry is LiquidatorsRegistryStorage, DiamondBase {
  using EnumerableSet for EnumerableSet.AddressSet;

  constructor(AddressesProvider _ap) SafeOwnable() {
    ap = _ap;
  }

  /**
   * @dev register a logic extension
   * @param extensionToAdd the extension whose functions are to be added
   * @param extensionToReplace the extension whose functions are to be removed/replaced
   */
  function _registerExtension(DiamondExtension extensionToAdd, DiamondExtension extensionToReplace)
    public
    override
    onlyOwner
  {
    LibDiamond.registerExtension(extensionToAdd, extensionToReplace);
  }

  function asExtension() public view returns (LiquidatorsRegistryExtension) {
    return LiquidatorsRegistryExtension(address(this));
  }
}

// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.10;

import "./ILiquidatorsRegistry.sol";
import "./LiquidatorsRegistryStorage.sol";

import "../IRedemptionStrategy.sol";
import "../../ionic/DiamondExtension.sol";
import { MasterPriceOracle } from "../../oracles/MasterPriceOracle.sol";

import { IRouter_Aerodrome as IAerodromeV2Router } from "../../external/aerodrome/IAerodromeRouter.sol";
import { IRouter_Velodrome as IVelodromeV2Router } from "../../external/velodrome/IVelodromeRouter.sol";
import { IUniswapV2Pair } from "../../external/uniswap/IUniswapV2Pair.sol";

import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/utils/SafeERC20Upgradeable.sol";

contract LiquidatorsRegistryExtension is LiquidatorsRegistryStorage, DiamondExtension, ILiquidatorsRegistryExtension {
  using EnumerableSet for EnumerableSet.AddressSet;
  using SafeERC20Upgradeable for IERC20Upgradeable;

  error NoRedemptionPath();
  error OutputTokenMismatch();

  event SlippageUpdated(
    IERC20Upgradeable indexed from,
    IERC20Upgradeable indexed to,
    uint256 prevValue,
    uint256 newValue
  );

  // @notice maximum slippage in swaps, in bps
  uint256 public constant MAX_SLIPPAGE = 900; // 9%

  function _getExtensionFunctions() external pure override returns (bytes4[] memory) {
    uint8 fnsCount = 7;
    bytes4[] memory functionSelectors = new bytes4[](fnsCount);
    functionSelectors[--fnsCount] = this.getRedemptionStrategies.selector;
    functionSelectors[--fnsCount] = this.getRedemptionStrategy.selector;
    functionSelectors[--fnsCount] = this.getInputTokensByOutputToken.selector;
    functionSelectors[--fnsCount] = this.swap.selector;
    functionSelectors[--fnsCount] = this.getAllRedemptionStrategies.selector;
    functionSelectors[--fnsCount] = this.amountOutAndSlippageOfSwap.selector;
    functionSelectors[--fnsCount] = this.getSlippage.selector;
    require(fnsCount == 0, "use the correct array length");
    return functionSelectors;
  }

  function getSlippage(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) external view returns (uint256 slippage) {
    slippage = conversionSlippage[inputToken][outputToken];
    // TODO slippage == 0 should be allowed
    if (slippage == 0) return MAX_SLIPPAGE;
  }

  function getAllRedemptionStrategies() public view returns (address[] memory) {
    return redemptionStrategies.values();
  }

  function amountOutAndSlippageOfSwap(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    IERC20Upgradeable outputToken
  ) external returns (uint256 outputAmount, uint256 slippage) {
    if (inputAmount == 0) return (0, 0);

    outputAmount = swap(inputToken, inputAmount, outputToken);
    if (outputAmount == 0) return (0, 0);

    MasterPriceOracle mpo = MasterPriceOracle(ap.getAddress("MasterPriceOracle"));
    uint256 inputTokenPrice = mpo.price(address(inputToken));
    uint256 outputTokenPrice = mpo.price(address(outputToken));

    uint256 inputTokensValue = inputAmount * toScaledPrice(inputTokenPrice, inputToken);
    uint256 outputTokensValue = outputAmount * toScaledPrice(outputTokenPrice, outputToken);

    if (outputTokensValue < inputTokensValue) {
      slippage = ((inputTokensValue - outputTokensValue) * 10000) / inputTokensValue;
    }
    // min slippage should be non-zero
    // just in case of rounding errors
    slippage += 1;

    // cache the slippage
    uint256 prevValue = conversionSlippage[inputToken][outputToken];
    if (prevValue == 0 || block.timestamp - conversionSlippageUpdated[inputToken][outputToken] > 5000) {
      emit SlippageUpdated(inputToken, outputToken, prevValue, slippage);

      conversionSlippage[inputToken][outputToken] = slippage;
      conversionSlippageUpdated[inputToken][outputToken] = block.timestamp;
    }
  }

  /// @dev returns price scaled to 1e36 - decimals
  function toScaledPrice(uint256 unscaledPrice, IERC20Upgradeable token) internal view returns (uint256) {
    uint256 tokenDecimals = uint256(ERC20Upgradeable(address(token)).decimals());
    return
      tokenDecimals <= 18
        ? uint256(unscaledPrice) * (10 ** (18 - tokenDecimals))
        : uint256(unscaledPrice) / (10 ** (tokenDecimals - 18));
  }

  function swap(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    IERC20Upgradeable outputToken
  ) public returns (uint256 outputAmount) {
    inputToken.safeTransferFrom(msg.sender, address(this), inputAmount);
    outputAmount = convertAllTo(inputToken, outputToken);
    outputToken.safeTransfer(msg.sender, outputAmount);
  }

  function convertAllTo(IERC20Upgradeable inputToken, IERC20Upgradeable outputToken) private returns (uint256) {
    uint256 inputAmount = inputToken.balanceOf(address(this));
    (IRedemptionStrategy[] memory redemptionStrategies, bytes[] memory strategiesData) = getRedemptionStrategies(
      inputToken,
      outputToken
    );

    if (redemptionStrategies.length == 0) revert NoRedemptionPath();

    IERC20Upgradeable swapInputToken = inputToken;
    uint256 swapInputAmount = inputAmount;
    for (uint256 i = 0; i < redemptionStrategies.length; i++) {
      IRedemptionStrategy redemptionStrategy = redemptionStrategies[i];
      bytes memory strategyData = strategiesData[i];
      (IERC20Upgradeable swapOutputToken, uint256 swapOutputAmount) = convertCustomFunds(
        swapInputToken,
        swapInputAmount,
        redemptionStrategy,
        strategyData
      );
      swapInputAmount = swapOutputAmount;
      swapInputToken = swapOutputToken;
    }

    if (swapInputToken != outputToken) revert OutputTokenMismatch();
    return outputToken.balanceOf(address(this));
  }

  function convertCustomFunds(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    IRedemptionStrategy strategy,
    bytes memory strategyData
  ) private returns (IERC20Upgradeable, uint256) {
    bytes memory returndata = _functionDelegateCall(
      address(strategy),
      abi.encodeWithSelector(strategy.redeem.selector, inputToken, inputAmount, strategyData)
    );
    return abi.decode(returndata, (IERC20Upgradeable, uint256));
  }

  function _functionDelegateCall(address target, bytes memory data) private returns (bytes memory) {
    require(AddressUpgradeable.isContract(target), "Address: delegate call to non-contract");
    (bool success, bytes memory returndata) = target.delegatecall(data);
    return _verifyCallResult(success, returndata, "Address: low-level delegate call failed");
  }

  function _verifyCallResult(
    bool success,
    bytes memory returndata,
    string memory errorMessage
  ) private pure returns (bytes memory) {
    if (success) {
      return returndata;
    } else {
      if (returndata.length > 0) {
        assembly {
          let returndata_size := mload(returndata)
          revert(add(32, returndata), returndata_size)
        }
      } else {
        revert(errorMessage);
      }
    }
  }

  function getInputTokensByOutputToken(IERC20Upgradeable outputToken) external view returns (address[] memory) {
    return inputTokensByOutputToken[outputToken].values();
  }

  function getRedemptionStrategies(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) public view returns (IRedemptionStrategy[] memory strategies, bytes[] memory strategiesData) {
    IERC20Upgradeable tokenToRedeem = inputToken;
    IERC20Upgradeable targetOutputToken = outputToken;
    IRedemptionStrategy[] memory strategiesTemp = new IRedemptionStrategy[](10);
    bytes[] memory strategiesDataTemp = new bytes[](10);
    IERC20Upgradeable[] memory tokenPath = new IERC20Upgradeable[](10);
    IERC20Upgradeable[] memory optimalPath = new IERC20Upgradeable[](0);
    uint256 optimalPathIterator = 0;

    uint256 k = 0;
    while (tokenToRedeem != targetOutputToken) {
      IERC20Upgradeable nextRedeemedToken;
      IRedemptionStrategy directStrategy = redemptionStrategiesByTokens[tokenToRedeem][targetOutputToken];
      if (address(directStrategy) != address(0)) {
        nextRedeemedToken = targetOutputToken;
      } else {
        // check if an optimal path is preconfigured
        if (optimalPath.length == 0 && _optimalSwapPath[tokenToRedeem][targetOutputToken].length != 0) {
          optimalPath = _optimalSwapPath[tokenToRedeem][targetOutputToken];
        }
        if (optimalPath.length != 0 && optimalPathIterator < optimalPath.length) {
          nextRedeemedToken = optimalPath[optimalPathIterator++];
        } else {
          // else if no optimal path is available, use the default
          nextRedeemedToken = defaultOutputToken[tokenToRedeem];
        }
      }

      // check if going in an endless loop
      for (uint256 i = 0; i < tokenPath.length; i++) {
        if (nextRedeemedToken == tokenPath[i]) break;
      }

      (IRedemptionStrategy strategy, bytes memory strategyData) = getRedemptionStrategy(
        tokenToRedeem,
        nextRedeemedToken
      );
      if (address(strategy) == address(0)) break;

      strategiesTemp[k] = strategy;
      strategiesDataTemp[k] = strategyData;
      tokenPath[k] = nextRedeemedToken;
      tokenToRedeem = nextRedeemedToken;

      k++;
      if (k == 10) break;
    }

    strategies = new IRedemptionStrategy[](k);
    strategiesData = new bytes[](k);

    for (uint8 i = 0; i < k; i++) {
      strategies[i] = strategiesTemp[i];
      strategiesData[i] = strategiesDataTemp[i];
    }
  }

  function getRedemptionStrategy(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) public view returns (IRedemptionStrategy strategy, bytes memory strategyData) {
    strategy = redemptionStrategiesByTokens[inputToken][outputToken];

    if (isStrategy(strategy, "UniswapV2LiquidatorFunder") || isStrategy(strategy, "KimUniV2Liquidator")) {
      strategyData = uniswapV2LiquidatorData(inputToken, outputToken);
    } else if (isStrategy(strategy, "UniswapV3LiquidatorFunder")) {
      strategyData = uniswapV3LiquidatorFunderData(inputToken, outputToken);
    } else if (isStrategy(strategy, "AlgebraSwapLiquidator")) {
      strategyData = algebraSwapLiquidatorData(inputToken, outputToken);
    } else if (isStrategy(strategy, "AerodromeV2Liquidator")) {
      strategyData = aerodromeV2LiquidatorData(inputToken, outputToken);
    } else if (isStrategy(strategy, "AerodromeCLLiquidator")) {
      strategyData = aerodromeCLLiquidatorData(inputToken, outputToken);
    } else if (isStrategy(strategy, "CurveSwapLiquidator")) {
      strategyData = curveSwapLiquidatorData(inputToken, outputToken);
    } else if (isStrategy(strategy, "VelodromeV2Liquidator")) {
      strategyData = velodromeV2LiquidatorData(inputToken, outputToken);
    } else {
      revert("no strategy data");
    }
  }

  function isStrategy(IRedemptionStrategy strategy, string memory name) internal view returns (bool) {
    return address(strategy) != address(0) && address(strategy) == address(redemptionStrategiesByName[name]);
  }

  function pickPreferredToken(address[] memory tokens, address strategyOutputToken) internal view returns (address) {
    for (uint256 i = 0; i < tokens.length; i++) {
      if (tokens[i] == strategyOutputToken) return strategyOutputToken;
    }
    address wnative = ap.getAddress("wtoken");
    for (uint256 i = 0; i < tokens.length; i++) {
      if (tokens[i] == wnative) return wnative;
    }
    address stableToken = ap.getAddress("stableToken");
    for (uint256 i = 0; i < tokens.length; i++) {
      if (tokens[i] == stableToken) return stableToken;
    }
    address wbtc = ap.getAddress("wBTCToken");
    for (uint256 i = 0; i < tokens.length; i++) {
      if (tokens[i] == wbtc) return wbtc;
    }
    return tokens[0];
  }

  function getUniswapV3Router(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) internal view returns (address) {
    address customRouter = customUniV3Router[inputToken][outputToken];
    if (customRouter == address(0)) {
      customRouter = customUniV3Router[outputToken][inputToken];
    }

    if (customRouter != address(0)) {
      return customRouter;
    } else {
      // get asset specific router or default
      return ap.getAddress("UNISWAP_V3_ROUTER");
    }
  }

  function getUniswapV2Router(IERC20Upgradeable inputToken) internal view returns (address) {
    // get asset specific router or default
    return ap.getAddress("IUniswapV2Router02");
  }

  function getAerodromeV2Router(IERC20Upgradeable inputToken) internal view returns (address) {
    // get asset specific router or default
    return ap.getAddress("AERODROME_V2_ROUTER");
  }

  function getAerodromeCLRouter(IERC20Upgradeable inputToken) internal view returns (address) {
    // get asset specific router or default
    return ap.getAddress("AERODROME_CL_ROUTER");
  }

  function uniswapV3LiquidatorFunderData(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) internal view returns (bytes memory strategyData) {
    uint24 fee = uniswapV3Fees[inputToken][outputToken];
    if (fee == 0) fee = uniswapV3Fees[outputToken][inputToken];
    if (fee == 0) fee = 500;

    address router = getUniswapV3Router(inputToken, outputToken);
    strategyData = abi.encode(inputToken, outputToken, fee, router, ap.getAddress("Quoter"));
  }

  function getWrappedToUnwrapped4626(IERC20Upgradeable inputToken) internal view returns (address) {
    return _wrappedToUnwrapped4626[address(inputToken)];
  }

  function getAeroCLTickSpacing(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) internal view returns (int24) {
    int24 tickSpacing = _aeroCLTickSpacings[address(inputToken)][address(outputToken)];
    if (tickSpacing == 0) {
      tickSpacing = 1;
    }
    return tickSpacing;
  }

  function aeroV2IsStable(IERC20Upgradeable inputToken, IERC20Upgradeable outputToken) internal view returns (bool) {
    return _aeroV2IsStable[address(inputToken)][address(outputToken)];
  }

  function uniswapV2LiquidatorData(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) internal view returns (bytes memory strategyData) {
    IERC20Upgradeable[] memory swapPath = new IERC20Upgradeable[](2);
    swapPath[0] = inputToken;
    swapPath[1] = outputToken;
    strategyData = abi.encode(getUniswapV2Router(inputToken), swapPath);
  }

  function aerodromeV2LiquidatorData(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) internal view returns (bytes memory strategyData) {
    IAerodromeV2Router.Route[] memory swapPath = new IAerodromeV2Router.Route[](1);
    swapPath[0] = IAerodromeV2Router.Route({
      from: address(inputToken),
      to: address(outputToken),
      stable: aeroV2IsStable(inputToken, outputToken),
      factory: ap.getAddress("AERODROME_V2_FACTORY")
    });
    strategyData = abi.encode(getAerodromeV2Router(inputToken), swapPath);
  }

  function aerodromeCLLiquidatorData(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) internal view returns (bytes memory strategyData) {
    strategyData = abi.encode(
      inputToken,
      outputToken,
      getAerodromeCLRouter(inputToken),
      getWrappedToUnwrapped4626(inputToken),
      getWrappedToUnwrapped4626(outputToken),
      getAeroCLTickSpacing(inputToken, outputToken)
    );
  }

  function algebraSwapLiquidatorData(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) internal view returns (bytes memory strategyData) {
    strategyData = abi.encode(outputToken, ap.getAddress("ALGEBRA_SWAP_ROUTER"));
  }

  function curveSwapLiquidatorData(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) internal view returns (bytes memory strategyData) {
    strategyData = abi.encode(
      ap.getAddress("CURVE_V2_ORACLE_NO_REGISTRY"),
      outputToken,
      getWrappedToUnwrapped4626(inputToken),
      getWrappedToUnwrapped4626(outputToken)
    );
  }

  function velodromeV2LiquidatorData(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) internal view returns (bytes memory strategyData) {
    IVelodromeV2Router.Route[] memory swapPath = new IVelodromeV2Router.Route[](1);
    swapPath[0] = IVelodromeV2Router.Route({
      from: address(inputToken),
      to: address(outputToken),
      stable: aeroV2IsStable(inputToken, outputToken)
    });
    strategyData = abi.encode(getAerodromeV2Router(inputToken), swapPath);
  }
}

// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.10;

import "./ILiquidatorsRegistry.sol";
import "./LiquidatorsRegistryStorage.sol";

import "../../ionic/DiamondExtension.sol";

contract LiquidatorsRegistrySecondExtension is
  LiquidatorsRegistryStorage,
  DiamondExtension,
  ILiquidatorsRegistrySecondExtension
{
  using EnumerableSet for EnumerableSet.AddressSet;

  function _getExtensionFunctions() external pure override returns (bytes4[] memory) {
    uint8 fnsCount = 20;
    bytes4[] memory functionSelectors = new bytes4[](fnsCount);
    functionSelectors[--fnsCount] = this.getAllPairsStrategies.selector;
    functionSelectors[--fnsCount] = this.pairsStrategiesMatch.selector;
    functionSelectors[--fnsCount] = this.uniswapPairsFeesMatch.selector;
    functionSelectors[--fnsCount] = this.uniswapPairsRoutersMatch.selector;
    functionSelectors[--fnsCount] = this._setSlippages.selector;
    functionSelectors[--fnsCount] = this._setUniswapV3Fees.selector;
    functionSelectors[--fnsCount] = this._setUniswapV3Routers.selector;
    functionSelectors[--fnsCount] = this._setDefaultOutputToken.selector;
    functionSelectors[--fnsCount] = this._setRedemptionStrategy.selector;
    functionSelectors[--fnsCount] = this._setRedemptionStrategies.selector;
    functionSelectors[--fnsCount] = this._removeRedemptionStrategy.selector;
    functionSelectors[--fnsCount] = this._resetRedemptionStrategies.selector;
    functionSelectors[--fnsCount] = this.optimalSwapPath.selector;
    functionSelectors[--fnsCount] = this._setOptimalSwapPath.selector;
    functionSelectors[--fnsCount] = this.wrappedToUnwrapped4626.selector;
    functionSelectors[--fnsCount] = this.aeroCLTickSpacings.selector;
    functionSelectors[--fnsCount] = this.aeroV2IsStable.selector;
    functionSelectors[--fnsCount] = this._setWrappedToUnwrapped4626.selector;
    functionSelectors[--fnsCount] = this._setAeroCLTickSpacings.selector;
    functionSelectors[--fnsCount] = this._setAeroV2IsStable.selector;
    require(fnsCount == 0, "use the correct array length");
    return functionSelectors;
  }

  function _setSlippages(
    IERC20Upgradeable[] calldata inputTokens,
    IERC20Upgradeable[] calldata outputTokens,
    uint256[] calldata slippages
  ) external onlyOwner {
    require(slippages.length == inputTokens.length && inputTokens.length == outputTokens.length, "!arrays len");

    for (uint256 i = 0; i < slippages.length; i++) {
      conversionSlippage[inputTokens[i]][outputTokens[i]] = slippages[i];
    }
  }

  function _setUniswapV3Fees(
    IERC20Upgradeable[] calldata inputTokens,
    IERC20Upgradeable[] calldata outputTokens,
    uint24[] calldata fees
  ) external onlyOwner {
    require(fees.length == inputTokens.length && inputTokens.length == outputTokens.length, "!arrays len");

    for (uint256 i = 0; i < fees.length; i++) {
      uniswapV3Fees[inputTokens[i]][outputTokens[i]] = fees[i];
    }
  }

  function _setUniswapV3Routers(
    IERC20Upgradeable[] calldata inputTokens,
    IERC20Upgradeable[] calldata outputTokens,
    address[] calldata routers
  ) external onlyOwner {
    require(routers.length == inputTokens.length && inputTokens.length == outputTokens.length, "!arrays len");

    for (uint256 i = 0; i < routers.length; i++) {
      customUniV3Router[inputTokens[i]][outputTokens[i]] = routers[i];
    }
  }

  function _setDefaultOutputToken(IERC20Upgradeable inputToken, IERC20Upgradeable outputToken) external onlyOwner {
    defaultOutputToken[inputToken] = outputToken;
  }

  function _setRedemptionStrategy(
    IRedemptionStrategy strategy,
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken
  ) public onlyOwner {
    string memory name = strategy.name();
    IRedemptionStrategy oldStrategy = redemptionStrategiesByName[name];

    redemptionStrategiesByTokens[inputToken][outputToken] = strategy;
    redemptionStrategiesByName[name] = strategy;

    redemptionStrategies.remove(address(oldStrategy));
    redemptionStrategies.add(address(strategy));

    if (defaultOutputToken[inputToken] == IERC20Upgradeable(address(0))) {
      defaultOutputToken[inputToken] = outputToken;
    }
    inputTokensByOutputToken[outputToken].add(address(inputToken));
    outputTokensSet.add(address(outputToken));
  }

  function _setRedemptionStrategies(
    IRedemptionStrategy[] calldata strategies,
    IERC20Upgradeable[] calldata inputTokens,
    IERC20Upgradeable[] calldata outputTokens
  ) external onlyOwner {
    require(strategies.length == inputTokens.length && inputTokens.length == outputTokens.length, "!arrays len");
    for (uint256 i = 0; i < strategies.length; i++) {
      _setRedemptionStrategy(strategies[i], inputTokens[i], outputTokens[i]);
    }
  }

  function _resetRedemptionStrategies(
    IRedemptionStrategy[] calldata strategies,
    IERC20Upgradeable[] calldata inputTokens,
    IERC20Upgradeable[] calldata outputTokens
  ) external onlyOwner {
    require(strategies.length == inputTokens.length && inputTokens.length == outputTokens.length, "!arrays len");

    // empty the input/output token mappings/sets
    address[] memory _outputTokens = outputTokensSet.values();
    for (uint256 i = 0; i < _outputTokens.length; i++) {
      IERC20Upgradeable _outputToken = IERC20Upgradeable(_outputTokens[i]);
      address[] memory _inputTokens = inputTokensByOutputToken[_outputToken].values();
      for (uint256 j = 0; j < _inputTokens.length; j++) {
        IERC20Upgradeable _inputToken = IERC20Upgradeable(_inputTokens[j]);
        redemptionStrategiesByTokens[_inputToken][_outputToken] = IRedemptionStrategy(address(0));
        inputTokensByOutputToken[_outputToken].remove(_inputTokens[j]);
        defaultOutputToken[_inputToken] = IERC20Upgradeable(address(0));
      }
      outputTokensSet.remove(_outputTokens[i]);
    }

    // empty the strategies mappings/sets
    address[] memory _currentStrategies = redemptionStrategies.values();
    for (uint256 i = 0; i < _currentStrategies.length; i++) {
      IRedemptionStrategy _currentStrategy = IRedemptionStrategy(_currentStrategies[i]);
      string memory _name = _currentStrategy.name();
      redemptionStrategiesByName[_name] = IRedemptionStrategy(address(0));
      redemptionStrategies.remove(_currentStrategies[i]);
    }

    // write the new strategies and their tokens configs
    for (uint256 i = 0; i < strategies.length; i++) {
      _setRedemptionStrategy(strategies[i], inputTokens[i], outputTokens[i]);
    }
  }

  function _removeRedemptionStrategy(IRedemptionStrategy strategyToRemove) external onlyOwner {
    // check all the input/output tokens if they match the strategy to remove
    address[] memory _outputTokens = outputTokensSet.values();
    for (uint256 i = 0; i < _outputTokens.length; i++) {
      IERC20Upgradeable _outputToken = IERC20Upgradeable(_outputTokens[i]);
      address[] memory _inputTokens = inputTokensByOutputToken[_outputToken].values();
      for (uint256 j = 0; j < _inputTokens.length; j++) {
        IERC20Upgradeable _inputToken = IERC20Upgradeable(_inputTokens[j]);
        IRedemptionStrategy _currentStrategy = redemptionStrategiesByTokens[_inputToken][_outputToken];

        // only nullify the input/output tokens config if the strategy matches
        if (_currentStrategy == strategyToRemove) {
          redemptionStrategiesByTokens[_inputToken][_outputToken] = IRedemptionStrategy(address(0));
          inputTokensByOutputToken[_outputToken].remove(_inputTokens[j]);
          if (defaultOutputToken[_inputToken] == _outputToken) {
            defaultOutputToken[_inputToken] = IERC20Upgradeable(address(0));
          }
        }
      }
      if (inputTokensByOutputToken[_outputToken].length() == 0) {
        outputTokensSet.remove(address(_outputToken));
      }
    }

    redemptionStrategiesByName[strategyToRemove.name()] = IRedemptionStrategy(address(0));
    redemptionStrategies.remove(address(strategyToRemove));
  }

  function uniswapPairsFeesMatch(
    IERC20Upgradeable[] calldata configInputTokens,
    IERC20Upgradeable[] calldata configOutputTokens,
    uint256[] calldata configFees
  ) external view returns (bool) {
    // find a match for each config fee
    for (uint256 i = 0; i < configFees.length; i++) {
      if (uniswapV3Fees[configInputTokens[i]][configOutputTokens[i]] != configFees[i]) return false;
    }

    return true;
  }

  function uniswapPairsRoutersMatch(
    IERC20Upgradeable[] calldata configInputTokens,
    IERC20Upgradeable[] calldata configOutputTokens,
    address[] calldata configRouters
  ) external view returns (bool) {
    // find a match for each config router
    for (uint256 i = 0; i < configRouters.length; i++) {
      if (customUniV3Router[configInputTokens[i]][configOutputTokens[i]] != configRouters[i]) return false;
    }

    return true;
  }

  function pairsStrategiesMatch(
    IRedemptionStrategy[] calldata configStrategies,
    IERC20Upgradeable[] calldata configInputTokens,
    IERC20Upgradeable[] calldata configOutputTokens
  ) external view returns (bool) {
    (
      IRedemptionStrategy[] memory onChainStrategies,
      IERC20Upgradeable[] memory onChainInputTokens,
      IERC20Upgradeable[] memory onChainOutputTokens
    ) = getAllPairsStrategies();
    // find a match for each config strategy
    for (uint256 i = 0; i < configStrategies.length; i++) {
      bool foundMatch = false;
      for (uint256 j = 0; j < onChainStrategies.length; j++) {
        if (
          onChainStrategies[j] == configStrategies[i] &&
          onChainInputTokens[j] == configInputTokens[i] &&
          onChainOutputTokens[j] == configOutputTokens[i]
        ) {
          foundMatch = true;
          break;
        }
      }
      if (!foundMatch) return false;
    }

    // find a match for each on-chain strategy
    for (uint256 i = 0; i < onChainStrategies.length; i++) {
      bool foundMatch = false;
      for (uint256 j = 0; j < configStrategies.length; j++) {
        if (
          onChainStrategies[i] == configStrategies[j] &&
          onChainInputTokens[i] == configInputTokens[j] &&
          onChainOutputTokens[i] == configOutputTokens[j]
        ) {
          foundMatch = true;
          break;
        }
      }
      if (!foundMatch) return false;
    }

    return true;
  }

  function getAllPairsStrategies()
    public
    view
    returns (
      IRedemptionStrategy[] memory strategies,
      IERC20Upgradeable[] memory inputTokens,
      IERC20Upgradeable[] memory outputTokens
    )
  {
    address[] memory _outputTokens = outputTokensSet.values();
    uint256 pairsCounter = 0;

    {
      for (uint256 i = 0; i < _outputTokens.length; i++) {
        IERC20Upgradeable _outputToken = IERC20Upgradeable(_outputTokens[i]);
        address[] memory _inputTokens = inputTokensByOutputToken[_outputToken].values();
        pairsCounter += _inputTokens.length;
      }

      strategies = new IRedemptionStrategy[](pairsCounter);
      inputTokens = new IERC20Upgradeable[](pairsCounter);
      outputTokens = new IERC20Upgradeable[](pairsCounter);
    }

    pairsCounter = 0;
    for (uint256 i = 0; i < _outputTokens.length; i++) {
      IERC20Upgradeable _outputToken = IERC20Upgradeable(_outputTokens[i]);
      address[] memory _inputTokens = inputTokensByOutputToken[_outputToken].values();
      for (uint256 j = 0; j < _inputTokens.length; j++) {
        IERC20Upgradeable _inputToken = IERC20Upgradeable(_inputTokens[j]);
        strategies[pairsCounter] = redemptionStrategiesByTokens[_inputToken][_outputToken];
        inputTokens[pairsCounter] = _inputToken;
        outputTokens[pairsCounter] = _outputToken;
        pairsCounter++;
      }
    }
  }

  function optimalSwapPath(IERC20Upgradeable inputToken, IERC20Upgradeable outputToken)
    external
    view
    returns (IERC20Upgradeable[] memory)
  {
    return _optimalSwapPath[inputToken][outputToken];
  }

  function _setOptimalSwapPath(
    IERC20Upgradeable inputToken,
    IERC20Upgradeable outputToken,
    IERC20Upgradeable[] calldata optimalPath
  ) external onlyOwner {
    _optimalSwapPath[inputToken][outputToken] = optimalPath;
  }

  function wrappedToUnwrapped4626(address wrapped) external view returns (address) {
    return _wrappedToUnwrapped4626[wrapped];
  }

  function aeroCLTickSpacings(address inputToken, address outputToken) external view returns (int24) {
    return _aeroCLTickSpacings[inputToken][outputToken];
  }

  function aeroV2IsStable(address inputToken, address outputToken) external view returns (bool) {
    return _aeroV2IsStable[inputToken][outputToken];
  }

  function _setWrappedToUnwrapped4626(address wrapped, address unwrapped) external onlyOwner {
    _wrappedToUnwrapped4626[wrapped] = unwrapped;
  }

  function _setAeroCLTickSpacings(address inputToken, address outputToken, int24 tickSpacing) external onlyOwner {
    _aeroCLTickSpacings[inputToken][outputToken] = tickSpacing;
  }

  function _setAeroV2IsStable(address inputToken, address outputToken, bool isStable) external onlyOwner {
    _aeroV2IsStable[inputToken][outputToken] = isStable;
  }
}

