// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";

import "../../external/chainlink/AggregatorV3Interface.sol";

import "../BasePriceOracle.sol";
import { SafeOwnableUpgradeable } from "../../ionic/SafeOwnableUpgradeable.sol";

/**
 * @title ChainlinkPriceOracleV2
 * @notice Returns prices from Chainlink.
 * @dev Implements `PriceOracle`.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
contract ChainlinkPriceOracleV2 is SafeOwnableUpgradeable, BasePriceOracle {
  /**
   * @notice Maps ERC20 token addresses to ETH-based Chainlink price feed contracts.
   */
  mapping(address => AggregatorV3Interface) public priceFeeds;

  /**
   * @notice Maps ERC20 token addresses to enums indicating the base currency of the feed.
   */
  mapping(address => FeedBaseCurrency) public feedBaseCurrencies;

  /**
   * @notice Enum indicating the base currency of a Chainlink price feed.
   * @dev ETH is interchangeable with the nativeToken of the current chain.
   */
  enum FeedBaseCurrency {
    ETH,
    USD
  }

  /**
   * @notice Chainlink NATIVE/USD price feed contracts.
   */
  address public NATIVE_TOKEN_USD_PRICE_FEED;

  /**
   * @notice The USD Token of the chain
   */
  address public USD_TOKEN;

  /**
   * @dev Constructor to set admin and canAdminOverwrite, wtoken address and native token USD price feed address
   * @param _usdToken The Wrapped native asset address
   * @param nativeTokenUsd Will this oracle return prices denominated in USD or in the native token.
   */
  function initialize(address _usdToken, address nativeTokenUsd) public initializer {
    __SafeOwnable_init(msg.sender);
    USD_TOKEN = _usdToken;
    NATIVE_TOKEN_USD_PRICE_FEED = nativeTokenUsd;
  }

  /**
   * @dev Admin-only function to set price feeds.
   * @param underlyings Underlying token addresses for which to set price feeds.
   * @param feeds The Chainlink price feed contract addresses for each of `underlyings`.
   * @param baseCurrency The currency in which `feeds` are based.
   */
  function setPriceFeeds(
    address[] memory underlyings,
    address[] memory feeds,
    FeedBaseCurrency baseCurrency
  ) external onlyOwner {
    // Input validation
    require(
      underlyings.length > 0 && underlyings.length == feeds.length,
      "Lengths of both arrays must be equal and greater than 0."
    );

    // For each token/feed
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];
      // Set feed and base currency
      priceFeeds[underlying] = AggregatorV3Interface(feeds[i]);
      feedBaseCurrencies[underlying] = baseCurrency;
    }
  }

  /**
   * @notice Internal function returning the price in of `underlying`.
   * @dev If the oracle got constructed with `nativeTokenUsd` = TRUE this will return a price denominated in USD otherwise in the native token
   */
  function _price(address underlying) internal view returns (uint256) {
    // Get token/ETH price from Chainlink
    AggregatorV3Interface feed = priceFeeds[underlying];
    require(address(feed) != address(0), "No Chainlink price feed found for this underlying ERC20 token.");
    FeedBaseCurrency baseCurrency = feedBaseCurrencies[underlying];

    if (baseCurrency == FeedBaseCurrency.ETH) {
      (, int256 tokenEthPrice, , , ) = feed.latestRoundData();
      return tokenEthPrice >= 0 ? (uint256(tokenEthPrice) * 1e18) / (10 ** uint256(feed.decimals())) : 0;
    } else if (baseCurrency == FeedBaseCurrency.USD) {
      int256 nativeTokenUsdPrice;
      uint8 usdPriceDecimals;

      if (NATIVE_TOKEN_USD_PRICE_FEED == address(0)) {
        uint256 usdNativeTokenPrice = BasePriceOracle(msg.sender).price(USD_TOKEN);
        nativeTokenUsdPrice = int256(1e36 / usdNativeTokenPrice); // 18 decimals
        usdPriceDecimals = 18;
      } else {
        (, nativeTokenUsdPrice, , , ) = AggregatorV3Interface(NATIVE_TOKEN_USD_PRICE_FEED).latestRoundData();
        if (nativeTokenUsdPrice <= 0) return 0;
        usdPriceDecimals = AggregatorV3Interface(NATIVE_TOKEN_USD_PRICE_FEED).decimals();
      }
      (, int256 tokenUsdPrice, , , ) = feed.latestRoundData();

      return
        tokenUsdPrice >= 0
          ? ((uint256(tokenUsdPrice) * 1e18 * (10 ** uint256(usdPriceDecimals))) / (10 ** uint256(feed.decimals()))) /
            uint256(nativeTokenUsdPrice)
          : 0;
    } else {
      revert("unknown base currency");
    }
  }

  /**
   * @notice Returns the price in of `underlying` either in USD or the native token (implements `BasePriceOracle`).
   * @dev If the oracle got constructed with `nativeTokenUsd` = TRUE this will return a price denominated in USD otherwise in the native token
   */
  function price(address underlying) external view override returns (uint256) {
    return _price(underlying);
  }

  /**
   * @notice Returns the price in ETH of the token underlying `cToken`.
   * @dev Implements the `PriceOracle` interface for Ionic pools (and Compound v2).
   * @return Price in ETH of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICErc20 cToken) external view override returns (uint256) {
    // Get underlying token address
    address underlying = cToken.underlying();

    uint256 oraclePrice = _price(underlying);

    uint256 underlyingDecimals = uint256(ERC20Upgradeable(underlying).decimals());
    return
      underlyingDecimals <= 18
        ? uint256(oraclePrice) * (10 ** (18 - underlyingDecimals))
        : uint256(oraclePrice) / (10 ** (underlyingDecimals - 18));
  }
}

contracts/oracles/default/ConcentratedLi.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import { EIP20Interface } from "../../compound/EIP20Interface.sol";
import { ERC20Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";
import { BasePriceOracle } from "../../oracles/BasePriceOracle.sol";
import { ICErc20 } from "../../compound/CTokenInterfaces.sol";

import "../../external/uniswap/FullMath.sol";
import "../../ionic/SafeOwnableUpgradeable.sol";

/**
 * @title ConcentratedLiquidityBasePriceOracle
 * @author Carlo Mazzaferro <carlo@midascapital.xyz> (https://github.com/carlomazzaferro)
 * @notice ConcentratedLiquidityBasePriceOracle is an abstract price oracle for concentrated liquidty (UniV3-like) pairs.
 * @dev Implements the `PriceOracle` interface used by Ionic pools (and Compound v2).
 */
abstract contract ConcentratedLiquidityBasePriceOracle is BasePriceOracle, SafeOwnableUpgradeable {
  /**
   * @notice Maps ERC20 token addresses to asset configs.
   */
  mapping(address => AssetConfig) public poolFeeds;

  /**
   * @dev Controls if `admin` can overwrite existing assignments of oracles to underlying tokens.
   */
  bool public canAdminOverwrite;

  struct AssetConfig {
    address poolAddress;
    uint256 twapWindow;
    address baseToken;
  }

  address public WTOKEN;
  address[] public SUPPORTED_BASE_TOKENS;

  function initialize(address _wtoken, address[] memory _supportedBaseTokens) public initializer {
    __SafeOwnable_init(msg.sender);
    WTOKEN = _wtoken;
    SUPPORTED_BASE_TOKENS = _supportedBaseTokens;
  }

  /**
   * @dev Admin-only function to set price feeds.
   * @param underlyings Underlying token addresses for which to set price feeds.
   * @param assetConfig The asset configuration which includes pool address and twap window.
   */
  function setPoolFeeds(address[] memory underlyings, AssetConfig[] memory assetConfig) external onlyOwner {
    // Input validation
    require(
      underlyings.length > 0 && underlyings.length == assetConfig.length,
      "Lengths of both arrays must be equal and greater than 0."
    );

    // For each token/config
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];
      // Set asset config for underlying
      require(
        assetConfig[i].baseToken == WTOKEN || _isBaseTokenSupported(assetConfig[i].baseToken),
        "Base token must be supported"
      );
      poolFeeds[underlying] = assetConfig[i];
    }
  }

  /**
   * @notice Get the token price price for an underlying token address.
   * @param underlying The underlying token address for which to get the price (set to zero address for WTOKEN)
   * @return Price denominated in NATIVE (scaled by 1e18)
   */
  function price(address underlying) external view returns (uint256) {
    return _price(underlying);
  }

  /**
   * @notice Returns the price in NATIVE of the token underlying `cToken`.
   * @dev Implements the `BasePriceOracle` interface for Ionic pools (and Compound v2).
   * @return Price in NATIVE of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICErc20 cToken) public view override returns (uint256) {
    address underlying = cToken.underlying();
    // Comptroller needs prices to be scaled by 1e(36 - decimals)
    // Since `_price` returns prices scaled by 18 decimals, we must scale them by 1e(36 - 18 - decimals)
    return (_price(underlying) * 1e18) / (10 ** uint256(EIP20Interface(underlying).decimals()));
  }

  /**
   * @dev Fetches the price for a token from Uniswap v3
   */
  function _price(address token) internal view virtual returns (uint256);

  function getPriceX96FromSqrtPriceX96(
    address token0,
    address priceToken,
    uint160 sqrtPriceX96
  ) public pure returns (uint256 price_) {
    price_ = FullMath.mulDiv(sqrtPriceX96, sqrtPriceX96, uint256(2 ** (96 * 2)) / 1e18);
    if (token0 != priceToken) price_ = 1e36 / price_;
  }

  function _isBaseTokenSupported(address token) internal view returns (bool) {
    for (uint256 i = 0; i < SUPPORTED_BASE_TOKENS.length; i++) {
      if (SUPPORTED_BASE_TOKENS[i] == token) {
        return true;
      }
    }
    return false;
  }

  function _setSupportedBaseTokens(address[] memory _supportedBaseTokens) external onlyOwner {
    SUPPORTED_BASE_TOKENS = _supportedBaseTokens;
  }

  function getSupportedBaseTokens() external view returns (address[] memory) {
    return SUPPORTED_BASE_TOKENS;
  }

  function scalePrices(address baseToken, address token, uint256 tokenPrice) internal view returns (uint256) {
    uint256 baseTokenDecimals;
    uint256 tokenPriceScaled;

    if (baseToken == address(0) || baseToken == WTOKEN) {
      baseTokenDecimals = 18;
    } else {
      baseTokenDecimals = uint256(ERC20Upgradeable(baseToken).decimals());
    }

    uint256 baseNativePrice = BasePriceOracle(msg.sender).price(baseToken);

    // scale tokenPrice by 1e18
    uint256 tokenDecimals = uint256(ERC20Upgradeable(token).decimals());
    if (baseTokenDecimals > tokenDecimals) {
      tokenPriceScaled = tokenPrice / (10 ** (baseTokenDecimals - tokenDecimals));
    } else if (baseTokenDecimals < tokenDecimals) {
      tokenPriceScaled = tokenPrice * (10 ** (tokenDecimals - baseTokenDecimals));
    } else {
      tokenPriceScaled = tokenPrice;
    }
    return (tokenPriceScaled * baseNativePrice) / 1e18;
  }
}

contracts/oracles/default/CurveLpTokenPr.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import { EIP20Interface } from "../../compound/EIP20Interface.sol";

import "../../external/curve/ICurvePool.sol";
import "../../ionic/SafeOwnableUpgradeable.sol";

import "../BasePriceOracle.sol";

/**
 * @title CurveLpTokenPriceOracleNoRegistry
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 * @notice CurveLpTokenPriceOracleNoRegistry is a price oracle for Curve LP tokens (using the sender as a root oracle).
 * @dev Implements the `PriceOracle` interface used by Ionic pools (and Compound v2).
 */
contract CurveLpTokenPriceOracleNoRegistry is SafeOwnableUpgradeable, BasePriceOracle {
  /**
   * @dev Maps Curve LP token addresses to underlying token addresses.
   */
  mapping(address => address[]) public underlyingTokens;

  /**
   * @dev Maps Curve LP token addresses to pool addresses.
   */
  mapping(address => address) public poolOf;

  address[] public lpTokens;

  /**
   * @dev Initializes an array of LP tokens and pools if desired.
   * @param _lpTokens Array of LP token addresses.
   * @param _pools Array of pool addresses.
   * @param _poolUnderlyings The underlying token addresses of a pool
   */
  function initialize(
    address[] memory _lpTokens,
    address[] memory _pools,
    address[][] memory _poolUnderlyings
  ) public initializer {
    require(
      _lpTokens.length == _pools.length && _lpTokens.length == _poolUnderlyings.length,
      "No LP tokens supplied or array lengths not equal."
    );

    __SafeOwnable_init(msg.sender);
    for (uint256 i = 0; i < _lpTokens.length; i++) {
      poolOf[_lpTokens[i]] = _pools[i];
      underlyingTokens[_lpTokens[i]] = _poolUnderlyings[i];
    }
  }

  function getAllLPTokens() public view returns (address[] memory) {
    return lpTokens;
  }

  function getPoolForSwap(address inputToken, address outputToken)
    public
    view
    returns (
      ICurvePool,
      int128,
      int128
    )
  {
    for (uint256 i = 0; i < lpTokens.length; i++) {
      ICurvePool pool = ICurvePool(poolOf[lpTokens[i]]);
      int128 inputIndex = -1;
      int128 outputIndex = -1;
      int128 j = 0;
      while (true) {
        try pool.coins(uint256(uint128(j))) returns (address coin) {
          if (coin == inputToken) inputIndex = j;
          else if (coin == outputToken) outputIndex = j;
          j++;
        } catch {
          break;
        }

        if (outputIndex > -1 && inputIndex > -1) {
          return (pool, inputIndex, outputIndex);
        }
      }
    }

    return (ICurvePool(address(0)), 0, 0);
  }

  /**
   * @notice Get the LP token price price for an underlying token address.
   * @param underlying The underlying token address for which to get the price (set to zero address for ETH).
   * @return Price denominated in ETH (scaled by 1e18).
   */
  function price(address underlying) external view override returns (uint256) {
    return _price(underlying);
  }

  /**
   * @notice Returns the price in ETH of the token underlying `cToken`.
   * @dev Implements the `PriceOracle` interface for Ionic pools (and Compound v2).
   * @return Price in ETH of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICErc20 cToken) external view override returns (uint256) {
    address underlying = cToken.underlying();
    // Comptroller needs prices to be scaled by 1e(36 - decimals)
    // Since `_price` returns prices scaled by 18 decimals, we must scale them by 1e(36 - 18 - decimals)
    return (_price(underlying) * 1e18) / (10**uint256(EIP20Interface(underlying).decimals()));
  }

  /**
   * @dev Fetches the fair LP token/ETH price from Curve, with 18 decimals of precision.
   * Source: https://github.com/AlphaFinanceLab/homora-v2/blob/master/contracts/oracle/CurveOracle.sol
   * @param lpToken The LP token contract address for price retrieval.
   */
  function _price(address lpToken) internal view returns (uint256) {
    address pool = poolOf[lpToken];
    require(pool != address(0), "LP token is not registered.");
    address[] memory tokens = underlyingTokens[lpToken];
    uint256 minPx = type(uint256).max;
    uint256 n = tokens.length;

    for (uint256 i = 0; i < n; i++) {
      address ulToken = tokens[i];
      uint256 tokenPx = BasePriceOracle(msg.sender).price(ulToken);
      if (tokenPx < minPx) minPx = tokenPx;
    }

    require(minPx != type(uint256).max, "No minimum underlying token price found.");
    return (minPx * ICurvePool(pool).get_virtual_price()) / 1e18; // Use min underlying token prices
  }

  /**
   * @dev Register the pool given LP token address and set the pool info.
   * @param _lpToken LP token to find the corresponding pool.
   * @param _pool Pool address.
   * @param _underlyings Underlying addresses.
   */
  function registerPool(
    address _lpToken,
    address _pool,
    address[] memory _underlyings
  ) external onlyOwner {
    poolOf[_lpToken] = _pool;
    underlyingTokens[_lpToken] = _underlyings;

    bool skip = false;
    for (uint256 j = 0; j < lpTokens.length; j++) {
      if (lpTokens[j] == _lpToken) {
        skip = true;
        break;
      }
    }
    if (!skip) lpTokens.push(_lpToken);
  }

  /**
   * @dev getter for the underlying tokens
   * @param lpToken the LP token address.
   * @return _underlyings Underlying addresses.
   */
  function getUnderlyingTokens(address lpToken) public view returns (address[] memory) {
    return underlyingTokens[lpToken];
  }
}

contracts/oracles/default/CurveV2LpToken.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import { EIP20Interface } from "../../compound/EIP20Interface.sol";
import { MasterPriceOracle } from "../MasterPriceOracle.sol";

import "../../external/curve/ICurveV2Pool.sol";
import "../../ionic/SafeOwnableUpgradeable.sol";

import "../BasePriceOracle.sol";

/**
 * @title CurveLpTokenPriceOracle
 * @author Carlo Mazzaferro <carlo@midascapital.xyz> (https://github.com/carlomazzaferro)
 * @notice CurveLpTokenPriceOracleNoRegistry is a price oracle for Curve V2 LP tokens (using the sender as a root oracle).
 * @dev Implements the `PriceOracle` interface used by Midas pools (and Compound v2).
 */
contract CurveV2LpTokenPriceOracleNoRegistry is SafeOwnableUpgradeable, BasePriceOracle {
  address public usdToken;
  MasterPriceOracle public masterPriceOracle;
  /**
   * @dev Maps Curve LP token addresses to pool addresses.
   */
  mapping(address => address) public poolOf;

  address[] public lpTokens;

  /**
   * @dev Initializes an array of LP tokens and pools if desired.
   * @param _lpTokens Array of LP token addresses.
   * @param _pools Array of pool addresses.
   */
  function initialize(address[] memory _lpTokens, address[] memory _pools) public initializer {
    require(_lpTokens.length == _pools.length, "No LP tokens supplied or array lengths not equal.");
    __SafeOwnable_init(msg.sender);

    for (uint256 i = 0; i < _pools.length; i++) {
      poolOf[_lpTokens[i]] = _pools[i];
    }
  }

  function getAllLPTokens() public view returns (address[] memory) {
    return lpTokens;
  }

  function getPoolForSwap(address inputToken, address outputToken)
    public
    view
    returns (
      ICurvePool,
      int128,
      int128
    )
  {
    for (uint256 i = 0; i < lpTokens.length; i++) {
      ICurvePool pool = ICurvePool(poolOf[lpTokens[i]]);
      int128 inputIndex = -1;
      int128 outputIndex = -1;
      int128 j = 0;
      while (true) {
        try pool.coins(uint256(uint128(j))) returns (address coin) {
          if (coin == inputToken) inputIndex = j;
          else if (coin == outputToken) outputIndex = j;
          j++;
        } catch {
          break;
        }

        if (outputIndex > -1 && inputIndex > -1) {
          return (pool, inputIndex, outputIndex);
        }
      }
    }

    return (ICurvePool(address(0)), int128(0), int128(0));
  }

  /**
   * @notice Get the LP token price price for an underlying token address.
   * @param underlying The underlying token address for which to get the price (set to zero address for ETH).
   * @return Price denominated in ETH (scaled by 1e18).
   */
  function price(address underlying) external view override returns (uint256) {
    return _price(underlying);
  }

  /**
   * @notice Returns the price in ETH of the token underlying `cToken`.
   * @dev Implements the `PriceOracle` interface for Ionic pools (and Compound v2).
   * @return Price in ETH of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICErc20 cToken) external view override returns (uint256) {
    address underlying = cToken.underlying();
    // Comptroller needs prices to be scaled by 1e(36 - decimals)
    // Since `_price` returns prices scaled by 18 decimals, we must scale them by 1e(36 - 18 - decimals)
    return (_price(underlying) * 1e18) / (10**uint256(EIP20Interface(underlying).decimals()));
  }

  /**
   * @dev Fetches the fair LP token price from Curve, with 18 decimals of precision.
   * @param lpToken The LP token contract address for price retrieval.
   */
  function _price(address lpToken) internal view returns (uint256) {
    address pool = poolOf[lpToken];
    require(address(pool) != address(0), "LP token is not registered.");

    address baseToken = ICurvePool(pool).coins(0);
    uint256 lpPrice = ICurveV2Pool(pool).lp_price();
    uint256 baseTokenPrice = BasePriceOracle(msg.sender).price(baseToken);
    return (lpPrice * baseTokenPrice) / 10**18;
  }

  /**
   * @dev Register the pool given LP token address and set the pool info.
   * @param _lpToken LP token to find the corresponding pool.
   * @param _pool Pool address.
   */
  function registerPool(address _lpToken, address _pool) external onlyOwner {
    address pool = poolOf[_lpToken];
    require(pool == address(0), "This LP token is already registered.");
    poolOf[_lpToken] = _pool;

    bool skip = false;
    for (uint256 j = 0; j < lpTokens.length; j++) {
      if (lpTokens[j] == _lpToken) {
        skip = true;
        break;
      }
    }
    if (!skip) lpTokens.push(_lpToken);
  }
}

contracts/oracles/default/CurveV2PriceOr.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import { EIP20Interface } from "../../compound/EIP20Interface.sol";

import "../../external/curve/ICurveV2Pool.sol";

import "../../ionic/SafeOwnableUpgradeable.sol";
import "../BasePriceOracle.sol";

/**
 * @title CurveLpTokenPriceOracle
 * @author Carlo Mazzaferro <carlo@midascapital.xyz> (https://github.com/carlomazzaferro)
 * @notice CurveLpTokenPriceOracleNoRegistry is a price oracle for Curve V2 LP tokens (using the sender as a root oracle).
 * @dev Implements the `PriceOracle` interface used by Midas pools (and Compound v2).
 */
contract CurveV2PriceOracle is SafeOwnableUpgradeable, BasePriceOracle {
  /**
   * @dev Maps Curve LP token addresses to pool addresses.
   */
  mapping(address => address) public poolFor;

  address[] public tokens;

  /**
   * @dev Initializes an array of tokens and pools if desired.
   * @param _tokens Array of token addresses.
   * @param _pools Array of pool addresses.
   */
  function initialize(address[] memory _tokens, address[] memory _pools) public initializer {
    require(_tokens.length == _pools.length, "No LP tokens supplied or array lengths not equal.");
    __SafeOwnable_init(msg.sender);

    for (uint256 i = 0; i < _pools.length; i++) {
      try ICurvePool(_pools[i]).coins(2) returns (address) {
        revert("!only two token pools");
      } catch {
        // ignore error
      }

      poolFor[_tokens[i]] = _pools[i];
    }
  }

  function getAllSupportedTokens() public view returns (address[] memory) {
    return tokens;
  }

  function getPoolForSwap(address inputToken, address outputToken)
    public
    view
    returns (
      ICurvePool,
      int128,
      int128
    )
  {
    for (uint256 i = 0; i < tokens.length; i++) {
      ICurvePool pool = ICurvePool(poolFor[tokens[i]]);
      int128 inputIndex = -1;
      int128 outputIndex = -1;
      int128 j = 0;
      while (true) {
        try pool.coins(uint256(uint128(j))) returns (address coin) {
          if (coin == inputToken) inputIndex = j;
          else if (coin == outputToken) outputIndex = j;
          j++;
        } catch {
          break;
        }

        if (outputIndex > -1 && inputIndex > -1) {
          return (pool, inputIndex, outputIndex);
        }
      }
    }

    return (ICurvePool(address(0)), int128(0), int128(0));
  }

  /**
   * @notice Get the LP token price price for an underlying token address.
   * @param underlying The underlying token address for which to get the price (set to zero address for ETH).
   * @return Price denominated in ETH (scaled by 1e18).
   */
  function price(address underlying) external view override returns (uint256) {
    return _price(underlying);
  }

  /**
   * @notice Returns the price in ETH of the token underlying `cToken`.
   * @dev Implements the `PriceOracle` interface for Ionic pools (and Compound v2).
   * @return Price in ETH of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICErc20 cToken) external view override returns (uint256) {
    address underlying = cToken.underlying();
    // Comptroller needs prices to be scaled by 1e(36 - decimals)
    // Since `_price` returns prices scaled by 18 decimals, we must scale them by 1e(36 - 18 - decimals)
    return (_price(underlying) * 1e18) / (10**uint256(EIP20Interface(underlying).decimals()));
  }

  /**
   * @dev Fetches the fair LP token price from Curve, with 18 decimals of precision.
   * @param token The LP token contract address for price retrieval.
   */
  function _price(address token) internal view returns (uint256) {
    address pool = poolFor[token];
    require(address(pool) != address(0), "Token is not registered.");

    address baseToken;
    // Returns always coin(1) / coin(0)  [ e.g. USDC (1) / eUSDC (1) ]
    uint256 exchangeRate = ICurveV2Pool(pool).price_oracle();

    if (ICurvePool(pool).coins(0) == token) {
      baseToken = ICurvePool(pool).coins(1);
      // USDC / ETH
      uint256 baseTokenPrice = BasePriceOracle(msg.sender).price(baseToken);
      // USDC / ETH * eUSDC / USDC = eUSDC / ETH
      return (baseTokenPrice * 10**18) / exchangeRate;
    } else {
      // if coin(1) is eUSDC, exchangeRate is USDC / eUSDC
      baseToken = ICurvePool(pool).coins(0);
      // USDC / ETH
      uint256 baseTokenPrice = BasePriceOracle(msg.sender).price(baseToken);
      // (USDC / ETH) *  (1 / (USDC / eUSDC)) = eUSDC / ETH
      return (baseTokenPrice * exchangeRate) / 10**18;
    }
  }

  /**
   * @dev Register the pool given token address and set the pool info.
   * @param _token token to find the corresponding pool.
   * @param _pool Pool address.
   */
  function registerPool(address _token, address _pool) external onlyOwner {
    try ICurvePool(_pool).coins(2) returns (address) {
      revert("!only two token pools");
    } catch {
      // ignore error
    }

    address pool = poolFor[_token];
    require(pool == address(0), "This LP token is already registered.");
    poolFor[_token] = _pool;

    bool skip = false;
    for (uint256 j = 0; j < tokens.length; j++) {
      if (tokens[j] == _token) {
        skip = true;
        break;
      }
    }
    if (!skip) tokens.push(_token);
  }
}

contracts/oracles/default/DiaPriceOracle.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import { ERC20Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";

import { IPriceOracle } from "../../external/compound/IPriceOracle.sol";
import { MasterPriceOracle } from "../MasterPriceOracle.sol";
import { BasePriceOracle, ICErc20 } from "../BasePriceOracle.sol";

interface DIAOracleV2 {
  function getValue(string memory key) external view returns (uint128, uint128);
}

/**
 * @title DiaPriceOracle
 * @notice Returns prices from DIA.
 * @dev Implements `PriceOracle`.
 * @author Rahul Sethuram <rahul@midascapital.xyz> (https://github.com/rhlsthrm)
 */
contract DiaPriceOracle is BasePriceOracle {
  struct DiaOracle {
    DIAOracleV2 feed;
    string key;
  }

  /**
   * @notice Maps ERC20 token addresses to ETH-based Chainlink price feed contracts.
   */
  mapping(address => DiaOracle) public priceFeeds;

  /**
   * @dev The administrator of this `MasterPriceOracle`.
   */
  address public admin;

  /**
   * @dev Controls if `admin` can overwrite existing assignments of oracles to underlying tokens.
   */
  bool public immutable CAN_ADMIN_OVERWRITE;

  /**
   * @dev The Wrapped native asset address.
   */
  address public immutable WTOKEN;

  /**
   * @notice DIA NATIVE/USD price feed contracts.
   */
  DIAOracleV2 public immutable NATIVE_TOKEN_USD_PRICE_FEED;
  string public NATIVE_TOKEN_USD_KEY;

  /**
   * @notice MasterPriceOracle for backup for USD price.
   */
  MasterPriceOracle public immutable MASTER_PRICE_ORACLE;
  address public immutable USD_TOKEN; // token to use as USD price (i.e. USDC)

  /**
   * @dev Constructor to set admin and canAdminOverwrite, wtoken address and native token USD price feed address
   */
  constructor(
    address _admin,
    bool canAdminOverwrite,
    address wtoken,
    DIAOracleV2 nativeTokenUsd,
    string memory nativeTokenUsdKey,
    MasterPriceOracle masterPriceOracle,
    address usdToken
  ) {
    admin = _admin;
    CAN_ADMIN_OVERWRITE = canAdminOverwrite;
    WTOKEN = wtoken;
    NATIVE_TOKEN_USD_PRICE_FEED = nativeTokenUsd;
    NATIVE_TOKEN_USD_KEY = nativeTokenUsdKey;
    MASTER_PRICE_ORACLE = masterPriceOracle;
    USD_TOKEN = usdToken;
  }

  /**
   * @dev Changes the admin and emits an event.
   */
  function changeAdmin(address newAdmin) external onlyAdmin {
    address oldAdmin = admin;
    admin = newAdmin;
    emit NewAdmin(oldAdmin, newAdmin);
  }

  /**
   * @dev Event emitted when `admin` is changed.
   */
  event NewAdmin(address oldAdmin, address newAdmin);

  /**
   * @dev Modifier that checks if `msg.sender == admin`.
   */
  modifier onlyAdmin() {
    require(msg.sender == admin, "Sender is not the admin.");
    _;
  }

  /**
   * @dev Admin-only function to set price feeds.
   * @param underlyings Underlying token addresses for which to set price feeds.
   * @param feeds The DIA price feed contract addresses for each of `underlyings`.
   * @param keys The keys for each of `underlyings`, in the format "ETH/USD" for example
   */
  function setPriceFeeds(
    address[] memory underlyings,
    DIAOracleV2[] memory feeds,
    string[] memory keys
  ) external onlyAdmin {
    // Input validation
    require(
      underlyings.length > 0 && underlyings.length == feeds.length && underlyings.length == keys.length,
      "Lengths of both arrays must be equal and greater than 0."
    );

    // For each token/feed
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];

      // Check for existing oracle if !canAdminOverwrite
      if (!CAN_ADMIN_OVERWRITE)
        require(
          address(priceFeeds[underlying].feed) == address(0),
          "Admin cannot overwrite existing assignments of price feeds to underlying tokens."
        );

      // Set feed and base currency
      priceFeeds[underlying] = DiaOracle({ feed: feeds[i], key: keys[i] });
    }
  }

  /**
   * @dev Internal function returning the price in ETH of `underlying`.
   * Assumes price feeds are 8 decimals!
   */
  function _price(address underlying) internal view returns (uint256) {
    // Return 1e18 for WTOKEN
    if (underlying == WTOKEN || underlying == address(0)) return 1e18;

    // Get token/Native price from Oracle
    DiaOracle memory feed = priceFeeds[underlying];
    require(address(feed.feed) != address(0), "No oracle price feed found for this underlying ERC20 token.");

    if (address(NATIVE_TOKEN_USD_PRICE_FEED) == address(0)) {
      // Get price from MasterPriceOracle
      uint256 usdNativeTokenPrice = MASTER_PRICE_ORACLE.price(USD_TOKEN);
      uint256 nativeTokenUsdPrice = 1e36 / usdNativeTokenPrice; // 18 decimals
      (uint128 tokenUsdPrice, ) = feed.feed.getValue(feed.key); // 8 decimals
      return tokenUsdPrice >= 0 ? (uint256(tokenUsdPrice) * 1e28) / uint256(nativeTokenUsdPrice) : 0;
    } else {
      (uint128 nativeTokenUsdPrice, ) = NATIVE_TOKEN_USD_PRICE_FEED.getValue(NATIVE_TOKEN_USD_KEY);
      if (nativeTokenUsdPrice <= 0) return 0;
      (uint128 tokenUsdPrice, ) = feed.feed.getValue(feed.key); // 8 decimals
      return tokenUsdPrice >= 0 ? (uint256(tokenUsdPrice) * 1e18) / uint256(nativeTokenUsdPrice) : 0;
    }
  }

  /**
   * @dev Returns the price in ETH of `underlying` (implements `BasePriceOracle`).
   */
  function price(address underlying) external view override returns (uint256) {
    return _price(underlying);
  }

  /**
   * @notice Returns the price in ETH of the token underlying `cToken`.
   * @dev Implements the `PriceOracle` interface for Ionic pools (and Compound v2).
   * @return Price in ETH of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICErc20 cToken) external view override returns (uint256) {
    // Get underlying token address
    address underlying = cToken.underlying();

    // Get price
    uint256 oraclePrice = _price(underlying);

    // Format and return price
    uint256 underlyingDecimals = uint256(ERC20Upgradeable(underlying).decimals());
    return
      underlyingDecimals <= 18
        ? uint256(oraclePrice) * (10 ** (18 - underlyingDecimals))
        : uint256(oraclePrice) / (10 ** (underlyingDecimals - 18));
  }
}

