contracts/oracles/default/SimplePriceOra.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "../BasePriceOracle.sol";
import { ERC20Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";
import "../../ionic/SafeOwnableUpgradeable.sol";

contract SimplePriceOracle is BasePriceOracle, SafeOwnableUpgradeable {
  mapping(address => uint256) prices;
  event PricePosted(
    address asset,
    uint256 previousPriceMantissa,
    uint256 requestedPriceMantissa,
    uint256 newPriceMantissa
  );

  function initialize() public initializer {
    __SafeOwnable_init(msg.sender);
  }

  function getUnderlyingPrice(ICErc20 cToken) public view override returns (uint256) {
    if (compareStrings(cToken.symbol(), "cETH")) {
      return 1e18;
    } else {
      address underlying = ICErc20(address(cToken)).underlying();
      uint256 oraclePrice = prices[underlying];

      uint256 underlyingDecimals = uint256(ERC20Upgradeable(underlying).decimals());
      return
        underlyingDecimals <= 18
          ? uint256(oraclePrice) * (10 ** (18 - underlyingDecimals))
          : uint256(oraclePrice) / (10 ** (underlyingDecimals - 18));
    }
  }

  function setUnderlyingPrice(ICErc20 cToken, uint256 underlyingPriceMantissa) public onlyOwner {
    address asset = ICErc20(address(cToken)).underlying();
    emit PricePosted(asset, prices[asset], underlyingPriceMantissa, underlyingPriceMantissa);
    prices[asset] = underlyingPriceMantissa;
  }

  function setDirectPrice(address asset, uint256 _price) public onlyOwner {
    emit PricePosted(asset, prices[asset], _price, _price);
    prices[asset] = _price;
  }

  function price(address underlying) external view returns (uint256) {
    return prices[address(underlying)];
  }

  // v1 price oracle interface for use as backing of proxy
  function assetPrices(address asset) external view returns (uint256) {
    return prices[asset];
  }

  function compareStrings(string memory a, string memory b) internal pure returns (bool) {
    return (keccak256(abi.encodePacked((a))) == keccak256(abi.encodePacked((b))));
  }
}

contracts/oracles/default/UniswapLikeLpT.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";

import "../../external/uniswap/IUniswapV2Pair.sol";

import "../BasePriceOracle.sol";

/**
 * @title UniswapLpTokenPriceOracle
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 * @notice UniswapLpTokenPriceOracle is a price oracle for Uniswap (and SushiSwap) LP tokens.
 * @dev Implements the `PriceOracle` interface used by Ionic pools (and Compound v2).
 */
abstract contract UniswapLikeLpTokenPriceOracle is BasePriceOracle {
  /**
   * @dev wtoken contract address.
   */
  address public immutable wtoken;

  /**
   * @dev Constructor to set admin and canAdminOverwrite, wtoken address and native token USD price feed address
   */
  constructor(address _wtoken) {
    wtoken = _wtoken;
  }

  function _price(address token) internal view virtual returns (uint256);

  function price(address underlying) external view override returns (uint256) {
    return _price(underlying);
  }

  function getUnderlyingPrice(ICErc20 cToken) external view override returns (uint256) {
    address underlying = cToken.underlying();
    // Comptroller needs prices to be scaled by 1e(36 - decimals)
    // Since `_price` returns prices scaled by 18 decimals, we must scale them by 1e(36 - 18 - decimals)
    return (_price(underlying) * 1e18) / (10 ** uint256(ERC20Upgradeable(underlying).decimals()));
  }

  /**
   * @dev Fast square root function.
   * Implementation from: https://github.com/Uniswap/uniswap-lib/commit/99f3f28770640ba1bb1ff460ac7c5292fb8291a0
   * Original implementation: https://github.com/abdk-consulting/abdk-libraries-solidity/blob/master/ABDKMath64x64.sol#L687
   */
  function sqrt(uint256 x) internal pure returns (uint256) {
    if (x == 0) return 0;
    uint256 xx = x;
    uint256 r = 1;

    if (xx >= 0x100000000000000000000000000000000) {
      xx >>= 128;
      r <<= 64;
    }
    if (xx >= 0x10000000000000000) {
      xx >>= 64;
      r <<= 32;
    }
    if (xx >= 0x100000000) {
      xx >>= 32;
      r <<= 16;
    }
    if (xx >= 0x10000) {
      xx >>= 16;
      r <<= 8;
    }
    if (xx >= 0x100) {
      xx >>= 8;
      r <<= 4;
    }
    if (xx >= 0x10) {
      xx >>= 4;
      r <<= 2;
    }
    if (xx >= 0x8) {
      r <<= 1;
    }

    r = (r + x / r) >> 1;
    r = (r + x / r) >> 1;
    r = (r + x / r) >> 1;
    r = (r + x / r) >> 1;
    r = (r + x / r) >> 1;
    r = (r + x / r) >> 1;
    r = (r + x / r) >> 1; // Seven iterations should be enough
    uint256 r1 = x / r;
    return (r < r1 ? r : r1);
  }
}

contracts/oracles/default/UniswapLpToken.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";

import "../../external/uniswap/IUniswapV2Pair.sol";

import "../BasePriceOracle.sol";
import { UniswapLikeLpTokenPriceOracle } from "./UniswapLikeLpTokenPriceOracle.sol";

/**
 * @title UniswapLpTokenPriceOracle
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 * @notice UniswapLpTokenPriceOracle is a price oracle for Uniswap (and SushiSwap) LP tokens.
 * @dev Implements the `PriceOracle` interface used by Ionic pools (and Compound v2).
 */
contract UniswapLpTokenPriceOracle is UniswapLikeLpTokenPriceOracle {
  /**
   * @dev Fetches the fair LP token/ETH price from Uniswap, with 18 decimals of precision.
   */
  constructor(address _wtoken) UniswapLikeLpTokenPriceOracle(_wtoken) {}

  function _price(address token) internal view virtual override returns (uint256) {
    IUniswapV2Pair pair = IUniswapV2Pair(token);
    uint256 totalSupply = pair.totalSupply();
    if (totalSupply == 0) return 0;
    (uint256 r0, uint256 r1, ) = pair.getReserves();

    r0 = r0 * 10 ** (18 - uint256(ERC20Upgradeable(pair.token0()).decimals()));
    r1 = r1 * 10 ** (18 - uint256(ERC20Upgradeable(pair.token1()).decimals()));

    address token0 = pair.token0();
    address token1 = pair.token1();

    // Get fair price of non-WETH token (underlying the pair) in terms of ETH
    uint256 token0FairPrice = token0 == wtoken ? 1e18 : BasePriceOracle(msg.sender).price(token0);
    uint256 token1FairPrice = token1 == wtoken ? 1e18 : BasePriceOracle(msg.sender).price(token1);

    // Implementation from https://github.com/AlphaFinanceLab/homora-v2/blob/e643392d582c81f6695136971cff4b685dcd2859/contracts/oracle/UniswapV2Oracle.sol#L18
    uint256 sqrtK = (sqrt(r0 * r1) * (2 ** 112)) / totalSupply;
    return (((sqrtK * 2 * sqrt(token0FairPrice)) / (2 ** 56)) * sqrt(token1FairPrice)) / (2 ** 56);
  }
}

contracts/oracles/default/UniswapTwapPri.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";

import "../BasePriceOracle.sol";
import "./UniswapTwapPriceOracleV2Root.sol";

/**
 * @title UniswapTwapPriceOracleV2
 * @notice Stores cumulative prices and returns TWAPs for assets on Uniswap V2 pairs.
 * @dev Implements `PriceOracle` and `BasePriceOracle`.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
contract UniswapTwapPriceOracleV2 is Initializable, BasePriceOracle {
  /**
   * @dev wtoken token contract address.
   */
  address public wtoken;

  /**
   * @dev UniswapTwapPriceOracleV2Root contract address.
   */
  UniswapTwapPriceOracleV2Root public rootOracle;

  /**
   * @dev UniswapV2Factory contract address.
   */
  address public uniswapV2Factory;

  /**
   * @dev The token on which to base TWAPs (its price must be available via `msg.sender`).
   */
  address public baseToken;

  /**
   * @dev Initalize that sets the UniswapTwapPriceOracleV2Root, UniswapV2Factory, and base token.
   * @param _rootOracle Sets `UniswapTwapPriceOracleV2Root`
   * @param _uniswapV2Factory Sets `UniswapV2Factory`
   * @param _baseToken The token on which to base TWAPs (its price must be available via `msg.sender`).
   * @param _wtoken The Wrapped native asset address
   */
  function initialize(
    address _rootOracle,
    address _uniswapV2Factory,
    address _baseToken,
    address _wtoken
  ) external initializer {
    require(_rootOracle != address(0), "UniswapTwapPriceOracleV2Root not defined.");
    require(_uniswapV2Factory != address(0), "UniswapV2Factory not defined.");
    rootOracle = UniswapTwapPriceOracleV2Root(_rootOracle);
    uniswapV2Factory = _uniswapV2Factory;
    wtoken = _wtoken;
    baseToken = _baseToken == address(0) ? address(wtoken) : _baseToken;
  }

  /**
   * @notice Returns the price in ETH of the token underlying `cToken`.
   * @dev Implements the `PriceOracle` interface for Ionic pools (and Compound v2).
   * @return Price in ETH of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICErc20 cToken) external view override returns (uint256) {
    // Get underlying ERC20 token address
    address underlying = cToken.underlying();

    // Get price, format, and return
    uint256 baseUnit = 10 ** uint256(ERC20Upgradeable(underlying).decimals());
    return (_price(underlying) * 1e18) / baseUnit;
  }

  /**
   * @dev Internal function returning the price in ETH of `underlying`.
   */
  function _price(address underlying) internal view returns (uint256) {
    // Return 1e18 for wtoken
    if (underlying == wtoken) return 1e18;

    // Return root oracle ERC20/ETH TWAP
    uint256 twap = rootOracle.price(underlying, baseToken, uniswapV2Factory);
    return
      baseToken == address(wtoken)
        ? twap
        : (twap * BasePriceOracle(msg.sender).price(baseToken)) /
          (10 ** uint256(ERC20Upgradeable(baseToken).decimals()));
  }

  /**
   * @dev Returns the price in ETH of `underlying` (implements `BasePriceOracle`).
   */
  function price(address underlying) external view override returns (uint256) {
    return _price(underlying);
  }
}

// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/proxy/ClonesUpgradeable.sol";

import "./UniswapTwapPriceOracleV2.sol";

/**
 * @title UniswapTwapPriceOracleV2Factory
 * @notice Deploys and catalogs UniswapTwapPriceOracleV2 contracts.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
contract UniswapTwapPriceOracleV2Factory {
  /**
   * @dev WETH token contract address.
   */
  address public immutable wtoken;

  /**
   * @dev `UniswapTwapPriceOracleV2Root` contract address.
   */
  address public immutable rootOracle;

  /**
   * @dev Implementation address for the `UniswapV3TwapPriceOracleV2`.
   */
  address public immutable logic;

  /**
   * @notice Maps `UniswapV2Factory` contracts to base tokens to `UniswapTwapPriceOracleV2` contract addresses.
   */
  mapping(address => mapping(address => UniswapTwapPriceOracleV2)) public oracles;

  /**
   * @dev Constructor that sets the `UniswapTwapPriceOracleV2Root` and `UniswapTwapPriceOracleV2` implementation contract.
   */
  constructor(address _rootOracle, address _logic, address _wtoken) {
    require(_rootOracle != address(0), "UniswapTwapPriceOracleV2Root not defined.");
    require(_logic != address(0), "UniswapTwapPriceOracleV2 implementation/logic contract not defined.");
    rootOracle = _rootOracle;
    logic = _logic;
    wtoken = _wtoken;
  }

  /**
   * @notice Deploys a `UniswapTwapPriceOracleV2`.
   * @param uniswapV2Factory The `UniswapV2Factory` contract of the pairs for which this oracle will be used.
   * @param baseToken The base token of the pairs for which this oracle will be used.
   */
  function deploy(address uniswapV2Factory, address baseToken) external returns (address) {
    // Input validation
    if (baseToken == address(0)) baseToken = address(wtoken);

    // Return existing oracle if present
    address currentOracle = address(oracles[uniswapV2Factory][baseToken]);
    if (currentOracle != address(0)) return currentOracle;

    // Deploy oracle
    bytes32 salt = keccak256(abi.encodePacked(uniswapV2Factory, baseToken));
    address oracle = ClonesUpgradeable.cloneDeterministic(logic, salt);
    UniswapTwapPriceOracleV2(oracle).initialize(rootOracle, uniswapV2Factory, baseToken, wtoken);

    // Set oracle in state
    oracles[uniswapV2Factory][baseToken] = UniswapTwapPriceOracleV2(oracle);

    // Return oracle address
    return oracle;
  }
}

// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";

import "../../external/uniswap/IUniswapV2Pair.sol";
import "../../external/uniswap/IUniswapV2Factory.sol";

/**
 * @title UniswapTwapPriceOracleV2Root
 * @notice Stores cumulative prices and returns TWAPs for assets on Uniswap V2 pairs.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
contract UniswapTwapPriceOracleV2Root {
  /**
   * @dev wtoken token contract address.
   */
  address public immutable wtoken;

  /**
   * @dev Minimum TWAP interval.
   */
  uint256 public constant MIN_TWAP_TIME = 15 minutes;

  /**
   * @dev Constructor to set wtoken address
   */
  constructor(address _wtoken) {
    wtoken = _wtoken;
  }

  /**
   * @dev Return the TWAP value price0. Revert if TWAP time range is not within the threshold.
   * Copied from: https://github.com/AlphaFinanceLab/homora-v2/blob/master/contracts/oracle/BaseKP3ROracle.sol
   * @param pair The pair to query for price0.
   */
  function price0TWAP(address pair) internal view returns (uint256) {
    uint256 length = observationCount[pair];
    require(length > 0, "No length-1 TWAP observation.");
    Observation memory lastObservation = observations[pair][(length - 1) % OBSERVATION_BUFFER];
    if (lastObservation.timestamp > block.timestamp - MIN_TWAP_TIME) {
      require(length > 1, "No length-2 TWAP observation.");
      lastObservation = observations[pair][(length - 2) % OBSERVATION_BUFFER];
    }
    uint256 elapsedTime = block.timestamp - lastObservation.timestamp;
    require(elapsedTime >= MIN_TWAP_TIME, "Bad TWAP time.");
    uint256 currPx0Cumu = currentPx0Cumu(pair);
    unchecked {
      return (currPx0Cumu - lastObservation.price0Cumulative) / (block.timestamp - lastObservation.timestamp); // overflow is desired
    }
  }

  /**
   * @dev Return the TWAP value price1. Revert if TWAP time range is not within the threshold.
   * Copied from: https://github.com/AlphaFinanceLab/homora-v2/blob/master/contracts/oracle/BaseKP3ROracle.sol
   * @param pair The pair to query for price1.
   */
  function price1TWAP(address pair) internal view returns (uint256) {
    uint256 length = observationCount[pair];
    require(length > 0, "No length-1 TWAP observation.");
    Observation memory lastObservation = observations[pair][(length - 1) % OBSERVATION_BUFFER];
    if (lastObservation.timestamp > block.timestamp - MIN_TWAP_TIME) {
      require(length > 1, "No length-2 TWAP observation.");
      lastObservation = observations[pair][(length - 2) % OBSERVATION_BUFFER];
    }
    uint256 elapsedTime = block.timestamp - lastObservation.timestamp;
    require(elapsedTime >= MIN_TWAP_TIME, "Bad TWAP time.");
    uint256 currPx1Cumu = currentPx1Cumu(pair);
    unchecked {
      return (currPx1Cumu - lastObservation.price1Cumulative) / (block.timestamp - lastObservation.timestamp); // overflow is desired
    }
  }

  /**
   * @dev Return the current price0 cumulative value on Uniswap.
   * Copied from: https://github.com/AlphaFinanceLab/homora-v2/blob/master/contracts/oracle/BaseKP3ROracle.sol
   * @param pair The uniswap pair to query for price0 cumulative value.
   */
  function currentPx0Cumu(address pair) internal view returns (uint256 px0Cumu) {
    uint32 currTime = uint32(block.timestamp);
    px0Cumu = IUniswapV2Pair(pair).price0CumulativeLast();
    (uint256 reserve0, uint256 reserve1, uint32 lastTime) = IUniswapV2Pair(pair).getReserves();
    if (lastTime != block.timestamp) {
      unchecked {
        uint32 timeElapsed = currTime - lastTime; // overflow is desired
        px0Cumu += uint256((reserve1 << 112) / reserve0) * timeElapsed; // overflow is desired
      }
    }
  }

  /**
   * @dev Return the current price1 cumulative value on Uniswap.
   * Copied from: https://github.com/AlphaFinanceLab/homora-v2/blob/master/contracts/oracle/BaseKP3ROracle.sol
   * @param pair The uniswap pair to query for price1 cumulative value.
   */
  function currentPx1Cumu(address pair) internal view returns (uint256 px1Cumu) {
    uint32 currTime = uint32(block.timestamp);
    px1Cumu = IUniswapV2Pair(pair).price1CumulativeLast();
    (uint256 reserve0, uint256 reserve1, uint32 lastTime) = IUniswapV2Pair(pair).getReserves();
    if (lastTime != currTime) {
      unchecked {
        uint32 timeElapsed = currTime - lastTime; // overflow is desired
        px1Cumu += uint256((reserve0 << 112) / reserve1) * timeElapsed; // overflow is desired
      }
    }
  }

  /**
   * @dev Returns the price of `underlying` in terms of `baseToken` given `factory`.
   */
  function price(address underlying, address baseToken, address factory) external view returns (uint256) {
    // Return ERC20/ETH TWAP
    address pair = IUniswapV2Factory(factory).getPair(underlying, baseToken);
    uint256 baseUnit = 10 ** uint256(ERC20Upgradeable(underlying).decimals());
    return (((underlying < baseToken ? price0TWAP(pair) : price1TWAP(pair)) / (2 ** 56)) * baseUnit) / (2 ** 56); // Scaled by 1e18, not 2 ** 112
  }

  /**
   * @dev Struct for cumulative price observations.
   */
  struct Observation {
    uint32 timestamp;
    uint256 price0Cumulative;
    uint256 price1Cumulative;
  }

  /**
   * @dev Length after which observations roll over to index 0.
   */
  uint8 public constant OBSERVATION_BUFFER = 4;

  /**
   * @dev Total observation count for each pair.
   */
  mapping(address => uint256) public observationCount;

  /**
   * @dev Array of cumulative price observations for each pair.
   */
  mapping(address => Observation[OBSERVATION_BUFFER]) public observations;

  /// @notice Get pairs for token combinations.
  function pairsFor(
    address[] calldata tokenA,
    address[] calldata tokenB,
    address factory
  ) external view returns (address[] memory) {
    require(
      tokenA.length > 0 && tokenA.length == tokenB.length,
      "Token array lengths must be equal and greater than 0."
    );
    address[] memory pairs = new address[](tokenA.length);
    for (uint256 i = 0; i < tokenA.length; i++) pairs[i] = IUniswapV2Factory(factory).getPair(tokenA[i], tokenB[i]);
    return pairs;
  }

  /// @notice Check which of multiple pairs are workable/updatable.
  function workable(
    address[] calldata pairs,
    address[] calldata baseTokens,
    uint256[] calldata minPeriods,
    uint256[] calldata deviationThresholds
  ) external view returns (bool[] memory) {
    require(
      pairs.length > 0 &&
        pairs.length == baseTokens.length &&
        pairs.length == minPeriods.length &&
        pairs.length == deviationThresholds.length,
      "Array lengths must be equal and greater than 0."
    );
    bool[] memory answers = new bool[](pairs.length);
    for (uint256 i = 0; i < pairs.length; i++)
      answers[i] = _workable(pairs[i], baseTokens[i], minPeriods[i], deviationThresholds[i]);
    return answers;
  }

  /// @dev Internal function to check if a pair is workable (updateable AND reserves have changed AND deviation threshold is satisfied).
  function _workable(
    address pair,
    address baseToken,
    uint256 minPeriod,
    uint256 deviationThreshold
  ) internal view returns (bool) {
    // Workable if:
    // 1) We have no observations
    // 2) The elapsed time since the last observation is > minPeriod AND reserves have changed AND deviation threshold is satisfied
    // Note that we loop observationCount[pair] around OBSERVATION_BUFFER so we don't waste gas on new storage slots
    if (observationCount[pair] <= 0) return true;
    (, , uint32 lastTime) = IUniswapV2Pair(pair).getReserves();
    return
      (block.timestamp - observations[pair][(observationCount[pair] - 1) % OBSERVATION_BUFFER].timestamp) >
      (minPeriod >= MIN_TWAP_TIME ? minPeriod : MIN_TWAP_TIME) &&
      lastTime != observations[pair][(observationCount[pair] - 1) % OBSERVATION_BUFFER].timestamp &&
      _deviation(pair, baseToken) >= deviationThreshold;
  }

  /// @dev Internal function to check if a pair's spot price's deviation from its TWAP price as a ratio scaled by 1e18
  function _deviation(address pair, address baseToken) internal view returns (uint256) {
    // Get token base unit
    address token0 = IUniswapV2Pair(pair).token0();
    bool useToken0Price = token0 != baseToken;
    address underlying = useToken0Price ? token0 : IUniswapV2Pair(pair).token1();
    uint256 baseUnit = 10 ** uint256(ERC20Upgradeable(underlying).decimals());

    // Get TWAP price
    uint256 twapPrice = (((useToken0Price ? price0TWAP(pair) : price1TWAP(pair)) / (2 ** 56)) * baseUnit) / (2 ** 56); // Scaled by 1e18, not 2 ** 112

    // Get spot price
    (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(pair).getReserves();
    uint256 spotPrice = useToken0Price ? (reserve1 * baseUnit) / reserve0 : (reserve0 * baseUnit) / reserve1;

    // Get ratio and return deviation
    uint256 ratio = (spotPrice * 1e18) / twapPrice;
    return ratio >= 1e18 ? ratio - 1e18 : 1e18 - ratio;
  }

  /// @dev Internal function to check if a pair is updatable at all.
  function _updateable(address pair) internal view returns (bool) {
    // Updateable if:
    // 1) We have no observations
    // 2) The elapsed time since the last observation is > MIN_TWAP_TIME
    // Note that we loop observationCount[pair] around OBSERVATION_BUFFER so we don't waste gas on new storage slots
    return
      observationCount[pair] <= 0 ||
      (block.timestamp - observations[pair][(observationCount[pair] - 1) % OBSERVATION_BUFFER].timestamp) >
      MIN_TWAP_TIME;
  }

  /// @notice Update one pair.
  function update(address pair) external {
    require(_update(pair), "Failed to update pair.");
  }

  /// @notice Update multiple pairs at once.
  function update(address[] calldata pairs) external {
    bool worked = false;
    for (uint256 i = 0; i < pairs.length; i++) if (_update(pairs[i])) worked = true;
    require(worked, "No pairs can be updated (yet).");
  }

  /// @dev Internal function to update a single pair.
  function _update(address pair) internal returns (bool) {
    // Check if workable
    if (!_updateable(pair)) return false;

    // Get cumulative price(s)
    uint256 price0Cumulative = IUniswapV2Pair(pair).price0CumulativeLast();
    uint256 price1Cumulative = IUniswapV2Pair(pair).price1CumulativeLast();

    // Loop observationCount[pair] around OBSERVATION_BUFFER so we don't waste gas on new storage slots
    (, , uint32 lastTime) = IUniswapV2Pair(pair).getReserves();
    observations[pair][observationCount[pair] % OBSERVATION_BUFFER] = Observation(
      lastTime,
      price0Cumulative,
      price1Cumulative
    );
    observationCount[pair]++;
    return true;
  }
}

