contracts/oracles/MasterPriceOracle.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";

import { ICErc20 } from "../compound/CTokenInterfaces.sol";

import { BasePriceOracle } from "./BasePriceOracle.sol";

/**
 * @title MasterPriceOracle
 * @notice Use a combination of price oracles.
 * @dev Implements `PriceOracle`.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
contract MasterPriceOracle is Initializable, BasePriceOracle {
  /**
   * @dev Maps underlying token addresses to `PriceOracle` contracts (can be `BasePriceOracle` contracts too).
   */
  mapping(address => BasePriceOracle) public oracles;

  /**
   * @dev Default/fallback `PriceOracle`.
   */
  BasePriceOracle public defaultOracle;

  /**
   * @dev The administrator of this `MasterPriceOracle`.
   */
  address public admin;

  /**
   * @dev Controls if `admin` can overwrite existing assignments of oracles to underlying tokens.
   */
  bool internal noAdminOverwrite;

  /**
   * @dev The Wrapped native asset address.
   */
  address public wtoken;

  /**
   * @dev Maps underlying token addresses to `PriceOracle` contracts (can be `BasePriceOracle` contracts too).
   */
  mapping(address => BasePriceOracle) public fallbackOracles;

  /**
   * @dev Returns a boolean indicating if `admin` can overwrite existing assignments of oracles to underlying tokens.
   */
  function canAdminOverwrite() external view returns (bool) {
    return !noAdminOverwrite;
  }

  /**
   * @dev Event emitted when `admin` is changed.
   */
  event NewAdmin(address oldAdmin, address newAdmin);

  /**
   * @dev Event emitted when the default oracle is changed.
   */
  event NewDefaultOracle(address oldOracle, address newOracle);

  /**
   * @dev Event emitted when an underlying token's oracle is changed.
   */
  event NewOracle(address underlying, address oldOracle, address newOracle);

  /**
   * @dev  Initialize state variables.
   * @param underlyings The underlying ERC20 token addresses to link to `_oracles`.
   * @param _oracles The `PriceOracle` contracts to be assigned to `underlyings`.
   * @param _defaultOracle The default `PriceOracle` contract to use.
   * @param _admin The admin who can assign oracles to underlying tokens.
   * @param _canAdminOverwrite Controls if `admin` can overwrite existing assignments of oracles to underlying tokens.
   * @param _wtoken The Wrapped native asset address
   */
  function initialize(
    address[] memory underlyings,
    BasePriceOracle[] memory _oracles,
    BasePriceOracle _defaultOracle,
    address _admin,
    bool _canAdminOverwrite,
    address _wtoken
  ) external initializer {
    // Input validation
    require(underlyings.length == _oracles.length, "Lengths of both arrays must be equal.");

    // Initialize state variables
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];
      BasePriceOracle newOracle = _oracles[i];
      oracles[underlying] = newOracle;
      emit NewOracle(underlying, address(0), address(newOracle));
    }

    defaultOracle = _defaultOracle;
    admin = _admin;
    noAdminOverwrite = !_canAdminOverwrite;
    wtoken = _wtoken;
  }

  /**
   * @dev Sets `_oracles` for `underlyings`.
   */
  function add(address[] calldata underlyings, BasePriceOracle[] calldata _oracles) external onlyAdmin {
    // Input validation
    require(
      underlyings.length > 0 && underlyings.length == _oracles.length,
      "Lengths of both arrays must be equal and greater than 0."
    );

    // Assign oracles to underlying tokens
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];
      address oldOracle = address(oracles[underlying]);
      if (noAdminOverwrite)
        require(
          oldOracle == address(0),
          "Admin cannot overwrite existing assignments of oracles to underlying tokens."
        );
      BasePriceOracle newOracle = _oracles[i];
      oracles[underlying] = newOracle;
      emit NewOracle(underlying, oldOracle, address(newOracle));
    }
  }

  /**
   * @dev Sets `_oracles` for `underlyings`.
   */
  function addFallbacks(address[] calldata underlyings, BasePriceOracle[] calldata _oracles) external onlyAdmin {
    // Input validation
    require(
      underlyings.length > 0 && underlyings.length == _oracles.length,
      "Lengths of both arrays must be equal and greater than 0."
    );

    // Assign oracles to underlying tokens
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];
      address oldOracle = address(fallbackOracles[underlying]);
      if (noAdminOverwrite)
        require(
          oldOracle == address(0),
          "Admin cannot overwrite existing assignments of oracles to underlying tokens."
        );
      BasePriceOracle newOracle = _oracles[i];
      fallbackOracles[underlying] = newOracle;
      emit NewOracle(underlying, oldOracle, address(newOracle));
    }
  }

  /**
   * @dev Changes the default price oracle
   */
  function setDefaultOracle(BasePriceOracle newOracle) external onlyAdmin {
    BasePriceOracle oldOracle = defaultOracle;
    defaultOracle = newOracle;
    emit NewDefaultOracle(address(oldOracle), address(newOracle));
  }

  /**
   * @dev Changes the admin and emits an event.
   */
  function changeAdmin(address newAdmin) external onlyAdmin {
    address oldAdmin = admin;
    admin = newAdmin;
    emit NewAdmin(oldAdmin, newAdmin);
  }

  /**
   * @dev Modifier that checks if `msg.sender == admin`.
   */
  modifier onlyAdmin() {
    require(msg.sender == admin, "Sender is not the admin.");
    _;
  }

  /**
   * @notice Returns the price in ETH of the token underlying `cToken`.
   * @dev Implements the `PriceOracle` interface for Ionic pools (and Compound v2).
   * @return Price in ETH of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICErc20 cToken) external view override returns (uint256) {
    // Get underlying ERC20 token address
    address underlying = address(ICErc20(address(cToken)).underlying());

    if (underlying == wtoken) return 1e18;

    BasePriceOracle oracle = oracles[underlying];
    BasePriceOracle fallbackOracle = fallbackOracles[underlying];

    if (address(oracle) != address(0)) {
      try oracle.getUnderlyingPrice(cToken) returns (uint256 underlyingPrice) {
        if (underlyingPrice == 0) {
          if (address(fallbackOracle) != address(0)) return fallbackOracle.getUnderlyingPrice(cToken);
        } else {
          return underlyingPrice;
        }
      } catch {
        if (address(fallbackOracle) != address(0)) return fallbackOracle.getUnderlyingPrice(cToken);
      }
    } else {
      if (address(fallbackOracle) != address(0)) return fallbackOracle.getUnderlyingPrice(cToken);
    }
    revert("Price oracle not found for this underlying token address.");
  }

  /**
   * @dev Attempts to return the price in ETH of `underlying` (implements `BasePriceOracle`).
   */
  function price(address underlying) public view override returns (uint256) {
    // Return 1e18 for WETH
    if (underlying == wtoken) return 1e18;

    // Get underlying price from assigned oracle
    BasePriceOracle oracle = oracles[underlying];
    BasePriceOracle fallbackOracle = fallbackOracles[underlying];

    if (address(oracle) != address(0)) {
      try oracle.price(underlying) returns (uint256 underlyingPrice) {
        if (underlyingPrice == 0) {
          if (address(fallbackOracle) != address(0)) return fallbackOracle.price(underlying);
        } else {
          return underlyingPrice;
        }
      } catch {
        if (address(fallbackOracle) != address(0)) return fallbackOracle.price(underlying);
      }
    } else {
      if (address(fallbackOracle) != address(0)) return fallbackOracle.price(underlying);
    }
    revert("Price oracle not found for this underlying token address.");
  }
}

contracts/PoolDirectory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/utils/Create2Upgradeable.sol";

import { IonicComptroller } from "./compound/ComptrollerInterface.sol";
import { BasePriceOracle } from "./oracles/BasePriceOracle.sol";
import { Unitroller } from "./compound/Unitroller.sol";
import "./ionic/SafeOwnableUpgradeable.sol";
import "./ionic/DiamondExtension.sol";

/**
 * @title PoolDirectory
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 * @notice PoolDirectory is a directory for Ionic interest rate pools.
 */
contract PoolDirectory is SafeOwnableUpgradeable {
  /**
   * @dev Initializes a deployer whitelist if desired.
   * @param _enforceDeployerWhitelist Boolean indicating if the deployer whitelist is to be enforced.
   * @param _deployerWhitelist Array of Ethereum accounts to be whitelisted.
   */
  function initialize(bool _enforceDeployerWhitelist, address[] memory _deployerWhitelist) public initializer {
    __SafeOwnable_init(msg.sender);
    enforceDeployerWhitelist = _enforceDeployerWhitelist;
    for (uint256 i = 0; i < _deployerWhitelist.length; i++) deployerWhitelist[_deployerWhitelist[i]] = true;
  }

  /**
   * @dev Struct for a Ionic interest rate pool.
   */
  struct Pool {
    string name;
    address creator;
    address comptroller;
    uint256 blockPosted;
    uint256 timestampPosted;
  }

  /**
   * @dev Array of Ionic interest rate pools.
   */
  Pool[] public pools;

  /**
   * @dev Maps Ethereum accounts to arrays of Ionic pool indexes.
   */
  mapping(address => uint256[]) private _poolsByAccount;

  /**
   * @dev Maps Ionic pool Comptroller addresses to bools indicating if they have been registered via the directory.
   */
  mapping(address => bool) public poolExists;

  /**
   * @dev Emitted when a new Ionic pool is added to the directory.
   */
  event PoolRegistered(uint256 index, Pool pool);

  /**
   * @dev Booleans indicating if the deployer whitelist is enforced.
   */
  bool public enforceDeployerWhitelist;

  /**
   * @dev Maps Ethereum accounts to booleans indicating if they are allowed to deploy pools.
   */
  mapping(address => bool) public deployerWhitelist;

  /**
   * @dev Controls if the deployer whitelist is to be enforced.
   * @param enforce Boolean indicating if the deployer whitelist is to be enforced.
   */
  function _setDeployerWhitelistEnforcement(bool enforce) external onlyOwner {
    enforceDeployerWhitelist = enforce;
  }

  /**
   * @dev Adds/removes Ethereum accounts to the deployer whitelist.
   * @param deployers Array of Ethereum accounts to be whitelisted.
   * @param status Whether to add or remove the accounts.
   */
  function _editDeployerWhitelist(address[] calldata deployers, bool status) external onlyOwner {
    require(deployers.length > 0, "No deployers supplied.");
    for (uint256 i = 0; i < deployers.length; i++) deployerWhitelist[deployers[i]] = status;
  }

  /**
   * @dev Adds a new Ionic pool to the directory (without checking msg.sender).
   * @param name The name of the pool.
   * @param comptroller The pool's Comptroller proxy contract address.
   * @return The index of the registered Ionic pool.
   */
  function _registerPool(string memory name, address comptroller) internal returns (uint256) {
    require(!poolExists[comptroller], "Pool already exists in the directory.");
    require(!enforceDeployerWhitelist || deployerWhitelist[msg.sender], "Sender is not on deployer whitelist.");
    require(bytes(name).length <= 100, "No pool name supplied.");
    Pool memory pool = Pool(name, msg.sender, comptroller, block.number, block.timestamp);
    pools.push(pool);
    _poolsByAccount[msg.sender].push(pools.length - 1);
    poolExists[comptroller] = true;
    emit PoolRegistered(pools.length - 1, pool);
    return pools.length - 1;
  }

  function _deprecatePool(address comptroller) external onlyOwner {
    for (uint256 i = 0; i < pools.length; i++) {
      if (pools[i].comptroller == comptroller) {
        _deprecatePool(i);
        break;
      }
    }
  }

  function _deprecatePool(uint256 index) public onlyOwner {
    Pool storage ionicPool = pools[index];

    require(ionicPool.comptroller != address(0), "pool already deprecated");

    // swap with the last pool of the creator and delete
    uint256[] storage creatorPools = _poolsByAccount[ionicPool.creator];
    for (uint256 i = 0; i < creatorPools.length; i++) {
      if (creatorPools[i] == index) {
        creatorPools[i] = creatorPools[creatorPools.length - 1];
        creatorPools.pop();
        break;
      }
    }

    // leave it to true to deny the re-registering of the same pool
    poolExists[ionicPool.comptroller] = true;

    // nullify the storage
    ionicPool.comptroller = address(0);
    ionicPool.creator = address(0);
    ionicPool.name = "";
    ionicPool.blockPosted = 0;
    ionicPool.timestampPosted = 0;
  }

  /**
   * @dev Deploys a new Ionic pool and adds to the directory.
   * @param name The name of the pool.
   * @param implementation The Comptroller implementation contract address.
   * @param constructorData Encoded construction data for `Unitroller constructor()`
   * @param enforceWhitelist Boolean indicating if the pool's supplier/borrower whitelist is to be enforced.
   * @param closeFactor The pool's close factor (scaled by 1e18).
   * @param liquidationIncentive The pool's liquidation incentive (scaled by 1e18).
   * @param priceOracle The pool's PriceOracle contract address.
   * @return Index of the registered Ionic pool and the Unitroller proxy address.
   */
  function deployPool(
    string memory name,
    address implementation,
    bytes calldata constructorData,
    bool enforceWhitelist,
    uint256 closeFactor,
    uint256 liquidationIncentive,
    address priceOracle
  ) external returns (uint256, address) {
    // Input validation
    require(implementation != address(0), "No Comptroller implementation contract address specified.");
    require(priceOracle != address(0), "No PriceOracle contract address specified.");

    // Deploy Unitroller using msg.sender, name, and block.number as a salt
    bytes memory unitrollerCreationCode = abi.encodePacked(type(Unitroller).creationCode, constructorData);
    address proxy = Create2Upgradeable.deploy(
      0,
      keccak256(abi.encodePacked(msg.sender, name, ++poolsCounter)),
      unitrollerCreationCode
    );

    // Setup the pool
    IonicComptroller comptrollerProxy = IonicComptroller(proxy);
    // Set up the extensions
    comptrollerProxy._upgrade();

    // Set pool parameters
    require(comptrollerProxy._setCloseFactor(closeFactor) == 0, "Failed to set pool close factor.");
    require(
      comptrollerProxy._setLiquidationIncentive(liquidationIncentive) == 0,
      "Failed to set pool liquidation incentive."
    );
    require(comptrollerProxy._setPriceOracle(BasePriceOracle(priceOracle)) == 0, "Failed to set pool price oracle.");

    // Whitelist
    if (enforceWhitelist)
      require(comptrollerProxy._setWhitelistEnforcement(true) == 0, "Failed to enforce supplier/borrower whitelist.");

    // Make msg.sender the admin
    require(comptrollerProxy._setPendingAdmin(msg.sender) == 0, "Failed to set pending admin on Unitroller.");

    // Register the pool with this PoolDirectory
    return (_registerPool(name, proxy), proxy);
  }

  /**
   * @notice Returns `ids` and directory information of all non-deprecated Ionic pools.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getActivePools() public view returns (uint256[] memory, Pool[] memory) {
    uint256 count = 0;
    for (uint256 i = 0; i < pools.length; i++) {
      if (pools[i].comptroller != address(0)) count++;
    }

    Pool[] memory activePools = new Pool[](count);
    uint256[] memory poolIds = new uint256[](count);

    uint256 index = 0;
    for (uint256 i = 0; i < pools.length; i++) {
      if (pools[i].comptroller != address(0)) {
        poolIds[index] = i;
        activePools[index] = pools[i];
        index++;
      }
    }

    return (poolIds, activePools);
  }

  /**
   * @notice Returns arrays of all Ionic pools' data.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getAllPools() public view returns (Pool[] memory) {
    uint256 count = 0;
    for (uint256 i = 0; i < pools.length; i++) {
      if (pools[i].comptroller != address(0)) count++;
    }

    Pool[] memory result = new Pool[](count);

    uint256 index = 0;
    for (uint256 i = 0; i < pools.length; i++) {
      if (pools[i].comptroller != address(0)) {
        result[index++] = pools[i];
      }
    }

    return result;
  }

  /**
   * @notice Returns arrays of all public Ionic pool indexes and data.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getPublicPools() external view returns (uint256[] memory, Pool[] memory) {
    uint256 arrayLength = 0;

    (, Pool[] memory activePools) = getActivePools();
    for (uint256 i = 0; i < activePools.length; i++) {
      try IonicComptroller(activePools[i].comptroller).enforceWhitelist() returns (bool enforceWhitelist) {
        if (enforceWhitelist) continue;
      } catch {}

      arrayLength++;
    }

    uint256[] memory indexes = new uint256[](arrayLength);
    Pool[] memory publicPools = new Pool[](arrayLength);
    uint256 index = 0;

    for (uint256 i = 0; i < activePools.length; i++) {
      try IonicComptroller(activePools[i].comptroller).enforceWhitelist() returns (bool enforceWhitelist) {
        if (enforceWhitelist) continue;
      } catch {}

      indexes[index] = i;
      publicPools[index] = activePools[i];
      index++;
    }

    return (indexes, publicPools);
  }

  /**
   * @notice Returns arrays of all public Ionic pool indexes and data.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getPoolsOfUser(address user) external view returns (uint256[] memory, Pool[] memory) {
    uint256 arrayLength = 0;

    (, Pool[] memory activePools) = getActivePools();
    for (uint256 i = 0; i < activePools.length; i++) {
      try IonicComptroller(activePools[i].comptroller).isUserOfPool(user) returns (bool isUsing) {
        if (!isUsing) continue;
      } catch {}

      arrayLength++;
    }

    uint256[] memory indexes = new uint256[](arrayLength);
    Pool[] memory poolsOfUser = new Pool[](arrayLength);
    uint256 index = 0;

    for (uint256 i = 0; i < activePools.length; i++) {
      try IonicComptroller(activePools[i].comptroller).isUserOfPool(user) returns (bool isUsing) {
        if (!isUsing) continue;
      } catch {}

      indexes[index] = i;
      poolsOfUser[index] = activePools[i];
      index++;
    }

    return (indexes, poolsOfUser);
  }

  /**
   * @notice Returns arrays of Ionic pool indexes and data created by `account`.
   */
  function getPoolsByAccount(address account) external view returns (uint256[] memory, Pool[] memory) {
    uint256[] memory indexes = new uint256[](_poolsByAccount[account].length);
    Pool[] memory accountPools = new Pool[](_poolsByAccount[account].length);
    (, Pool[] memory activePools) = getActivePools();

    for (uint256 i = 0; i < _poolsByAccount[account].length; i++) {
      indexes[i] = _poolsByAccount[account][i];
      accountPools[i] = activePools[_poolsByAccount[account][i]];
    }

    return (indexes, accountPools);
  }

  /**
   * @notice Modify existing Ionic pool name.
   */
  function setPoolName(uint256 index, string calldata name) external {
    IonicComptroller _comptroller = IonicComptroller(pools[index].comptroller);
    require(
      (msg.sender == _comptroller.admin() && _comptroller.adminHasRights()) || msg.sender == owner(),
      "!permission"
    );
    pools[index].name = name;
  }

  /**
   * @dev Maps Ethereum accounts to booleans indicating if they are a whitelisted admin.
   */
  mapping(address => bool) public adminWhitelist;

  /**
   * @dev used as salt for the creation of new pools
   */
  uint256 public poolsCounter;

  /**
   * @dev Event emitted when the admin whitelist is updated.
   */
  event AdminWhitelistUpdated(address[] admins, bool status);

  /**
   * @dev Adds/removes Ethereum accounts to the admin whitelist.
   * @param admins Array of Ethereum accounts to be whitelisted.
   * @param status Whether to add or remove the accounts.
   */
  function _editAdminWhitelist(address[] calldata admins, bool status) external onlyOwner {
    require(admins.length > 0, "No admins supplied.");
    for (uint256 i = 0; i < admins.length; i++) adminWhitelist[admins[i]] = status;
    emit AdminWhitelistUpdated(admins, status);
  }

  /**
   * @notice Returns arrays of all Ionic pool indexes and data with whitelisted admins.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getPublicPoolsByVerification(bool whitelistedAdmin) external view returns (uint256[] memory, Pool[] memory) {
    uint256 arrayLength = 0;

    (, Pool[] memory activePools) = getActivePools();
    for (uint256 i = 0; i < activePools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(activePools[i].comptroller);

      try comptroller.admin() returns (address admin) {
        if (whitelistedAdmin != adminWhitelist[admin]) continue;
      } catch {}

      arrayLength++;
    }

    uint256[] memory indexes = new uint256[](arrayLength);
    Pool[] memory publicPools = new Pool[](arrayLength);
    uint256 index = 0;

    for (uint256 i = 0; i < activePools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(activePools[i].comptroller);

      try comptroller.admin() returns (address admin) {
        if (whitelistedAdmin != adminWhitelist[admin]) continue;
      } catch {}

      indexes[index] = i;
      publicPools[index] = activePools[i];
      index++;
    }

    return (indexes, publicPools);
  }

  /**
   * @notice Returns arrays of all verified Ionic pool indexes and data for which the account is whitelisted
   * @param account who is whitelisted in the returned verified whitelist-enabled pools.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getVerifiedPoolsOfWhitelistedAccount(
    address account
  ) external view returns (uint256[] memory, Pool[] memory) {
    uint256 arrayLength = 0;
    (, Pool[] memory activePools) = getActivePools();
    for (uint256 i = 0; i < activePools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(activePools[i].comptroller);

      try comptroller.enforceWhitelist() returns (bool enforceWhitelist) {
        if (!enforceWhitelist || !comptroller.whitelist(account)) continue;
      } catch {}

      arrayLength++;
    }

    uint256[] memory indexes = new uint256[](arrayLength);
    Pool[] memory accountWhitelistedPools = new Pool[](arrayLength);
    uint256 index = 0;

    for (uint256 i = 0; i < activePools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(activePools[i].comptroller);
      try comptroller.enforceWhitelist() returns (bool enforceWhitelist) {
        if (!enforceWhitelist || !comptroller.whitelist(account)) continue;
      } catch {}

      indexes[index] = i;
      accountWhitelistedPools[index] = activePools[i];
      index++;
    }

    return (indexes, accountWhitelistedPools);
  }
}

contracts/PoolLens.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";

import { IonicComptroller } from "./compound/ComptrollerInterface.sol";
import { BasePriceOracle } from "./oracles/BasePriceOracle.sol";
import { ICErc20 } from "./compound/CTokenInterfaces.sol";

import { PoolDirectory } from "./PoolDirectory.sol";
import { MasterPriceOracle } from "./oracles/MasterPriceOracle.sol";

/**
 * @title PoolLens
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 * @notice PoolLens returns data on Ionic interest rate pools in mass for viewing by dApps, bots, etc.
 */
contract PoolLens is Initializable {
  error ComptrollerError(uint256 errCode);

  /**
   * @notice Initialize the `PoolDirectory` contract object.
   * @param _directory The PoolDirectory
   * @param _name Name for the nativeToken
   * @param _symbol Symbol for the nativeToken
   * @param _hardcodedAddresses Underlying token addresses for a token like maker which are DSToken and/or use bytes32 for `symbol`
   * @param _hardcodedNames Harcoded name for these tokens
   * @param _hardcodedSymbols Harcoded symbol for these tokens
   * @param _uniswapLPTokenNames Harcoded names for underlying uniswap LpToken
   * @param _uniswapLPTokenSymbols Harcoded symbols for underlying uniswap LpToken
   * @param _uniswapLPTokenDisplayNames Harcoded display names for underlying uniswap LpToken
   */
  function initialize(
    PoolDirectory _directory,
    string memory _name,
    string memory _symbol,
    address[] memory _hardcodedAddresses,
    string[] memory _hardcodedNames,
    string[] memory _hardcodedSymbols,
    string[] memory _uniswapLPTokenNames,
    string[] memory _uniswapLPTokenSymbols,
    string[] memory _uniswapLPTokenDisplayNames
  ) public initializer {
    require(address(_directory) != address(0), "PoolDirectory instance cannot be the zero address.");
    require(
      _hardcodedAddresses.length == _hardcodedNames.length && _hardcodedAddresses.length == _hardcodedSymbols.length,
      "Hardcoded addresses lengths not equal."
    );
    require(
      _uniswapLPTokenNames.length == _uniswapLPTokenSymbols.length &&
        _uniswapLPTokenNames.length == _uniswapLPTokenDisplayNames.length,
      "Uniswap LP token names lengths not equal."
    );

    directory = _directory;
    name = _name;
    symbol = _symbol;
    for (uint256 i = 0; i < _hardcodedAddresses.length; i++) {
      hardcoded[_hardcodedAddresses[i]] = TokenData({ name: _hardcodedNames[i], symbol: _hardcodedSymbols[i] });
    }

    for (uint256 i = 0; i < _uniswapLPTokenNames.length; i++) {
      uniswapData.push(
        UniswapData({
          name: _uniswapLPTokenNames[i],
          symbol: _uniswapLPTokenSymbols[i],
          displayName: _uniswapLPTokenDisplayNames[i]
        })
      );
    }
  }

  string public name;
  string public symbol;

  struct TokenData {
    string name;
    string symbol;
  }
  mapping(address => TokenData) hardcoded;

  struct UniswapData {
    string name; // ie "Uniswap V2" or "SushiSwap LP Token"
    string symbol; // ie "UNI-V2" or "SLP"
    string displayName; // ie "SushiSwap" or "Uniswap"
  }
  UniswapData[] uniswapData;

  /**
   * @notice `PoolDirectory` contract object.
   */
  PoolDirectory public directory;

  /**
   * @dev Struct for Ionic pool summary data.
   */
  struct IonicPoolData {
    uint256 totalSupply;
    uint256 totalBorrow;
    address[] underlyingTokens;
    string[] underlyingSymbols;
    bool whitelistedAdmin;
  }

  /**
   * @notice Returns arrays of all public Ionic pool indexes, data, total supply balances (in ETH), total borrow balances (in ETH), arrays of underlying token addresses, arrays of underlying asset symbols, and booleans indicating if retrieving each pool's data failed.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getPublicPoolsWithData()
    external
    returns (uint256[] memory, PoolDirectory.Pool[] memory, IonicPoolData[] memory, bool[] memory)
  {
    (uint256[] memory indexes, PoolDirectory.Pool[] memory publicPools) = directory.getPublicPools();
    (IonicPoolData[] memory data, bool[] memory errored) = getPoolsData(publicPools);
    return (indexes, publicPools, data, errored);
  }

  /**
   * @notice Returns arrays of all whitelisted public Ionic pool indexes, data, total supply balances (in ETH), total borrow balances (in ETH), arrays of underlying token addresses, arrays of underlying asset symbols, and booleans indicating if retrieving each pool's data failed.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getPublicPoolsByVerificationWithData(
    bool whitelistedAdmin
  ) external returns (uint256[] memory, PoolDirectory.Pool[] memory, IonicPoolData[] memory, bool[] memory) {
    (uint256[] memory indexes, PoolDirectory.Pool[] memory publicPools) = directory.getPublicPoolsByVerification(
      whitelistedAdmin
    );
    (IonicPoolData[] memory data, bool[] memory errored) = getPoolsData(publicPools);
    return (indexes, publicPools, data, errored);
  }

  /**
   * @notice Returns arrays of the indexes of Ionic pools created by `account`, data, total supply balances (in ETH), total borrow balances (in ETH), arrays of underlying token addresses, arrays of underlying asset symbols, and booleans indicating if retrieving each pool's data failed.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getPoolsByAccountWithData(
    address account
  ) external returns (uint256[] memory, PoolDirectory.Pool[] memory, IonicPoolData[] memory, bool[] memory) {
    (uint256[] memory indexes, PoolDirectory.Pool[] memory accountPools) = directory.getPoolsByAccount(account);
    (IonicPoolData[] memory data, bool[] memory errored) = getPoolsData(accountPools);
    return (indexes, accountPools, data, errored);
  }

  /**
   * @notice Returns arrays of the indexes of Ionic pools used by `user`, data, total supply balances (in ETH), total borrow balances (in ETH), arrays of underlying token addresses, arrays of underlying asset symbols, and booleans indicating if retrieving each pool's data failed.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getPoolsOIonicrWithData(
    address user
  ) external returns (uint256[] memory, PoolDirectory.Pool[] memory, IonicPoolData[] memory, bool[] memory) {
    (uint256[] memory indexes, PoolDirectory.Pool[] memory userPools) = directory.getPoolsOfUser(user);
    (IonicPoolData[] memory data, bool[] memory errored) = getPoolsData(userPools);
    return (indexes, userPools, data, errored);
  }

  /**
   * @notice Internal function returning arrays of requested Ionic pool indexes, data, total supply balances (in ETH), total borrow balances (in ETH), arrays of underlying token addresses, arrays of underlying asset symbols, and booleans indicating if retrieving each pool's data failed.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getPoolsData(PoolDirectory.Pool[] memory pools) internal returns (IonicPoolData[] memory, bool[] memory) {
    IonicPoolData[] memory data = new IonicPoolData[](pools.length);
    bool[] memory errored = new bool[](pools.length);

    for (uint256 i = 0; i < pools.length; i++) {
      try this.getPoolSummary(IonicComptroller(pools[i].comptroller)) returns (
        uint256 _totalSupply,
        uint256 _totalBorrow,
        address[] memory _underlyingTokens,
        string[] memory _underlyingSymbols,
        bool _whitelistedAdmin
      ) {
        data[i] = IonicPoolData(_totalSupply, _totalBorrow, _underlyingTokens, _underlyingSymbols, _whitelistedAdmin);
      } catch {
        errored[i] = true;
      }
    }

    return (data, errored);
  }

  /**
   * @notice Returns total supply balance (in ETH), total borrow balance (in ETH), underlying token addresses, and underlying token symbols of a Ionic pool.
   */
  function getPoolSummary(
    IonicComptroller comptroller
  ) external returns (uint256, uint256, address[] memory, string[] memory, bool) {
    uint256 totalBorrow = 0;
    uint256 totalSupply = 0;
    ICErc20[] memory cTokens = comptroller.getAllMarkets();
    address[] memory underlyingTokens = new address[](cTokens.length);
    string[] memory underlyingSymbols = new string[](cTokens.length);
    BasePriceOracle oracle = comptroller.oracle();

    for (uint256 i = 0; i < cTokens.length; i++) {
      ICErc20 cToken = cTokens[i];
      (bool isListed, ) = comptroller.markets(address(cToken));
      if (!isListed) continue;
      cToken.accrueInterest();
      uint256 assetTotalBorrow = cToken.totalBorrowsCurrent();
      uint256 assetTotalSupply = cToken.getCash() +
        assetTotalBorrow -
        (cToken.totalReserves() + cToken.totalAdminFees() + cToken.totalIonicFees());
      uint256 underlyingPrice = oracle.getUnderlyingPrice(cToken);
      totalBorrow = totalBorrow + (assetTotalBorrow * underlyingPrice) / 1e18;
      totalSupply = totalSupply + (assetTotalSupply * underlyingPrice) / 1e18;

      underlyingTokens[i] = ICErc20(address(cToken)).underlying();
      (, underlyingSymbols[i]) = getTokenNameAndSymbol(underlyingTokens[i]);
    }

    bool whitelistedAdmin = directory.adminWhitelist(comptroller.admin());
    return (totalSupply, totalBorrow, underlyingTokens, underlyingSymbols, whitelistedAdmin);
  }

  /**
   * @dev Struct for a Ionic pool asset.
   */
  struct PoolAsset {
    address cToken;
    address underlyingToken;
    string underlyingName;
    string underlyingSymbol;
    uint256 underlyingDecimals;
    uint256 underlyingBalance;
    uint256 supplyRatePerBlock;
    uint256 borrowRatePerBlock;
    uint256 totalSupply;
    uint256 totalBorrow;
    uint256 supplyBalance;
    uint256 borrowBalance;
    uint256 liquidity;
    bool membership;
    uint256 exchangeRate; // Price of cTokens in terms of underlying tokens
    uint256 underlyingPrice; // Price of underlying tokens in ETH (scaled by 1e18)
    address oracle;
    uint256 collateralFactor;
    uint256 reserveFactor;
    uint256 adminFee;
    uint256 ionicFee;
    bool borrowGuardianPaused;
    bool mintGuardianPaused;
  }

  /**
   * @notice Returns data on the specified assets of the specified Ionic pool.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   * @param comptroller The Comptroller proxy contract address of the Ionic pool.
   * @param cTokens The cToken contract addresses of the assets to query.
   * @param user The user for which to get account data.
   * @return An array of Ionic pool assets.
   */
  function getPoolAssetsWithData(
    IonicComptroller comptroller,
    ICErc20[] memory cTokens,
    address user
  ) internal returns (PoolAsset[] memory) {
    uint256 arrayLength = 0;

    for (uint256 i = 0; i < cTokens.length; i++) {
      (bool isListed, ) = comptroller.markets(address(cTokens[i]));
      if (isListed) arrayLength++;
    }

    PoolAsset[] memory detailedAssets = new PoolAsset[](arrayLength);
    uint256 index = 0;
    BasePriceOracle oracle = BasePriceOracle(address(comptroller.oracle()));

    for (uint256 i = 0; i < cTokens.length; i++) {
      // Check if market is listed and get collateral factor
      (bool isListed, uint256 collateralFactorMantissa) = comptroller.markets(address(cTokens[i]));
      if (!isListed) continue;

      // Start adding data to PoolAsset
      PoolAsset memory asset;
      ICErc20 cToken = cTokens[i];
      asset.cToken = address(cToken);

      cToken.accrueInterest();

      // Get underlying asset data
      asset.underlyingToken = ICErc20(address(cToken)).underlying();
      ERC20Upgradeable underlying = ERC20Upgradeable(asset.underlyingToken);
      (asset.underlyingName, asset.underlyingSymbol) = getTokenNameAndSymbol(asset.underlyingToken);
      asset.underlyingDecimals = underlying.decimals();
      asset.underlyingBalance = underlying.balanceOf(user);

      // Get cToken data
      asset.supplyRatePerBlock = cToken.supplyRatePerBlock();
      asset.borrowRatePerBlock = cToken.borrowRatePerBlock();
      asset.liquidity = cToken.getCash();
      asset.totalBorrow = cToken.totalBorrowsCurrent();
      asset.totalSupply =
        asset.liquidity +
        asset.totalBorrow -
        (cToken.totalReserves() + cToken.totalAdminFees() + cToken.totalIonicFees());
      asset.supplyBalance = cToken.balanceOfUnderlying(user);
      asset.borrowBalance = cToken.borrowBalanceCurrent(user);
      asset.membership = comptroller.checkMembership(user, cToken);
      asset.exchangeRate = cToken.exchangeRateCurrent(); // We would use exchangeRateCurrent but we already accrue interest above
      asset.underlyingPrice = oracle.price(asset.underlyingToken);

      // Get oracle for this cToken
      asset.oracle = address(oracle);

      try MasterPriceOracle(asset.oracle).oracles(asset.underlyingToken) returns (BasePriceOracle _oracle) {
        asset.oracle = address(_oracle);
      } catch {}

      // More cToken data
      asset.collateralFactor = collateralFactorMantissa;
      asset.reserveFactor = cToken.reserveFactorMantissa();
      asset.adminFee = cToken.adminFeeMantissa();
      asset.ionicFee = cToken.ionicFeeMantissa();
      asset.borrowGuardianPaused = comptroller.borrowGuardianPaused(address(cToken));
      asset.mintGuardianPaused = comptroller.mintGuardianPaused(address(cToken));

      // Add to assets array and increment index
      detailedAssets[index] = asset;
      index++;
    }

    return (detailedAssets);
  }

  function getBorrowCapsPerCollateral(
    ICErc20 borrowedAsset,
    IonicComptroller comptroller
  )
    internal
    view
    returns (
      address[] memory collateral,
      uint256[] memory borrowCapsAgainstCollateral,
      bool[] memory borrowingBlacklistedAgainstCollateral
    )
  {
    ICErc20[] memory poolMarkets = comptroller.getAllMarkets();

    collateral = new address[](poolMarkets.length);
    borrowCapsAgainstCollateral = new uint256[](poolMarkets.length);
    borrowingBlacklistedAgainstCollateral = new bool[](poolMarkets.length);

    for (uint256 i = 0; i < poolMarkets.length; i++) {
      address collateralAddress = address(poolMarkets[i]);
      if (collateralAddress != address(borrowedAsset)) {
        collateral[i] = collateralAddress;
        borrowCapsAgainstCollateral[i] = comptroller.borrowCapForCollateral(address(borrowedAsset), collateralAddress);
        borrowingBlacklistedAgainstCollateral[i] = comptroller.borrowingAgainstCollateralBlacklist(
          address(borrowedAsset),
          collateralAddress
        );
      }
    }
  }

  /**
   * @notice Returns the `name` and `symbol` of `token`.
   * Supports Uniswap V2 and SushiSwap LP tokens as well as MKR.
   * @param token An ERC20 token contract object.
   * @return The `name` and `symbol`.
   */
  function getTokenNameAndSymbol(address token) internal view returns (string memory, string memory) {
    // i.e. MKR is a DSToken and uses bytes32
    if (bytes(hardcoded[token].symbol).length != 0) {
      return (hardcoded[token].name, hardcoded[token].symbol);
    }

    // Get name and symbol from token contract
    ERC20Upgradeable tokenContract = ERC20Upgradeable(token);
    string memory _name = tokenContract.name();
    string memory _symbol = tokenContract.symbol();

    return (_name, _symbol);
  }

  /**
   * @notice Returns the assets of the specified Ionic pool.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   * @param comptroller The Comptroller proxy contract of the Ionic pool.
   * @return An array of Ionic pool assets.
   */
  function getPoolAssetsWithData(IonicComptroller comptroller) external returns (PoolAsset[] memory) {
    return getPoolAssetsWithData(comptroller, comptroller.getAllMarkets(), msg.sender);
  }

  /**
   * @dev Struct for a Ionic pool user.
   */
  struct IonicPoolUser {
    address account;
    uint256 totalBorrow;
    uint256 totalCollateral;
    uint256 health;
  }

  /**
   * @notice Returns arrays of PoolAsset for a specific user
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getPoolAssetsByUser(IonicComptroller comptroller, address user) public returns (PoolAsset[] memory) {
    PoolAsset[] memory assets = getPoolAssetsWithData(comptroller, comptroller.getAssetsIn(user), user);
    return assets;
  }

  /**
   * @notice returns the total supply cap for each asset in the pool
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getSupplyCapsForPool(IonicComptroller comptroller) public view returns (address[] memory, uint256[] memory) {
    ICErc20[] memory poolMarkets = comptroller.getAllMarkets();

    address[] memory assets = new address[](poolMarkets.length);
    uint256[] memory supplyCapsPerAsset = new uint256[](poolMarkets.length);
    for (uint256 i = 0; i < poolMarkets.length; i++) {
      assets[i] = address(poolMarkets[i]);
      supplyCapsPerAsset[i] = comptroller.effectiveSupplyCaps(assets[i]);
    }

    return (assets, supplyCapsPerAsset);
  }

  /**
   * @notice returns the total supply cap for each asset in the pool and the total non-whitelist supplied assets
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getSupplyCapsDataForPool(
    IonicComptroller comptroller
  ) public view returns (address[] memory, uint256[] memory, uint256[] memory) {
    ICErc20[] memory poolMarkets = comptroller.getAllMarkets();

    address[] memory assets = new address[](poolMarkets.length);
    uint256[] memory supplyCapsPerAsset = new uint256[](poolMarkets.length);
    uint256[] memory nonWhitelistedTotalSupply = new uint256[](poolMarkets.length);
    for (uint256 i = 0; i < poolMarkets.length; i++) {
      assets[i] = address(poolMarkets[i]);
      supplyCapsPerAsset[i] = comptroller.effectiveSupplyCaps(assets[i]);
      uint256 assetTotalSupplied = poolMarkets[i].getTotalUnderlyingSupplied();
      uint256 whitelistedSuppliersSupply = comptroller.getWhitelistedSuppliersSupply(assets[i]);
      if (whitelistedSuppliersSupply >= assetTotalSupplied) nonWhitelistedTotalSupply[i] = 0;
      else nonWhitelistedTotalSupply[i] = assetTotalSupplied - whitelistedSuppliersSupply;
    }

    return (assets, supplyCapsPerAsset, nonWhitelistedTotalSupply);
  }

  /**
   * @notice returns the total borrow cap and the per collateral borrowing cap/blacklist for the asset
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getBorrowCapsForAsset(
    ICErc20 asset
  )
    public
    view
    returns (
      address[] memory collateral,
      uint256[] memory borrowCapsPerCollateral,
      bool[] memory collateralBlacklisted,
      uint256 totalBorrowCap
    )
  {
    IonicComptroller comptroller = IonicComptroller(asset.comptroller());
    (collateral, borrowCapsPerCollateral, collateralBlacklisted) = getBorrowCapsPerCollateral(asset, comptroller);
    totalBorrowCap = comptroller.effectiveBorrowCaps(address(asset));
  }

  /**
   * @notice returns the total borrow cap, the per collateral borrowing cap/blacklist for the asset and the total non-whitelist borrows
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getBorrowCapsDataForAsset(
    ICErc20 asset
  )
    public
    view
    returns (
      address[] memory collateral,
      uint256[] memory borrowCapsPerCollateral,
      bool[] memory collateralBlacklisted,
      uint256 totalBorrowCap,
      uint256 nonWhitelistedTotalBorrows
    )
  {
    IonicComptroller comptroller = IonicComptroller(asset.comptroller());
    (collateral, borrowCapsPerCollateral, collateralBlacklisted) = getBorrowCapsPerCollateral(asset, comptroller);
    totalBorrowCap = comptroller.effectiveBorrowCaps(address(asset));
    uint256 totalBorrows = asset.totalBorrowsCurrent();
    uint256 whitelistedBorrowersBorrows = comptroller.getWhitelistedBorrowersBorrows(address(asset));
    if (whitelistedBorrowersBorrows >= totalBorrows) nonWhitelistedTotalBorrows = 0;
    else nonWhitelistedTotalBorrows = totalBorrows - whitelistedBorrowersBorrows;
  }

  /**
   * @notice Returns arrays of Ionic pool indexes and data with a whitelist containing `account`.
   * Note that the whitelist does not have to be enforced.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getWhitelistedPoolsByAccount(
    address account
  ) public view returns (uint256[] memory, PoolDirectory.Pool[] memory) {
    (, PoolDirectory.Pool[] memory pools) = directory.getActivePools();
    uint256 arrayLength = 0;

    for (uint256 i = 0; i < pools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(pools[i].comptroller);

      if (comptroller.whitelist(account)) arrayLength++;
    }

    uint256[] memory indexes = new uint256[](arrayLength);
    PoolDirectory.Pool[] memory accountPools = new PoolDirectory.Pool[](arrayLength);
    uint256 index = 0;

    for (uint256 i = 0; i < pools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(pools[i].comptroller);

      if (comptroller.whitelist(account)) {
        indexes[index] = i;
        accountPools[index] = pools[i];
        index++;
        break;
      }
    }

    return (indexes, accountPools);
  }

  /**
   * @notice Returns arrays of the indexes of Ionic pools with a whitelist containing `account`, data, total supply balances (in ETH), total borrow balances (in ETH), arrays of underlying token addresses, arrays of underlying asset symbols, and booleans indicating if retrieving each pool's data failed.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getWhitelistedPoolsByAccountWithData(
    address account
  ) external returns (uint256[] memory, PoolDirectory.Pool[] memory, IonicPoolData[] memory, bool[] memory) {
    (uint256[] memory indexes, PoolDirectory.Pool[] memory accountPools) = getWhitelistedPoolsByAccount(account);
    (IonicPoolData[] memory data, bool[] memory errored) = getPoolsData(accountPools);
    return (indexes, accountPools, data, errored);
  }

  function getHealthFactor(address user, IonicComptroller pool) external view returns (uint256) {
    return getHealthFactorHypothetical(pool, user, address(0), 0, 0, 0);
  }

  function getHealthFactorHypothetical(
    IonicComptroller pool,
    address account,
    address cTokenModify,
    uint256 redeemTokens,
    uint256 borrowAmount,
    uint256 repayAmount
  ) public view returns (uint256) {
    (uint256 err, uint256 collateralValue, uint256 liquidity, uint256 shortfall) = pool.getHypotheticalAccountLiquidity(
      account,
      cTokenModify,
      redeemTokens,
      borrowAmount,
      repayAmount
    );

    if (err != 0) revert ComptrollerError(err);

    if (shortfall > 0) {
      // HF < 1.0
      return (collateralValue * 1e18) / (collateralValue + shortfall);
    } else {
      // HF >= 1.0
      if (collateralValue <= liquidity) return type(uint256).max;
      else return (collateralValue * 1e18) / (collateralValue - liquidity);
    }
  }
}

contracts/PoolLensSecondary.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";

import { IonicComptroller } from "./compound/ComptrollerInterface.sol";
import { ICErc20 } from "./compound/CTokenInterfaces.sol";
import { IUniswapV2Pair } from "./external/uniswap/IUniswapV2Pair.sol";

import { PoolDirectory } from "./PoolDirectory.sol";

interface IRewardsDistributor_PLS {
  function rewardToken() external view returns (address);

  function compSupplySpeeds(address) external view returns (uint256);

  function compBorrowSpeeds(address) external view returns (uint256);

  function compAccrued(address) external view returns (uint256);

  function flywheelPreSupplierAction(address cToken, address supplier) external;

  function flywheelPreBorrowerAction(address cToken, address borrower) external;

  function getAllMarkets() external view returns (ICErc20[] memory);
}

/**
 * @title PoolLensSecondary
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 * @notice PoolLensSecondary returns data on Ionic interest rate pools in mass for viewing by dApps, bots, etc.
 */
contract PoolLensSecondary is Initializable {
  /**
   * @notice Constructor to set the `PoolDirectory` contract object.
   */
  function initialize(PoolDirectory _directory) public initializer {
    require(address(_directory) != address(0), "PoolDirectory instance cannot be the zero address.");
    directory = _directory;
  }

  /**
   * @notice `PoolDirectory` contract object.
   */
  PoolDirectory public directory;

  /**
   * @notice Struct for ownership over a CToken.
   */
  struct CTokenOwnership {
    address cToken;
    address admin;
    bool adminHasRights;
    bool ionicAdminHasRights;
  }

  /**
   * @notice Returns the admin, admin rights, Ionic admin (constant), Ionic admin rights, and an array of cTokens with differing properties.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   * Ideally, we can add the `view` modifier, but many cToken functions potentially modify the state.
   */
  function getPoolOwnership(
    IonicComptroller comptroller
  ) external view returns (address, bool, bool, CTokenOwnership[] memory) {
    // Get pool ownership
    address comptrollerAdmin = comptroller.admin();
    bool comptrollerAdminHasRights = comptroller.adminHasRights();
    bool comptrollerIonicAdminHasRights = comptroller.ionicAdminHasRights();

    // Get cToken ownership
    ICErc20[] memory cTokens = comptroller.getAllMarkets();
    uint256 arrayLength = 0;

    for (uint256 i = 0; i < cTokens.length; i++) {
      ICErc20 cToken = cTokens[i];
      (bool isListed, ) = comptroller.markets(address(cToken));
      if (!isListed) continue;

      address cTokenAdmin;
      try cToken.admin() returns (address _cTokenAdmin) {
        cTokenAdmin = _cTokenAdmin;
      } catch {
        continue;
      }
      bool cTokenAdminHasRights = cToken.adminHasRights();
      bool cTokenIonicAdminHasRights = cToken.ionicAdminHasRights();

      // If outlier, push to array
      if (
        cTokenAdmin != comptrollerAdmin ||
        cTokenAdminHasRights != comptrollerAdminHasRights ||
        cTokenIonicAdminHasRights != comptrollerIonicAdminHasRights
      ) arrayLength++;
    }

    CTokenOwnership[] memory outliers = new CTokenOwnership[](arrayLength);
    uint256 arrayIndex = 0;

    for (uint256 i = 0; i < cTokens.length; i++) {
      ICErc20 cToken = cTokens[i];
      (bool isListed, ) = comptroller.markets(address(cToken));
      if (!isListed) continue;

      address cTokenAdmin;
      try cToken.admin() returns (address _cTokenAdmin) {
        cTokenAdmin = _cTokenAdmin;
      } catch {
        continue;
      }
      bool cTokenAdminHasRights = cToken.adminHasRights();
      bool cTokenIonicAdminHasRights = cToken.ionicAdminHasRights();

      // If outlier, push to array and increment array index
      if (
        cTokenAdmin != comptrollerAdmin ||
        cTokenAdminHasRights != comptrollerAdminHasRights ||
        cTokenIonicAdminHasRights != comptrollerIonicAdminHasRights
      ) {
        outliers[arrayIndex] = CTokenOwnership(
          address(cToken),
          cTokenAdmin,
          cTokenAdminHasRights,
          cTokenIonicAdminHasRights
        );
        arrayIndex++;
      }
    }

    return (comptrollerAdmin, comptrollerAdminHasRights, comptrollerIonicAdminHasRights, outliers);
  }

  /**
   * @notice Determine the maximum redeem amount of a cToken.
   * @param cTokenModify The market to hypothetically redeem in.
   * @param account The account to determine liquidity for.
   * @return Maximum redeem amount.
   */
  function getMaxRedeem(address account, ICErc20 cTokenModify) external returns (uint256) {
    return getMaxRedeemOrBorrow(account, cTokenModify, false);
  }

  /**
   * @notice Determine the maximum borrow amount of a cToken.
   * @param cTokenModify The market to hypothetically borrow in.
   * @param account The account to determine liquidity for.
   * @return Maximum borrow amount.
   */
  function getMaxBorrow(address account, ICErc20 cTokenModify) external returns (uint256) {
    return getMaxRedeemOrBorrow(account, cTokenModify, true);
  }

  /**
   * @dev Internal function to determine the maximum borrow/redeem amount of a cToken.
   * @param cTokenModify The market to hypothetically borrow/redeem in.
   * @param account The account to determine liquidity for.
   * @return Maximum borrow/redeem amount.
   */
  function getMaxRedeemOrBorrow(address account, ICErc20 cTokenModify, bool isBorrow) internal returns (uint256) {
    IonicComptroller comptroller = IonicComptroller(cTokenModify.comptroller());
    return comptroller.getMaxRedeemOrBorrow(account, cTokenModify, isBorrow);
  }

  /**
   * @notice Returns an array of all markets, an array of all `RewardsDistributor` contracts, an array of reward token addresses for each `RewardsDistributor`, an array of supply speeds for each distributor for each, and their borrow speeds.
   * @param comptroller The Ionic pool Comptroller to check.
   */
  function getRewardSpeedsByPool(
    IonicComptroller comptroller
  ) public view returns (ICErc20[] memory, address[] memory, address[] memory, uint256[][] memory, uint256[][] memory) {
    ICErc20[] memory allMarkets = comptroller.getAllMarkets();
    address[] memory distributors;

    try comptroller.getRewardsDistributors() returns (address[] memory _distributors) {
      distributors = _distributors;
    } catch {
      distributors = new address[](0);
    }

    address[] memory rewardTokens = new address[](distributors.length);
    uint256[][] memory supplySpeeds = new uint256[][](allMarkets.length);
    uint256[][] memory borrowSpeeds = new uint256[][](allMarkets.length);

    // Get reward tokens for each distributor
    for (uint256 i = 0; i < distributors.length; i++) {
      rewardTokens[i] = IRewardsDistributor_PLS(distributors[i]).rewardToken();
    }

    // Get reward speeds for each market for each distributor
    for (uint256 i = 0; i < allMarkets.length; i++) {
      address cToken = address(allMarkets[i]);
      supplySpeeds[i] = new uint256[](distributors.length);
      borrowSpeeds[i] = new uint256[](distributors.length);

      for (uint256 j = 0; j < distributors.length; j++) {
        IRewardsDistributor_PLS distributor = IRewardsDistributor_PLS(distributors[j]);
        supplySpeeds[i][j] = distributor.compSupplySpeeds(cToken);
        borrowSpeeds[i][j] = distributor.compBorrowSpeeds(cToken);
      }
    }

    return (allMarkets, distributors, rewardTokens, supplySpeeds, borrowSpeeds);
  }

  /**
   * @notice For each `Comptroller`, returns an array of all markets, an array of all `RewardsDistributor` contracts, an array of reward token addresses for each `RewardsDistributor`, an array of supply speeds for each distributor for each, and their borrow speeds.
   * @param comptrollers The Ionic pool Comptrollers to check.
   */
  function getRewardSpeedsByPools(
    IonicComptroller[] memory comptrollers
  )
    external
    view
    returns (ICErc20[][] memory, address[][] memory, address[][] memory, uint256[][][] memory, uint256[][][] memory)
  {
    ICErc20[][] memory allMarkets = new ICErc20[][](comptrollers.length);
    address[][] memory distributors = new address[][](comptrollers.length);
    address[][] memory rewardTokens = new address[][](comptrollers.length);
    uint256[][][] memory supplySpeeds = new uint256[][][](comptrollers.length);
    uint256[][][] memory borrowSpeeds = new uint256[][][](comptrollers.length);
    for (uint256 i = 0; i < comptrollers.length; i++)
      (allMarkets[i], distributors[i], rewardTokens[i], supplySpeeds[i], borrowSpeeds[i]) = getRewardSpeedsByPool(
        comptrollers[i]
      );
    return (allMarkets, distributors, rewardTokens, supplySpeeds, borrowSpeeds);
  }

  /**
   * @notice Returns unaccrued rewards by `holder` from `cToken` on `distributor`.
   * @param holder The address to check.
   * @param distributor The RewardsDistributor to check.
   * @param cToken The CToken to check.
   * @return Unaccrued (unclaimed) supply-side rewards and unaccrued (unclaimed) borrow-side rewards.
   */
  function getUnaccruedRewards(
    address holder,
    IRewardsDistributor_PLS distributor,
    ICErc20 cToken
  ) internal returns (uint256, uint256) {
    // Get unaccrued supply rewards
    uint256 compAccruedPrior = distributor.compAccrued(holder);
    distributor.flywheelPreSupplierAction(address(cToken), holder);
    uint256 supplyRewardsUnaccrued = distributor.compAccrued(holder) - compAccruedPrior;

    // Get unaccrued borrow rewards
    compAccruedPrior = distributor.compAccrued(holder);
    distributor.flywheelPreBorrowerAction(address(cToken), holder);
    uint256 borrowRewardsUnaccrued = distributor.compAccrued(holder) - compAccruedPrior;

    // Return both
    return (supplyRewardsUnaccrued, borrowRewardsUnaccrued);
  }

  /**
   * @notice Returns all unclaimed rewards accrued by the `holder` on `distributors`.
   * @param holder The address to check.
   * @param distributors The `RewardsDistributor` contracts to check.
   * @return For each of `distributors`: total quantity of unclaimed rewards, array of cTokens, array of unaccrued (unclaimed) supply-side and borrow-side rewards per cToken, and quantity of funds available in the distributor.
   */
  function getUnclaimedRewardsByDistributors(
    address holder,
    IRewardsDistributor_PLS[] memory distributors
  ) external returns (address[] memory, uint256[] memory, ICErc20[][] memory, uint256[2][][] memory, uint256[] memory) {
    address[] memory rewardTokens = new address[](distributors.length);
    uint256[] memory compUnclaimedTotal = new uint256[](distributors.length);
    ICErc20[][] memory allMarkets = new ICErc20[][](distributors.length);
    uint256[2][][] memory rewardsUnaccrued = new uint256[2][][](distributors.length);
    uint256[] memory distributorFunds = new uint256[](distributors.length);

    for (uint256 i = 0; i < distributors.length; i++) {
      IRewardsDistributor_PLS distributor = distributors[i];
      rewardTokens[i] = distributor.rewardToken();
      allMarkets[i] = distributor.getAllMarkets();
      rewardsUnaccrued[i] = new uint256[2][](allMarkets[i].length);
      for (uint256 j = 0; j < allMarkets[i].length; j++)
        (rewardsUnaccrued[i][j][0], rewardsUnaccrued[i][j][1]) = getUnaccruedRewards(
          holder,
          distributor,
          allMarkets[i][j]
        );
      compUnclaimedTotal[i] = distributor.compAccrued(holder);
      distributorFunds[i] = IERC20Upgradeable(rewardTokens[i]).balanceOf(address(distributor));
    }

    return (rewardTokens, compUnclaimedTotal, allMarkets, rewardsUnaccrued, distributorFunds);
  }

  /**
   * @notice Returns arrays of indexes, `Comptroller` proxy contracts, and `RewardsDistributor` contracts for Ionic pools supplied to by `account`.
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getRewardsDistributorsBySupplier(
    address supplier
  ) external view returns (uint256[] memory, IonicComptroller[] memory, address[][] memory) {
    // Get array length
    (, PoolDirectory.Pool[] memory pools) = directory.getActivePools();
    uint256 arrayLength = 0;

    for (uint256 i = 0; i < pools.length; i++) {
      try IonicComptroller(pools[i].comptroller).suppliers(supplier) returns (bool isSupplier) {
        if (isSupplier) arrayLength++;
      } catch {}
    }

    // Build array
    uint256[] memory indexes = new uint256[](arrayLength);
    IonicComptroller[] memory comptrollers = new IonicComptroller[](arrayLength);
    address[][] memory distributors = new address[][](arrayLength);
    uint256 index = 0;

    for (uint256 i = 0; i < pools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(pools[i].comptroller);

      try comptroller.suppliers(supplier) returns (bool isSupplier) {
        if (isSupplier) {
          indexes[index] = i;
          comptrollers[index] = comptroller;

          try comptroller.getRewardsDistributors() returns (address[] memory _distributors) {
            distributors[index] = _distributors;
          } catch {}

          index++;
        }
      } catch {}
    }

    // Return distributors
    return (indexes, comptrollers, distributors);
  }

  /**
   * @notice The returned list of flywheels contains address(0) for flywheels for which the user has no rewards to claim
   * @dev This function is not designed to be called in a transaction: it is too gas-intensive.
   */
  function getFlywheelsToClaim(
    address user
  ) external view returns (uint256[] memory, IonicComptroller[] memory, address[][] memory) {
    (uint256[] memory poolIds, PoolDirectory.Pool[] memory pools) = directory.getActivePools();

    IonicComptroller[] memory comptrollers = new IonicComptroller[](pools.length);
    address[][] memory distributors = new address[][](pools.length);

    for (uint256 i = 0; i < pools.length; i++) {
      IonicComptroller comptroller = IonicComptroller(pools[i].comptroller);
      try comptroller.getRewardsDistributors() returns (address[] memory _distributors) {
        comptrollers[i] = comptroller;
        distributors[i] = flywheelsWithRewardsForPoolUser(user, _distributors);
      } catch {}
    }

    return (poolIds, comptrollers, distributors);
  }

  function flywheelsWithRewardsForPoolUser(
    address user,
    address[] memory _distributors
  ) internal view returns (address[] memory) {
    address[] memory distributors = new address[](_distributors.length);
    for (uint256 j = 0; j < _distributors.length; j++) {
      if (IRewardsDistributor_PLS(_distributors[j]).compAccrued(user) > 0) {
        distributors[j] = _distributors[j];
      }
    }

    return distributors;
  }
}

