// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import "../stake/IStakeStrategy.sol";

/// @title IveION Interface
/// @notice Interface for veION contract
interface IveIONCore {
  /**
   * @notice Creates a new lock for multiple tokens and assigns it to a specified address
   * @param _tokenAddress Array of token addresses to lock
   * @param _tokenAmount Array of token amounts to lock
   * @param _duration Array of lock durations
   * @param _stakeUnderlying Array of booleans indicating whether to stake underlying tokens
   * @param _to Address to assign the lock to
   * @return The ID of the newly created veNFT
   */
  function createLockFor(
    address[] memory _tokenAddress,
    uint256[] memory _tokenAmount,
    uint256[] memory _duration,
    bool[] memory _stakeUnderlying,
    address _to
  ) external returns (uint256);

  /**
   * @notice Creates a new lock for multiple tokens
   * @param _tokenAddress Array of token addresses to lock
   * @param _tokenAmount Array of token amounts to lock
   * @param _duration Array of lock durations
   * @param _stakeUnderlying Array of booleans indicating whether to stake underlying tokens
   * @return The ID of the newly created veNFT
   */
  function createLock(
    address[] calldata _tokenAddress,
    uint256[] calldata _tokenAmount,
    uint256[] calldata _duration,
    bool[] memory _stakeUnderlying
  ) external returns (uint256);

  /**
   * @notice Increases the amount of tokens locked for a specific veNFT
   * @param _tokenAddress Address of the token to increase lock amount for
   * @param _tokenId ID of the veNFT
   * @param _tokenAmount Amount of tokens to add to the lock
   * @param _stakeUnderlying Whether to stake the underlying tokens
   */
  function increaseAmount(
    address _tokenAddress,
    uint256 _tokenId,
    uint256 _tokenAmount,
    bool _stakeUnderlying
  ) external;

  /**
   * @notice Locks additional asset type for an existing veNFT
   * @param _tokenAddress Address of the new token to lock
   * @param _tokenAmount Amount of tokens to lock
   * @param _tokenId ID of the veNFT
   * @param _duration Duration of the lock
   * @param _stakeUnderlying Whether to stake the underlying tokens
   */
  function lockAdditionalAsset(
    address _tokenAddress,
    uint256 _tokenAmount,
    uint256 _tokenId,
    uint256 _duration,
    bool _stakeUnderlying
  ) external;

  /**
   * @notice Increases the lock duration for a specific token in a veNFT
   * @param _tokenAddress Address of the token
   * @param _tokenId ID of the veNFT
   * @param _lockDuration New lock duration to extend to
   */
  function increaseUnlockTime(address _tokenAddress, uint256 _tokenId, uint256 _lockDuration) external;

  /**
   * @notice Delegates voting power from one veNFT to another.
   * @param fromTokenId The ID of the veNFT from which voting power is delegated.
   * @param toTokenId The ID of the veNFT to which voting power is delegated.
   * @param lpToken The address of the LP token associated with the delegation.
   * @param amount The amount of voting power to delegate.
   */
  function delegate(uint256 fromTokenId, uint256 toTokenId, address lpToken, uint256 amount) external;

  /**
   * @notice Removes delegatees from a specific veNFT
   * @param fromTokenId ID of the veNFT from which delegatees are removed
   * @param toTokenIds Array of veNFT IDs that are delegatees to be removed
   * @param lpToken Address of the LP token associated with the delegation
   * @param amounts Array of amounts of voting power to remove from each delegatee
   */
  function removeDelegatees(
    uint256 fromTokenId,
    uint256[] memory toTokenIds,
    address lpToken,
    uint256[] memory amounts
  ) external;

  /**
   * @notice Removes delegators from a specific veNFT
   * @param fromTokenIds Array of veNFT IDs that are delegators to be removed
   * @param toTokenId ID of the veNFT from which delegators are removed
   * @param lpToken Address of the LP token associated with the delegation
   * @param amounts Array of amounts of voting power to remove from each delegator
   */
  function removeDelegators(
    uint256[] memory fromTokenIds,
    uint256 toTokenId,
    address lpToken,
    uint256[] memory amounts
  ) external;

  /**
   * @notice Locks a token permanently.
   * @param _tokenAddress The address of the token to lock.
   * @param _tokenId The ID of the token to lock.
   */
  function lockPermanent(address _tokenAddress, uint256 _tokenId) external;

  /**
   * @notice Unlocks a permanently locked token.
   * @param _tokenAddress The address of the token to unlock.
   * @param _tokenId The ID of the token to unlock.
   */
  function unlockPermanent(address _tokenAddress, uint256 _tokenId) external;

  /**
   * @notice Updates voting status for a veNFT
   * @param _tokenId ID of the veNFT
   * @param _voting Voting status
   */
  function voting(uint256 _tokenId, bool _voting) external;

  /**
   * @notice Sets the implementation addresses for the veION contract extensions.
   * @dev This function can only be called by authorized entities.
   * @param _veIONFirstExtension The address of the first extension contract.
   * @param _veIONSecondExtension The address of the second extension contract.
   */
  function setExtensions(address _veIONFirstExtension, address _veIONSecondExtension) external;
}

/// @title IAeroVotingEscrow Interface
/// @notice Interface for Aero Voting Escrow contract
interface IAeroVotingEscrow {
  /**
   * @notice Returns the balance of the specified owner.
   * @param _owner The address of the owner.
   * @return The balance of the owner.
   */
  function balanceOf(address _owner) external view returns (uint256);

  /**
   * @notice Retrieves the token ID at a specific index for a given owner.
   * @param _owner The address of the owner.
   * @param _index The index of the token ID in the owner's list.
   * @return The token ID at the specified index.
   */
  function ownerToNFTokenIdList(address _owner, uint256 _index) external view returns (uint256);
}

/// @title IAeroVoter Interface
/// @notice Interface for Aero Voter contract
interface IAeroVoter {
  /**
   * @notice Returns the list of pools voted for by a specific token ID.
   * @param tokenId The ID of the token.
   * @return An array of addresses representing the pools voted for.
   */
  function poolVote(uint256 tokenId) external view returns (address[] memory);

  /**
   * @notice Retrieves the weight of a specific pool.
   * @param pool The address of the pool.
   * @return The weight of the pool.
   */
  function weights(address pool) external view returns (uint256);

  /**
   * @notice Returns the number of votes a specific token ID has for a given pool.
   * @param tokenId The ID of the token.
   * @param pool The address of the pool.
   * @return The number of votes for the pool.
   */
  function votes(uint256 tokenId, address pool) external view returns (uint256);
}

interface IAddressesProvider {
  function getAddress(string calldata id) external view returns (address);
}

interface IMasterPriceOracle {
  function price(address underlying) external view returns (uint256);
}

contracts/veION/interfaces/IveIONFirstEx.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import "../stake/IStakeStrategy.sol";
import { IveIONStructsEnumsErrorsEvents } from "./IveIONStructsEnumsErrorsEvents.sol";

/// @title IveION Interface First Extensions
/// @notice Interface for veION contract
interface IveIONFirstExtension is IveIONStructsEnumsErrorsEvents {
  /**
   * @notice Withdraws tokens associated with a specific token ID.
   * @param _tokenAddress The address of the token to withdraw.
   * @param _tokenId The ID of the token to withdraw.
   */
  function withdraw(address _tokenAddress, uint256 _tokenId) external;

  /**
   * @notice Merges two token IDs into one.
   * @param _from The ID of the token to merge from.
   * @param _to The ID of the token to merge into.
   */
  function merge(uint256 _from, uint256 _to) external;

  /**
   * @notice Splits a token into two separate tokens.
   * @param _tokenAddress The address of the token to split.
   * @param _from The ID of the token to split.
   * @param _splitAmount The amount to split from the original token.
   * @return _tokenId1 The ID of the first resulting token.
   * @return _tokenId2 The ID of the second resulting token.
   */
  function split(
    address _tokenAddress,
    uint256 _from,
    uint256 _splitAmount
  ) external returns (uint256 _tokenId1, uint256 _tokenId2);

  /**
   * @notice Claims emissions for a specific token.
   * @param _tokenAddress The address of the token for which to claim emissions.
   */
  function claimEmissions(address _tokenAddress) external;

  /**
   * @notice Allows or blocks delegators for a specific token ID.
   * @param _tokenId The ID of the token.
   * @param _tokenAddress The address of the token.
   * @param _blocked Boolean indicating if delegators are blocked.
   */
  function allowDelegators(uint256 _tokenId, address _tokenAddress, bool _blocked) external;

  /**
   * @notice Retrieves the balance of a specific NFT.
   * @param _tokenId The ID of the NFT.
   * @return _assets An array of asset addresses.
   * @return _balances An array of balances for each asset.
   * @return _boosts An array of boost values for each asset.
   */
  function balanceOfNFT(
    uint256 _tokenId
  ) external view returns (address[] memory _assets, uint256[] memory _balances, uint256[] memory _boosts);

  /**
   * @notice Retrieves the total ETH value of tokens owned by a specific address.
   * @param _owner The address of the owner.
   * @return totalValue The total ETH value of the tokens.
   */
  function getTotalEthValueOfTokens(address _owner) external view returns (uint256 totalValue);
}

contracts/veION/interfaces/IveIONSecondE.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import "../stake/IStakeStrategy.sol";
import { IveIONStructsEnumsErrorsEvents } from "./IveIONStructsEnumsErrorsEvents.sol";

/// @title IveION Interface Second Extension
/// @notice Interface for veION contract
interface IveIONSecondExtension is IveIONStructsEnumsErrorsEvents {
  /**
   * @notice Whitelists or removes tokens from the whitelist.
   * @param _tokens An array of token addresses to be whitelisted or removed.
   * @param _isWhitelisted An array of booleans indicating the whitelist status for each token.
   */
  function whitelistTokens(address[] memory _tokens, bool[] memory _isWhitelisted) external;

  /**
   * @notice Withdraws protocol fees for a specific token to a recipient address.
   * @param _tokenAddress The address of the token for which protocol fees are to be withdrawn.
   * @param _recipient The address to which the protocol fees will be sent.
   */
  function withdrawProtocolFees(address _tokenAddress, address _recipient) external;

  /**
   * @notice Withdraws distributed fees for a specific token to a recipient address.
   * @param _tokenAddress The address of the token for which distributed fees are to be withdrawn.
   * @param _recipient The address to which the distributed fees will be sent.
   */
  function withdrawDistributedFees(address _tokenAddress, address _recipient) external;

  /**
   * @notice Toggles the ability to split tokens for a specific account.
   * @param _account The address of the account.
   * @param _isAllowed Boolean indicating if splitting is allowed.
   */
  function toggleSplit(address _account, bool _isAllowed) external;

  /**
   * @notice Toggles the limited boost feature.
   * @param _isBoosted Boolean indicating if the boost is active.
   */
  function toggleLimitedBoost(bool _isBoosted) external;

  /**
   * @notice Sets the amount for a limited time boost.
   * @param _boostAmount The amount of the boost.
   */
  function setLimitedTimeBoost(uint256 _boostAmount) external;

  /**
   * @notice Sets the address of the voter.
   * @param _voter The address of the voter.
   */
  function setVoter(address _voter) external;

  /**
   * @notice Sets the minimum lock amount for a specific token.
   * @param _tokenAddress The address of the token.
   * @param _minimumAmount The minimum amount to lock.
   */
  function setMinimumLockAmount(address _tokenAddress, uint256 _minimumAmount) external;

  /**
   * @notice Sets the minimum lock duration.
   * @param _minimumLockDuration The minimum duration for locking.
   */
  function setMinimumLockDuration(uint256 _minimumLockDuration) external;

  /**
   * @notice Sets the address of the Ionic Pool.
   * @param _ionicPool The address of the Ionic Pool.
   */
  function setIonicPool(address _ionicPool) external;

  /**
   * @notice Sets the address of the Aero Voting contract.
   * @param _aeroVoting The address of the Aero Voting contract.
   */
  function setAeroVoting(address _aeroVoting) external;

  /**
   * @notice Sets the boost amount for Aero Voter.
   * @param _aeroVoterBoost The boost amount for Aero Voter.
   */
  function setAeroVoterBoost(uint256 _aeroVoterBoost) external;

  /**
   * @notice Sets the maximum early withdrawal fee.
   * @param _maxEarlyWithdrawFee The maximum fee for early withdrawal.
   */
  function setMaxEarlyWithdrawFee(uint256 _maxEarlyWithdrawFee) external;

  /**
   * @notice Sets the LP token type for a specific token.
   * @param _token The address of the token.
   * @param _type The LP token type.
   */
  function setLpTokenType(address _token, LpTokenType _type) external;

  /**
   * @notice Sets the stake strategy for a specific LP token type.
   * @param _lpType The LP token type.
   * @param _strategy The stake strategy.
   */
  function setStakeStrategy(LpTokenType _lpType, IStakeStrategy _strategy) external;

  /**
   * @notice Sets the address of the veAERO contract.
   * @param _veAERO The address of the veAERO contract.
   */
  function setVeAERO(address _veAERO) external;

  /**
   * @notice Retrieves the lock information for a specific user.
   * @param _tokenId The ID of the token.
   * @param _lpType The LP token type.
   * @return A LockedBalance struct containing lock details.
   */
  function getUserLock(uint256 _tokenId, LpTokenType _lpType) external view returns (LockedBalance memory);

  /**
   * @notice Retrieves the token IDs owned by a specific address.
   * @param _owner The address of the owner.
   * @return An array of token IDs owned by the address.
   */
  function getOwnedTokenIds(address _owner) external view returns (uint256[] memory);

  /**
   * @notice Retrieves the assets locked for a specific token ID.
   * @param _tokenId The ID of the token.
   * @return An array of addresses representing the locked assets.
   */
  function getAssetsLocked(uint256 _tokenId) external view returns (address[] memory);

  /**
   * @notice Retrieves the delegatees for a specific token ID and LP token type.
   * @param _tokenId The ID of the token.
   * @param _lpType The LP token type.
   * @return An array of delegatee IDs.
   */
  function getDelegatees(uint256 _tokenId, LpTokenType _lpType) external view returns (uint256[] memory);

  /**
   * @notice Retrieves the delegators for a specific token ID and LP token type.
   * @param _tokenId The ID of the token.
   * @param _lpType The LP token type.
   * @return An array of delegator IDs.
   */
  function getDelegators(uint256 _tokenId, LpTokenType _lpType) external view returns (uint256[] memory);

  /**
   * @notice Retrieves the user point for a specific token ID, LP token type, and epoch.
   * @param _tokenId The ID of the token.
   * @param _lpType The LP token type.
   * @param _epoch The epoch number.
   * @return A UserPoint struct containing user point details.
   */
  function getUserPoint(uint256 _tokenId, LpTokenType _lpType, uint256 _epoch) external view returns (UserPoint memory);
}

contracts/veION/interfaces/IveIONStructs.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

interface IveIONStructsEnumsErrorsEvents {
  /**
   * @notice Structure representing a locked balance
   * @param tokenAddress Address of the token
   * @param amount Amount of tokens locked
   * @param delegateAmount Amount of tokens delegated
   * @param start Start time of the lock
   * @param end End time of the lock
   * @param isPermanent Indicates if the lock is permanent
   * @param boost Boost value for the lock
   */
  struct LockedBalance {
    address tokenAddress;
    uint256 amount;
    uint256 delegateAmount;
    uint256 start;
    uint256 end;
    bool isPermanent;
    uint256 boost;
  }

  /**
   * @notice Structure representing a delegation
   * @param amount Amount of tokens delegated
   * @param delegatee ID of the delegatee
   */
  struct Delegation {
    uint256 amount;
    uint256 delegatee;
  }

  /**
   * @notice Structure representing a user point
   * @param bias Bias value
   * @param slope Slope value, representing -dweight / dt
   * @param ts Timestamp of the point
   * @param blk Block number of the point
   * @param permanent Permanent value
   * @param permanentDelegate Permanent delegate value
   */
  struct UserPoint {
    uint256 bias;
    uint256 slope;
    uint256 ts;
    uint256 blk;
    uint256 permanent;
    uint256 permanentDelegate;
  }

  /**
   * @notice Structure representing a global point
   * @param bias Bias value
   * @param slope Slope value, representing -dweight / dt
   * @param ts Timestamp of the point
   * @param blk Block number of the point
   * @param permanentLockBalance Permanent lock balance
   */
  struct GlobalPoint {
    int128 bias;
    int128 slope;
    uint256 ts;
    uint256 blk;
    uint256 permanentLockBalance;
  }

  /**
   * @notice Structure representing a checkpoint
   * @param fromTimestamp Timestamp from which the checkpoint is valid
   * @param owner Address of the owner
   * @param delegatedBalance Balance that has been delegated
   * @param delegatee ID of the delegatee
   */
  struct Checkpoint {
    uint256 fromTimestamp;
    address owner;
    uint256 delegatedBalance;
    uint256 delegatee;
  }

  /**
   * @notice Enum representing deposit types
   */
  enum DepositType {
    DEPOSIT_FOR_TYPE,
    CREATE_LOCK_TYPE,
    INCREASE_LOCK_AMOUNT,
    INCREASE_UNLOCK_TIME,
    LOCK_ADDITIONAL
  }

  /**
   * @notice Enum representing LP token types
   */
  enum LpTokenType {
    Mode_Velodrome_5050_ION_MODE,
    Mode_Balancer_8020_ION_ETH,
    Base_Aerodrome_5050_ION_wstETH,
    Base_Balancer_8020_ION_ETH,
    Optimism_Velodrome_5050_ION_OP,
    Optimism_Balancer_8020_ION_ETH
  }

  error LockDurationNotInFuture();
  error ZeroAmount();
  error LockDurationTooLong();
  error TokenNotWhitelisted();
  error NotOwner();
  error AlreadyVoted();
  error PermanentLock();
  error NoLockFound();
  error LockExpired();
  error SameNFT();
  error SplitNotAllowed();
  error AmountTooBig();
  error NotPermanentLock();
  error TokenHasDelegatees();
  error TokenHasDelegators();
  error NotVoter();
  error MinimumNotMet();
  error ArrayMismatch();
  error LockDurationTooShort();
  error DuplicateAsset();
  error SplitTooSmall();
  error NotEnoughRemainingAfterSplit();
  error NoDelegationBetweenTokens(uint256 _tokenId1, uint256 _tokenId2);
  error NoUnderlyingStake();
  error NotAcceptingDelegators();
  error BoostAmountMustBeGreaterThanZero();
  error InvalidAddress();
  error MinimumAmountMustBeGreaterThanZero();
  error MinimumLockDurationMustBeGreaterThanZero();
  error AeroBoostAmountMustBeGreaterThanZero();
  error MaxEarlyWithdrawFeeMustBeGreaterThanZero();
  error InvalidTokenAddress();
  error InvalidStrategyAddress();
  error InvalidVeAEROAddress();

  event Deposit(
    address indexed provider,
    uint256 indexed tokenId,
    DepositType indexed depositType,
    uint256 value,
    uint256 locktime,
    uint256 ts
  );
  event Withdraw(address indexed provider, uint256 indexed tokenId, uint256 value, uint256 ts);
  event Supply(uint256 prevSupply, uint256 supply);
  event Delegated(uint256 indexed fromTokenId, uint256 indexed toTokenId, address lpToken, uint256 amount);
  event DelegationRemoved(uint256 indexed fromTokenId, uint256 indexed toTokenId, address lpToken, uint256 amount);
  event ProtocolFeesWithdrawn(address indexed tokenAddress, address indexed recipient, uint256 amount);
  event DistributedFeesWithdrawn(address indexed tokenAddress, address indexed recipient, uint256 amount);
  event SplitToggle(address indexed account, bool isAllowed);
  event LimitedBoostToggled(bool isBoosted);
  event LimitedTimeBoostSet(uint256 boostAmount);
  event VoterSet(address indexed newVoter);
  event AeroVotingSet(address indexed newAeroVoting);
  event AeroVoterBoostSet(uint256 newAeroVoterBoost);
  event TokensWhitelisted(address[] token, bool[] isWhitelisted);
  event LpTokenTypeSet(address indexed token, LpTokenType lpTokenType);
  event VeAEROSet(address indexed veAERO);
  event StakeStrategySet(LpTokenType indexed lpTokenType, address indexed strategy);
  event MinimumLockAmountSet(address indexed tokenAddress, uint256 minimumAmount);
  event MinimumLockDurationSet(uint256 minimumDuration);
  event IonicPoolSet(address indexed newIonicPool);
  event SplitCompleted(
    uint256 indexed fromTokenId,
    uint256 indexed tokenId1,
    uint256 indexed tokenId2,
    uint256 splitAmount,
    address tokenAddress
  );
  event MergeCompleted(
    uint256 indexed fromTokenId,
    uint256 indexed toTokenId,
    address[] assetsLocked,
    uint256 lengthOfAssets
  );
  event EmissionsClaimed(address indexed claimant, address indexed tokenAddress);
  event MaxEarlyWithdrawFeeSet(uint256 maxEarlyWithdrawFee);
  event PermanentLockCreated(address indexed tokenAddress, uint256 indexed tokenId, uint256 amount);
  event PermanentLockRemoved(address indexed tokenAddress, uint256 indexed tokenId, uint256 amount);
  event Voted(uint256 _tokenId, bool _voting);
  event DelegatorsBlocked(uint256 indexed _tokenId, address indexed _lpToken, bool _blocked);
  event Initialized(address indexed addressesProvider);
  event ExtensionsSet(address indexed _firstExtension, address indexed _secondExtension);
}

contracts/veION/interfaces/IVoter.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

/**
 * @title IVoter
 * @notice Interface for the Voter contract, which manages voting and reward distribution.
 */
interface IVoter {
  /// @notice Error thrown when a user has already voted or deposited.
  error AlreadyVotedOrDeposited();

  /// @notice Error thrown when an action is attempted outside the distribution window.
  error DistributeWindow();

  /// @notice Error thrown when a reward accumulator does not exist for a given pool.
  error RewardAccumulatorDoesNotExist(address _pool);

  /// @notice Error thrown when a reward accumulator is not alive.
  error RewardAccumulatorNotAlive(address _rewardAccumulator);

  /// @notice Error thrown when a market already exists.
  error MarketAlreadyExists();

  /// @notice Error thrown when the maximum voting number is too low.
  error MaximumVotingNumberTooLow();

  /// @notice Error thrown when array lengths do not match.
  error MismatchedArrayLengths();

  /// @notice Error thrown when there are non-zero votes.
  error NonZeroVotes();

  /// @notice Error thrown when the caller is not the owner.
  error NotOwner();

  /// @notice Error thrown when an action is attempted outside the distribution window.
  error NotDistributeWindow();

  /// @notice Error thrown when the caller is not the governor.
  error NotGovernor();

  /// @notice Error thrown when an NFT is not whitelisted.
  error NotWhitelistedNFT();

  /// @notice Error thrown when the same value is provided.
  error SameValue();

  /// @notice Error thrown when there are too many pools.
  error TooManyPools();

  /// @notice Error thrown when array lengths are unequal.
  error UnequalLengths();

  /// @notice Error thrown when an address is zero.
  error ZeroAddress();

  /// @notice Error thrown when the tokens array is empty.
  error TokensArrayEmpty();

  /// @notice Error thrown when the weight is zero.
  error ZeroWeight();

  /**
   * @notice Struct to store vote details.
   * @param marketVotes Array of market addresses voted for.
   * @param marketVoteSides Array of market sides voted for.
   * @param votes Array of vote weights.
   * @param usedWeight Total weight used in voting.
   */
  struct VoteDetails {
    address[] marketVotes;
    MarketSide[] marketVoteSides;
    uint256[] votes;
    uint256 usedWeight;
  }

  /**
   * @notice Struct to store market information.
   * @param marketAddress Address of the market.
   * @param side Side of the market (Supply or Borrow).
   */
  struct Market {
    address marketAddress;
    MarketSide side;
  }

  /**
   * @notice Struct to store variables used in voting.
   * @param totalWeight Total weight available for voting.
   * @param usedWeight Weight used in voting.
   * @param market Address of the market.
   * @param marketSide Side of the market.
   * @param rewardAccumulator Address of the reward accumulator.
   * @param marketWeight Weight of the market.
   * @param bribes Address of the bribes.
   */
  struct VoteVars {
    uint256 totalWeight;
    uint256 usedWeight;
    address market;
    MarketSide marketSide;
    address rewardAccumulator;
    uint256 marketWeight;
    address bribes;
  }

  /**
   * @notice Struct to store local variables used in voting.
   * @param sender Address of the sender.
   * @param timestamp Timestamp of the vote.
   * @param votingLPs Array of voting LP addresses.
   * @param votingLPBalances Array of voting LP balances.
   * @param boosts Array of boosts.
   */
  struct VoteLocalVars {
    address sender;
    uint256 timestamp;
    address[] votingLPs;
    uint256[] votingLPBalances;
    uint256[] boosts;
  }

  /**
   * @notice Enum to represent the side of a market.
   */
  enum MarketSide {
    Supply,
    Borrow
  }

  /**
   * @notice Event emitted when a vote is cast.
   * @param voter Address of the voter.
   * @param pool Address of the pool.
   * @param tokenId ID of the token.
   * @param weight Weight of the vote.
   * @param totalWeight Total weight of the vote.
   * @param timestamp Timestamp of the vote.
   */
  event Voted(
    address indexed voter,
    address indexed pool,
    uint256 indexed tokenId,
    uint256 weight,
    uint256 totalWeight,
    uint256 timestamp
  );

  /**
   * @notice Event emitted when a vote is abstained.
   * @param voter Address of the voter.
   * @param pool Address of the pool.
   * @param tokenId ID of the token.
   * @param weight Weight of the vote.
   * @param totalWeight Total weight of the vote.
   * @param timestamp Timestamp of the vote.
   */
  event Abstained(
    address indexed voter,
    address indexed pool,
    uint256 indexed tokenId,
    uint256 weight,
    uint256 totalWeight,
    uint256 timestamp
  );

  /**
   * @notice Event emitted when a reward is notified.
   * @param sender Address of the sender.
   * @param reward Address of the reward.
   * @param amount Amount of the reward.
   */
  event NotifyReward(address indexed sender, address indexed reward, uint256 amount);

  /**
   * @notice Event emitted when a token is whitelisted.
   * @param whitelister Address of the whitelister.
   * @param token Address of the token.
   * @param _bool Boolean indicating whitelist status.
   */
  event WhitelistToken(address indexed whitelister, address indexed token, bool indexed _bool);

  /**
   * @notice Event emitted when an NFT is whitelisted.
   * @param whitelister Address of the whitelister.
   * @param tokenId ID of the token.
   * @param _bool Boolean indicating whitelist status.
   */
  event WhitelistNFT(address indexed whitelister, uint256 indexed tokenId, bool indexed _bool);

  event LpTokensSet(address[] indexed lpTokens);
  event MpoSet(address indexed mpo);
  event GovernorSet(address indexed governor);
  event MarketsAdded(Market[] markets);
  event MarketRewardAccumulatorsSet(
    address[] indexed markets,
    MarketSide[] indexed marketSides,
    address[] indexed rewardAccumulators
  );
  event BribesSet(address[] indexed rewardAccumulators, address[] indexed bribes);
  event MaxVotingNumSet(uint256 indexed maxVotingNum);
  event RewardAccumulatorAliveToggled(address indexed market, MarketSide indexed marketSide, bool isAlive);
  event Initialized(address[] tokens, address mpo, address rewardToken, address ve, address governor);
  event DistributionTimelockAliveToggled(bool isAlive);

  /**
   * @notice Get the weight of a market.
   * @param market Address of the market.
   * @param marketSide Side of the market.
   * @param lpToken Address of the LP token.
   * @return The weight of the market.
   */
  function weights(address market, MarketSide marketSide, address lpToken) external view returns (uint256);

  /**
   * @notice Get the votes for a token.
   * @param tokenId ID of the token.
   * @param market Address of the market.
   * @param marketSide Side of the market.
   * @param lpToken Address of the LP token.
   * @return The votes for the token.
   */
  function votes(
    uint256 tokenId,
    address market,
    MarketSide marketSide,
    address lpToken
  ) external view returns (uint256);

  /**
   * @notice Get the used weights for a token.
   * @param tokenId ID of the token.
   * @param lpToken Address of the LP token.
   * @return The used weights for the token.
   */
  function usedWeights(uint256 tokenId, address lpToken) external view returns (uint256);

  /**
   * @notice Get the last voted timestamp for a token.
   * @param tokenId ID of the token.
   * @return The last voted timestamp for the token.
   */
  function lastVoted(uint256 tokenId) external view returns (uint256);

  /**
   * @notice Check if a token is whitelisted.
   * @param token Address of the token.
   * @return True if the token is whitelisted, false otherwise.
   */
  function isWhitelistedToken(address token) external view returns (bool);

  /**
   * @notice Check if an NFT is whitelisted.
   * @param tokenId ID of the token.
   * @return True if the NFT is whitelisted, false otherwise.
   */
  function isWhitelistedNFT(uint256 tokenId) external view returns (bool);

  /**
   * @notice Get the address of the ve contract.
   * @return The address of the ve contract.
   */
  function ve() external view returns (address);

  /**
   * @notice Get the address of the governor.
   * @return The address of the governor.
   */
  function governor() external view returns (address);

  /**
   * @notice Update voting balances in voting rewards contracts.
   * @param _tokenId ID of veNFT whose balance you wish to update.
   */
  function poke(uint256 _tokenId) external;

  /**
   * @notice Vote for pools. Votes distributed proportionally based on weights.
   * @dev Can only vote or deposit into a managed NFT once per epoch.
   *      Can only vote for gauges that have not been killed.
   *      Throws if length of _poolVote and _weights do not match.
   * @param _tokenId ID of veNFT you are voting with.
   * @param _poolVote Array of pools you are voting for.
   * @param _marketVoteSide Array of market vote sides you are voting for.
   * @param _weights Weights of pools.
   */
  function vote(
    uint256 _tokenId,
    address[] calldata _poolVote,
    MarketSide[] calldata _marketVoteSide,
    uint256[] calldata _weights
  ) external;

  /**
   * @notice Reset voting state. Required if you wish to make changes to veNFT state.
   * @dev Cannot reset in the same epoch that you voted in.
   *      Can vote or deposit into a managed NFT again after reset.
   * @param _tokenId ID of veNFT that you are resetting.
   */
  function reset(uint256 _tokenId) external;

  /**
   * @notice Distributes rewards to eligible markets.
   */
  function distributeRewards() external;

  /**
   * @notice Claim bribes for a given NFT.
   * @dev Utility to help batch bribe claims.
   * @param _bribes Array of BribeVotingReward contracts to collect from.
   * @param _tokens Array of tokens that are used as bribes.
   * @param _tokenId ID of veNFT that you wish to claim bribes for.
   */
  function claimBribes(address[] memory _bribes, address[][] memory _tokens, uint256 _tokenId) external;

  /**
   * @notice Whitelist (or unwhitelist) token for use in bribes.
   * @dev Throws if not called by governor.
   * @param _token Address of the token.
   * @param _bool Boolean indicating whitelist status.
   */
  function whitelistToken(address _token, bool _bool) external;

  /**
   * @notice Whitelist (or unwhitelist) token id for voting in last hour prior to epoch flip.
   * @dev Throws if not called by governor.
   *      Throws if already whitelisted.
   * @param _tokenId ID of the token.
   * @param _bool Boolean indicating whitelist status.
   */
  function whitelistNFT(uint256 _tokenId, bool _bool) external;

  /**
   * @notice Set the LP tokens.
   * @param _lpTokens Array of LP token addresses.
   */
  function setLpTokens(address[] memory _lpTokens) external;

  /**
   * @notice Set the Master Price Oracle (MPO) address.
   * @param _mpo Address of the Master Price Oracle.
   */
  function setMpo(address _mpo) external;

  /**
   * @notice Set a new governor.
   * @param _governor Address of the new governor.
   */
  function setGovernor(address _governor) external;

  /**
   * @notice Add new markets.
   * @param _markets Array of Market structs to be added.
   */
  function addMarkets(Market[] calldata _markets) external;

  /**
   * @notice Set reward accumulators for markets.
   * @param _markets Array of market addresses.
   * @param _marketSides Array of market sides.
   * @param _rewardAccumulators Array of reward accumulator addresses.
   */
  function setMarketRewardAccumulators(
    address[] calldata _markets,
    MarketSide[] calldata _marketSides,
    address[] calldata _rewardAccumulators
  ) external;

  /**
   * @notice Set bribes for reward accumulators.
   * @param _rewardAccumulators Array of reward accumulator addresses.
   * @param _bribes Array of bribe addresses.
   */
  function setBribes(address[] calldata _rewardAccumulators, address[] calldata _bribes) external;

  /**
   * @notice Set the maximum number of votes.
   * @param _maxVotingNum Maximum number of votes allowed.
   */
  function setMaxVotingNum(uint256 _maxVotingNum) external;

  /**
   * @notice Toggle the alive status of a reward accumulator.
   * @param _market Address of the market.
   * @param _marketSide Side of the market.
   * @param _isAlive Boolean indicating if the reward accumulator is alive.
   */
  function toggleRewardAccumulatorAlive(address _market, MarketSide _marketSide, bool _isAlive) external;

  /**
   * @notice Toggle the alive status of a timelock on distributing rewards.
   * @param _isAlive Boolean indicating if the timelock is alive.
   */
  function toggleDistributionTimelockAlive(bool _isAlive) external;

  /**
   * @notice Get the start of the epoch for a given timestamp.
   * @param _timestamp The timestamp to calculate the epoch start for.
   * @return The start of the epoch.
   */
  function epochStart(uint256 _timestamp) external pure returns (uint256);

  /**
   * @notice Get the next epoch for a given timestamp.
   * @param _timestamp The timestamp to calculate the next epoch for.
   * @return The next epoch.
   */
  function epochNext(uint256 _timestamp) external pure returns (uint256);

  /**
   * @notice Get the start of the voting period for a given timestamp.
   * @param _timestamp The timestamp to calculate the voting start for.
   * @return The start of the voting period.
   */
  function epochVoteStart(uint256 _timestamp) external pure returns (uint256);

  /**
   * @notice Get the end of the voting period for a given timestamp.
   * @param _timestamp The timestamp to calculate the voting end for.
   * @return The end of the voting period.
   */
  function epochVoteEnd(uint256 _timestamp) external pure returns (uint256);

  /**
   * @notice Get the number of markets.
   * @return The number of markets.
   */
  function marketsLength() external view returns (uint256);

  /**
   * @notice Get all LP reward tokens.
   * @return An array of addresses representing all LP reward tokens.
   */
  function getAllLpRewardTokens() external view returns (address[] memory);

  /**
   * @notice Get vote details for a specific token ID and LP asset.
   * @param _tokenId The ID of the token.
   * @param _lpAsset The address of the LP asset.
   * @return A struct containing vote details.
   */
  function getVoteDetails(uint256 _tokenId, address _lpAsset) external view returns (VoteDetails memory);
}

contracts/veION/libraries/BalanceLogicLi.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import "../interfaces/IveIONStructsEnumsErrorsEvents.sol";

library BalanceLogicLibrary {
  uint256 internal constant _WEEK = 1 weeks;

  /// @notice Calculates the voting power for a given NFT at a specific time.
  /// @dev This function is compatible with the ERC20 `balanceOf` interface for Aragon.
  ///      It retrieves the last user point before a specified timestamp and computes the voting power at that time.
  /// @param s_userPointEpoch Mapping of user point epochs for all tokens.
  /// @param s_userPointHistory Mapping of user point history for all tokens.
  /// @param _lpType The type of LP token associated with the NFT.
  /// @param _tokenId The ID of the NFT for which to calculate voting power.
  /// @param _t The epoch time at which to calculate the voting power.
  /// @param _isPermanent A boolean indicating if the lock is permanent.
  /// @return The calculated voting power of the user at the specified time.
  function balanceOfNFTAt(
    mapping(uint256 => mapping(IveIONStructsEnumsErrorsEvents.LpTokenType => uint256)) storage s_userPointEpoch,
    mapping(uint256 => mapping(IveIONStructsEnumsErrorsEvents.LpTokenType => IveIONStructsEnumsErrorsEvents.UserPoint[1000000000]))
      storage s_userPointHistory,
    IveIONStructsEnumsErrorsEvents.LpTokenType _lpType,
    uint256 _tokenId,
    uint256 _t,
    bool _isPermanent
  ) internal view returns (uint256) {
    uint256 _epoch = getPastUserPointIndex(s_userPointEpoch, s_userPointHistory, _lpType, _tokenId, _t);
    // epoch 0 is an empty point
    if (_epoch == 0) return 0;
    IveIONStructsEnumsErrorsEvents.UserPoint memory lastPoint = s_userPointHistory[_tokenId][_lpType][_epoch];
    if (_isPermanent) {
      return lastPoint.permanent + lastPoint.permanentDelegate;
    } else {
      uint256 reduction = lastPoint.slope * (_t - lastPoint.ts);
      if (reduction > lastPoint.bias) {
        lastPoint.bias = 0;
      } else {
        lastPoint.bias -= reduction;
      }
      return lastPoint.bias;
    }
  }

  /// @notice Binary search to get the user point index for a token id at or prior to a given timestamp
  /// @dev If a user point does not exist prior to the timestamp, this will return 0.
  /// @param s_userPointEpoch State of all user point epochs
  /// @param s_userPointHistory State of all user point history
  /// @param _tokenId .
  /// @param _timestamp .
  /// @return User point index
  function getPastUserPointIndex(
    mapping(uint256 => mapping(IveIONStructsEnumsErrorsEvents.LpTokenType => uint256)) storage s_userPointEpoch,
    mapping(uint256 => mapping(IveIONStructsEnumsErrorsEvents.LpTokenType => IveIONStructsEnumsErrorsEvents.UserPoint[1000000000]))
      storage s_userPointHistory,
    IveIONStructsEnumsErrorsEvents.LpTokenType _lpType,
    uint256 _tokenId,
    uint256 _timestamp
  ) internal view returns (uint256) {
    uint256 _userEpoch = s_userPointEpoch[_tokenId][_lpType];
    if (_userEpoch == 0) return 0;
    // First check most recent balance
    if (s_userPointHistory[_tokenId][_lpType][_userEpoch].ts <= _timestamp) return (_userEpoch);
    // Next check implicit zero balance
    if (s_userPointHistory[_tokenId][_lpType][1].ts > _timestamp) return 0;
    uint256 lower = 0;
    uint256 upper = _userEpoch;
    while (upper > lower) {
      uint256 center = upper - (upper - lower) / 2; // ceil, avoiding overflow
      IveIONStructsEnumsErrorsEvents.UserPoint storage userPoint = s_userPointHistory[_tokenId][_lpType][center];
      if (userPoint.ts == _timestamp) {
        return center;
      } else if (userPoint.ts < _timestamp) {
        lower = center;
      } else {
        upper = center - 1;
      }
    }
    return lower;
  }
}

contracts/veION/libraries/IonicTimeLibra.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

library IonicTimeLibrary {
  uint256 internal constant _WEEK = 7 days;

  /// @dev Returns start of epoch based on current timestamp
  function epochStart(uint256 timestamp) internal pure returns (uint256) {
    unchecked {
      return timestamp - (timestamp % _WEEK);
    }
  }

  /// @dev Returns start of next epoch / end of current epoch
  function epochNext(uint256 timestamp) internal pure returns (uint256) {
    unchecked {
      return timestamp - (timestamp % _WEEK) + _WEEK;
    }
  }

  /// @dev Returns start of voting window
  function epochVoteStart(uint256 timestamp) internal pure returns (uint256) {
    unchecked {
      return timestamp - (timestamp % _WEEK) + 1 hours;
    }
  }

  /// @dev Returns end of voting window / beginning of unrestricted voting window
  function epochVoteEnd(uint256 timestamp) internal pure returns (uint256) {
    unchecked {
      return timestamp - (timestamp % _WEEK) + _WEEK - 12 hours;
    }
  }
}

contracts/veION/RewardAccumulator.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import { Ownable2StepUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/access/Ownable2StepUpgradeable.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract RewardAccumulator is Ownable2StepUpgradeable {
  using SafeERC20 for IERC20;

  constructor() {
    _disableInitializers(); // Locks the implementation contract from being initialized
  }

  function initialize() external initializer {
    __Ownable2Step_init();
  }

  function approve(address _token, address _spender) external onlyOwner {
    IERC20(_token).safeIncreaseAllowance(_spender, type(uint256).max);
  }
}

contracts/veION/stake/IStakeStrategy.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

/**
 * @title IStakeStrategy
 * @notice Interface for the VeloIonModeStakingModeReward contract.
 */
interface IStakeStrategy {
  /// @notice Emitted when the contract is initialized
  event Initialized(
    address indexed escrow,
    address indexed stakingToken,
    address indexed stakingContract,
    address stakingWalletImplementation
  );

  /// @notice Emitted when tokens are staked
  event Staked(address indexed from, uint256 amount, address indexed veloWallet);

  /// @notice Emitted when rewards are claimed
  event Claimed(address indexed from, address indexed veloWallet);

  /// @notice Emitted when tokens are withdrawn
  event Withdrawn(address indexed owner, address indexed withdrawTo, uint256 amount);

  /// @notice Emitted when staking wallet is transferred
  event StakingWalletTransferred(address indexed from, address indexed to, uint256 amount);

  /// @notice Emitted when escrow is set
  event EscrowSet(address indexed newEscrow);

  /// @notice Emitted when staking token is set
  event StakingTokenSet(address indexed newStakingToken);

  /// @notice Emitted when staking contract is set
  event StakingContractSet(address indexed newStakingContract);

  /// @notice Emitted when upgradeable beacon is set
  event UpgradeableBeaconSet(address indexed newBeacon);

  /**
   * @notice Stakes a specified amount of tokens from a given address.
   * @param _from The address from which tokens will be staked.
   * @param _amount The amount of tokens to stake.
   * @param _data Additional data that might be needed for staking.
   */
  function stake(address _from, uint256 _amount, bytes memory _data) external;

  /**
   * @notice Claims rewards for a given address.
   * @param _from The address for which to claim rewards.
   */
  function claim(address _from) external;

  /**
   * @notice Withdraws a specified amount of tokens for a given address.
   * @param _owner The address from which tokens will be withdrawn.
   * @param _amount The amount of tokens to withdraw.
   */
  function withdraw(address _owner, address _withdrawTo, uint256 _amount) external;

  /**
   * @notice Returns the current reward rate for the staking strategy.
   * @return The reward rate as a uint256.
   */
  function rewardRate() external view returns (uint256);

  /**
   * @notice Returns the period finish time for the staking strategy.
   * @return The period finish time as a uint256.
   */
  function periodFinish() external view returns (uint256);

  /**
   * @notice Returns the balance of a specific address.
   * @param account The address to query the balance of.
   * @return The balance as a uint256.
   */
  function balanceOf(address account) external view returns (uint256);

  /**
   * @notice Returns the total supply of staked tokens.
   * @return The total supply as a uint256.
   */
  function totalSupply() external view returns (uint256);

  /**
   * @notice Returns the address of the reward token for the staking strategy.
   * @return The address of the reward token.
   */
  function rewardToken() external view returns (address);

  /**
   * @notice Returns the address of the staking contract.
   * @return The address of the staking contract.
   */
  function stakingContract() external view returns (address);

  /**
   * @notice Returns the address of the staking token.
   * @return The address of the staking token.
   */
  function stakingToken() external view returns (address);

  /**
   * @notice Returns the staking wallet address for a specific user.
   * @param user The address of the user.
   * @return The address of the user's staking wallet.
   */
  function userStakingWallet(address user) external view returns (address);

  /**
   * @notice Transfers the staking wallet from one user to another.
   * @param from The current owner of the staking wallet.
   * @param to The new owner of the staking wallet.
   */
  function transferStakingWallet(address from, address to, uint256 _amount) external;

  /**
   * @notice Sets the escrow address.
   * @param _escrow The address of the new escrow.
   */
  function setEscrow(address _escrow) external;

  /**
   * @notice Sets the staking token address.
   * @param _stakingToken The address of the new staking token.
   */
  function setStakingToken(address _stakingToken) external;

  /**
   * @notice Sets the staking contract address.
   * @param _stakingContract The address of the new staking contract.
   */
  function setStakingContract(address _stakingContract) external;

  /**
   * @notice Sets the address of the  beacon.
   * @param _beacon The address of the new beacon contract.
   */
  function setUpgradeableBeacon(address _beacon) external;
}

