contracts/veION/stake/velo/IVeloIonModeS.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

interface IVeloIonModeStaking {
  event Deposit(address indexed from, address indexed to, uint256 amount);
  event Withdraw(address indexed from, uint256 amount);
  event NotifyReward(address indexed from, uint256 amount);
  event ClaimFees(uint256 claimed0, uint256 claimed1);
  event ClaimRewards(address indexed from, uint256 amount);

  /// @notice Address of the pool LP token which is deposited (staked) for rewards
  function stakingToken() external view returns (address);

  /// @notice Address of the token (VELO v2) rewarded to stakers
  function rewardToken() external view returns (address);

  /// @notice Address of the Converter contract linked to the gauge
  function feeConverter() external view returns (address);

  /// @notice Address of the staking rewards factory that created this gauge
  function factory() external view returns (address);

  /// @notice Timestamp end of current rewards period
  function periodFinish() external view returns (uint256);

  /// @notice Current reward rate of rewardToken to distribute per second
  function rewardRate() external view returns (uint256);

  /// @notice Most recent timestamp contract has updated state
  function lastUpdateTime() external view returns (uint256);

  /// @notice Most recent stored value of rewardPerToken
  function rewardPerTokenStored() external view returns (uint256);

  /// @notice Amount of stakingToken deposited for rewards
  function totalSupply() external view returns (uint256);

  /// @notice Get the amount of stakingToken deposited by an account
  function balanceOf(address) external view returns (uint256);

  /// @notice Cached rewardPerTokenStored for an account based on their most recent action
  function userRewardPerTokenPaid(address) external view returns (uint256);

  /// @notice Cached amount of rewardToken earned for an account
  function rewards(address) external view returns (uint256);

  /// @notice View to see the rewardRate given the timestamp of the start of the epoch
  function rewardRateByEpoch(uint256) external view returns (uint256);

  /// @notice Get the current reward rate per unit of stakingToken deposited
  function rewardPerToken() external view returns (uint256 _rewardPerToken);

  /// @notice Returns the last time the reward was modified or periodFinish if the reward has ended
  function lastTimeRewardApplicable() external view returns (uint256 _time);

  /// @notice Returns accrued balance to date from last claim / first deposit.
  function earned(address _account) external view returns (uint256 _earned);

  /// @notice Total amount of rewardToken to distribute for the current rewards period
  function left() external view returns (uint256 _left);

  /// @notice Claims accrued Fees from Pool and distributes them to the Converter
  /// @return _claimed0 Amount of Fees claimed in token0
  /// @return _claimed1 Amount of Fees claimed in token1
  function claimFees() external returns (uint256 _claimed0, uint256 _claimed1);

  /// @notice Retrieve rewards for an address.
  /// @dev Throws if not called by same address or voter.
  /// @param _account .
  function getReward(address _account) external;

  /// @notice Deposit LP tokens into gauge for msg.sender
  /// @param _amount .
  function deposit(uint256 _amount) external;

  /// @notice Deposit LP tokens into gauge for any user
  /// @param _amount .
  /// @param _recipient Recipient to give balance to
  function deposit(uint256 _amount, address _recipient) external;

  /// @notice Withdraw LP tokens for user
  /// @param _amount .
  function withdraw(uint256 _amount) external;

  /// @dev Notifies gauge of gauge rewards. Assumes gauge reward tokens is 18 decimals.
  ///      If not 18 decimals, rewardRate may have rounding issues.
  function notifyRewardMatch(uint256 amount) external;

  /// @dev Notifies gauge of gauge rewards. Assumes gauge reward tokens is 18 decimals.
  ///      If not 18 decimals, rewardRate may have rounding issues.
  function notifyRewardAmount(uint256 amount) external;
}

contracts/veION/stake/velo/VeloAeroStaki.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import "../IStakeStrategy.sol";
import "./VeloAeroStakingWallet.sol";
import "./IVeloIonModeStaking.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { Ownable2StepUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/access/Ownable2StepUpgradeable.sol";
import { BeaconProxy } from "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import { UpgradeableBeacon } from "@openzeppelin/contracts/proxy/beacon/UpgradeableBeacon.sol";

/**
 * @title VeloAeroStakingStrategy
 * @notice Staking interface for usage in veION when staking Velodrome/Aerodrome style LP.
 * @author Jourdan Dunkley <jourdan@ionic.money> (https://github.com/jourdanDunkley)
 */
contract VeloAeroStakingStrategy is IStakeStrategy, Ownable2StepUpgradeable {
  using SafeERC20 for IERC20;

  /// @notice Address of the escrow responsible for managing staking operations
  address public escrow;
  /// @notice Address of the token being staked
  address public stakingToken;
  /// @notice Address of the contract where staking operations are executed
  address public stakingContract;
  /// @notice Address of beacon contract that manages wallet proxies
  UpgradeableBeacon public veloAeroBeacon;
  /// @notice Mapping of user addresses to their respective staking wallet addresses
  mapping(address => address) public userStakingWallet;

  /// @dev Modifier to restrict function access to only the escrow address
  modifier onlyEscrow() {
    require(msg.sender == escrow, "Not authorized: Only escrow can call this function");
    _;
  }

  constructor() {
    _disableInitializers(); // Locks the implementation contract from being initialized
  }

  /**
   * @notice Initializes the staking strategy contract with necessary parameters
   * @dev This function can only be called once due to the initializer modifier
   * @param _escrow The address of the escrow responsible for staking operations
   * @param _stakingToken The address of the token to be staked
   * @param _stakingContract The address of the contract handling staking
   * @param _stakingWalletImplementation The address of the staking wallet implementation
   */
  function initialize(
    address _escrow,
    address _stakingToken,
    address _stakingContract,
    address _stakingWalletImplementation
  ) public initializer {
    __Ownable2Step_init();
    escrow = _escrow;
    stakingToken = _stakingToken;
    stakingContract = _stakingContract;

    veloAeroBeacon = new UpgradeableBeacon(_stakingWalletImplementation);
    veloAeroBeacon.transferOwnership(msg.sender);

    emit Initialized(_escrow, _stakingToken, _stakingContract, _stakingWalletImplementation);
  }

  /// @inheritdoc IStakeStrategy
  function stake(address _from, uint256 _amount, bytes memory _data) external override onlyEscrow {
    IERC20(stakingToken).safeTransferFrom(msg.sender, address(this), _amount);

    address veloWallet = userStakingWallet[_from];
    if (veloWallet == address(0)) {
      BeaconProxy newWallet = new BeaconProxy(address(veloAeroBeacon), "");
      veloWallet = address(newWallet);
      VeloAeroStakingWallet(veloWallet).initialize(IStakeStrategy(address(this)));
      userStakingWallet[_from] = veloWallet;
    }

    IERC20(stakingToken).approve(veloWallet, _amount);
    VeloAeroStakingWallet(veloWallet).stake(_from, _amount, _data);
    emit Staked(_from, _amount, veloWallet);
  }

  /// @inheritdoc IStakeStrategy
  function claim(address _from) external onlyEscrow {
    VeloAeroStakingWallet veloWallet = VeloAeroStakingWallet(userStakingWallet[_from]);
    veloWallet.claim(_from);
    emit Claimed(_from, address(veloWallet));
  }

  /// @inheritdoc IStakeStrategy
  function withdraw(address _owner, address _withdrawTo, uint256 _amount) external onlyEscrow {
    VeloAeroStakingWallet veloWallet = VeloAeroStakingWallet(userStakingWallet[_owner]);
    veloWallet.withdraw(_withdrawTo, _amount);
    emit Withdrawn(_owner, _withdrawTo, _amount);
  }

  /// @inheritdoc IStakeStrategy
  function transferStakingWallet(address _from, address _to, uint256 _amount) external onlyEscrow {
    address fromWallet = userStakingWallet[_from];
    address toWallet = userStakingWallet[_to];

    if (toWallet == address(0)) {
      BeaconProxy newWallet = new BeaconProxy(address(veloAeroBeacon), "");
      toWallet = address(newWallet);
      VeloAeroStakingWallet(toWallet).initialize(IStakeStrategy(address(this)));
      userStakingWallet[_to] = toWallet;
    }

    VeloAeroStakingWallet(fromWallet).withdraw(address(this), _amount);
    IERC20(stakingToken).approve(address(toWallet), _amount);
    VeloAeroStakingWallet(toWallet).stake(_to, _amount, "");
    emit StakingWalletTransferred(_from, _to, _amount);
  }

  /// @inheritdoc IStakeStrategy
  function rewardRate() external view override returns (uint256) {
    return IVeloIonModeStaking(stakingContract).rewardRate();
  }

  /// @inheritdoc IStakeStrategy
  function periodFinish() external view override returns (uint256) {
    return IVeloIonModeStaking(stakingContract).periodFinish();
  }

  /// @inheritdoc IStakeStrategy
  function balanceOf(address account) public view override returns (uint256) {
    return IVeloIonModeStaking(stakingContract).balanceOf(account);
  }

  /// @inheritdoc IStakeStrategy
  function totalSupply() external view override returns (uint256) {
    return IVeloIonModeStaking(stakingContract).totalSupply();
  }

  /// @inheritdoc IStakeStrategy
  function rewardToken() public view returns (address) {
    return IVeloIonModeStaking(stakingContract).rewardToken();
  }

  /// @inheritdoc IStakeStrategy
  function setEscrow(address _escrow) external onlyOwner {
    require(_escrow != address(0), "Invalid address");
    escrow = _escrow;
    emit EscrowSet(_escrow);
  }

  /// @inheritdoc IStakeStrategy
  function setStakingToken(address _stakingToken) external onlyOwner {
    require(_stakingToken != address(0), "Invalid address");
    stakingToken = _stakingToken;
    emit StakingTokenSet(_stakingToken);
  }

  /// @inheritdoc IStakeStrategy
  function setStakingContract(address _stakingContract) external onlyOwner {
    require(_stakingContract != address(0), "Invalid address");
    stakingContract = _stakingContract;
    emit StakingContractSet(_stakingContract);
  }

  /// @inheritdoc IStakeStrategy
  function setUpgradeableBeacon(address _beacon) external onlyOwner {
    require(_beacon != address(0), "Invalid address");
    veloAeroBeacon = UpgradeableBeacon(_beacon);
    emit UpgradeableBeaconSet(_beacon);
  }
}

// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import { IStakeWallet } from "../IStakeWallet.sol";
import { IStakeStrategy } from "../IStakeStrategy.sol";
import { IVeloIonModeStaking } from "./IVeloIonModeStaking.sol";
import { IERC20Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";
import { SafeERC20Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC20/utils/SafeERC20Upgradeable.sol";
import { Initializable } from "@openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";

/**
 * @title VeloAeroStakingWallet
 * @notice Staking interface for usage in veION when staking Velodrome/Aerodrome style LP.
 * @dev This contract allows staking and claiming rewards with a specific staking strategy.
 * @dev The staking strategy is set during contract deployment and can only be called by the strategy.
 * @dev The contract is designed to be used with the Velodrome/Aerodrome style LP.
 * @author Jourdan Dunkley <jourdan@ionic.money>
 */
contract VeloAeroStakingWallet is IStakeWallet, Initializable {
  using SafeERC20Upgradeable for IERC20Upgradeable;
  IStakeStrategy public stakeStrategy;

  /// @dev Modifier to restrict function access to only the stake strategy contract
  modifier onlyStakeStrategy() {
    require(msg.sender == address(stakeStrategy), "Not authorized: Only stake strategy can call this function");
    _;
  }

  constructor() {
    _disableInitializers(); // Locks the implementation contract from being initialized
  }

  /**
   * @notice Initializes the contract with a staking strategy
   * @dev Can only be called once due to initializer modifier
   * @param _stakeStrategy The address of the staking strategy contract
   */
  function initialize(IStakeStrategy _stakeStrategy) external initializer {
    stakeStrategy = _stakeStrategy;
  }

  /// @inheritdoc IStakeWallet
  function stake(address /* _from */, uint256 _amount, bytes memory /* _data */) external override onlyStakeStrategy {
    IERC20Upgradeable stakingToken = IERC20Upgradeable(stakeStrategy.stakingToken());
    IVeloIonModeStaking stakingContract = IVeloIonModeStaking(stakeStrategy.stakingContract());

    stakingToken.safeTransferFrom(msg.sender, address(this), _amount);
    stakingToken.approve(address(stakingContract), _amount);
    stakingContract.deposit(_amount);
    emit Staked(_amount);
  }

  /// @inheritdoc IStakeWallet
  function claim(address _from) external onlyStakeStrategy {
    IERC20Upgradeable rewardToken = IERC20Upgradeable(stakeStrategy.rewardToken());
    IVeloIonModeStaking stakingContract = IVeloIonModeStaking(stakeStrategy.stakingContract());

    stakingContract.getReward(address(this));
    uint256 rewardAmount = rewardToken.balanceOf(address(this));
    IERC20Upgradeable(rewardToken).safeTransfer(_from, rewardAmount);
    emit Claimed(_from, rewardAmount);
  }

  /// @inheritdoc IStakeWallet
  function withdraw(address _withdrawTo, uint256 _amount) external onlyStakeStrategy {
    IERC20Upgradeable stakingToken = IERC20Upgradeable(stakeStrategy.stakingToken());
    IVeloIonModeStaking stakingContract = IVeloIonModeStaking(stakeStrategy.stakingContract());

    stakingContract.withdraw(_amount);
    stakingToken.safeTransfer(_withdrawTo, _amount);
    emit Withdrawn(_withdrawTo, _amount);
  }
}

