// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import { ERC721Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC721/ERC721Upgradeable.sol";
import { Ownable2StepUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/access/Ownable2StepUpgradeable.sol";
import { IveIONCore, IMasterPriceOracle, IAeroVotingEscrow, IAeroVoter } from "./interfaces/IveIONCore.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IVoter } from "./interfaces/IVoter.sol";
import { ReentrancyGuardUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/security/ReentrancyGuardUpgradeable.sol";
import { veIONStorage } from "./veIONStorage.sol";
import { EnumerableSet } from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import { IAddressesProvider } from "./interfaces/IveIONCore.sol";
import { IStakeStrategy } from "./stake/IStakeStrategy.sol";

/**
 * @title veION Contract
 * @notice This contract manages the veION framework, enabling the staking and management LP tokens for voting power.
 * @author Jourdan Dunkley <jourdan@ionic.money> (https://github.com/jourdanDunkley)
 */
contract veION is Ownable2StepUpgradeable, ERC721Upgradeable, ReentrancyGuardUpgradeable, veIONStorage, IveIONCore {
  using EnumerableSet for EnumerableSet.UintSet;
  using EnumerableSet for EnumerableSet.AddressSet;
  using SafeERC20 for IERC20;

  constructor() {
    _disableInitializers(); // Locks the implementation contract from being initialized
  }

  /**
   * @notice Initializes the veION contract with the given AddressesProvider.
   * @dev This function is called only once during the contract deployment.
   * It initializes the Ownable, ERC721, and ReentrancyGuard modules.
   * @param _ap The AddressesProvider contract used for address management.
   */
  function initialize(IAddressesProvider _ap) public initializer {
    __Ownable2Step_init();
    __ERC721_init("veION", "veION");
    __ReentrancyGuard_init();
    ap = _ap;
    emit Initialized(address(_ap));
  }

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                           External Functions                              ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝

  /// @inheritdoc IveIONCore
  function createLockFor(
    address[] calldata _tokenAddress,
    uint256[] calldata _tokenAmount,
    uint256[] calldata _duration,
    bool[] memory _stakeUnderlying,
    address _to
  ) external override nonReentrant returns (uint256) {
    return _createLock(_tokenAddress, _tokenAmount, _duration, _stakeUnderlying, _to);
  }

  /// @inheritdoc IveIONCore
  function createLock(
    address[] calldata _tokenAddress,
    uint256[] calldata _tokenAmount,
    uint256[] calldata _duration,
    bool[] memory _stakeUnderlying
  ) external override nonReentrant returns (uint256) {
    return _createLock(_tokenAddress, _tokenAmount, _duration, _stakeUnderlying, _msgSender());
  }

  /// @inheritdoc IveIONCore
  function increaseAmount(
    address _tokenAddress,
    uint256 _tokenId,
    uint256 _tokenAmount,
    bool _stakeUnderlying
  ) external nonReentrant {
    LpTokenType _lpType = s_lpType[_tokenAddress];
    LockedBalance memory oldLocked = s_locked[_tokenId][_lpType];

    if (ownerOf(_tokenId) != _msgSender()) revert NotOwner();
    if (_tokenAmount == 0) revert ZeroAmount();
    if (oldLocked.amount == 0) revert NoLockFound();
    if (oldLocked.end <= block.timestamp && !oldLocked.isPermanent) revert LockExpired();

    if (oldLocked.isPermanent) s_permanentLockBalance[_lpType] += _tokenAmount;

    _depositFor(
      _tokenAddress,
      _tokenId,
      _tokenAmount,
      0,
      _stakeUnderlying,
      oldLocked,
      DepositType.INCREASE_LOCK_AMOUNT,
      _lpType,
      _msgSender()
    );
  }

  /// @inheritdoc IveIONCore
  function lockAdditionalAsset(
    address _tokenAddress,
    uint256 _tokenAmount,
    uint256 _tokenId,
    uint256 _duration,
    bool _stakeUnderlying
  ) external nonReentrant {
    LpTokenType lpType = s_lpType[_tokenAddress];
    LockedBalance storage lockedBalance = s_locked[_tokenId][lpType];
    uint256 unlockTime = ((block.timestamp + _duration) / _WEEK) * _WEEK;

    if (ownerOf(_tokenId) != _msgSender()) revert NotOwner();
    if (_tokenAmount == 0) revert ZeroAmount();
    if (s_voted[_tokenId]) revert AlreadyVoted();
    if (!s_assetsLocked[_tokenId].add(_tokenAddress)) revert DuplicateAsset();
    if (_tokenAmount < s_minimumLockAmount[lpType]) revert MinimumNotMet();
    if (unlockTime > block.timestamp + _MAXTIME) revert LockDurationTooLong();
    if (_duration < s_minimumLockDuration) revert LockDurationTooShort();

    if (lockedBalance.isPermanent) s_permanentLockBalance[lpType] += _tokenAmount;

    _depositFor(
      _tokenAddress,
      _tokenId,
      _tokenAmount,
      unlockTime,
      _stakeUnderlying,
      lockedBalance,
      DepositType.LOCK_ADDITIONAL,
      lpType,
      _msgSender()
    );
  }

  /// @inheritdoc IveIONCore
  function increaseUnlockTime(address _tokenAddress, uint256 _tokenId, uint256 _lockDuration) external nonReentrant {
    LpTokenType _lpType = s_lpType[_tokenAddress];
    LockedBalance memory oldLocked = s_locked[_tokenId][_lpType];
    uint256 unlockTime = ((block.timestamp + _lockDuration) / _WEEK) * _WEEK; // Locktime is rounded down to weeks

    if (ownerOf(_tokenId) != _msgSender()) revert NotOwner();
    if (oldLocked.isPermanent) revert PermanentLock();
    if (oldLocked.end <= block.timestamp) revert LockExpired();
    if (oldLocked.amount <= 0) revert NoLockFound();
    if (unlockTime <= oldLocked.end) revert LockDurationNotInFuture();
    if (unlockTime > block.timestamp + _MAXTIME) revert LockDurationTooLong();

    _depositFor(
      _tokenAddress,
      _tokenId,
      0,
      unlockTime,
      false,
      oldLocked,
      DepositType.INCREASE_UNLOCK_TIME,
      _lpType,
      _msgSender()
    );
  }

  /// @inheritdoc IveIONCore
  function delegate(uint256 fromTokenId, uint256 toTokenId, address lpToken, uint256 amount) external nonReentrant {
    LpTokenType lpType = s_lpType[lpToken];
    LockedBalance memory fromLocked = s_locked[fromTokenId][lpType];
    LockedBalance memory toLocked = s_locked[toTokenId][lpType];

    if (ownerOf(fromTokenId) != _msgSender()) revert NotOwner();
    if (amount > fromLocked.amount) revert AmountTooBig();
    if (!fromLocked.isPermanent) revert NotPermanentLock();
    if (!toLocked.isPermanent) revert NotPermanentLock();
    if (s_delegatorsBlocked[toTokenId][lpToken]) revert NotAcceptingDelegators();

    fromLocked.amount -= amount;
    toLocked.delegateAmount += amount;

    if (s_delegations[fromTokenId][toTokenId][lpType] == 0) {
      s_delegatees[fromTokenId][lpType].add(toTokenId);
      s_delegators[toTokenId][lpType].add(fromTokenId);
    }

    s_delegations[fromTokenId][toTokenId][lpType] += amount;

    s_locked[fromTokenId][lpType] = fromLocked;
    s_locked[toTokenId][lpType] = toLocked;
    _checkpoint(fromTokenId, s_locked[fromTokenId][lpType], lpType);
    _checkpoint(toTokenId, s_locked[toTokenId][lpType], lpType);

    if (s_voted[toTokenId]) IVoter(s_voter).poke(toTokenId);
    if (s_voted[fromTokenId]) IVoter(s_voter).poke(fromTokenId);

    emit Delegated(fromTokenId, toTokenId, lpToken, amount);
  }

  /**
   * @dev Internal function to remove a delegation between two veNFTs.
   * @param fromTokenId ID of the veNFT from which delegation is being removed.
   * @param toTokenId ID of the veNFT to which delegation is being removed.
   * @param lpToken Address of the LP token associated with the delegation.
   * @param amount Amount of delegation to remove.
   */
  function _removeDelegation(uint256 fromTokenId, uint256 toTokenId, address lpToken, uint256 amount) internal {
    LpTokenType lpType = s_lpType[lpToken];
    LockedBalance memory fromLocked = s_locked[fromTokenId][lpType];
    LockedBalance memory toLocked = s_locked[toTokenId][lpType];

    if (ownerOf(fromTokenId) != _msgSender() && ownerOf(toTokenId) != _msgSender()) revert NotOwner();
    if (s_delegations[fromTokenId][toTokenId][lpType] == 0) revert NoDelegationBetweenTokens(fromTokenId, toTokenId);

    amount = amount > s_delegations[fromTokenId][toTokenId][lpType]
      ? s_delegations[fromTokenId][toTokenId][lpType]
      : amount;

    toLocked.delegateAmount -= amount;
    fromLocked.amount += amount;

    s_delegations[fromTokenId][toTokenId][lpType] -= amount;
    if (s_delegations[fromTokenId][toTokenId][lpType] == 0) {
      s_delegatees[fromTokenId][lpType].remove(toTokenId);
      s_delegators[toTokenId][lpType].remove(fromTokenId);
    }

    s_locked[toTokenId][lpType] = toLocked;
    s_locked[fromTokenId][lpType] = fromLocked;
    _checkpoint(toTokenId, s_locked[toTokenId][lpType], lpType);
    _checkpoint(fromTokenId, s_locked[fromTokenId][lpType], lpType);

    if (s_voted[toTokenId]) IVoter(s_voter).poke(toTokenId);
    if (s_voted[fromTokenId]) IVoter(s_voter).poke(fromTokenId);

    emit DelegationRemoved(fromTokenId, toTokenId, lpToken, amount);
  }

  /// @inheritdoc IveIONCore
  function removeDelegatees(
    uint256 fromTokenId,
    uint256[] memory toTokenIds,
    address lpToken,
    uint256[] memory amounts
  ) public nonReentrant {
    if (toTokenIds.length != amounts.length) revert ArrayMismatch();
    uint256 toTokenIdsLength = toTokenIds.length;
    for (uint256 i = 0; i < toTokenIdsLength; i++) {
      _removeDelegation(fromTokenId, toTokenIds[i], lpToken, amounts[i]);
    }
  }

  /// @inheritdoc IveIONCore
  function removeDelegators(
    uint256[] memory fromTokenIds,
    uint256 toTokenId,
    address lpToken,
    uint256[] memory amounts
  ) external nonReentrant {
    if (fromTokenIds.length != amounts.length) revert ArrayMismatch();
    uint256 fromTokenIdsLength = fromTokenIds.length;
    for (uint256 i = 0; i < fromTokenIdsLength; i++) {
      _removeDelegation(fromTokenIds[i], toTokenId, lpToken, amounts[i]);
    }
  }

  /// @inheritdoc IveIONCore
  function lockPermanent(address _tokenAddress, uint256 _tokenId) external nonReentrant {
    LpTokenType _lpType = s_lpType[_tokenAddress];
    LockedBalance memory _newLocked = s_locked[_tokenId][_lpType];
    if (ownerOf(_tokenId) != _msgSender()) revert NotOwner();
    if (_newLocked.isPermanent) revert PermanentLock();
    if (_newLocked.end <= block.timestamp) revert LockExpired();
    if (_newLocked.amount <= 0) revert NoLockFound();

    s_permanentLockBalance[_lpType] += _newLocked.amount;
    _newLocked.end = 0;
    _newLocked.isPermanent = true;
    _newLocked.boost = _calculateBoost(_MAXTIME);

    s_locked[_tokenId][_lpType] = _newLocked;
    _checkpoint(_tokenId, _newLocked, _lpType);

    emit PermanentLockCreated(_tokenAddress, _tokenId, _newLocked.amount);
  }

  /// @inheritdoc IveIONCore
  function unlockPermanent(address _tokenAddress, uint256 _tokenId) external nonReentrant {
    LpTokenType _lpType = s_lpType[_tokenAddress];
    LockedBalance memory _newLocked = s_locked[_tokenId][_lpType];
    if (ownerOf(_tokenId) != _msgSender()) revert NotOwner();
    if (!_newLocked.isPermanent) revert NotPermanentLock();
    if (s_delegatees[_tokenId][_lpType].length() != 0) revert TokenHasDelegatees();
    if (s_delegators[_tokenId][_lpType].length() != 0) revert TokenHasDelegators();

    s_permanentLockBalance[_lpType] -= _newLocked.amount;
    _newLocked.end = ((block.timestamp + _MAXTIME) / _WEEK) * _WEEK;
    _newLocked.isPermanent = false;

    s_locked[_tokenId][_lpType] = _newLocked;
    _checkpoint(_tokenId, _newLocked, _lpType);

    emit PermanentLockRemoved(_tokenAddress, _tokenId, _newLocked.amount);
  }

  /**
   * @notice Overrides the _burn function from ERC721 to include additional logic for bridging.
   * @param tokenId Token ID to burn.
   */
  function _burn(uint256 tokenId) internal override {
    super._burn(tokenId);
  }

  /**
   * @notice Hook that is called before any token transfer. This includes minting
   * and burning. It updates the ownership mappings and handles delegation and
   * staking logic when transferring tokens between addresses.
   *
   * @param from The address which previously owned the token.
   * @param to The address that will receive the token.
   * @param tokenId The ID of the token being transferred.
   */
  function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal override {
    super._beforeTokenTransfer(from, to, tokenId);

    if (from != address(0)) s_ownerToTokenIds[from].remove(tokenId);
    if (to != address(0)) s_ownerToTokenIds[to].add(tokenId);

    if (from != address(0) && to != address(0)) {
      address[] memory assetsLocked = s_assetsLocked[tokenId].values();
      uint256 assetsLockedLength = assetsLocked.length;
      for (uint256 i = 0; i < assetsLockedLength; i++) {
        address asset = assetsLocked[i];
        LpTokenType _lpType = s_lpType[asset];

        uint256[] memory delegatees = s_delegatees[tokenId][_lpType].values();
        uint256[] memory amounts = new uint256[](delegatees.length);
        uint256 delegateesLength = delegatees.length;
        for (uint256 j = 0; j < delegateesLength; j++) {
          amounts[j] = type(uint256).max;
        }

        if (delegateesLength != 0) {
          removeDelegatees(tokenId, delegatees, asset, amounts);
        }

        uint256 amountStaked = s_underlyingStake[tokenId][asset];
        if (amountStaked != 0) {
          IStakeStrategy _stakeStrategy = s_stakeStrategy[_lpType];
          _stakeStrategy.transferStakingWallet(from, to, amountStaked);
        }

        LockedBalance memory lock = s_locked[tokenId][_lpType];
        s_userCumulativeAssetValues[from][asset] -= lock.amount;
        s_userCumulativeAssetValues[to][asset] += lock.amount;
      }
    }
  }

  /// @inheritdoc IveIONCore
  function voting(uint256 _tokenId, bool _voting) external {
    if (_msgSender() != s_voter) revert NotVoter();
    s_voted[_tokenId] = _voting;
    emit Voted(_tokenId, _voting);
  }

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                           Internal Functions                              ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝

  struct DepositVars {
    uint256 supplyBefore;
    uint256 totalLockTime;
    LockedBalance newLocked;
    address from;
  }

  /**
   * @notice Deposits tokens for a specific veNFT, updating its locked balance and boost.
   * @dev This function handles the deposit logic for veNFTs, including updating the locked balance,
   *      calculating the boost based on the lock duration, and transferring tokens.
   * @param _tokenAddress The address of the token to deposit.
   * @param _tokenId The ID of the veNFT to deposit tokens for.
   * @param _tokenAmount The amount of tokens to deposit.
   * @param _unlockTime The time at which the lock will expire.
   * @param _stakeUnderlying A boolean indicating whether to stake the underlying tokens.
   * @param _oldLocked The previous locked balance of the veNFT.
   * @param _depositType The type of deposit being made.
   * @param _lpType The LP token type associated with the deposit.
   * @param _to The address to which the veNFT is assigned.
   */
  function _depositFor(
    address _tokenAddress,
    uint256 _tokenId,
    uint256 _tokenAmount,
    uint256 _unlockTime,
    bool _stakeUnderlying,
    LockedBalance memory _oldLocked,
    DepositType _depositType,
    LpTokenType _lpType,
    address _to
  ) internal {
    if (!s_whitelistedToken[_tokenAddress]) revert TokenNotWhitelisted();

    DepositVars memory vars;
    vars.supplyBefore = s_supply[_lpType];
    s_supply[_lpType] = vars.supplyBefore + _tokenAmount;

    (
      vars.newLocked.tokenAddress,
      vars.newLocked.amount,
      vars.newLocked.start,
      vars.newLocked.end,
      vars.newLocked.isPermanent,
      vars.newLocked.boost
    ) = (
      _oldLocked.tokenAddress,
      _oldLocked.amount,
      _oldLocked.start,
      _oldLocked.end,
      _oldLocked.isPermanent,
      _oldLocked.boost
    );

    vars.newLocked.tokenAddress = _tokenAddress;
    vars.newLocked.amount += _tokenAmount;
    if (_unlockTime != 0) {
      if (vars.newLocked.start == 0) vars.newLocked.start = block.timestamp;
      vars.newLocked.end = _unlockTime;
      vars.totalLockTime = vars.newLocked.end - vars.newLocked.start;
      vars.newLocked.boost = _calculateBoost(vars.totalLockTime);
    }
    s_locked[_tokenId][_lpType] = vars.newLocked;

    _checkpoint(_tokenId, vars.newLocked, _lpType);

    vars.from = _msgSender();
    if (_tokenAmount != 0) {
      s_userCumulativeAssetValues[ownerOf(_tokenId)][_tokenAddress] += _tokenAmount;
      IERC20(_tokenAddress).safeTransferFrom(vars.from, address(this), _tokenAmount);
      (IStakeStrategy _stakeStrategy, bytes memory _stakeData) = _getStakeStrategy(_lpType);
      if (address(_stakeStrategy) != address(0) && _stakeUnderlying) {
        _handleTokenStake(_to, _tokenId, _tokenAddress, _tokenAmount, _stakeStrategy, _stakeData);
      }
    }

    emit Deposit(_to, _tokenId, _depositType, _tokenAmount, vars.newLocked.end, block.timestamp);
    emit Supply(vars.supplyBefore, s_supply[_lpType]);
  }

  /**
   * @notice Handles the staking of tokens using a specified staking strategy.
   * @param _to The address to which the stake is attributed.
   * @param _tokenId The ID of the token being staked.
   * @param _tokenAddress The address of the token being staked.
   * @param _tokenAmount The amount of tokens to stake.
   * @param _stakeStrategy The staking strategy to use.
   * @param _stakeData Additional data required for staking.
   */
  function _handleTokenStake(
    address _to,
    uint256 _tokenId,
    address _tokenAddress,
    uint256 _tokenAmount,
    IStakeStrategy _stakeStrategy,
    bytes memory _stakeData
  ) internal {
    IERC20(_tokenAddress).approve(address(_stakeStrategy), _tokenAmount);
    _stakeStrategy.stake(_to, _tokenAmount, _stakeData);
    s_underlyingStake[_tokenId][_tokenAddress] += _tokenAmount;
  }

  /**
   * @notice Updates the user point history and epoch for a given token and LP token type.
   * @param _tokenId The ID of the token.
   * @param _newLocked The new locked balance information.
   * @param _lpType The LP token type.
   */
  function _checkpoint(uint256 _tokenId, LockedBalance memory _newLocked, LpTokenType _lpType) internal {
    UserPoint memory uNew;
    uNew.permanent = _newLocked.isPermanent ? _newLocked.amount : 0;
    uNew.permanentDelegate = _newLocked.isPermanent ? _newLocked.delegateAmount : 0;

    if (_newLocked.end > block.timestamp && _newLocked.amount > 0) {
      uNew.slope = _newLocked.amount / _MAXTIME;
      uNew.bias = uNew.slope * (_newLocked.end - block.timestamp);
    }

    uNew.ts = block.timestamp;
    uNew.blk = block.number;
    uint256 userEpoch = s_userPointEpoch[_tokenId][_lpType];
    if (userEpoch != 0 && s_userPointHistory[_tokenId][_lpType][userEpoch].ts == block.timestamp) {
      s_userPointHistory[_tokenId][_lpType][userEpoch] = uNew;
    } else {
      s_userPointEpoch[_tokenId][_lpType] = ++userEpoch;
      s_userPointHistory[_tokenId][_lpType][userEpoch] = uNew;
    }
  }

  /**
   * @notice Creates a lock for multiple tokens with specified durations and staking options.
   * @param _tokenAddress Array of token addresses to lock.
   * @param _tokenAmount Array of token amounts to lock.
   * @param _duration Array of durations for each lock.
   * @param _stakeUnderlying Array of booleans indicating whether to stake the underlying tokens.
   * @param _to The address to which the lock is attributed.
   * @return The ID of the newly created lock.
   */
  function _createLock(
    address[] memory _tokenAddress,
    uint256[] memory _tokenAmount,
    uint256[] memory _duration,
    bool[] memory _stakeUnderlying,
    address _to
  ) internal returns (uint256) {
    uint256 _tokenId = ++s_tokenId;
    uint256 _length = _tokenAddress.length;
    _safeMint(_to, _tokenId);

    if (
      _tokenAddress.length != _tokenAmount.length ||
      _tokenAmount.length != _duration.length ||
      _duration.length != _stakeUnderlying.length
    ) {
      revert ArrayMismatch();
    }

    for (uint256 i = 0; i < _length; i++) {
      LpTokenType _lpType = s_lpType[_tokenAddress[i]];
      uint256 unlockTime = ((block.timestamp + _duration[i]) / _WEEK) * _WEEK;

      if (!s_assetsLocked[_tokenId].add(_tokenAddress[i])) revert DuplicateAsset();
      if (_tokenAmount[i] == 0) revert ZeroAmount();
      if (_duration[i] < s_minimumLockDuration) revert LockDurationTooShort();
      if (unlockTime > block.timestamp + _MAXTIME) revert LockDurationTooLong();
      if (_tokenAmount[i] < s_minimumLockAmount[_lpType]) revert MinimumNotMet();

      _depositFor(
        _tokenAddress[i],
        _tokenId,
        _tokenAmount[i],
        unlockTime,
        _stakeUnderlying[i],
        s_locked[_tokenId][_lpType],
        DepositType.CREATE_LOCK_TYPE,
        _lpType,
        _to
      );
    }
    return _tokenId;
  }

  /**
   * @notice Calculates the boost for a given lock duration.
   * @param _duration The duration of the lock.
   * @return The calculated boost value.
   */
  function _calculateBoost(uint256 _duration) internal view returns (uint256) {
    uint256 minDuration = s_minimumLockDuration;
    uint256 maxDuration = _MAXTIME;
    uint256 minBoost = 1e18;
    uint256 maxBoost = 2e18;

    if (_duration <= minDuration) {
      return minBoost;
    } else if (_duration >= maxDuration) {
      return maxBoost;
    } else {
      return minBoost + ((_duration - minDuration) * (maxBoost - minBoost)) / (maxDuration - minDuration);
    }
  }

  /**
   * @notice Retrieves the staking strategy and data for a given LP token type.
   * @param _lpType The LP token type.
   * @return _stakeStrategy The staking strategy for the LP token type.
   * @return _stakeData The staking data for the LP token type.
   */
  function _getStakeStrategy(
    LpTokenType _lpType
  ) internal view returns (IStakeStrategy _stakeStrategy, bytes memory _stakeData) {
    IStakeStrategy strategy = s_stakeStrategy[_lpType];
    return (strategy, "");
  }

  /// @inheritdoc IveIONCore
  function setExtensions(address _veIONFirstExtension, address _veIONSecondExtension) external onlyOwner {
    require(_veIONFirstExtension != address(0), "Invalid First Extension Address");
    require(_veIONSecondExtension != address(0), "Invalid Second Extension Address");
    require(_veIONFirstExtension != _veIONSecondExtension, "Submitted Identical Addresses");
    veIONFirstExtension = _veIONFirstExtension;
    veIONSecondExtension = _veIONSecondExtension;
    emit ExtensionsSet(_veIONFirstExtension, _veIONSecondExtension);
  }

  /**
   * @dev Fallback function that delegates calls to the address returned by `_implementation()`. Will run if no other
   * function in the contract matches the call data.
   */
  fallback() external {
    address impl = veIONFirstExtension;
    require(impl != address(0), "Implementation not set");

    assembly {
      calldatacopy(0, 0, calldatasize())

      let result := delegatecall(gas(), impl, 0, calldatasize(), 0, 0)

      returndatacopy(0, 0, returndatasize())
      switch result
      case 0 {
        revert(0, returndatasize())
      }
      default {
        return(0, returndatasize())
      }
    }
  }
}

contracts/veION/veIONFirstExtension.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import { ERC721Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC721/ERC721Upgradeable.sol";
import { Ownable2StepUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/access/Ownable2StepUpgradeable.sol";
import { IMasterPriceOracle, IAeroVotingEscrow, IAeroVoter } from "./interfaces/IveIONCore.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IVoter } from "./interfaces/IVoter.sol";
import { ReentrancyGuardUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/security/ReentrancyGuardUpgradeable.sol";
import { veIONStorage } from "./veIONStorage.sol";
import { BalanceLogicLibrary } from "./libraries/BalanceLogicLibrary.sol";
import { IveIONFirstExtension } from "./interfaces/IveIONFirstExtension.sol";
import { EnumerableSet } from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import { IAddressesProvider } from "./interfaces/IveIONCore.sol";
import { IStakeStrategy } from "./stake/IStakeStrategy.sol";

/**
 * @title veION Contract First Extensions
 * @notice This contract manages the veION framework, enabling the staking and management LP tokens for voting power.
 * @author Jourdan Dunkley <jourdan@ionic.money> (https://github.com/jourdanDunkley)
 */
contract veIONFirstExtension is
  Ownable2StepUpgradeable,
  ERC721Upgradeable,
  ReentrancyGuardUpgradeable,
  veIONStorage,
  IveIONFirstExtension
{
  using EnumerableSet for EnumerableSet.UintSet;
  using EnumerableSet for EnumerableSet.AddressSet;
  using SafeERC20 for IERC20;

  constructor() {
    _disableInitializers(); // Locks the implementation contract from being initialized
  }

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                           External Functions                              ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝

  /// @inheritdoc IveIONFirstExtension
  function withdraw(address _tokenAddress, uint256 _tokenId) external nonReentrant {
    address sender = _msgSender();
    LpTokenType _lpType = s_lpType[_tokenAddress];
    LockedBalance memory oldLocked = s_locked[_tokenId][_lpType];

    if (ownerOf(_tokenId) != _msgSender()) revert NotOwner();
    if (s_voted[_tokenId]) revert AlreadyVoted();
    if (oldLocked.isPermanent) revert PermanentLock();
    if (!s_whitelistedToken[_tokenAddress]) revert TokenNotWhitelisted();
    if (oldLocked.amount == 0 || !s_assetsLocked[_tokenId].contains(_tokenAddress)) revert NoLockFound();

    uint256 value = oldLocked.amount;
    s_userCumulativeAssetValues[sender][_tokenAddress] -= value;
    uint256 fee = 0;

    if (block.timestamp < oldLocked.end) {
      uint256 daysLocked = ((oldLocked.end - oldLocked.start) * 1e18) / 1 days;
      uint256 daysLeft = ((oldLocked.end - block.timestamp) * 1e18) / 1 days;
      uint256 timeFactor = (daysLeft * 1e18) / daysLocked;
      uint256 veLPLocked = s_supply[_lpType];
      uint256 LPInCirculation = IERC20(_tokenAddress).totalSupply();
      uint256 ratioFactor = 1e18 - (veLPLocked * 1e18) / LPInCirculation;
      fee = (timeFactor * ratioFactor * oldLocked.boost) / 1e36;
      if (fee > s_maxEarlyWithdrawFee) fee = s_maxEarlyWithdrawFee;
      fee = (value * fee) / 1e18;
      value -= fee;

      uint256 feeToDistribute = (fee * 75) / 100;
      uint256 feeToProtocol = fee - feeToDistribute;
      s_protocolFees[_lpType] += feeToProtocol;
      s_distributedFees[_lpType] += feeToDistribute;
    }

    s_locked[_tokenId][_lpType] = LockedBalance(address(0), 0, 0, 0, 0, false, 0);
    s_assetsLocked[_tokenId].remove(_tokenAddress);
    uint256 supplyBefore = s_supply[_lpType];

    uint256 amountStaked = s_underlyingStake[_tokenId][_tokenAddress];
    if (amountStaked != 0) {
      (IStakeStrategy _stakeStrategy, ) = _getStakeStrategy(_lpType);
      if (address(_stakeStrategy) != address(0)) {
        _handleTokenWithdrawStake(sender, address(this), _tokenId, _tokenAddress, amountStaked, _stakeStrategy);
      }
    }

    s_supply[_lpType] = supplyBefore - oldLocked.amount;
    _checkpoint(_tokenId, LockedBalance(address(0), 0, 0, 0, 0, false, 0), _lpType);

    // Check if all LP types for this token have zero balance
    bool shouldBurn = true;
    address[] memory lockedAssets = s_assetsLocked[_tokenId].values();
    uint256 lockedAssetsLength = lockedAssets.length;
    for (uint256 i = 0; i < lockedAssetsLength; i++) {
      LpTokenType assetLpType = s_lpType[lockedAssets[i]];
      if (s_locked[_tokenId][assetLpType].amount > 0) {
        shouldBurn = false;
        break;
      }
    }

    if (shouldBurn) _burn(_tokenId);

    IERC20(_tokenAddress).safeTransfer(sender, value);
    emit Withdraw(sender, _tokenId, value, block.timestamp);
    emit Supply(supplyBefore, supplyBefore - oldLocked.amount);
  }

  /// @inheritdoc IveIONFirstExtension
  function merge(uint256 _from, uint256 _to) external nonReentrant {
    if (_from == _to) revert SameNFT();
    if (s_voted[_from] || s_voted[_to]) revert AlreadyVoted();
    if (ownerOf(_from) != _msgSender()) revert NotOwner();
    if (ownerOf(_to) != _msgSender()) revert NotOwner();

    address[] memory assetsLocked = s_assetsLocked[_from].values();
    uint256 assetsLockedLength = assetsLocked.length;
    for (uint256 i = 0; i < assetsLockedLength; i++) {
      address asset = assetsLocked[i];
      LpTokenType lpType = s_lpType[asset];

      LockedBalance memory oldLockedTo = s_locked[_to][lpType];
      LockedBalance memory oldLockedFrom = s_locked[_from][lpType];

      if (oldLockedTo.end != 0 && oldLockedTo.end <= block.timestamp) revert LockExpired();
      if (oldLockedFrom.end != 0 && oldLockedFrom.end <= block.timestamp) revert LockExpired();
      if (oldLockedFrom.isPermanent) revert PermanentLock();
      if (oldLockedTo.isPermanent) revert PermanentLock();

      LockedBalance memory newLockedTo;

      newLockedTo.tokenAddress = asset;
      newLockedTo.amount = oldLockedTo.amount + oldLockedFrom.amount;
      newLockedTo.start = oldLockedTo.start < oldLockedFrom.start && oldLockedTo.start != 0
        ? oldLockedTo.start
        : oldLockedFrom.start;
      newLockedTo.end = oldLockedTo.end > oldLockedFrom.end ? oldLockedTo.end : oldLockedFrom.end;
      newLockedTo.boost = _calculateBoost(newLockedTo.end - newLockedTo.start);

      s_locked[_from][lpType] = LockedBalance(address(0), 0, 0, 0, 0, false, 0);
      _checkpoint(_from, LockedBalance(address(0), 0, 0, 0, 0, false, 0), lpType);
      s_locked[_to][lpType] = newLockedTo;
      _checkpoint(_to, newLockedTo, lpType);

      s_assetsLocked[_from].remove(asset);
      if (!s_assetsLocked[_to].contains(asset)) {
        s_assetsLocked[_to].add(asset);
      }

      if (s_underlyingStake[_from][asset] != 0) {
        s_underlyingStake[_to][asset] += s_underlyingStake[_from][asset];
        s_underlyingStake[_from][asset] = 0;
      }
    }
    _burn(_from);
    emit MergeCompleted(_from, _to, assetsLocked, assetsLocked.length);
  }

  /// @inheritdoc IveIONFirstExtension
  function split(
    address _tokenAddress,
    uint256 _from,
    uint256 _splitAmount
  ) external nonReentrant returns (uint256 _tokenId1, uint256 _tokenId2) {
    address ownerFrom = _ownerOf(_from);
    LpTokenType _lpType = s_lpType[_tokenAddress];
    LockedBalance memory oldLocked = s_locked[_from][_lpType];
    uint256 minimumLockAmount = s_minimumLockAmount[_lpType];

    if (s_voted[_from]) revert AlreadyVoted();
    if (!s_canSplit[ownerFrom] && !s_canSplit[address(0)]) revert SplitNotAllowed();
    if (ownerFrom != _msgSender()) revert NotOwner();
    if (oldLocked.end <= block.timestamp && !oldLocked.isPermanent) revert LockExpired();
    if (_splitAmount >= oldLocked.amount) revert AmountTooBig();
    if (_splitAmount < minimumLockAmount) revert SplitTooSmall();
    if (oldLocked.amount - _splitAmount < minimumLockAmount) revert NotEnoughRemainingAfterSplit();

    LockedBalance memory oldLockedTemp = oldLocked;

    oldLocked.amount -= _splitAmount;
    s_locked[_from][_lpType] = oldLocked;
    _checkpoint(_from, oldLocked, _lpType);

    LockedBalance memory splitLocked = oldLockedTemp;
    splitLocked.amount = _splitAmount;
    _tokenId2 = _createSplitVE(ownerFrom, splitLocked, _lpType, _tokenAddress);
    _tokenId1 = _from;

    if (s_underlyingStake[_from][_tokenAddress] != 0) {
      s_underlyingStake[_from][_tokenAddress] -= _splitAmount;
      s_underlyingStake[_tokenId2][_tokenAddress] = _splitAmount;
    }

    emit SplitCompleted(_from, _tokenId1, _tokenId2, _splitAmount, _tokenAddress);
  }

  /// @inheritdoc IveIONFirstExtension
  function claimEmissions(address _tokenAddress) external nonReentrant {
    LpTokenType _lpType = s_lpType[_tokenAddress];
    IStakeStrategy _stakeStrategy = s_stakeStrategy[_lpType];
    if (_stakeStrategy.userStakingWallet(_msgSender()) == address(0)) revert NoUnderlyingStake();
    _stakeStrategy.claim(_msgSender());
    emit EmissionsClaimed(_msgSender(), _tokenAddress);
  }

  /**
   * @notice Overrides the _burn function from ERC721 to include additional logic for bridging.
   * @param tokenId Token ID to burn.
   */
  function _burn(uint256 tokenId) internal override {
    super._burn(tokenId);
  }

  /**
   * @notice Hook that is called before any token transfer. This includes minting
   * and burning. It updates the ownership mappings and handles delegation and
   * staking logic when transferring tokens between addresses.
   *
   * @param from The address which previously owned the token.
   * @param to The address that will receive the token.
   * @param tokenId The ID of the token being transferred.
   */
  function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal override {
    super._beforeTokenTransfer(from, to, tokenId);

    if (from != address(0)) s_ownerToTokenIds[from].remove(tokenId);
    if (to != address(0)) s_ownerToTokenIds[to].add(tokenId);

    if (from != address(0) && to != address(0)) {
      address[] memory assetsLocked = s_assetsLocked[tokenId].values();
      uint256 assetsLockedLength = assetsLocked.length;
      for (uint256 i = 0; i < assetsLockedLength; i++) {
        address asset = assetsLocked[i];
        LpTokenType _lpType = s_lpType[asset];

        uint256[] memory delegatees = s_delegatees[tokenId][_lpType].values();
        uint256[] memory amounts = new uint256[](delegatees.length);
        uint256 delegateesLength = delegatees.length;
        for (uint256 j = 0; j < delegateesLength; j++) {
          amounts[j] = type(uint256).max;
        }

        if (delegateesLength != 0) {
          removeDelegatees(tokenId, delegatees, asset, amounts);
        }

        uint256 amountStaked = s_underlyingStake[tokenId][asset];
        if (amountStaked != 0) {
          IStakeStrategy _stakeStrategy = s_stakeStrategy[_lpType];
          _stakeStrategy.transferStakingWallet(from, to, amountStaked);
        }

        LockedBalance memory lock = s_locked[tokenId][_lpType];
        s_userCumulativeAssetValues[from][asset] -= lock.amount;
        s_userCumulativeAssetValues[to][asset] += lock.amount;
      }
    }
  }

  /**
   * @dev Internal function to remove a delegation between two veNFTs.
   * @param fromTokenId ID of the veNFT from which delegation is being removed.
   * @param toTokenId ID of the veNFT to which delegation is being removed.
   * @param lpToken Address of the LP token associated with the delegation.
   * @param amount Amount of delegation to remove.
   */
  function _removeDelegation(uint256 fromTokenId, uint256 toTokenId, address lpToken, uint256 amount) internal {
    LpTokenType lpType = s_lpType[lpToken];
    LockedBalance memory fromLocked = s_locked[fromTokenId][lpType];
    LockedBalance memory toLocked = s_locked[toTokenId][lpType];

    if (ownerOf(fromTokenId) != _msgSender() && ownerOf(toTokenId) != _msgSender()) revert NotOwner();
    if (s_delegations[fromTokenId][toTokenId][lpType] == 0) revert NoDelegationBetweenTokens(fromTokenId, toTokenId);

    amount = amount > s_delegations[fromTokenId][toTokenId][lpType]
      ? s_delegations[fromTokenId][toTokenId][lpType]
      : amount;

    toLocked.delegateAmount -= amount;
    fromLocked.amount += amount;

    s_delegations[fromTokenId][toTokenId][lpType] -= amount;
    if (s_delegations[fromTokenId][toTokenId][lpType] == 0) {
      s_delegatees[fromTokenId][lpType].remove(toTokenId);
      s_delegators[toTokenId][lpType].remove(fromTokenId);
    }

    s_locked[toTokenId][lpType] = toLocked;
    s_locked[fromTokenId][lpType] = fromLocked;
    _checkpoint(toTokenId, s_locked[toTokenId][lpType], lpType);
    _checkpoint(fromTokenId, s_locked[fromTokenId][lpType], lpType);

    if (s_voted[toTokenId]) IVoter(s_voter).poke(toTokenId);
    if (s_voted[fromTokenId]) IVoter(s_voter).poke(fromTokenId);

    emit DelegationRemoved(fromTokenId, toTokenId, lpToken, amount);
  }

  /**
   * @notice Removes delegatees from a specific veNFT
   * @param fromTokenId ID of the veNFT from which delegatees are removed
   * @param toTokenIds Array of veNFT IDs that are delegatees to be removed
   * @param lpToken Address of the LP token associated with the delegation
   * @param amounts Array of amounts of voting power to remove from each delegatee
   */
  function removeDelegatees(
    uint256 fromTokenId,
    uint256[] memory toTokenIds,
    address lpToken,
    uint256[] memory amounts
  ) public nonReentrant {
    if (toTokenIds.length != amounts.length) revert ArrayMismatch();
    uint256 toTokenIdsLength = toTokenIds.length;
    for (uint256 i = 0; i < toTokenIdsLength; i++) {
      _removeDelegation(fromTokenId, toTokenIds[i], lpToken, amounts[i]);
    }
  }

  /// @inheritdoc IveIONFirstExtension
  function allowDelegators(uint256 _tokenId, address _tokenAddress, bool _blocked) external nonReentrant {
    if (ownerOf(_tokenId) != _msgSender()) revert NotOwner();
    s_delegatorsBlocked[_tokenId][_tokenAddress] = _blocked;
    emit DelegatorsBlocked(_tokenId, _tokenAddress, _blocked);
  }

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                           Internal Functions                              ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝

  /**
   * @notice Handles the withdrawal of staked tokens using a specified staking strategy.
   * @param _owner The address of the owner of the stake.
   * @param _withdrawTo The address to which the withdrawn tokens are sent.
   * @param _tokenId The ID of the token being withdrawn.
   * @param _tokenAddress The address of the token being withdrawn.
   * @param _tokenAmount The amount of tokens to withdraw.
   * @param _stakeStrategy The staking strategy to use for withdrawal.
   */
  function _handleTokenWithdrawStake(
    address _owner,
    address _withdrawTo,
    uint256 _tokenId,
    address _tokenAddress,
    uint256 _tokenAmount,
    IStakeStrategy _stakeStrategy
  ) internal {
    _stakeStrategy.claim(_owner);
    _stakeStrategy.withdraw(_owner, _withdrawTo, _tokenAmount);
    s_underlyingStake[_tokenId][_tokenAddress] -= _tokenAmount;
  }

  /**
   * @notice Updates the user point history and epoch for a given token and LP token type.
   * @param _tokenId The ID of the token.
   * @param _newLocked The new locked balance information.
   * @param _lpType The LP token type.
   */
  function _checkpoint(uint256 _tokenId, LockedBalance memory _newLocked, LpTokenType _lpType) internal {
    UserPoint memory uNew;
    uNew.permanent = _newLocked.isPermanent ? _newLocked.amount : 0;
    uNew.permanentDelegate = _newLocked.isPermanent ? _newLocked.delegateAmount : 0;

    if (_newLocked.end > block.timestamp && _newLocked.amount > 0) {
      uNew.slope = _newLocked.amount / _MAXTIME;
      uNew.bias = uNew.slope * (_newLocked.end - block.timestamp);
    }

    uNew.ts = block.timestamp;
    uNew.blk = block.number;
    uint256 userEpoch = s_userPointEpoch[_tokenId][_lpType];
    if (userEpoch != 0 && s_userPointHistory[_tokenId][_lpType][userEpoch].ts == block.timestamp) {
      s_userPointHistory[_tokenId][_lpType][userEpoch] = uNew;
    } else {
      s_userPointEpoch[_tokenId][_lpType] = ++userEpoch;
      s_userPointHistory[_tokenId][_lpType][userEpoch] = uNew;
    }
  }

  /**
   * @notice Calculates the boost for a given lock duration.
   * @param _duration The duration of the lock.
   * @return The calculated boost value.
   */
  function _calculateBoost(uint256 _duration) internal view returns (uint256) {
    uint256 minDuration = s_minimumLockDuration;
    uint256 maxDuration = _MAXTIME;
    uint256 minBoost = 1e18;
    uint256 maxBoost = 2e18;

    if (_duration <= minDuration) {
      return minBoost;
    } else if (_duration >= maxDuration) {
      return maxBoost;
    } else {
      return minBoost + ((_duration - minDuration) * (maxBoost - minBoost)) / (maxDuration - minDuration);
    }
  }

  /**
   * @notice Creates a new split veNFT with specified locked balance and LP token type.
   * @param _to The address to which the new veNFT is attributed.
   * @param _newLocked The locked balance information for the new veNFT.
   * @param _lpType The LP token type.
   * @param _tokenAddress The address of the token being locked.
   * @return _tokenId The ID of the newly created veNFT.
   */
  function _createSplitVE(
    address _to,
    LockedBalance memory _newLocked,
    LpTokenType _lpType,
    address _tokenAddress
  ) private returns (uint256 _tokenId) {
    _tokenId = ++s_tokenId;
    _safeMint(_to, _tokenId);
    s_locked[_tokenId][_lpType] = _newLocked;
    s_assetsLocked[_tokenId].add(_tokenAddress);
    _checkpoint(_tokenId, _newLocked, _lpType);
  }

  /**
   * @notice Retrieves the staking strategy and data for a given LP token type.
   * @param _lpType The LP token type.
   * @return _stakeStrategy The staking strategy for the LP token type.
   * @return _stakeData The staking data for the LP token type.
   */
  function _getStakeStrategy(
    LpTokenType _lpType
  ) internal view returns (IStakeStrategy _stakeStrategy, bytes memory _stakeData) {
    IStakeStrategy strategy = s_stakeStrategy[_lpType];
    return (strategy, "");
  }

  /**
   * @notice Calculates the total boost for a given token ID and LP token type.
   * @param _tokenId The ID of the token.
   * @param _lpType The LP token type.
   * @return The total boost value.
   */
  function _getTotalBoost(uint256 _tokenId, LpTokenType _lpType) internal view returns (uint256) {
    uint256 totalBoost = s_locked[_tokenId][_lpType].boost;
    if (s_limitedBoostActive) totalBoost += s_limitedBoost;
    if (s_veAERO == address(0)) return totalBoost;

    address _owner = ownerOf(_tokenId);
    IAeroVoter aeroVoter = IAeroVoter(s_aeroVoting);
    IAeroVotingEscrow veAERO = IAeroVotingEscrow(s_veAERO);
    uint256 _balance = veAERO.balanceOf(_owner);
    for (uint256 i = 0; i < _balance; i++) {
      uint256 veAeroTokenId = veAERO.ownerToNFTokenIdList(_owner, i);
      uint256 weightToVoteRatio = (aeroVoter.votes(veAeroTokenId, s_ionicPool) * 1e18) / aeroVoter.weights(s_ionicPool);
      totalBoost += (s_aeroVoterBoost * weightToVoteRatio) / 1e18;
    }

    return totalBoost;
  }

  /**
   * @notice Retrieves the ETH price of a given token.
   * @dev Uses the MasterPriceOracle to fetch the price.
   * @param _token The address of the token for which the ETH price is requested.
   * @return The ETH price of the specified token.
   */
  function _getEthPrice(address _token) internal view returns (uint256) {
    IMasterPriceOracle mpo = IMasterPriceOracle(ap.getAddress("MasterPriceOracle"));
    return mpo.price(_token);
  }

  // // ╔═══════════════════════════════════════════════════════════════════════════╗
  // // ║                           View Functions                                  ║
  // // ╚═══════════════════════════════════════════════════════════════════════════╝

  /// @inheritdoc IveIONFirstExtension
  function balanceOfNFT(
    uint256 _tokenId
  ) public view returns (address[] memory _assets, uint256[] memory _balances, uint256[] memory _boosts) {
    address[] memory assetsLocked = s_assetsLocked[_tokenId].values();

    _assets = new address[](assetsLocked.length);
    _balances = new uint256[](assetsLocked.length);
    _boosts = new uint256[](assetsLocked.length);
    uint256 assetsLockedLength = assetsLocked.length;
    for (uint256 i = 0; i < assetsLockedLength; i++) {
      address asset = assetsLocked[i];
      LpTokenType lpType = s_lpType[asset];
      LockedBalance memory lockedBalance = s_locked[_tokenId][lpType];
      _boosts[i] = _getTotalBoost(_tokenId, lpType);
      _assets[i] = asset;
      _balances[i] = BalanceLogicLibrary.balanceOfNFTAt(
        s_userPointEpoch,
        s_userPointHistory,
        lpType,
        _tokenId,
        block.timestamp,
        lockedBalance.isPermanent
      );
    }

    return (_assets, _balances, _boosts);
  }

  /// @inheritdoc IveIONFirstExtension
  function getTotalEthValueOfTokens(address _owner) external view returns (uint256 totalValue) {
    IVoter voter = IVoter(s_voter);
    address[] memory lpTokens = voter.getAllLpRewardTokens();
    uint256 lpTokensLength = lpTokens.length;
    for (uint256 i = 0; i < lpTokensLength; i++) {
      uint256 ethValue = (s_userCumulativeAssetValues[_owner][lpTokens[i]] * _getEthPrice(lpTokens[i])) / PRECISION;
      totalValue += ethValue;
    }
  }

  /**
   * @dev Fallback function that delegates calls to the address returned by `_implementation()`. Will run if no other
   * function in the contract matches the call data.
   */
  fallback() external {
    address impl = veIONSecondExtension;
    require(impl != address(0), "Implementation not set");

    assembly {
      calldatacopy(0, 0, calldatasize())

      let result := delegatecall(gas(), impl, 0, calldatasize(), 0, 0)

      returndatacopy(0, 0, returndatasize())
      switch result
      case 0 {
        revert(0, returndatasize())
      }
      default {
        return(0, returndatasize())
      }
    }
  }
}

contracts/veION/veIONSecondExtension.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import { ERC721Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC721/ERC721Upgradeable.sol";
import { Ownable2StepUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/access/Ownable2StepUpgradeable.sol";
import { IMasterPriceOracle, IAeroVotingEscrow, IAeroVoter } from "./interfaces/IveIONCore.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IVoter } from "./interfaces/IVoter.sol";
import { ReentrancyGuardUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/security/ReentrancyGuardUpgradeable.sol";
import { veIONStorage } from "./veIONStorage.sol";
import { BalanceLogicLibrary } from "./libraries/BalanceLogicLibrary.sol";
import { IveIONSecondExtension } from "./interfaces/IveIONSecondExtension.sol";
import { EnumerableSet } from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import { IAddressesProvider } from "./interfaces/IveIONCore.sol";
import { IStakeStrategy } from "./stake/IStakeStrategy.sol";

/**
 * @title veION Contract Second Extension
 * @notice This contract manages the veION framework, enabling the staking and management LP tokens for voting power.
 * @author Jourdan Dunkley <jourdan@ionic.money> (https://github.com/jourdanDunkley)
 */
contract veIONSecondExtension is
  Ownable2StepUpgradeable,
  ERC721Upgradeable,
  ReentrancyGuardUpgradeable,
  veIONStorage,
  IveIONSecondExtension
{
  using EnumerableSet for EnumerableSet.UintSet;
  using EnumerableSet for EnumerableSet.AddressSet;
  using SafeERC20 for IERC20;

  constructor() {
    _disableInitializers(); // Locks the implementation contract from being initialized
  }

  /// @inheritdoc IveIONSecondExtension
  function whitelistTokens(address[] memory _tokens, bool[] memory _isWhitelisted) external onlyOwner {
    require(_tokens.length == _isWhitelisted.length, "Unequal Arrays");
    for (uint256 i; i < _tokens.length; i++) s_whitelistedToken[_tokens[i]] = _isWhitelisted[i];
    emit TokensWhitelisted(_tokens, _isWhitelisted);
  }

  /// @inheritdoc IveIONSecondExtension
  function withdrawProtocolFees(address _tokenAddress, address _recipient) external onlyOwner {
    LpTokenType lpType = s_lpType[_tokenAddress];
    uint256 protocolFees = s_protocolFees[lpType];
    require(protocolFees > 0, "No protocol fees available");
    s_protocolFees[lpType] = 0;
    IERC20(_tokenAddress).safeTransfer(_recipient, protocolFees);
    emit ProtocolFeesWithdrawn(_tokenAddress, _recipient, protocolFees);
  }

  /// @inheritdoc IveIONSecondExtension
  function withdrawDistributedFees(address _tokenAddress, address _recipient) external onlyOwner {
    LpTokenType lpType = s_lpType[_tokenAddress];
    uint256 distributedFees = s_distributedFees[lpType];
    require(distributedFees > 0, "No distributed fees available");
    s_distributedFees[lpType] = 0;
    IERC20(_tokenAddress).safeTransfer(_recipient, distributedFees);
    emit DistributedFeesWithdrawn(_tokenAddress, _recipient, distributedFees);
  }

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                           Setter Functions                                ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝

  /// @inheritdoc IveIONSecondExtension
  function toggleSplit(address _account, bool _isAllowed) external onlyOwner {
    s_canSplit[_account] = _isAllowed;
    emit SplitToggle(_account, _isAllowed);
  }

  /// @inheritdoc IveIONSecondExtension
  function toggleLimitedBoost(bool _isBoosted) external onlyOwner {
    s_limitedBoostActive = _isBoosted;
    emit LimitedBoostToggled(_isBoosted);
  }

  /// @inheritdoc IveIONSecondExtension
  function setLimitedTimeBoost(uint256 _boostAmount) external onlyOwner {
    if (_boostAmount <= 0) revert BoostAmountMustBeGreaterThanZero();
    s_limitedBoost = _boostAmount;
    emit LimitedTimeBoostSet(_boostAmount);
  }

  /// @inheritdoc IveIONSecondExtension
  function setVoter(address _voter) external onlyOwner {
    if (address(_voter) == address(0)) revert InvalidAddress();
    s_voter = _voter;
    emit VoterSet(_voter);
  }

  /// @inheritdoc IveIONSecondExtension
  function setMinimumLockAmount(address _tokenAddress, uint256 _minimumAmount) external onlyOwner {
    if (_minimumAmount <= 0) revert MinimumAmountMustBeGreaterThanZero();
    LpTokenType lpType = s_lpType[_tokenAddress];
    s_minimumLockAmount[lpType] = _minimumAmount;
    emit MinimumLockAmountSet(_tokenAddress, _minimumAmount);
  }

  /// @inheritdoc IveIONSecondExtension
  function setMinimumLockDuration(uint256 _minimumLockDuration) external onlyOwner {
    if (_minimumLockDuration <= 0) revert MinimumLockDurationMustBeGreaterThanZero();
    s_minimumLockDuration = _minimumLockDuration;
    emit MinimumLockDurationSet(_minimumLockDuration);
  }

  /// @inheritdoc IveIONSecondExtension
  function setIonicPool(address _ionicPool) external onlyOwner {
    if (address(_ionicPool) == address(0)) revert InvalidAddress();
    s_ionicPool = _ionicPool;
    emit IonicPoolSet(_ionicPool);
  }

  /// @inheritdoc IveIONSecondExtension
  function setAeroVoting(address _aeroVoting) external onlyOwner {
    if (address(_aeroVoting) == address(0)) revert InvalidAddress();
    s_aeroVoting = _aeroVoting;
    emit AeroVotingSet(_aeroVoting);
  }

  /// @inheritdoc IveIONSecondExtension
  function setAeroVoterBoost(uint256 _aeroVoterBoost) external onlyOwner {
    if (_aeroVoterBoost <= 0) revert AeroBoostAmountMustBeGreaterThanZero();
    s_aeroVoterBoost = _aeroVoterBoost;
    emit AeroVoterBoostSet(_aeroVoterBoost);
  }

  /// @inheritdoc IveIONSecondExtension
  function setMaxEarlyWithdrawFee(uint256 _maxEarlyWithdrawFee) external onlyOwner {
    if (_maxEarlyWithdrawFee <= 0) revert MaxEarlyWithdrawFeeMustBeGreaterThanZero();
    s_maxEarlyWithdrawFee = _maxEarlyWithdrawFee;
    emit MaxEarlyWithdrawFeeSet(_maxEarlyWithdrawFee);
  }

  /// @inheritdoc IveIONSecondExtension
  function setLpTokenType(address _token, LpTokenType _type) external onlyOwner {
    if (_token == address(0)) revert InvalidTokenAddress();
    s_lpType[_token] = _type;
    emit LpTokenTypeSet(_token, _type);
  }

  /// @inheritdoc IveIONSecondExtension
  function setStakeStrategy(LpTokenType _lpType, IStakeStrategy _strategy) external onlyOwner {
    if (address(_strategy) == address(0)) revert InvalidStrategyAddress();
    s_stakeStrategy[_lpType] = IStakeStrategy(_strategy);
    emit StakeStrategySet(_lpType, address(_strategy));
  }

  /// @inheritdoc IveIONSecondExtension
  function setVeAERO(address _veAERO) external onlyOwner {
    if (_veAERO == address(0)) revert InvalidVeAEROAddress();
    s_veAERO = _veAERO;
    emit VeAEROSet(_veAERO);
  }

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                           View Functions                                  ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝

  /// @inheritdoc IveIONSecondExtension
  function getUserLock(uint256 _tokenId, LpTokenType _lpType) external view returns (LockedBalance memory) {
    return s_locked[_tokenId][_lpType];
  }

  /// @inheritdoc IveIONSecondExtension
  function getOwnedTokenIds(address _owner) external view returns (uint256[] memory) {
    return s_ownerToTokenIds[_owner].values();
  }

  /// @inheritdoc IveIONSecondExtension
  function getAssetsLocked(uint256 _tokenId) external view returns (address[] memory) {
    return s_assetsLocked[_tokenId].values();
  }

  /// @inheritdoc IveIONSecondExtension
  function getDelegatees(uint256 _tokenId, LpTokenType _lpType) external view returns (uint256[] memory) {
    return s_delegatees[_tokenId][_lpType].values();
  }

  /// @inheritdoc IveIONSecondExtension
  function getDelegators(uint256 _tokenId, LpTokenType _lpType) external view returns (uint256[] memory) {
    return s_delegators[_tokenId][_lpType].values();
  }

  /// @inheritdoc IveIONSecondExtension
  function getUserPoint(
    uint256 _tokenId,
    LpTokenType _lpType,
    uint256 _epoch
  ) external view returns (UserPoint memory) {
    return s_userPointHistory[_tokenId][_lpType][_epoch];
  }
}

contracts/veION/veIONStorage.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import { IAddressesProvider } from "./interfaces/IveIONCore.sol";
import { IStakeStrategy } from "./stake/IStakeStrategy.sol";
import { EnumerableSet } from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import { IveIONStructsEnumsErrorsEvents } from "./interfaces/IveIONStructsEnumsErrorsEvents.sol";

abstract contract veIONStorage is IveIONStructsEnumsErrorsEvents {
  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                                Constants                                  ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝
  /// @notice Represents the duration of one week in seconds.
  uint256 internal constant _WEEK = 1 weeks;
  /// @notice Represents the maximum lock time in seconds (2 years).
  uint256 internal constant _MAXTIME = 2 * 365 * 86400;
  /// @notice Precision used for calculations, set to 1e18.
  uint256 public constant PRECISION = 1e18;

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                             State Variables                               ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝
  /// @notice The current token ID counter.
  uint256 public s_tokenId;
  /// @notice The amount of limited boost available.
  uint256 public s_limitedBoost;
  /// @notice Indicates whether the limited boost is active.
  bool public s_limitedBoostActive;
  /// @notice Address of the veAERO contract.
  address public s_veAERO;
  /// @notice Address of the AeroVoting contract.
  address public s_aeroVoting;
  /// @notice Address of the Ionic Pool.
  address public s_ionicPool;
  /// @notice Address of the voter contract.
  address public s_voter;
  /// @notice The boost amount for AeroVoter.
  uint256 public s_aeroVoterBoost;
  /// @notice The minimum duration for locking.
  uint256 public s_minimumLockDuration;
  /// @notice The maximum fee for early withdrawal.
  uint256 public s_maxEarlyWithdrawFee;
  /// @notice The AddressesProvider contract used for address management.
  IAddressesProvider public ap;
  /// @notice The address of the logic contract for the veION first extension.
  address public veIONFirstExtension;
  /// @notice The address of the logic contract for the veION second extension.
  address public veIONSecondExtension;

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                                Mappings                                   ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝
  /// @dev Maps LP token types to their minimum lock amounts.
  mapping(LpTokenType => uint256) public s_minimumLockAmount;
  /// @dev Maps token addresses to their whitelist status.
  mapping(address => bool) public s_whitelistedToken;
  /// @dev Maps token addresses to their corresponding LP token types.
  mapping(address => LpTokenType) public s_lpType;
  /// @dev Maps user addresses to their ability to split.
  mapping(address => bool) public s_canSplit;
  /// @dev Maps token IDs and LP token types to their locked balances.
  mapping(uint256 => mapping(LpTokenType => LockedBalance)) public s_locked;
  /// @dev Maps token IDs and LP token types to user epochs.
  mapping(uint256 => mapping(LpTokenType => uint256)) public s_userPointEpoch;
  /// @dev Maps token IDs and LP token types to user point history.
  mapping(uint256 => mapping(LpTokenType => UserPoint[1000000000])) public s_userPointHistory;
  /// @dev Maps token IDs to sets of locked asset addresses.
  mapping(uint256 => EnumerableSet.AddressSet) internal s_assetsLocked;
  /// @dev Maps token IDs to their voting status.
  mapping(uint256 => bool) public s_voted;
  /// @dev Maps LP token types to their total supply.
  mapping(LpTokenType => uint256) public s_supply;
  /// @dev Maps LP token types to their permanent lock balances.
  mapping(LpTokenType => uint256) public s_permanentLockBalance;
  /// @dev Maps LP token types to their underlying stake strategies.
  mapping(LpTokenType => IStakeStrategy) public s_stakeStrategy;
  /// @dev Maps token IDs and LP token addresses to their underlying stake amounts.
  mapping(uint256 => mapping(address => uint256)) public s_underlyingStake;
  /// @dev Maps LP token types to their protocol fees.
  mapping(LpTokenType => uint256) public s_protocolFees;
  /// @dev Maps LP token types to their distributed fees.
  mapping(LpTokenType => uint256) public s_distributedFees;
  /// @dev Maps delegators, delegatees, and LP token types to delegation amounts.
  mapping(uint256 => mapping(uint256 => mapping(LpTokenType => uint256))) public s_delegations;
  /// @dev Maps token IDs and LP token types to sets of delegatees.
  mapping(uint256 => mapping(LpTokenType => EnumerableSet.UintSet)) internal s_delegatees;
  /// @dev Maps token IDs and LP token types to sets of delegators.
  mapping(uint256 => mapping(LpTokenType => EnumerableSet.UintSet)) internal s_delegators;
  /// @dev Maps owner addresses to sets of token IDs they own.
  mapping(address => EnumerableSet.UintSet) internal s_ownerToTokenIds;
  /// @dev Maps user addresses and token addresses to cumulative asset values.
  mapping(address => mapping(address => uint256)) public s_userCumulativeAssetValues;
  /// @dev Maps token Id and lp onto delegator permissioning.
  mapping(uint256 => mapping(address => bool)) public s_delegatorsBlocked;

  uint256[50] private __gap;
}

contracts/veION/Voter.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";
import { IVoter } from "./interfaces/IVoter.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IonicTimeLibrary } from "./libraries/IonicTimeLibrary.sol";
import { IveION } from "./interfaces/IveION.sol";
import { IBribeRewards } from "./interfaces/IBribeRewards.sol";
import { IonicComptroller } from "../compound/ComptrollerInterface.sol";
import { ICErc20 } from "../compound/CTokenInterfaces.sol";
import { MasterPriceOracle } from "../oracles/MasterPriceOracle.sol";
import { ERC721Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC721/ERC721Upgradeable.sol";
import { Ownable2StepUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/access/Ownable2StepUpgradeable.sol";
import { ReentrancyGuardUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/security/ReentrancyGuardUpgradeable.sol";

/**
 * @title Voter Contract
 * @notice This contract allows veION holders to vote for various markets
 * @author Jourdan Dunkley <jourdan@ionic.money> (https://github.com/jourdanDunkley)
 */
contract Voter is IVoter, Ownable2StepUpgradeable, ReentrancyGuardUpgradeable {
  using SafeERC20 for IERC20;

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                           State Variables                                 ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝
  ///@notice The ve token that governs these contracts
  address public ve;
  ///@notice Base token of ve contract
  address internal rewardToken;
  ///@notice Standard OZ IGovernor using ve for vote weights
  address public governor;
  ///@notice Master Price Oracle instance
  MasterPriceOracle public mpo;
  ///@notice List of LP tokens
  address[] public lpTokens;
  ///@notice Total Voting Weights for each address
  mapping(address => uint256) public totalWeight;
  ///@notice Maximum number of markets one voter can vote for at once
  uint256 public maxVotingNum;
  ///@notice Minimum value for maxVotingNum
  uint256 internal constant MIN_MAXVOTINGNUM = 10;
  ///@notice All markets viable for incentives
  Market[] public markets;

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                                Mappings                                   ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝
  ///@notice Mapping from Reward Accumulator to Bribes Voting Reward
  mapping(address => address) public rewardAccumulatorToBribe;
  ///@notice Mapping from Market to Market Side to LP Asset to weights
  mapping(address => mapping(MarketSide => mapping(address => uint256))) public weights;
  ///@notice Mapping from NFT to Pool to LP Asset to Votes
  mapping(uint256 => mapping(address => mapping(MarketSide => mapping(address => uint256)))) public votes;
  ///@notice Mapping from NFT to Pool to LP Asset to Base Weights
  mapping(uint256 => mapping(address => mapping(MarketSide => mapping(address => uint256)))) public baseWeights;
  ///@notice Mapping from NFT to List of markets voted for by NFT
  mapping(uint256 => mapping(address => address[])) public marketVote;
  ///@notice Mapping from NFT to List of market vote sides voted for by NFT
  mapping(uint256 => mapping(address => MarketSide[])) public marketVoteSide;
  ///@notice Mapping from NFT to Total voting weight of NFT
  mapping(uint256 => mapping(address => uint256)) public usedWeights;
  ///@notice Mapping from NFT to Timestamp of last vote (ensures single vote per epoch)
  mapping(uint256 => uint256) public lastVoted;
  ///@notice Mapping from Token to Whitelisted status
  mapping(address => bool) public isWhitelistedToken;
  ///@notice Mapping from TokenId to Whitelisted status
  mapping(uint256 => bool) public isWhitelistedNFT;
  ///@notice Mapping from Reward Accumulator to Liveness status
  mapping(address => bool) public isAlive;
  ///@notice Mapping from Market to Market Side to Reward Accumulator
  mapping(address => mapping(MarketSide => address)) public marketToRewardAccumulators;

  bool distributionTimelockAlive;

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                               Modifiers                                   ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝
  /**
   * @notice Modifier to ensure that the function is called only in a new epoch since the last vote.
   * @dev Reverts if the current epoch start time is less than or equal to the last voted timestamp for the given token ID.
   *      Also reverts if the current time is within the vote distribution window.
   * @param _tokenId The ID of the veNFT to check the last voted timestamp.
   */
  modifier onlyNewEpoch(uint256 _tokenId) {
    if (IonicTimeLibrary.epochStart(block.timestamp) <= lastVoted[_tokenId]) revert AlreadyVotedOrDeposited();
    if (block.timestamp <= IonicTimeLibrary.epochVoteStart(block.timestamp)) revert DistributeWindow();
    _;
  }

  /**
   * @notice Modifier to ensure that the function is called only by the governance address.
   * @dev Reverts if the caller is not the current governor.
   */
  modifier onlyGovernance() {
    if (msg.sender != governor) revert NotGovernor();
    _;
  }

  constructor() {
    _disableInitializers(); // Locks the implementation contract from being initialized
  }

  /**
   * @notice Initializes the Voter contract with the specified parameters.
   * @dev Requires initialization with at least one reward token.
   * @param _tokens An array of token addresses to be whitelisted.
   * @param _mpo The MasterPriceOracle contract address.
   * @param _rewardToken The address of the reward token.
   * @param _ve The address of the veION contract.
   * @custom:reverts TokensArrayEmpty if the _tokens array is empty.
   */
  function initialize(
    address[] calldata _tokens,
    MasterPriceOracle _mpo,
    address _rewardToken,
    address _ve
  ) external initializer {
    __Ownable2Step_init();
    __ReentrancyGuard_init();
    uint256 _length = _tokens.length;
    if (_length == 0) revert TokensArrayEmpty();
    for (uint256 i = 0; i < _length; i++) {
      _whitelistToken(_tokens[i], true);
    }
    mpo = _mpo;
    rewardToken = _rewardToken;
    ve = _ve;
    governor = msg.sender;

    emit Initialized(_tokens, address(_mpo), _rewardToken, _ve, governor);
  }

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                           External Functions                              ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝

  /// @inheritdoc IVoter
  function vote(
    uint256 _tokenId,
    address[] calldata _marketVote,
    MarketSide[] calldata _marketVoteSide,
    uint256[] calldata _weights
  ) external nonReentrant onlyNewEpoch(_tokenId) {
    VoteLocalVars memory vars;
    vars.sender = msg.sender;
    if (ERC721Upgradeable(ve).ownerOf(_tokenId) != vars.sender) revert NotOwner();
    if (
      _marketVote.length != _marketVoteSide.length ||
      _marketVoteSide.length != _weights.length ||
      _weights.length != _marketVote.length
    ) revert UnequalLengths();
    if (_marketVote.length > maxVotingNum) revert TooManyPools();
    vars.timestamp = block.timestamp;
    if ((vars.timestamp > IonicTimeLibrary.epochVoteEnd(vars.timestamp)) && !isWhitelistedNFT[_tokenId])
      revert NotWhitelistedNFT();
    uint256 totalVoteWeight = 0;

    for (uint256 i = 0; i < _marketVote.length; i++) {
      totalVoteWeight += _weights[i];
    }
    for (uint256 i = 0; i < lpTokens.length; i++) {
      _reset(_tokenId, lpTokens[i]);
    }

    lastVoted[_tokenId] = vars.timestamp;
    (vars.votingLPs, vars.votingLPBalances, vars.boosts) = IveION(ve).balanceOfNFT(_tokenId);
    for (uint256 j = 0; j < vars.votingLPs.length; j++) {
      _vote(
        _tokenId,
        vars.votingLPs[j],
        (vars.votingLPBalances[j] * vars.boosts[j]) / 1e18,
        _marketVote,
        _marketVoteSide,
        _weights,
        totalVoteWeight
      );
    }
  }

  /// @inheritdoc IVoter
  function poke(uint256 _tokenId) external nonReentrant {
    if (block.timestamp <= IonicTimeLibrary.epochVoteStart(block.timestamp)) revert DistributeWindow();
    (address[] memory _votingLPs, uint256[] memory _votingLPBalances, uint256[] memory _boosts) = IveION(ve)
      .balanceOfNFT(_tokenId);

    for (uint256 i = 0; i < _votingLPs.length; i++) {
      uint256 effectiveBalance = (_votingLPBalances[i] * _boosts[i]) / 1e18;
      _poke(_tokenId, lpTokens[i], effectiveBalance);
    }
  }

  /// @inheritdoc IVoter
  function reset(uint256 _tokenId) public nonReentrant onlyNewEpoch(_tokenId) {
    if (ERC721Upgradeable(ve).ownerOf(_tokenId) != msg.sender) revert NotOwner();
    for (uint256 i = 0; i < lpTokens.length; i++) {
      _reset(_tokenId, lpTokens[i]);
    }
  }

  /// @inheritdoc IVoter
  function claimBribes(address[] memory _bribes, address[][] memory _tokens, uint256 _tokenId) external nonReentrant {
    if (_bribes.length != _tokens.length) revert UnequalLengths();
    if (ERC721Upgradeable(ve).ownerOf(_tokenId) != _msgSender()) revert NotOwner();
    uint256 _length = _bribes.length;
    for (uint256 i = 0; i < _length; i++) {
      IBribeRewards(_bribes[i]).getReward(_tokenId, _tokens[i]);
    }
  }

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                           Admin External Functions                        ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝

  /// @inheritdoc IVoter
  function distributeRewards() external onlyGovernance {
    if (distributionTimelockAlive && block.timestamp <= IonicTimeLibrary.epochVoteEnd(block.timestamp)) revert NotDistributeWindow();
    uint256 _reward = IERC20(rewardToken).balanceOf(address(this));
    uint256 _totalLPValueETH = _calculateTotalLPValue();
    for (uint256 i = 0; i < markets.length; i++) {
      uint256 _marketWeightETH = _calculateMarketLPValue(markets[i].marketAddress, markets[i].side);
      if (_marketWeightETH > 0) {
        IERC20(rewardToken).safeTransfer(
          marketToRewardAccumulators[markets[i].marketAddress][markets[i].side],
          (_reward * _marketWeightETH) / _totalLPValueETH
        );
      }
    }
  }

  /// @inheritdoc IVoter
  function whitelistToken(address _token, bool _bool) external onlyGovernance {
    _whitelistToken(_token, _bool);
  }

  /// @inheritdoc IVoter
  function whitelistNFT(uint256 _tokenId, bool _bool) external onlyGovernance {
    address _sender = msg.sender;
    isWhitelistedNFT[_tokenId] = _bool;
    emit WhitelistNFT(_sender, _tokenId, _bool);
  }

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                           Internal Functions                              ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝

  /**
   * @notice Internal function to handle voting logic for a given token ID and voting asset.
   * @param _tokenId The ID of the token used for voting.
   * @param _votingAsset The address of the asset being used for voting.
   * @param _votingAssetBalance The balance of the voting asset.
   * @param _marketVote An array of market addresses to vote for.
   * @param _marketVoteSide An array of market sides corresponding to the markets.
   * @param _weights An array of weights for each market.
   * @param totalVoteWeight The total weight of the vote.
   */
  function _vote(
    uint256 _tokenId,
    address _votingAsset,
    uint256 _votingAssetBalance,
    address[] memory _marketVote,
    MarketSide[] memory _marketVoteSide,
    uint256[] memory _weights,
    uint256 totalVoteWeight
  ) internal {
    VoteVars memory vars;
    uint256 marketVoteLength = _marketVote.length;
    for (uint256 i = 0; i < marketVoteLength; i++) {
      vars.market = _marketVote[i];
      vars.marketSide = _marketVoteSide[i];
      vars.rewardAccumulator = marketToRewardAccumulators[vars.market][vars.marketSide];
      vars.bribes = rewardAccumulatorToBribe[vars.rewardAccumulator];
      if (_weights[i] == 0) revert ZeroWeight();
      if (vars.rewardAccumulator == address(0)) revert RewardAccumulatorDoesNotExist(vars.market);
      if (!isAlive[vars.rewardAccumulator]) revert RewardAccumulatorNotAlive(vars.rewardAccumulator);

      vars.marketWeight = (_weights[i] * _votingAssetBalance) / totalVoteWeight;
      if (votes[_tokenId][vars.market][vars.marketSide][_votingAsset] != 0) revert NonZeroVotes();

      marketVote[_tokenId][_votingAsset].push(vars.market);
      marketVoteSide[_tokenId][_votingAsset].push(vars.marketSide);

      weights[vars.market][vars.marketSide][_votingAsset] += vars.marketWeight;
      votes[_tokenId][vars.market][vars.marketSide][_votingAsset] += vars.marketWeight;
      baseWeights[_tokenId][vars.market][vars.marketSide][_votingAsset] = _weights[i];
      IBribeRewards(vars.bribes).deposit(_votingAsset, uint256(vars.marketWeight), _tokenId);
      vars.usedWeight += vars.marketWeight;
      vars.totalWeight += vars.marketWeight;
      emit Voted(
        msg.sender,
        vars.market,
        _tokenId,
        vars.marketWeight,
        weights[vars.market][vars.marketSide][_votingAsset],
        block.timestamp
      );
    }
    IveION(ve).voting(_tokenId, true);
    totalWeight[_votingAsset] += uint256(vars.totalWeight);
    usedWeights[_tokenId][_votingAsset] = uint256(vars.usedWeight);
  }

  /**
   * @notice Internal function to update voting balances for a given token ID and voting asset.
   * @param _tokenId The ID of the token whose voting balance is being updated.
   * @param _votingAsset The address of the asset being used for voting.
   * @param _votingAssetBalance The balance of the voting asset.
   */
  function _poke(uint256 _tokenId, address _votingAsset, uint256 _votingAssetBalance) internal {
    address[] memory _marketVote = marketVote[_tokenId][_votingAsset];
    MarketSide[] memory _marketVoteSide = marketVoteSide[_tokenId][_votingAsset];
    uint256 _marketCnt = _marketVote.length;
    uint256[] memory _weights = new uint256[](_marketCnt);
    uint256 totalVoteWeight = 0;

    for (uint256 i = 0; i < _marketCnt; i++) {
      _weights[i] = baseWeights[_tokenId][_marketVote[i]][_marketVoteSide[i]][_votingAsset];
    }

    for (uint256 i = 0; i < _marketVote.length; i++) {
      totalVoteWeight += _weights[i];
    }

    _reset(_tokenId, _votingAsset);
    _vote(_tokenId, _votingAsset, _votingAssetBalance, _marketVote, _marketVoteSide, _weights, totalVoteWeight);
  }

  /**
   * @notice Internal function to reset voting state for a given token ID and voting asset.
   * @param _tokenId The ID of the token whose voting state is being reset.
   * @param _votingAsset The address of the asset being used for voting.
   */
  function _reset(uint256 _tokenId, address _votingAsset) internal {
    address[] storage _marketVote = marketVote[_tokenId][_votingAsset];
    MarketSide[] storage _marketVoteSide = marketVoteSide[_tokenId][_votingAsset];
    uint256 _marketVoteCnt = _marketVote.length;

    for (uint256 i = 0; i < _marketVoteCnt; i++) {
      address _market = _marketVote[i];
      MarketSide _marketSide = _marketVoteSide[i];

      uint256 _votes = votes[_tokenId][_market][_marketSide][_votingAsset];
      if (_votes != 0) {
        weights[_market][_marketSide][_votingAsset] -= _votes;
        delete votes[_tokenId][_market][_marketSide][_votingAsset];
        IBribeRewards(rewardAccumulatorToBribe[marketToRewardAccumulators[_market][_marketSide]]).withdraw(
          _votingAsset,
          uint256(_votes),
          _tokenId
        );
        totalWeight[_votingAsset] -= _votes;
        emit Abstained(
          msg.sender,
          _market,
          _tokenId,
          _votes,
          weights[_market][_marketSide][_votingAsset],
          block.timestamp
        );
      }
    }
    usedWeights[_tokenId][_votingAsset] = 0;
    delete marketVote[_tokenId][_votingAsset];
    delete marketVoteSide[_tokenId][_votingAsset];
    IveION(ve).voting(_tokenId, false);
  }

  /**
   * @notice Internal function to whitelist or unwhitelist a token for use in bribes.
   * @param _token The address of the token to be whitelisted or unwhitelisted.
   * @param _bool Boolean indicating whether to whitelist (true) or unwhitelist (false) the token.
   */
  function _whitelistToken(address _token, bool _bool) internal {
    isWhitelistedToken[_token] = _bool;
    emit WhitelistToken(msg.sender, _token, _bool);
  }

  /**
   * @notice Internal function to calculate the ETH value of a given amount of LP tokens.
   * @param amount The amount of LP tokens.
   * @param lpToken The address of the LP token.
   * @return The ETH value of the given amount of LP tokens.
   */
  function _getTokenEthValue(uint256 amount, address lpToken) internal view returns (uint256) {
    uint256 tokenPriceInEth = mpo.price(lpToken); // Fetch price of 1 lpToken in ETH
    uint256 ethValue = amount * tokenPriceInEth;
    return ethValue;
  }

  /**
   * @notice Internal function to calculate the total ETH value of all LP tokens in the markets.
   * @return _totalLPValueETH The total ETH value of all LP tokens.
   */
  function _calculateTotalLPValue() internal view returns (uint256 _totalLPValueETH) {
    uint256 marketLength = markets.length;
    for (uint256 i = 0; i < marketLength; i++)
      _totalLPValueETH += _calculateMarketLPValue(markets[i].marketAddress, markets[i].side);
  }

  /**
   * @notice Internal function to calculate the ETH value of LP tokens for a specific market.
   * @param _market The address of the market.
   * @param _marketSide The side of the market.
   * @return _marketLPValueETH The ETH value of LP tokens for the specified market.
   */
  function _calculateMarketLPValue(
    address _market,
    MarketSide _marketSide
  ) internal view returns (uint256 _marketLPValueETH) {
    uint256 lpTokensLength = lpTokens.length;
    for (uint256 i = 0; i < lpTokensLength; i++) {
      uint256 _lpAmount = weights[_market][_marketSide][lpTokens[i]];
      uint256 tokenEthValue = _getTokenEthValue(_lpAmount, lpTokens[i]);
      _marketLPValueETH += tokenEthValue;
    }
  }

  /**
   * @notice Internal function to check if a market exists.
   * @param _marketAddress The address of the market.
   * @param _marketSide The side of the market.
   * @return True if the market exists, false otherwise.
   */
  function _marketExists(address _marketAddress, MarketSide _marketSide) internal view returns (bool) {
    uint256 marketLength = markets.length;
    for (uint256 j = 0; j < marketLength; j++) {
      if (markets[j].marketAddress == _marketAddress && markets[j].side == _marketSide) {
        return true;
      }
    }
    return false;
  }

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                           Setter Functions                                ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝

  /// @inheritdoc IVoter
  function setLpTokens(address[] memory _lpTokens) external onlyOwner {
    require(_lpTokens.length != 0, "LpTokens array cannot be empty");
    lpTokens = _lpTokens;
    emit LpTokensSet(_lpTokens);
  }

  /// @inheritdoc IVoter
  function setMpo(address _mpo) external onlyOwner {
    if (_mpo == address(0)) revert ZeroAddress();
    mpo = MasterPriceOracle(_mpo);
    emit MpoSet(_mpo);
  }

  /// @inheritdoc IVoter
  function setGovernor(address _governor) public onlyOwner {
    if (_governor == address(0)) revert ZeroAddress();
    governor = _governor;
    emit GovernorSet(_governor);
  }

  /// @inheritdoc IVoter
  function addMarkets(Market[] calldata _markets) external onlyGovernance {
    for (uint256 i = 0; i < _markets.length; i++) {
      Market memory newMarket = _markets[i];
      if (_marketExists(newMarket.marketAddress, newMarket.side)) revert MarketAlreadyExists();
      markets.push(newMarket);
    }
    emit MarketsAdded(_markets);
  }

  /// @inheritdoc IVoter
  function setMarketRewardAccumulators(
    address[] calldata _markets,
    MarketSide[] calldata _marketSides,
    address[] calldata _rewardAccumulators
  ) external onlyGovernance {
    uint256 _length = _markets.length;
    if (_marketSides.length != _length) revert MismatchedArrayLengths();
    if (_rewardAccumulators.length != _length) revert MismatchedArrayLengths();
    for (uint256 i = 0; i < _length; i++) {
      marketToRewardAccumulators[_markets[i]][_marketSides[i]] = _rewardAccumulators[i];
      isAlive[_rewardAccumulators[i]] = true;
    }
    emit MarketRewardAccumulatorsSet(_markets, _marketSides, _rewardAccumulators);
  }

  /// @inheritdoc IVoter
  function setBribes(address[] calldata _rewardAccumulators, address[] calldata _bribes) external onlyGovernance {
    uint256 _length = _bribes.length;
    if (_rewardAccumulators.length != _length) revert MismatchedArrayLengths();
    for (uint256 i = 0; i < _length; i++) {
      rewardAccumulatorToBribe[_rewardAccumulators[i]] = _bribes[i];
    }
    emit BribesSet(_rewardAccumulators, _bribes);
  }

  /// @inheritdoc IVoter
  function setMaxVotingNum(uint256 _maxVotingNum) external onlyGovernance {
    if (_maxVotingNum < MIN_MAXVOTINGNUM) revert MaximumVotingNumberTooLow();
    if (_maxVotingNum == maxVotingNum) revert SameValue();
    maxVotingNum = _maxVotingNum;
    emit MaxVotingNumSet(_maxVotingNum);
  }

  /// @inheritdoc IVoter
  function toggleRewardAccumulatorAlive(
    address _market,
    MarketSide _marketSide,
    bool _isAlive
  ) external onlyGovernance {
    address _rewardAccumulator = marketToRewardAccumulators[_market][_marketSide];
    if (_rewardAccumulator == address(0)) revert RewardAccumulatorDoesNotExist(_market);
    isAlive[_rewardAccumulator] = _isAlive;
    emit RewardAccumulatorAliveToggled(_market, _marketSide, _isAlive);
  }

  /// @inheritdoc IVoter
  function toggleDistributionTimelockAlive(bool _isAlive) external onlyGovernance {
    distributionTimelockAlive = _isAlive;
    emit DistributionTimelockAliveToggled(_isAlive);
  }

  // ╔═══════════════════════════════════════════════════════════════════════════╗
  // ║                           Pure/View Functions                             ║
  // ╚═══════════════════════════════════════════════════════════════════════════╝

  /// @inheritdoc IVoter
  function epochStart(uint256 _timestamp) external pure returns (uint256) {
    return IonicTimeLibrary.epochStart(_timestamp);
  }

  /// @inheritdoc IVoter
  function epochNext(uint256 _timestamp) external pure returns (uint256) {
    return IonicTimeLibrary.epochNext(_timestamp);
  }

  /// @inheritdoc IVoter
  function epochVoteStart(uint256 _timestamp) external pure returns (uint256) {
    return IonicTimeLibrary.epochVoteStart(_timestamp);
  }

  /// @inheritdoc IVoter
  function epochVoteEnd(uint256 _timestamp) external pure returns (uint256) {
    return IonicTimeLibrary.epochVoteEnd(_timestamp);
  }

  /// @inheritdoc IVoter
  function marketsLength() external view returns (uint256) {
    return markets.length;
  }

  /// @inheritdoc IVoter
  function getAllLpRewardTokens() external view returns (address[] memory) {
    return lpTokens;
  }

  /// @inheritdoc IVoter
  function getVoteDetails(uint256 _tokenId, address _lpAsset) external view returns (VoteDetails memory) {
    uint256 length = marketVote[_tokenId][_lpAsset].length;
    address[] memory _marketVotes = new address[](length);
    MarketSide[] memory _marketVoteSides = new MarketSide[](length);
    uint256[] memory _votes = new uint256[](length);

    for (uint256 i = 0; i < length; i++) {
      _marketVotes[i] = marketVote[_tokenId][_lpAsset][i];
      _marketVoteSides[i] = marketVoteSide[_tokenId][_lpAsset][i];
      _votes[i] = votes[_tokenId][_marketVotes[i]][_marketVoteSides[i]][_lpAsset];
    }

    uint256 _usedWeight = usedWeights[_tokenId][_lpAsset];

    return
      VoteDetails({
        marketVotes: _marketVotes,
        marketVoteSides: _marketVoteSides,
        votes: _votes,
        usedWeight: _usedWeight
      });
  }
}

contracts/veION/VoterLens.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import "@openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";
import "./interfaces/IVoter.sol";
import "../PoolDirectory.sol";
import { ICErc20 } from "../compound/CTokenInterfaces.sol";
import { ComptrollerExtensionInterface } from "../compound/ComptrollerInterface.sol";
import { ERC20 } from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import { Ownable2StepUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/access/Ownable2StepUpgradeable.sol";

contract VoterLens is Initializable, Ownable2StepUpgradeable {
  struct BribeInfo {
    address market;
    address bribeSupply;
    address bribeBorrow;
  }

  struct IncentiveInfo {
    address market;
    address bribeSupply;
    address[] rewardsSupply;
    uint256[] rewardsSupplyAmounts;
    uint256[] rewardsSupplyETHValues;
    address bribeBorrow;
    address[] rewardsBorrow;
    uint256[] rewardsBorrowAmounts;
    uint256[] rewardsBorrowETHValues;
  }

  address voter;
  PoolDirectory poolDirectory;
  IMasterPriceOracle mpo;
  uint256 constant PRECISION = 1e18;

  function initialize(address _voter, PoolDirectory _poolDirectory) public initializer {
    voter = _voter;
    poolDirectory = _poolDirectory;
    __Ownable2Step_init();
  }

  function getAllBribes() public view returns (BribeInfo[] memory _bribeInfo) {
    uint256 count = 0;
    uint256 totalMarketsLength = 0;
    (, PoolDirectory.Pool[] memory activePools) = poolDirectory.getActivePools();

    for (uint256 i = 0; i < activePools.length; i++) {
      ComptrollerExtensionInterface comptroller = ComptrollerExtensionInterface(activePools[i].comptroller);
      ICErc20[] memory markets = comptroller.getAllMarkets();
      totalMarketsLength += markets.length;
    }

    _bribeInfo = new BribeInfo[](totalMarketsLength);

    for (uint256 i = 0; i < activePools.length; i++) {
      ComptrollerExtensionInterface comptroller = ComptrollerExtensionInterface(activePools[i].comptroller);
      ICErc20[] memory markets = comptroller.getAllMarkets();

      for (uint256 j = 0; j < markets.length; j++) {
        address rewardAccumulatorSupply = IVoterView(voter).marketToRewardAccumulators(
          address(markets[j]),
          IVoter.MarketSide.Supply
        );
        address rewardAccumulatorBorrow = IVoterView(voter).marketToRewardAccumulators(
          address(markets[j]),
          IVoter.MarketSide.Borrow
        );

        address bribeSupply = IVoterView(voter).rewardAccumulatorToBribe(rewardAccumulatorSupply);
        address bribeBorrow = IVoterView(voter).rewardAccumulatorToBribe(rewardAccumulatorBorrow);

        _bribeInfo[count] = BribeInfo({
          market: address(markets[j]),
          bribeSupply: bribeSupply,
          bribeBorrow: bribeBorrow
        });
        count++;
      }
    }
  }

  function getAllIncentivesForBribes() external view returns (IncentiveInfo[] memory _incentiveInfo) {
    BribeInfo[] memory _bribeInfo = getAllBribes();
    _incentiveInfo = new IncentiveInfo[](_bribeInfo.length);
    for (uint256 i; i < _bribeInfo.length; i++) {
      _incentiveInfo[i].market = _bribeInfo[i].market;
      if (_bribeInfo[i].bribeSupply != address(0)) {
        _incentiveInfo[i].bribeSupply = _bribeInfo[i].bribeSupply;
        uint256 supplyRewardsLength = IBribeRewardsView(_bribeInfo[i].bribeSupply).rewardsListLength();
        _incentiveInfo[i].rewardsSupply = new address[](supplyRewardsLength);
        _incentiveInfo[i].rewardsSupplyAmounts = new uint256[](supplyRewardsLength);
        _incentiveInfo[i].rewardsSupplyETHValues = new uint256[](supplyRewardsLength);
        for (uint256 j; j < supplyRewardsLength; j++) {
          _incentiveInfo[i].rewardsSupply[j] = IBribeRewardsView(_bribeInfo[i].bribeSupply).rewards(j);
          _incentiveInfo[i].rewardsSupplyAmounts[j] = ERC20(_incentiveInfo[i].rewardsSupply[j]).balanceOf(
            _bribeInfo[i].bribeSupply
          );

          uint256 tokenPrice = mpo.price(_incentiveInfo[i].rewardsSupply[j]);

          if (tokenPrice != 0) {
            uint256 decimals = ERC20(_incentiveInfo[i].rewardsSupply[j]).decimals();
            _incentiveInfo[i].rewardsSupplyETHValues[j] =
              (_incentiveInfo[i].rewardsSupplyAmounts[j] * 10 ** (18 - decimals) * tokenPrice) /
              PRECISION;
          }
        }
      }

      if (_bribeInfo[i].bribeBorrow != address(0)) {
        _incentiveInfo[i].bribeBorrow = _bribeInfo[i].bribeBorrow;
        uint256 borrowRewardsLength = IBribeRewardsView(_bribeInfo[i].bribeBorrow).rewardsListLength();
        _incentiveInfo[i].rewardsBorrow = new address[](borrowRewardsLength);
        _incentiveInfo[i].rewardsBorrowAmounts = new uint256[](borrowRewardsLength);
        _incentiveInfo[i].rewardsBorrowETHValues = new uint256[](borrowRewardsLength);
        for (uint256 j; j < borrowRewardsLength; j++) {
          _incentiveInfo[i].rewardsBorrow[j] = IBribeRewardsView(_bribeInfo[i].bribeBorrow).rewards(j);
          _incentiveInfo[i].rewardsBorrowAmounts[j] = ERC20(_incentiveInfo[i].rewardsBorrow[j]).balanceOf(
            _bribeInfo[i].bribeBorrow
          );

          uint256 tokenPrice = mpo.price(_incentiveInfo[i].rewardsBorrow[j]);

          if (tokenPrice != 0) {
            uint256 decimals = (ERC20(_incentiveInfo[i].rewardsBorrow[j])).decimals();
            _incentiveInfo[i].rewardsBorrowETHValues[j] =
              (_incentiveInfo[i].rewardsBorrowAmounts[j] * 10 ** (18 - decimals) * tokenPrice) /
              PRECISION;
          }
        }
      }
    }
  }

  function setMasterPriceOracle(address _masterPriceOracle) external onlyOwner {
    mpo = IMasterPriceOracle(_masterPriceOracle);
  }
}

interface IVoterView {
  function rewardAccumulatorToBribe(address rewardAccumulator) external view returns (address);
  function marketToRewardAccumulators(address market, IVoter.MarketSide marketSide) external view returns (address);
}

interface IBribeRewardsView {
  function rewards(uint256 index) external view returns (address);
  function rewardsListLength() external view returns (uint256);
}

interface IMasterPriceOracle {
  function price(address underlying) external view returns (uint256);
}

