// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.8.0;

import { SafeOwnableUpgradeable } from "../ionic/SafeOwnableUpgradeable.sol";

/**
 * @title AddressesProvider
 * @notice The Addresses Provider serves as a central storage of system internal and external
 *         contract addresses that change between deploys and across chains
 * @author Veliko Minkov <veliko@midascapital.xyz>
 */
contract AddressesProvider is SafeOwnableUpgradeable {
  mapping(string => address) private _addresses;
  mapping(address => Contract) public plugins;
  mapping(address => Contract) public flywheelRewards;
  mapping(address => RedemptionStrategy) public redemptionStrategiesConfig;
  mapping(address => FundingStrategy) public fundingStrategiesConfig;
  JarvisPool[] public jarvisPoolsConfig;
  CurveSwapPool[] public curveSwapPoolsConfig;
  mapping(address => mapping(address => address)) public balancerPoolForTokens;

  /// @dev Initializer to set the admin that can set and change contracts addresses
  function initialize(address owner) public initializer {
    __SafeOwnable_init(owner);
  }

  /**
   * @dev The contract address and a string that uniquely identifies the contract's interface
   */
  struct Contract {
    address addr;
    string contractInterface;
  }

  struct RedemptionStrategy {
    address addr;
    string contractInterface;
    address outputToken;
  }

  struct FundingStrategy {
    address addr;
    string contractInterface;
    address inputToken;
  }

  struct JarvisPool {
    address syntheticToken;
    address collateralToken;
    address liquidityPool;
    uint256 expirationTime;
  }

  struct CurveSwapPool {
    address poolAddress;
    address[] coins;
  }

  /**
   * @dev sets the address and contract interface ID of the flywheel for the reward token
   * @param rewardToken the reward token address
   * @param flywheelRewardsModule the flywheel rewards module address
   * @param contractInterface a string that uniquely identifies the contract's interface
   */
  function setFlywheelRewards(
    address rewardToken,
    address flywheelRewardsModule,
    string calldata contractInterface
  ) public onlyOwner {
    flywheelRewards[rewardToken] = Contract(flywheelRewardsModule, contractInterface);
  }

  /**
   * @dev sets the address and contract interface ID of the ERC4626 plugin for the asset
   * @param asset the asset address
   * @param plugin the ERC4626 plugin address
   * @param contractInterface a string that uniquely identifies the contract's interface
   */
  function setPlugin(
    address asset,
    address plugin,
    string calldata contractInterface
  ) public onlyOwner {
    plugins[asset] = Contract(plugin, contractInterface);
  }

  /**
   * @dev sets the address and contract interface ID of the redemption strategy for the asset
   * @param asset the asset address
   * @param strategy redemption strategy address
   * @param contractInterface a string that uniquely identifies the contract's interface
   */
  function setRedemptionStrategy(
    address asset,
    address strategy,
    string calldata contractInterface,
    address outputToken
  ) public onlyOwner {
    redemptionStrategiesConfig[asset] = RedemptionStrategy(strategy, contractInterface, outputToken);
  }

  function getRedemptionStrategy(address asset) public view returns (RedemptionStrategy memory) {
    return redemptionStrategiesConfig[asset];
  }

  /**
   * @dev sets the address and contract interface ID of the funding strategy for the asset
   * @param asset the asset address
   * @param strategy funding strategy address
   * @param contractInterface a string that uniquely identifies the contract's interface
   */
  function setFundingStrategy(
    address asset,
    address strategy,
    string calldata contractInterface,
    address inputToken
  ) public onlyOwner {
    fundingStrategiesConfig[asset] = FundingStrategy(strategy, contractInterface, inputToken);
  }

  function getFundingStrategy(address asset) public view returns (FundingStrategy memory) {
    return fundingStrategiesConfig[asset];
  }

  /**
   * @dev configures the Jarvis pool of a Jarvis synthetic token
   * @param syntheticToken the synthetic token address
   * @param collateralToken the collateral token address
   * @param liquidityPool the liquidity pool address
   * @param expirationTime the operation expiration time
   */
  function setJarvisPool(
    address syntheticToken,
    address collateralToken,
    address liquidityPool,
    uint256 expirationTime
  ) public onlyOwner {
    jarvisPoolsConfig.push(JarvisPool(syntheticToken, collateralToken, liquidityPool, expirationTime));
  }

  function setCurveSwapPool(address poolAddress, address[] calldata coins) public onlyOwner {
    curveSwapPoolsConfig.push(CurveSwapPool(poolAddress, coins));
  }

  /**
   * @dev Sets an address for an id replacing the address saved in the addresses map
   * @param id The id
   * @param newAddress The address to set
   */
  function setAddress(string calldata id, address newAddress) external onlyOwner {
    _addresses[id] = newAddress;
  }

  /**
   * @dev Returns an address by id
   * @return The address
   */
  function getAddress(string calldata id) public view returns (address) {
    return _addresses[id];
  }

  function getCurveSwapPools() public view returns (CurveSwapPool[] memory) {
    return curveSwapPoolsConfig;
  }

  function getJarvisPools() public view returns (JarvisPool[] memory) {
    return jarvisPoolsConfig;
  }

  function setBalancerPoolForTokens(
    address inputToken,
    address outputToken,
    address pool
  ) external onlyOwner {
    balancerPoolForTokens[inputToken][outputToken] = pool;
  }

  function getBalancerPoolForTokens(address inputToken, address outputToken) external view returns (address) {
    return balancerPoolForTokens[inputToken][outputToken];
  }
}

contracts/ionic/AuthoritiesRegistry.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import { PoolRolesAuthority } from "../ionic/PoolRolesAuthority.sol";
import { SafeOwnableUpgradeable } from "../ionic/SafeOwnableUpgradeable.sol";
import { IonicComptroller } from "../compound/ComptrollerInterface.sol";

import { TransparentUpgradeableProxy } from "@openzeppelin/contracts/proxy/transparent/TransparentUpgradeableProxy.sol";

contract AuthoritiesRegistry is SafeOwnableUpgradeable {
  mapping(address => PoolRolesAuthority) public poolsAuthorities;
  PoolRolesAuthority public poolAuthLogic;
  address public leveredPositionsFactory;
  bool public noAuthRequired;

  function initialize(address _leveredPositionsFactory) public initializer {
    __SafeOwnable_init(msg.sender);
    leveredPositionsFactory = _leveredPositionsFactory;
    poolAuthLogic = new PoolRolesAuthority();
  }

  function reinitialize(address _leveredPositionsFactory) public onlyOwnerOrAdmin {
    leveredPositionsFactory = _leveredPositionsFactory;
    poolAuthLogic = new PoolRolesAuthority();
    // for Neon the auth is not required
    noAuthRequired = block.chainid == 245022934;
  }

  function createPoolAuthority(address pool) public onlyOwner returns (PoolRolesAuthority auth) {
    require(address(poolsAuthorities[pool]) == address(0), "already created");

    TransparentUpgradeableProxy proxy = new TransparentUpgradeableProxy(address(poolAuthLogic), _getProxyAdmin(), "");
    auth = PoolRolesAuthority(address(proxy));
    auth.initialize(address(this));
    poolsAuthorities[pool] = auth;

    auth.openPoolSupplierCapabilities(IonicComptroller(pool));
    auth.setUserRole(address(this), auth.REGISTRY_ROLE(), true);
    // sets the registry owner as the auth owner
    reconfigureAuthority(pool);
  }

  function reconfigureAuthority(address poolAddress) public {
    IonicComptroller pool = IonicComptroller(poolAddress);
    PoolRolesAuthority auth = poolsAuthorities[address(pool)];

    if (msg.sender != poolAddress || address(auth) != address(0)) {
      require(address(auth) != address(0), "no such authority");
      require(msg.sender == owner() || msg.sender == poolAddress, "not owner or pool");

      auth.configureRegistryCapabilities();
      auth.configurePoolSupplierCapabilities(pool);
      auth.configurePoolBorrowerCapabilities(pool);
      // everyone can be a liquidator
      auth.configureOpenPoolLiquidatorCapabilities(pool);
      auth.configureLeveredPositionCapabilities(pool);

      if (auth.owner() != owner()) {
        auth.setOwner(owner());
      }
    }
  }

  function canCall(
    address pool,
    address user,
    address target,
    bytes4 functionSig
  ) external view returns (bool) {
    PoolRolesAuthority authorityForPool = poolsAuthorities[pool];
    if (address(authorityForPool) == address(0)) {
      return noAuthRequired;
    } else {
      // allow only if an auth exists and it allows the action
      return authorityForPool.canCall(user, target, functionSig);
    }
  }

  function setUserRole(
    address pool,
    address user,
    uint8 role,
    bool enabled
  ) external {
    PoolRolesAuthority poolAuth = poolsAuthorities[pool];

    require(address(poolAuth) != address(0), "auth does not exist");
    require(msg.sender == owner() || msg.sender == leveredPositionsFactory, "not owner or factory");
    require(msg.sender != leveredPositionsFactory || role == poolAuth.LEVERED_POSITION_ROLE(), "only lev pos role");

    poolAuth.setUserRole(user, role, enabled);
  }
}

contracts/ionic/DiamondExtension.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

/**
 * @notice a base contract for logic extensions that use the diamond pattern storage
 * to map the functions when looking up the extension contract to delegate to.
 */
abstract contract DiamondExtension {
  /**
   * @return a list of all the function selectors that this logic extension exposes
   */
  function _getExtensionFunctions() external pure virtual returns (bytes4[] memory);
}

// When no function exists for function called
error FunctionNotFound(bytes4 _functionSelector);

// When no extension exists for function called
error ExtensionNotFound(bytes4 _functionSelector);

// When the function is already added
error FunctionAlreadyAdded(bytes4 _functionSelector, address _currentImpl);

abstract contract DiamondBase {
  /**
   * @dev register a logic extension
   * @param extensionToAdd the extension whose functions are to be added
   * @param extensionToReplace the extension whose functions are to be removed/replaced
   */
  function _registerExtension(DiamondExtension extensionToAdd, DiamondExtension extensionToReplace) external virtual;

  function _listExtensions() public view returns (address[] memory) {
    return LibDiamond.listExtensions();
  }

  fallback() external {
    address extension = LibDiamond.getExtensionForFunction(msg.sig);
    if (extension == address(0)) revert FunctionNotFound(msg.sig);
    // Execute external function from extension using delegatecall and return any value.
    assembly {
      // copy function selector and any arguments
      calldatacopy(0, 0, calldatasize())
      // execute function call using the extension
      let result := delegatecall(gas(), extension, 0, calldatasize(), 0, 0)
      // get any return value
      returndatacopy(0, 0, returndatasize())
      // return any return value or error back to the caller
      switch result
      case 0 {
        revert(0, returndatasize())
      }
      default {
        return(0, returndatasize())
      }
    }
  }
}

/**
 * @notice a library to use in a contract, whose logic is extended with diamond extension
 */
library LibDiamond {
  bytes32 constant DIAMOND_STORAGE_POSITION = keccak256("diamond.extensions.diamond.storage");

  struct Function {
    address extension;
    bytes4 selector;
  }

  struct LogicStorage {
    Function[] functions;
    address[] extensions;
  }

  function getExtensionForFunction(bytes4 msgSig) internal view returns (address) {
    return getExtensionForSelector(msgSig, diamondStorage());
  }

  function diamondStorage() internal pure returns (LogicStorage storage ds) {
    bytes32 position = DIAMOND_STORAGE_POSITION;
    assembly {
      ds.slot := position
    }
  }

  function listExtensions() internal view returns (address[] memory) {
    return diamondStorage().extensions;
  }

  function registerExtension(DiamondExtension extensionToAdd, DiamondExtension extensionToReplace) internal {
    if (address(extensionToReplace) != address(0)) {
      removeExtension(extensionToReplace);
    }
    addExtension(extensionToAdd);
  }

  function removeExtension(DiamondExtension extension) internal {
    LogicStorage storage ds = diamondStorage();
    // remove all functions of the extension to replace
    removeExtensionFunctions(extension);
    for (uint8 i = 0; i < ds.extensions.length; i++) {
      if (ds.extensions[i] == address(extension)) {
        ds.extensions[i] = ds.extensions[ds.extensions.length - 1];
        ds.extensions.pop();
      }
    }
  }

  function addExtension(DiamondExtension extension) internal {
    LogicStorage storage ds = diamondStorage();
    for (uint8 i = 0; i < ds.extensions.length; i++) {
      require(ds.extensions[i] != address(extension), "extension already added");
    }
    addExtensionFunctions(extension);
    ds.extensions.push(address(extension));
  }

  function removeExtensionFunctions(DiamondExtension extension) internal {
    bytes4[] memory fnsToRemove = extension._getExtensionFunctions();
    LogicStorage storage ds = diamondStorage();
    for (uint16 i = 0; i < fnsToRemove.length; i++) {
      bytes4 selectorToRemove = fnsToRemove[i];
      // must never fail
      assert(address(extension) == getExtensionForSelector(selectorToRemove, ds));
      // swap with the last element in the selectorAtIndex array and remove the last element
      uint16 indexToKeep = getIndexForSelector(selectorToRemove, ds);
      ds.functions[indexToKeep] = ds.functions[ds.functions.length - 1];
      ds.functions.pop();
    }
  }

  function addExtensionFunctions(DiamondExtension extension) internal {
    bytes4[] memory fnsToAdd = extension._getExtensionFunctions();
    LogicStorage storage ds = diamondStorage();
    uint16 functionsCount = uint16(ds.functions.length);
    for (uint256 functionsIndex = 0; functionsIndex < fnsToAdd.length; functionsIndex++) {
      bytes4 selector = fnsToAdd[functionsIndex];
      address oldImplementation = getExtensionForSelector(selector, ds);
      if (oldImplementation != address(0)) revert FunctionAlreadyAdded(selector, oldImplementation);
      ds.functions.push(Function(address(extension), selector));
      functionsCount++;
    }
  }

  function getExtensionForSelector(bytes4 selector, LogicStorage storage ds) internal view returns (address) {
    uint256 fnsLen = ds.functions.length;
    for (uint256 i = 0; i < fnsLen; i++) {
      if (ds.functions[i].selector == selector) return ds.functions[i].extension;
    }

    return address(0);
  }

  function getIndexForSelector(bytes4 selector, LogicStorage storage ds) internal view returns (uint16) {
    uint16 fnsLen = uint16(ds.functions.length);
    for (uint16 i = 0; i < fnsLen; i++) {
      if (ds.functions[i].selector == selector) return i;
    }

    return type(uint16).max;
  }
}

contracts/ionic/PoolRolesAuthority.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import { IonicComptroller, ComptrollerInterface } from "../compound/ComptrollerInterface.sol";
import { ICErc20, CTokenSecondExtensionInterface, CTokenFirstExtensionInterface } from "../compound/CTokenInterfaces.sol";

import { RolesAuthority, Authority } from "solmate/auth/authorities/RolesAuthority.sol";

import "@openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";

contract PoolRolesAuthority is RolesAuthority, Initializable {
  constructor() RolesAuthority(address(0), Authority(address(0))) {
    _disableInitializers();
  }

  function initialize(address _owner) public initializer {
    owner = _owner;
    authority = this;
  }

  // up to 256 roles
  uint8 public constant REGISTRY_ROLE = 0;
  uint8 public constant SUPPLIER_ROLE = 1;
  uint8 public constant BORROWER_ROLE = 2;
  uint8 public constant LIQUIDATOR_ROLE = 3;
  uint8 public constant LEVERED_POSITION_ROLE = 4;

  function configureRegistryCapabilities() external requiresAuth {
    setRoleCapability(REGISTRY_ROLE, address(this), PoolRolesAuthority.configureRegistryCapabilities.selector, true);
    setRoleCapability(
      REGISTRY_ROLE,
      address(this),
      PoolRolesAuthority.configurePoolSupplierCapabilities.selector,
      true
    );
    setRoleCapability(
      REGISTRY_ROLE,
      address(this),
      PoolRolesAuthority.configurePoolBorrowerCapabilities.selector,
      true
    );
    setRoleCapability(
      REGISTRY_ROLE,
      address(this),
      PoolRolesAuthority.configureClosedPoolLiquidatorCapabilities.selector,
      true
    );
    setRoleCapability(
      REGISTRY_ROLE,
      address(this),
      PoolRolesAuthority.configureOpenPoolLiquidatorCapabilities.selector,
      true
    );
    setRoleCapability(
      REGISTRY_ROLE,
      address(this),
      PoolRolesAuthority.configureLeveredPositionCapabilities.selector,
      true
    );
    setRoleCapability(REGISTRY_ROLE, address(this), RolesAuthority.setUserRole.selector, true);
  }

  function openPoolSupplierCapabilities(IonicComptroller pool) external requiresAuth {
    _setPublicPoolSupplierCapabilities(pool, true);
  }

  function closePoolSupplierCapabilities(IonicComptroller pool) external requiresAuth {
    _setPublicPoolSupplierCapabilities(pool, false);
  }

  function _setPublicPoolSupplierCapabilities(IonicComptroller pool, bool setPublic) internal {
    setPublicCapability(address(pool), pool.enterMarkets.selector, setPublic);
    setPublicCapability(address(pool), pool.exitMarket.selector, setPublic);
    ICErc20[] memory allMarkets = pool.getAllMarkets();
    for (uint256 i = 0; i < allMarkets.length; i++) {
      bytes4[] memory selectors = getSupplierMarketSelectors();
      for (uint256 j = 0; j < selectors.length; j++) {
        setPublicCapability(address(allMarkets[i]), selectors[j], setPublic);
      }
    }
  }

  function configurePoolSupplierCapabilities(IonicComptroller pool) external requiresAuth {
    _configurePoolSupplierCapabilities(pool, SUPPLIER_ROLE);
  }

  function getSupplierMarketSelectors() internal pure returns (bytes4[] memory selectors) {
    uint8 fnsCount = 6;
    selectors = new bytes4[](fnsCount);
    selectors[--fnsCount] = CTokenSecondExtensionInterface.mint.selector;
    selectors[--fnsCount] = CTokenSecondExtensionInterface.redeem.selector;
    selectors[--fnsCount] = CTokenSecondExtensionInterface.redeemUnderlying.selector;
    selectors[--fnsCount] = CTokenFirstExtensionInterface.transfer.selector;
    selectors[--fnsCount] = CTokenFirstExtensionInterface.transferFrom.selector;
    selectors[--fnsCount] = CTokenFirstExtensionInterface.approve.selector;

    require(fnsCount == 0, "use the correct array length");
    return selectors;
  }

  function _configurePoolSupplierCapabilities(IonicComptroller pool, uint8 role) internal {
    setRoleCapability(role, address(pool), pool.enterMarkets.selector, true);
    setRoleCapability(role, address(pool), pool.exitMarket.selector, true);
    ICErc20[] memory allMarkets = pool.getAllMarkets();
    for (uint256 i = 0; i < allMarkets.length; i++) {
      bytes4[] memory selectors = getSupplierMarketSelectors();
      for (uint256 j = 0; j < selectors.length; j++) {
        setRoleCapability(role, address(allMarkets[i]), selectors[j], true);
      }
    }
  }

  function openPoolBorrowerCapabilities(IonicComptroller pool) external requiresAuth {
    _setPublicPoolBorrowerCapabilities(pool, true);
  }

  function closePoolBorrowerCapabilities(IonicComptroller pool) external requiresAuth {
    _setPublicPoolBorrowerCapabilities(pool, false);
  }

  function _setPublicPoolBorrowerCapabilities(IonicComptroller pool, bool setPublic) internal {
    ICErc20[] memory allMarkets = pool.getAllMarkets();
    for (uint256 i = 0; i < allMarkets.length; i++) {
      setPublicCapability(address(allMarkets[i]), allMarkets[i].borrow.selector, setPublic);
      setPublicCapability(address(allMarkets[i]), allMarkets[i].repayBorrow.selector, setPublic);
      setPublicCapability(address(allMarkets[i]), allMarkets[i].repayBorrowBehalf.selector, setPublic);
      setPublicCapability(address(allMarkets[i]), allMarkets[i].flash.selector, setPublic);
    }
  }

  function configurePoolBorrowerCapabilities(IonicComptroller pool) external requiresAuth {
    // borrowers have the SUPPLIER_ROLE capabilities by default
    _configurePoolSupplierCapabilities(pool, BORROWER_ROLE);
    ICErc20[] memory allMarkets = pool.getAllMarkets();
    for (uint256 i = 0; i < allMarkets.length; i++) {
      setRoleCapability(BORROWER_ROLE, address(allMarkets[i]), allMarkets[i].borrow.selector, true);
      setRoleCapability(BORROWER_ROLE, address(allMarkets[i]), allMarkets[i].repayBorrow.selector, true);
      setRoleCapability(BORROWER_ROLE, address(allMarkets[i]), allMarkets[i].repayBorrowBehalf.selector, true);
      setRoleCapability(BORROWER_ROLE, address(allMarkets[i]), allMarkets[i].flash.selector, true);
    }
  }

  function configureClosedPoolLiquidatorCapabilities(IonicComptroller pool) external requiresAuth {
    ICErc20[] memory allMarkets = pool.getAllMarkets();
    for (uint256 i = 0; i < allMarkets.length; i++) {
      setPublicCapability(address(allMarkets[i]), allMarkets[i].liquidateBorrow.selector, false);
      setRoleCapability(LIQUIDATOR_ROLE, address(allMarkets[i]), allMarkets[i].liquidateBorrow.selector, true);
      setRoleCapability(LIQUIDATOR_ROLE, address(allMarkets[i]), allMarkets[i].redeem.selector, true);
    }
  }

  function configureOpenPoolLiquidatorCapabilities(IonicComptroller pool) external requiresAuth {
    ICErc20[] memory allMarkets = pool.getAllMarkets();
    for (uint256 i = 0; i < allMarkets.length; i++) {
      setPublicCapability(address(allMarkets[i]), allMarkets[i].liquidateBorrow.selector, true);
      // TODO this leaves redeeming open for everyone
      setPublicCapability(address(allMarkets[i]), allMarkets[i].redeem.selector, true);
    }
  }

  function configureLeveredPositionCapabilities(IonicComptroller pool) external requiresAuth {
    setRoleCapability(LEVERED_POSITION_ROLE, address(pool), pool.enterMarkets.selector, true);
    setRoleCapability(LEVERED_POSITION_ROLE, address(pool), pool.exitMarket.selector, true);
    ICErc20[] memory allMarkets = pool.getAllMarkets();
    for (uint256 i = 0; i < allMarkets.length; i++) {
      setRoleCapability(LEVERED_POSITION_ROLE, address(allMarkets[i]), allMarkets[i].mint.selector, true);
      setRoleCapability(LEVERED_POSITION_ROLE, address(allMarkets[i]), allMarkets[i].redeem.selector, true);
      setRoleCapability(LEVERED_POSITION_ROLE, address(allMarkets[i]), allMarkets[i].redeemUnderlying.selector, true);

      setRoleCapability(LEVERED_POSITION_ROLE, address(allMarkets[i]), allMarkets[i].borrow.selector, true);
      setRoleCapability(LEVERED_POSITION_ROLE, address(allMarkets[i]), allMarkets[i].repayBorrow.selector, true);
      setRoleCapability(LEVERED_POSITION_ROLE, address(allMarkets[i]), allMarkets[i].flash.selector, true);
    }
  }
}

contracts/ionic/SafeOwnableUpgradeable.s.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/access/OwnableUpgradeable.sol";

/**
 * @dev Ownable extension that requires a two-step process of setting the pending owner and the owner accepting it.
 * @notice Existing OwnableUpgradeable contracts cannot be upgraded due to the extra storage variable
 * that will shift the other.
 */
abstract contract SafeOwnableUpgradeable is OwnableUpgradeable {
  /**
   * @notice Pending owner of this contract
   */
  address public pendingOwner;

  function __SafeOwnable_init(address owner_) internal onlyInitializing {
    __Ownable_init();
    _transferOwnership(owner_);
  }

  struct AddressSlot {
    address value;
  }

  modifier onlyOwnerOrAdmin() {
    bool isOwner = owner() == _msgSender();
    if (!isOwner) {
      address admin = _getProxyAdmin();
      bool isAdmin = admin == _msgSender();
      require(isAdmin, "Ownable: caller is neither the owner nor the admin");
    }
    _;
  }

  /**
   * @notice Emitted when pendingOwner is changed
   */
  event NewPendingOwner(address oldPendingOwner, address newPendingOwner);

  /**
   * @notice Emitted when pendingOwner is accepted, which means owner is updated
   */
  event NewOwner(address oldOwner, address newOwner);

  /**
   * @notice Begins transfer of owner rights. The newPendingOwner must call `_acceptOwner` to finalize the transfer.
   * @dev Owner function to begin change of owner. The newPendingOwner must call `_acceptOwner` to finalize the transfer.
   * @param newPendingOwner New pending owner.
   */
  function _setPendingOwner(address newPendingOwner) public onlyOwner {
    // Save current value, if any, for inclusion in log
    address oldPendingOwner = pendingOwner;

    // Store pendingOwner with value newPendingOwner
    pendingOwner = newPendingOwner;

    // Emit NewPendingOwner(oldPendingOwner, newPendingOwner)
    emit NewPendingOwner(oldPendingOwner, newPendingOwner);
  }

  /**
   * @notice Accepts transfer of owner rights. msg.sender must be pendingOwner
   * @dev Owner function for pending owner to accept role and update owner
   */
  function _acceptOwner() public {
    // Check caller is pendingOwner and pendingOwner  address(0)
    require(msg.sender == pendingOwner, "not the pending owner");

    // Save current values for inclusion in log
    address oldOwner = owner();
    address oldPendingOwner = pendingOwner;

    // Store owner with value pendingOwner
    _transferOwnership(pendingOwner);

    // Clear the pending value
    pendingOwner = address(0);

    emit NewOwner(oldOwner, pendingOwner);
    emit NewPendingOwner(oldPendingOwner, pendingOwner);
  }

  function renounceOwnership() public override onlyOwner {
    // do not remove this overriding fn
    revert("not used anymore");
  }

  function transferOwnership(address newOwner) public override onlyOwner {
    emit NewPendingOwner(pendingOwner, newOwner);
    pendingOwner = newOwner;
  }

  function _getProxyAdmin() internal view returns (address admin) {
    bytes32 _ADMIN_SLOT = 0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103;
    AddressSlot storage adminSlot;
    assembly {
      adminSlot.slot := _ADMIN_SLOT
    }
    admin = adminSlot.value;
  }
}

contracts/oracles/BasePriceOracle.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "../compound/CTokenInterfaces.sol";

/**
 * @title BasePriceOracle
 * @notice Returns prices of underlying tokens directly without the caller having to specify a cToken address.
 * @dev Implements the `PriceOracle` interface.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
interface BasePriceOracle {
  /**
   * @notice Get the price of an underlying asset.
   * @param underlying The underlying asset to get the price of.
   * @return The underlying asset price in ETH as a mantissa (scaled by 1e18).
   * Zero means the price is unavailable.
   */
  function price(address underlying) external view returns (uint256);

  /**
   * @notice Get the underlying price of a cToken asset
   * @param cToken The cToken to get the underlying price of
   * @return The underlying asset price mantissa (scaled by 1e18).
   *  Zero means the price is unavailable.
   */
  function getUnderlyingPrice(ICErc20 cToken) external view returns (uint256);
}

contracts/oracles/MasterPriceOracle.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "@openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";

import { ICErc20 } from "../compound/CTokenInterfaces.sol";

import { BasePriceOracle } from "./BasePriceOracle.sol";

/**
 * @title MasterPriceOracle
 * @notice Use a combination of price oracles.
 * @dev Implements `PriceOracle`.
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 */
contract MasterPriceOracle is Initializable, BasePriceOracle {
  /**
   * @dev Maps underlying token addresses to `PriceOracle` contracts (can be `BasePriceOracle` contracts too).
   */
  mapping(address => BasePriceOracle) public oracles;

  /**
   * @dev Default/fallback `PriceOracle`.
   */
  BasePriceOracle public defaultOracle;

  /**
   * @dev The administrator of this `MasterPriceOracle`.
   */
  address public admin;

  /**
   * @dev Controls if `admin` can overwrite existing assignments of oracles to underlying tokens.
   */
  bool internal noAdminOverwrite;

  /**
   * @dev The Wrapped native asset address.
   */
  address public wtoken;

  /**
   * @dev Maps underlying token addresses to `PriceOracle` contracts (can be `BasePriceOracle` contracts too).
   */
  mapping(address => BasePriceOracle) public fallbackOracles;

  /**
   * @dev Returns a boolean indicating if `admin` can overwrite existing assignments of oracles to underlying tokens.
   */
  function canAdminOverwrite() external view returns (bool) {
    return !noAdminOverwrite;
  }

  /**
   * @dev Event emitted when `admin` is changed.
   */
  event NewAdmin(address oldAdmin, address newAdmin);

  /**
   * @dev Event emitted when the default oracle is changed.
   */
  event NewDefaultOracle(address oldOracle, address newOracle);

  /**
   * @dev Event emitted when an underlying token's oracle is changed.
   */
  event NewOracle(address underlying, address oldOracle, address newOracle);

  /**
   * @dev  Initialize state variables.
   * @param underlyings The underlying ERC20 token addresses to link to `_oracles`.
   * @param _oracles The `PriceOracle` contracts to be assigned to `underlyings`.
   * @param _defaultOracle The default `PriceOracle` contract to use.
   * @param _admin The admin who can assign oracles to underlying tokens.
   * @param _canAdminOverwrite Controls if `admin` can overwrite existing assignments of oracles to underlying tokens.
   * @param _wtoken The Wrapped native asset address
   */
  function initialize(
    address[] memory underlyings,
    BasePriceOracle[] memory _oracles,
    BasePriceOracle _defaultOracle,
    address _admin,
    bool _canAdminOverwrite,
    address _wtoken
  ) external initializer {
    // Input validation
    require(underlyings.length == _oracles.length, "Lengths of both arrays must be equal.");

    // Initialize state variables
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];
      BasePriceOracle newOracle = _oracles[i];
      oracles[underlying] = newOracle;
      emit NewOracle(underlying, address(0), address(newOracle));
    }

    defaultOracle = _defaultOracle;
    admin = _admin;
    noAdminOverwrite = !_canAdminOverwrite;
    wtoken = _wtoken;
  }

  /**
   * @dev Sets `_oracles` for `underlyings`.
   */
  function add(address[] calldata underlyings, BasePriceOracle[] calldata _oracles) external onlyAdmin {
    // Input validation
    require(
      underlyings.length > 0 && underlyings.length == _oracles.length,
      "Lengths of both arrays must be equal and greater than 0."
    );

    // Assign oracles to underlying tokens
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];
      address oldOracle = address(oracles[underlying]);
      if (noAdminOverwrite)
        require(
          oldOracle == address(0),
          "Admin cannot overwrite existing assignments of oracles to underlying tokens."
        );
      BasePriceOracle newOracle = _oracles[i];
      oracles[underlying] = newOracle;
      emit NewOracle(underlying, oldOracle, address(newOracle));
    }
  }

  /**
   * @dev Sets `_oracles` for `underlyings`.
   */
  function addFallbacks(address[] calldata underlyings, BasePriceOracle[] calldata _oracles) external onlyAdmin {
    // Input validation
    require(
      underlyings.length > 0 && underlyings.length == _oracles.length,
      "Lengths of both arrays must be equal and greater than 0."
    );

    // Assign oracles to underlying tokens
    for (uint256 i = 0; i < underlyings.length; i++) {
      address underlying = underlyings[i];
      address oldOracle = address(fallbackOracles[underlying]);
      if (noAdminOverwrite)
        require(
          oldOracle == address(0),
          "Admin cannot overwrite existing assignments of oracles to underlying tokens."
        );
      BasePriceOracle newOracle = _oracles[i];
      fallbackOracles[underlying] = newOracle;
      emit NewOracle(underlying, oldOracle, address(newOracle));
    }
  }

  /**
   * @dev Changes the default price oracle
   */
  function setDefaultOracle(BasePriceOracle newOracle) external onlyAdmin {
    BasePriceOracle oldOracle = defaultOracle;
    defaultOracle = newOracle;
    emit NewDefaultOracle(address(oldOracle), address(newOracle));
  }

  /**
   * @dev Changes the admin and emits an event.
   */
  function changeAdmin(address newAdmin) external onlyAdmin {
    address oldAdmin = admin;
    admin = newAdmin;
    emit NewAdmin(oldAdmin, newAdmin);
  }

  /**
   * @dev Modifier that checks if `msg.sender == admin`.
   */
  modifier onlyAdmin() {
    require(msg.sender == admin, "Sender is not the admin.");
    _;
  }

  /**
   * @notice Returns the price in ETH of the token underlying `cToken`.
   * @dev Implements the `PriceOracle` interface for Ionic pools (and Compound v2).
   * @return Price in ETH of the token underlying `cToken`, scaled by `10 ** (36 - underlyingDecimals)`.
   */
  function getUnderlyingPrice(ICErc20 cToken) external view override returns (uint256) {
    // Get underlying ERC20 token address
    address underlying = address(ICErc20(address(cToken)).underlying());

    if (underlying == wtoken) return 1e18;

    BasePriceOracle oracle = oracles[underlying];
    BasePriceOracle fallbackOracle = fallbackOracles[underlying];

    if (address(oracle) != address(0)) {
      try oracle.getUnderlyingPrice(cToken) returns (uint256 underlyingPrice) {
        if (underlyingPrice == 0) {
          if (address(fallbackOracle) != address(0)) return fallbackOracle.getUnderlyingPrice(cToken);
        } else {
          return underlyingPrice;
        }
      } catch {
        if (address(fallbackOracle) != address(0)) return fallbackOracle.getUnderlyingPrice(cToken);
      }
    } else {
      if (address(fallbackOracle) != address(0)) return fallbackOracle.getUnderlyingPrice(cToken);
    }
    revert("Price oracle not found for this underlying token address.");
  }

  /**
   * @dev Attempts to return the price in ETH of `underlying` (implements `BasePriceOracle`).
   */
  function price(address underlying) public view override returns (uint256) {
    // Return 1e18 for WETH
    if (underlying == wtoken) return 1e18;

    // Get underlying price from assigned oracle
    BasePriceOracle oracle = oracles[underlying];
    BasePriceOracle fallbackOracle = fallbackOracles[underlying];

    if (address(oracle) != address(0)) {
      try oracle.price(underlying) returns (uint256 underlyingPrice) {
        if (underlyingPrice == 0) {
          if (address(fallbackOracle) != address(0)) return fallbackOracle.price(underlying);
        } else {
          return underlyingPrice;
        }
      } catch {
        if (address(fallbackOracle) != address(0)) return fallbackOracle.price(underlying);
      }
    } else {
      if (address(fallbackOracle) != address(0)) return fallbackOracle.price(underlying);
    }
    revert("Price oracle not found for this underlying token address.");
  }
}

contracts/veION/BribeRewards.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";
import { IBribeRewards } from "./interfaces/IBribeRewards.sol";
import { IVoter } from "./interfaces/IVoter.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { ReentrancyGuardUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/security/ReentrancyGuardUpgradeable.sol";
import { Ownable2StepUpgradeable } from "@openzeppelin-contracts-upgradeable/contracts/access/Ownable2StepUpgradeable.sol";
import { IonicTimeLibrary } from "./libraries/IonicTimeLibrary.sol";
import { ERC721Upgradeable } from "@openzeppelin-contracts-upgradeable/contracts/token/ERC721/ERC721Upgradeable.sol";

/**
 * @title BribeRewards Contract
 * @notice This contract allows veION to benefit from bribes when voting for various markets
 * @author Jourdan Dunkley <jourdan@ionic.money> (https://github.com/jourdanDunkley)
 */
contract BribeRewards is IBribeRewards, ReentrancyGuardUpgradeable, Ownable2StepUpgradeable {
  using SafeERC20 for IERC20;

  // 
  //                            State Variables                                 
  // 
  /// @notice Duration of the reward period in seconds
  uint256 public constant DURATION = 7 days;
  /// @notice Address of the voter contract
  address public voter;
  /// @notice Address of the veION contract
  address public ve;
  /// @notice List of reward tokens
  address[] public rewards;

  // 
  //                                 Mappings                                   
  // 
  /// @notice Mapping to check if an address is a reward token
  mapping(address => bool) public isReward;
  /// @notice Total supply of LP tokens for each reward token
  mapping(address => uint256) public totalSupply;
  /// @notice Balance of LP tokens for each tokenId and reward token
  mapping(uint256 => mapping(address => uint256)) public balanceOf;
  /// @notice Rewards per epoch for each reward token
  mapping(address => mapping(uint256 => uint256)) public tokenRewardsPerEpoch;
  /// @notice Last earned timestamp for each reward token and tokenId
  mapping(address => mapping(uint256 => uint256)) public lastEarn;
  /// @notice A record of balance checkpoints for each account, by index
  mapping(uint256 => mapping(address => mapping(uint256 => Checkpoint))) public checkpoints;
  /// @notice The number of checkpoints for each account
  mapping(uint256 => mapping(address => uint256)) public numCheckpoints;
  /// @notice A record of balance checkpoints for each token, by index
  mapping(uint256 => mapping(address => SupplyCheckpoint)) public supplyCheckpoints;
  /// @notice The number of supply checkpoints for each token
  mapping(address => uint256) public supplyNumCheckpoints;
  /// @notice Historical prices for each reward token and epoch
  mapping(address => mapping(uint256 => uint256)) public historicalPrices;

  /**
   * @notice Modifier to restrict access to only the voter contract
   * @dev Ensures that the caller is the voter contract
   */
  modifier onlyVoter() {
    require(msg.sender == voter, "Caller is not the voter");
    _;
  }

  constructor() {
    _disableInitializers(); // Locks the implementation contract from being initialized
  }

  /**
   * @notice Initializes the BribeRewards contract with the voter and veION addresses
   * @dev This function is called only once during contract deployment
   * @param _voter The address of the voter contract
   * @param _ve The address of the veION contract
   */
  function initialize(address _voter, address _ve) public initializer {
    __ReentrancyGuard_init();
    __Ownable2Step_init();
    voter = _voter;
    ve = _ve;
  }

  // 
  //                            External Functions                              
  // 

  /// @inheritdoc IBribeRewards
  function deposit(address lpToken, uint256 amount, uint256 tokenId) external onlyVoter {
    address sender = msg.sender;

    totalSupply[lpToken] += amount;
    balanceOf[tokenId][lpToken] += amount;

    _writeCheckpoint(tokenId, lpToken, balanceOf[tokenId][lpToken]);
    _writeSupplyCheckpoint(lpToken);

    emit Deposit(sender, tokenId, amount);
  }

  /// @inheritdoc IBribeRewards
  function withdraw(address lpToken, uint256 amount, uint256 tokenId) external onlyVoter {
    address sender = msg.sender;

    totalSupply[lpToken] -= amount;
    balanceOf[tokenId][lpToken] -= amount;

    _writeCheckpoint(tokenId, lpToken, balanceOf[tokenId][lpToken]);
    _writeSupplyCheckpoint(lpToken);

    emit Withdraw(sender, tokenId, amount);
  }

  /**
   * @inheritdoc IBribeRewards
   * @notice This function can accept any token, regardless of its whitelisting status.
   * @dev If we were to check the whitelisting status, it could prevent tokens that were initially whitelisted and later de-whitelisted from having their rewards claimed, leading to unclaimable rewards.
   */
  function getReward(uint256 tokenId, address[] memory tokens) external nonReentrant onlyVoter {
    address sender = msg.sender;
    if (ERC721Upgradeable(ve).ownerOf(tokenId) != sender && sender != voter) revert Unauthorized();

    address _owner = ERC721Upgradeable(ve).ownerOf(tokenId);
    _getReward(_owner, tokenId, tokens);
  }

  /// @inheritdoc IBribeRewards
  function notifyRewardAmount(address token, uint256 amount) external override nonReentrant {
    address sender = msg.sender;

    if (!isReward[token]) {
      if (!IVoter(voter).isWhitelistedToken(token)) revert TokenNotWhitelisted();
      isReward[token] = true;
      rewards.push(token);
    }

    _notifyRewardAmount(sender, token, amount);
  }

  // 
  //                            Internal Functions                              
  // 

  /// @dev used with all getReward implementations
  function _getReward(address recipient, uint256 tokenId, address[] memory tokens) internal {
    // check if token whitelisted
    uint256 _length = tokens.length;
    for (uint256 i = 0; i < _length; i++) {
      uint256 _reward = earned(tokens[i], tokenId);
      lastEarn[tokens[i]][tokenId] = block.timestamp;
      if (_reward > 0) IERC20(tokens[i]).safeTransfer(recipient, _reward);

      emit RewardsClaimed(recipient, tokens[i], _reward);
    }
  }

  /**
   * @notice Writes a new checkpoint for a token's balance
   * @param tokenId The ID of the veION token
   * @param lpToken The LP token address
   * @param balance The balance to record
   */
  function _writeCheckpoint(uint256 tokenId, address lpToken, uint256 balance) internal {
    uint256 _nCheckPoints = numCheckpoints[tokenId][lpToken];
    uint256 _timestamp = block.timestamp;

    if (
      _nCheckPoints > 0 &&
      IonicTimeLibrary.epochStart(checkpoints[tokenId][lpToken][_nCheckPoints - 1].timestamp) ==
      IonicTimeLibrary.epochStart(_timestamp)
    ) {
      checkpoints[tokenId][lpToken][_nCheckPoints - 1] = Checkpoint(_timestamp, balance);
    } else {
      checkpoints[tokenId][lpToken][_nCheckPoints] = Checkpoint(_timestamp, balance);
      numCheckpoints[tokenId][lpToken] = _nCheckPoints + 1;
    }
  }

  /// @notice Writes a new checkpoint for total supply
  /// @param lpToken The LP token address
  function _writeSupplyCheckpoint(address lpToken) internal {
    uint256 _nCheckPoints = supplyNumCheckpoints[lpToken];
    uint256 _timestamp = block.timestamp;

    if (
      _nCheckPoints > 0 &&
      IonicTimeLibrary.epochStart(supplyCheckpoints[_nCheckPoints - 1][lpToken].timestamp) ==
      IonicTimeLibrary.epochStart(_timestamp)
    ) {
      supplyCheckpoints[_nCheckPoints - 1][lpToken] = SupplyCheckpoint(_timestamp, totalSupply[lpToken]);
    } else {
      supplyCheckpoints[_nCheckPoints][lpToken] = SupplyCheckpoint(_timestamp, totalSupply[lpToken]);
      supplyNumCheckpoints[lpToken] = _nCheckPoints + 1;
    }
  }

  /// @dev used within all notifyRewardAmount implementations
  function _notifyRewardAmount(address sender, address token, uint256 amount) internal {
    if (amount == 0) revert AmountCannotBeZero();
    IERC20(token).safeTransferFrom(sender, address(this), amount);

    uint256 epochStart = IonicTimeLibrary.epochStart(block.timestamp);
    tokenRewardsPerEpoch[token][epochStart] += amount;

    emit RewardNotification(sender, token, epochStart, amount);
  }

  /**
   * @notice Calculates the ETH value of a token amount at a specific epoch
   * @param amount The amount of tokens
   * @param lpToken The LP token address
   * @param epochTimestamp The timestamp of the epoch
   * @return The ETH value of the tokens
   */
  function _getTokenEthValueAt(
    uint256 amount,
    address lpToken,
    uint256 epochTimestamp
  ) internal view returns (uint256) {
    uint256 epochStart = IonicTimeLibrary.epochStart(epochTimestamp);
    if (historicalPrices[lpToken][epochStart] == 0) revert HistoricalPriceNotSet(lpToken, epochStart);
    uint256 _priceAtTimestamp = historicalPrices[lpToken][epochStart];
    uint256 ethValue = (amount * _priceAtTimestamp) / 1e18;
    return ethValue;
  }

  // 
  //                            Pure/View Functions                             
  // 

  /// @notice Returns the total number of reward tokens
  /// @return The length of the rewards array
  function rewardsListLength() external view returns (uint256) {
    return rewards.length;
  }

  /// @inheritdoc IBribeRewards
  function earned(address token, uint256 tokenId) public view returns (uint256) {
    EarnedVars memory vars;
    vars.totalReward = 0;
    address[] memory lpTokens = getAllLpRewardTokens();
    uint256 lpTokensLength = lpTokens.length;
    for (uint256 j = 0; j < lpTokensLength; j++) {
      address lpToken = lpTokens[j];

      if (numCheckpoints[tokenId][lpToken] == 0) {
        continue;
      }

      vars.currTs = IonicTimeLibrary.epochStart(lastEarn[token][tokenId]);
      vars.index = getPriorBalanceIndex(tokenId, lpToken, vars.currTs);
      Checkpoint memory cp0 = checkpoints[tokenId][lpToken][vars.index];

      vars.currTs = Math.max(vars.currTs, IonicTimeLibrary.epochStart(cp0.timestamp));
      vars.numEpochs = (IonicTimeLibrary.epochStart(block.timestamp) - vars.currTs) / DURATION;

      if (vars.numEpochs > 0) {
        for (uint256 i = 0; i < vars.numEpochs; i++) {
          vars.index = getPriorBalanceIndex(tokenId, lpToken, vars.currTs + DURATION - 1);
          cp0 = checkpoints[tokenId][lpToken][vars.index];
          vars.epochBalanceValue = _getTokenEthValueAt(cp0.balanceOf, lpToken, vars.currTs);

          vars.supplyValue = 0;
          for (uint256 k = 0; k < lpTokensLength; k++) {
            address currentLpToken = lpTokens[k];
            uint256 supplyAmount = Math.max(
              supplyCheckpoints[getPriorSupplyIndex(vars.currTs + DURATION - 1, currentLpToken)][currentLpToken].supply,
              1
            );
            vars.supplyValue += _getTokenEthValueAt(supplyAmount, currentLpToken, vars.currTs);
          }
          if (vars.supplyValue > 0) {
            vars.totalReward += (vars.epochBalanceValue * tokenRewardsPerEpoch[token][vars.currTs]) / vars.supplyValue;
          }
          vars.currTs += DURATION;
        }
      }
    }

    return vars.totalReward;
  }

  /// @notice Gets all LP tokens that can receive rewards
  /// @return Array of LP token addresses
  function getAllLpRewardTokens() public view returns (address[] memory) {
    return IVoter(voter).getAllLpRewardTokens();
  }

  /**
   * @notice Sets historical prices for LP tokens at specific epochs
   * @param epochTimestamp The timestamp of the epoch
   * @param lpToken The LP token address
   * @param price The price to set
   */
  function setHistoricalPrices(uint256 epochTimestamp, address lpToken, uint256 price) external onlyOwner {
    uint256 epochStart = IonicTimeLibrary.epochStart(epochTimestamp);
    historicalPrices[lpToken][epochStart] = price;
    emit HistoricalPriceSet(epochTimestamp, lpToken, price);
  }

  /**
   * @notice Gets a specific checkpoint for a token
   * @param tokenId The ID of the veION token
   * @param lpToken The LP token address
   * @param index The index of the checkpoint to retrieve
   * @return The checkpoint data
   */
  function getCheckpoint(uint256 tokenId, address lpToken, uint256 index) external view returns (Checkpoint memory) {
    return checkpoints[tokenId][lpToken][index];
  }

  /// @inheritdoc IBribeRewards
  function getPriorBalanceIndex(uint256 tokenId, address lpToken, uint256 timestamp) public view returns (uint256) {
    uint256 nCheckpoints = numCheckpoints[tokenId][lpToken];
    if (nCheckpoints == 0) {
      return 0;
    }

    // First check most recent balance
    if (checkpoints[tokenId][lpToken][nCheckpoints - 1].timestamp <= timestamp) {
      return (nCheckpoints - 1);
    }

    // Next check implicit zero balance
    if (checkpoints[tokenId][lpToken][0].timestamp > timestamp) {
      return 0;
    }

    uint256 lower = 0;
    uint256 upper = nCheckpoints - 1;
    while (upper > lower) {
      uint256 center = upper - (upper - lower) / 2; // ceil, avoiding overflow
      Checkpoint memory cp = checkpoints[tokenId][lpToken][center];
      if (cp.timestamp == timestamp) {
        return center;
      } else if (cp.timestamp < timestamp) {
        lower = center;
      } else {
        upper = center - 1;
      }
    }
    return lower;
  }

  /// @inheritdoc IBribeRewards
  function getPriorSupplyIndex(uint256 timestamp, address lpToken) public view returns (uint256) {
    uint256 nCheckpoints = supplyNumCheckpoints[lpToken];
    if (nCheckpoints == 0) {
      return 0;
    }

    // First check most recent balance
    if (supplyCheckpoints[nCheckpoints - 1][lpToken].timestamp <= timestamp) {
      return (nCheckpoints - 1);
    }

    // Next check implicit zero balance
    if (supplyCheckpoints[0][lpToken].timestamp > timestamp) {
      return 0;
    }

    uint256 lower = 0;
    uint256 upper = nCheckpoints - 1;
    while (upper > lower) {
      uint256 center = upper - (upper - lower) / 2; // ceil, avoiding overflow
      SupplyCheckpoint memory cp = supplyCheckpoints[center][lpToken];
      if (cp.timestamp == timestamp) {
        return center;
      } else if (cp.timestamp < timestamp) {
        lower = center;
      } else {
        upper = center - 1;
      }
    }
    return lower;
  }
}

contracts/veION/interfaces/IBribeRewards.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

interface IBribeRewards {
  /// @notice A checkpoint for marking balance
  struct Checkpoint {
    uint256 timestamp;
    uint256 balanceOf;
  }

  /// @notice A checkpoint for marking supply
  struct SupplyCheckpoint {
    uint256 timestamp;
    uint256 supply;
  }

  struct EarnedVars {
    uint256 totalReward;
    uint256 reward;
    uint256 supplyValue;
    uint256 epochBalanceValue;
    uint256 currTs;
    uint256 index;
    uint256 numEpochs;
    uint256 overallBalance;
    uint256 overallSupply;
    uint256 historicalPrice;
  }

  error InvalidReward();
  error Unauthorized();
  error InvalidGauge();
  error InvalidEscrowToken();
  error SingleTokenExpected();
  error InvalidVotingEscrow();
  error TokenNotWhitelisted();
  error AmountCannotBeZero();
  error HistoricalPriceNotSet(address lpToken, uint256 epochStart);

  event Deposit(address indexed user, uint256 indexed tokenId, uint256 amount);
  event Withdraw(address indexed user, uint256 indexed tokenId, uint256 amount);
  event RewardNotification(address indexed user, address indexed rewardToken, uint256 indexed epoch, uint256 amount);
  event RewardsClaimed(address indexed user, address indexed rewardToken, uint256 amount);
  event HistoricalPriceSet(uint256 indexed epochTimestamp, address indexed lpToken, uint256 price);

  /// @notice Deposit an amount into the bribe rewards contract for a specific veNFT
  /// @dev Can only be called internally by authorized entities.
  /// @param lpToken  Address of the liquidity pool token
  /// @param amount   Amount to be deposited for the veNFT
  /// @param tokenId  Unique identifier of the veNFT
  function deposit(address lpToken, uint256 amount, uint256 tokenId) external;

  /// @notice Withdraw an amount from the bribe rewards contract for a specific veNFT
  /// @dev Can only be called internally by authorized entities.
  /// @param lpToken  Address of the liquidity pool token
  /// @param amount   Amount to be withdrawn for the veNFT
  /// @param tokenId  Unique identifier of the veNFT
  function withdraw(address lpToken, uint256 amount, uint256 tokenId) external;

  /// @notice Claim the rewards earned by a veNFT holder
  /// @param tokenId  Unique identifier of the veNFT
  /// @param tokens   Array of tokens to claim rewards for
  function getReward(uint256 tokenId, address[] memory tokens) external;

  /// @notice Notify the contract about new rewards for stakers
  /// @param token    Address of the reward token
  /// @param amount   Amount of the reward token to be added
  function notifyRewardAmount(address token, uint256 amount) external;

  /// @notice Get the prior balance index for a veNFT at a specific timestamp
  /// @dev Timestamp must be in the past or present.
  /// @param tokenId      The veNFT token ID to check
  /// @param lpToken      Address of the liquidity pool token
  /// @param timestamp    The timestamp to get the balance at
  /// @return The balance index at the given timestamp
  function getPriorBalanceIndex(uint256 tokenId, address lpToken, uint256 timestamp) external view returns (uint256);

  /// @notice Get the prior supply index at a specific timestamp
  /// @dev Timestamp must be in the past or present.
  /// @param timestamp The timestamp to get the supply index at
  /// @return The supply index at the given timestamp
  function getPriorSupplyIndex(uint256 timestamp, address lpToken) external view returns (uint256);

  /// @notice Calculate the rewards earned for a specific token and veNFT
  /// @param token Address of the reward token
  /// @param tokenId Unique identifier of the veNFT
  /// @return Amount of the reward token earned
  function earned(address token, uint256 tokenId) external view returns (uint256);
}

contracts/veION/interfaces/IveION.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.22;

import "../stake/IStakeStrategy.sol";

/// @title IveION Interface
/// @notice Interface for veION contract
interface IveION {
  /**
   * @notice Structure representing a locked balance
   * @param tokenAddress Address of the token
   * @param amount Amount of tokens locked
   * @param delegateAmount Amount of tokens delegated
   * @param start Start time of the lock
   * @param end End time of the lock
   * @param isPermanent Indicates if the lock is permanent
   * @param boost Boost value for the lock
   */
  struct LockedBalance {
    address tokenAddress;
    uint256 amount;
    uint256 delegateAmount;
    uint256 start;
    uint256 end;
    bool isPermanent;
    uint256 boost;
  }

  /**
   * @notice Structure representing a delegation
   * @param amount Amount of tokens delegated
   * @param delegatee ID of the delegatee
   */
  struct Delegation {
    uint256 amount;
    uint256 delegatee;
  }

  /**
   * @notice Structure representing a user point
   * @param bias Bias value
   * @param slope Slope value, representing -dweight / dt
   * @param ts Timestamp of the point
   * @param blk Block number of the point
   * @param permanent Permanent value
   * @param permanentDelegate Permanent delegate value
   */
  struct UserPoint {
    uint256 bias;
    uint256 slope;
    uint256 ts;
    uint256 blk;
    uint256 permanent;
    uint256 permanentDelegate;
  }

  /**
   * @notice Structure representing a global point
   * @param bias Bias value
   * @param slope Slope value, representing -dweight / dt
   * @param ts Timestamp of the point
   * @param blk Block number of the point
   * @param permanentLockBalance Permanent lock balance
   */
  struct GlobalPoint {
    int128 bias;
    int128 slope;
    uint256 ts;
    uint256 blk;
    uint256 permanentLockBalance;
  }

  /**
   * @notice Structure representing a checkpoint
   * @param fromTimestamp Timestamp from which the checkpoint is valid
   * @param owner Address of the owner
   * @param delegatedBalance Balance that has been delegated
   * @param delegatee ID of the delegatee
   */
  struct Checkpoint {
    uint256 fromTimestamp;
    address owner;
    uint256 delegatedBalance;
    uint256 delegatee;
  }

  /**
   * @notice Enum representing deposit types
   */
  enum DepositType {
    DEPOSIT_FOR_TYPE,
    CREATE_LOCK_TYPE,
    INCREASE_LOCK_AMOUNT,
    INCREASE_UNLOCK_TIME,
    LOCK_ADDITIONAL
  }

  /**
   * @notice Enum representing LP token types
   */
  enum LpTokenType {
    Mode_Velodrome_5050_ION_MODE,
    Mode_Balancer_8020_ION_ETH,
    Base_Aerodrome_5050_ION_wstETH,
    Base_Balancer_8020_ION_ETH,
    Optimism_Velodrome_5050_ION_OP,
    Optimism_Balancer_8020_ION_ETH
  }

  error LockDurationNotInFuture();
  error ZeroAmount();
  error LockDurationTooLong();
  error TokenNotWhitelisted();
  error NotOwner();
  error AlreadyVoted();
  error PermanentLock();
  error NoLockFound();
  error LockExpired();
  error SameNFT();
  error SplitNotAllowed();
  error AmountTooBig();
  error NotPermanentLock();
  error TokenHasDelegatees();
  error TokenHasDelegators();
  error NotVoter();
  error MinimumNotMet();
  error ArrayMismatch();
  error LockDurationTooShort();
  error DuplicateAsset();
  error SplitTooSmall();
  error NotEnoughRemainingAfterSplit();
  error NoDelegationBetweenTokens(uint256 _tokenId1, uint256 _tokenId2);
  error NoUnderlyingStake();
  error NotAcceptingDelegators();

  event Deposit(
    address indexed provider,
    uint256 indexed tokenId,
    DepositType indexed depositType,
    uint256 value,
    uint256 locktime,
    uint256 ts
  );
  event Withdraw(address indexed provider, uint256 indexed tokenId, uint256 value, uint256 ts);
  event Supply(uint256 prevSupply, uint256 supply);
  event Delegated(uint256 indexed fromTokenId, uint256 indexed toTokenId, address lpToken, uint256 amount);
  event DelegationRemoved(uint256 indexed fromTokenId, uint256 indexed toTokenId, address lpToken, uint256 amount);
  event ProtocolFeesWithdrawn(address indexed tokenAddress, address indexed recipient, uint256 amount);
  event DistributedFeesWithdrawn(address indexed tokenAddress, address indexed recipient, uint256 amount);
  event SplitToggle(address indexed account, bool isAllowed);
  event LimitedBoostToggled(bool isBoosted);
  event LimitedTimeBoostSet(uint256 boostAmount);
  event VoterSet(address indexed newVoter);
  event AeroVotingSet(address indexed newAeroVoting);
  event AeroVoterBoostSet(uint256 newAeroVoterBoost);
  event TokensWhitelisted(address[] token, bool[] isWhitelisted);
  event LpTokenTypeSet(address indexed token, LpTokenType lpTokenType);
  event VeAEROSet(address indexed veAERO);
  event StakeStrategySet(LpTokenType indexed lpTokenType, address indexed strategy);
  event MinimumLockAmountSet(address indexed tokenAddress, uint256 minimumAmount);
  event MinimumLockDurationSet(uint256 minimumDuration);
  event IonicPoolSet(address indexed newIonicPool);
  event SplitCompleted(
    uint256 indexed fromTokenId,
    uint256 indexed tokenId1,
    uint256 indexed tokenId2,
    uint256 splitAmount,
    address tokenAddress
  );
  event MergeCompleted(
    uint256 indexed fromTokenId,
    uint256 indexed toTokenId,
    address[] assetsLocked,
    uint256 lengthOfAssets
  );
  event EmissionsClaimed(address indexed claimant, address indexed tokenAddress);
  event MaxEarlyWithdrawFeeSet(uint256 maxEarlyWithdrawFee);
  event PermanentLockCreated(address indexed tokenAddress, uint256 indexed tokenId, uint256 amount);
  event PermanentLockRemoved(address indexed tokenAddress, uint256 indexed tokenId, uint256 amount);
  event Voted(uint256 _tokenId, bool _voting);
  event DelegatorsBlocked(uint256 indexed _tokenId, address indexed _lpToken, bool _blocked);
  event Initialized(address indexed addressesProvider);

  /**
   * @notice Creates a new lock for multiple tokens and assigns it to a specified address
   * @param _tokenAddress Array of token addresses to lock
   * @param _tokenAmount Array of token amounts to lock
   * @param _duration Array of lock durations
   * @param _stakeUnderlying Array of booleans indicating whether to stake underlying tokens
   * @param _to Address to assign the lock to
   * @return The ID of the newly created veNFT
   */
  function createLockFor(
    address[] memory _tokenAddress,
    uint256[] memory _tokenAmount,
    uint256[] memory _duration,
    bool[] memory _stakeUnderlying,
    address _to
  ) external returns (uint256);

  /**
   * @notice Creates a new lock for multiple tokens
   * @param _tokenAddress Array of token addresses to lock
   * @param _tokenAmount Array of token amounts to lock
   * @param _duration Array of lock durations
   * @param _stakeUnderlying Array of booleans indicating whether to stake underlying tokens
   * @return The ID of the newly created veNFT
   */
  function createLock(
    address[] calldata _tokenAddress,
    uint256[] calldata _tokenAmount,
    uint256[] calldata _duration,
    bool[] memory _stakeUnderlying
  ) external returns (uint256);

  /**
   * @notice Increases the amount of tokens locked for a specific veNFT
   * @param _tokenAddress Address of the token to increase lock amount for
   * @param _tokenId ID of the veNFT
   * @param _tokenAmount Amount of tokens to add to the lock
   * @param _stakeUnderlying Whether to stake the underlying tokens
   */
  function increaseAmount(
    address _tokenAddress,
    uint256 _tokenId,
    uint256 _tokenAmount,
    bool _stakeUnderlying
  ) external;

  /**
   * @notice Locks additional asset type for an existing veNFT
   * @param _tokenAddress Address of the new token to lock
   * @param _tokenAmount Amount of tokens to lock
   * @param _tokenId ID of the veNFT
   * @param _duration Duration of the lock
   * @param _stakeUnderlying Whether to stake the underlying tokens
   */
  function lockAdditionalAsset(
    address _tokenAddress,
    uint256 _tokenAmount,
    uint256 _tokenId,
    uint256 _duration,
    bool _stakeUnderlying
  ) external;

  /**
   * @notice Increases the lock duration for a specific token in a veNFT
   * @param _tokenAddress Address of the token
   * @param _tokenId ID of the veNFT
   * @param _lockDuration New lock duration to extend to
   */
  function increaseUnlockTime(address _tokenAddress, uint256 _tokenId, uint256 _lockDuration) external;

  /**
   * @notice Withdraws underlying assets from the veNFT. If the unlock time has not passed, a penalty fee is applied.
   * @param _tokenAddress Address of the token to withdraw
   * @param _tokenId Token ID of the veNFT to withdraw from
   */
  function withdraw(address _tokenAddress, uint256 _tokenId) external;

  /**
   * @notice Merges two veNFTs into one, combining their locked assets
   * @param _from ID of the source veNFT
   * @param _to ID of the destination veNFT
   */
  function merge(uint256 _from, uint256 _to) external;

  /**
   * @notice Splits a veNFT into two separate veNFTs
   * @param _tokenAddress Address of the token to split
   * @param _from ID of the source veNFT
   * @param _splitAmount Amount of tokens to split into new veNFT
   * @return _tokenId1 ID of the original veNFT
   * @return _tokenId2 ID of the new veNFT created from the split
   */
  function split(
    address _tokenAddress,
    uint256 _from,
    uint256 _splitAmount
  ) external returns (uint256 _tokenId1, uint256 _tokenId2);

  /**
   * @notice Enables or disables splitting capability for a specific account
   * @param _account Address to toggle split permission for
   * @param _isAllowed Whether splitting should be allowed
   */
  function toggleSplit(address _account, bool _isAllowed) external;

  /**
   * @notice Converts a lock to a permanent lock that cannot be withdrawn
   * @param _tokenAddress Address of the token
   * @param _tokenId ID of the veNFT
   */
  function lockPermanent(address _tokenAddress, uint256 _tokenId) external;

  /**
   * @notice Removes permanent lock status from a veNFT
   * @param _tokenAddress Address of the token
   * @param _tokenId ID of the veNFT
   */
  function unlockPermanent(address _tokenAddress, uint256 _tokenId) external;

  /**
   * @notice Delegates voting power from one veNFT to another
   * @param fromTokenId ID of the source veNFT
   * @param toTokenId ID of the destination veNFT
   * @param lpToken Address of the LP token
   * @param amount Amount of voting power to delegate
   */
  function delegate(uint256 fromTokenId, uint256 toTokenId, address lpToken, uint256 amount) external;

  /**
   * @notice Removes delegatees from a specific veNFT
   * @param fromTokenId ID of the veNFT from which delegatees are removed
   * @param toTokenIds Array of veNFT IDs that are delegatees to be removed
   * @param lpToken Address of the LP token associated with the delegation
   * @param amounts Array of amounts of voting power to remove from each delegatee
   */
  function removeDelegatees(
    uint256 fromTokenId,
    uint256[] memory toTokenIds,
    address lpToken,
    uint256[] memory amounts
  ) external;

  /**
   * @notice Removes delegators from a specific veNFT
   * @param fromTokenIds Array of veNFT IDs that are delegators to be removed
   * @param toTokenId ID of the veNFT from which delegators are removed
   * @param lpToken Address of the LP token associated with the delegation
   * @param amounts Array of amounts of voting power to remove from each delegator
   */
  function removeDelegators(
    uint256[] memory fromTokenIds,
    uint256 toTokenId,
    address lpToken,
    uint256[] memory amounts
  ) external;

  /**
   * @notice Claims accumulated emissions rewards for staked tokens
   * @param _tokenAddress Address of the token to claim emissions for
   */
  function claimEmissions(address _tokenAddress) external;

  /**
   * @notice Whitelists tokens for locking
   * @param _tokens Array of token addresses to whitelist
   * @param _isWhitelisted Array of booleans indicating whitelist status
   */
  function whitelistTokens(address[] memory _tokens, bool[] memory _isWhitelisted) external;

  /**
   * @notice Updates voting status for a veNFT
   * @param _tokenId ID of the veNFT
   * @param _voting Voting status
   */
  function voting(uint256 _tokenId, bool _voting) external;

  /**
   * @notice Withdraws protocol fees collected
   * @param _tokenAddress Address of the token to withdraw fees for
   * @param _recipient Address to receive the fees
   */
  function withdrawProtocolFees(address _tokenAddress, address _recipient) external;

  /**
   * @notice Withdraws distributed fees collected
   * @param _tokenAddress Address of the token to withdraw fees for
   * @param _recipient Address to receive the fees
   */
  function withdrawDistributedFees(address _tokenAddress, address _recipient) external;

  /**
   * @notice Toggles the limited boost feature
   * @param _isBoosted Boolean indicating whether the boost is active
   */
  function toggleLimitedBoost(bool _isBoosted) external;

  /**
   * @notice Sets the limited time boost amount
   * @param _boostAmount The amount of boost to set
   */
  function setLimitedTimeBoost(uint256 _boostAmount) external;

  /**
   * @notice Sets the voter address
   * @param _voter Address of the voter
   */
  function setVoter(address _voter) external;

  /**
   * @notice Sets the minimum lock amount for a specific token
   * @param _tokenAddress Address of the token
   * @param _minimumAmount Minimum amount to lock
   */
  function setMinimumLockAmount(address _tokenAddress, uint256 _minimumAmount) external;

  /**
   * @notice Sets the minimum lock duration
   * @param _minimumLockDuration Minimum duration for locking
   */
  function setMinimumLockDuration(uint256 _minimumLockDuration) external;

  /**
   * @notice Sets the Ionic pool address
   * @param _ionicPool Address of the Ionic pool
   */
  function setIonicPool(address _ionicPool) external;

  /**
   * @notice Sets the Aero voting address
   * @param _aeroVoting Address of the Aero voting
   */
  function setAeroVoting(address _aeroVoting) external;

  /**
   * @notice Sets the Aero voter boost amount
   * @param _aeroVoterBoost Amount of Aero voter boost
   */
  function setAeroVoterBoost(uint256 _aeroVoterBoost) external;

  /**
   * @notice Sets the maximum early withdraw fee
   * @param _maxEarlyWithdrawFee Maximum fee for early withdrawal
   */
  function setMaxEarlyWithdrawFee(uint256 _maxEarlyWithdrawFee) external;

  /**
   * @notice Sets the LP token type
   * @param _token Address of the token
   * @param _type Type of the LP token
   */
  function setLpTokenType(address _token, LpTokenType _type) external;

  /**
   * @notice Sets the stake strategy for a specific LP token type
   * @param _lpType LP token type
   * @param _strategy Address of the stake strategy
   */
  function setStakeStrategy(LpTokenType _lpType, IStakeStrategy _strategy) external;

  /**
   * @notice Sets the veAERO address
   * @param _veAERO Address of the veAERO
   */
  function setVeAERO(address _veAERO) external;

  /**
   * @notice Retrieves the locked balance of a veNFT for a specific LP token type
   * @param _tokenId ID of the veNFT
   * @param _lpType LP token type
   * @return LockedBalance structure of the veNFT
   */
  function getUserLock(uint256 _tokenId, LpTokenType _lpType) external view returns (LockedBalance memory);

  /**
   * @notice Retrieves the list of owned token IDs for a given address
   * @param _owner Address of the token owner
   * @return Array of token IDs
   */
  function getOwnedTokenIds(address _owner) external view returns (uint256[] memory);

  /**
   * @notice Retrieves the total ETH value of locked tokens for a user
   * @param _owner Address of the user
   * @return Total ETH value of locked tokens
   */
  function getTotalEthValueOfTokens(address _owner) external view returns (uint256);

  /**
   * @notice Retrieves the list of assets locked in a veNFT
   * @param _tokenId ID of the veNFT
   * @return Array of asset addresses
   */
  function getAssetsLocked(uint256 _tokenId) external view returns (address[] memory);

  /**
   * @notice Retrieves the list of delegatees for a veNFT and LP token type
   * @param _tokenId ID of the veNFT
   * @param _lpType LP token type
   * @return Array of delegatee token IDs
   */
  function getDelegatees(uint256 _tokenId, LpTokenType _lpType) external view returns (uint256[] memory);

  /**
   * @notice Retrieves the list of delegators for a veNFT and LP token type
   * @param _tokenId ID of the veNFT
   * @param _lpType LP token type
   * @return Array of delegator token IDs
   */
  function getDelegators(uint256 _tokenId, LpTokenType _lpType) external view returns (uint256[] memory);

  /**
   * @notice Retrieves user point history for a veNFT and LP token type
   * @param _tokenId ID of the veNFT
   * @param _lpType LP token type
   * @param _epoch User point epoch
   * @return UserPoint structure
   */
  function getUserPoint(uint256 _tokenId, LpTokenType _lpType, uint256 _epoch) external view returns (UserPoint memory);

  /**
   * @notice Retrieves the balance of NFT assets for a given veNFT
   * @param _tokenId ID of the veNFT
   * @return _assets Array of asset addresses
   * @return _balances Array of corresponding balances for each asset
   * @return _boosts Array of corresponding boosts for each asset
   */
  function balanceOfNFT(
    uint256 _tokenId
  ) external view returns (address[] memory _assets, uint256[] memory _balances, uint256[] memory _boosts);

  // Constants
  function PRECISION() external view returns (uint256);

  // State Variables
  function s_tokenId() external view returns (uint256);
  function s_limitedBoost() external view returns (uint256);
  function s_limitedBoostActive() external view returns (bool);
  function s_veAERO() external view returns (address);
  function s_aeroVoting() external view returns (address);
  function s_ionicPool() external view returns (address);
  function s_voter() external view returns (address);
  function s_aeroVoterBoost() external view returns (uint256);
  function s_minimumLockDuration() external view returns (uint256);
  function s_maxEarlyWithdrawFee() external view returns (uint256);
  function ap() external view returns (address);
  function implementation() external view returns (address);

  // Mappings
  function s_minimumLockAmount(LpTokenType lpTokenType) external view returns (uint256);
  function s_whitelistedToken(address token) external view returns (bool);
  function s_lpType(address token) external view returns (LpTokenType);
  function s_canSplit(address user) external view returns (bool);
  function s_locked(uint256 tokenId, LpTokenType lpTokenType) external view returns (LockedBalance memory);
  function s_userPointEpoch(uint256 tokenId, LpTokenType lpTokenType) external view returns (uint256);
  function s_userPointHistory(
    uint256 tokenId,
    LpTokenType lpTokenType,
    uint256 epoch
  ) external view returns (UserPoint memory);
  function s_voted(uint256 tokenId) external view returns (bool);
  function s_supply(LpTokenType lpTokenType) external view returns (uint256);
  function s_permanentLockBalance(LpTokenType lpTokenType) external view returns (uint256);
  function s_stakeStrategy(LpTokenType lpTokenType) external view returns (address);
  function s_underlyingStake(uint256 tokenId, address token) external view returns (uint256);
  function s_protocolFees(LpTokenType lpTokenType) external view returns (uint256);
  function s_distributedFees(LpTokenType lpTokenType) external view returns (uint256);
  function s_delegations(
    uint256 delegatorTokenId,
    uint256 delegateeTokenId,
    LpTokenType lpTokenType
  ) external view returns (uint256);
  function s_userCumulativeAssetValues(address user, address token) external view returns (uint256);
  function s_delegatorsBlocked(uint256 tokenId, address token) external view returns (bool);

  function transferFrom(address from, address to, uint256 tokenId) external;

  function ownerOf(uint256 tokenId) external returns (address);

  function owner() external returns (address);

  function balanceOf(address owner) external returns (uint256);
}

