// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import { IRedemptionStrategy } from "./IRedemptionStrategy.sol";
import { ISwapRouter_Aerodrome } from "../external/aerodrome/IAerodromeSwapRouter.sol";

import { IERC20Upgradeable } from "openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";
import { IERC4626 } from "../compound/IERC4626.sol";

contract AerodromeCLLiquidator is IRedemptionStrategy {
  /**
   * @dev Redeems `inputToken` for `outputToken` where `inputAmount` < `outputAmount`
   * @param inputToken Address of the token
   * @param inputAmount input amount
   * @param strategyData context specific data like input token, pool address and tx expiratio period
   */
  function redeem(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    bytes memory strategyData
  ) external override returns (IERC20Upgradeable outputToken, uint256 outputAmount) {
    return _convert(inputToken, inputAmount, strategyData);
  }

  function _convert(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    bytes memory strategyData
  ) internal returns (IERC20Upgradeable outputToken, uint256 outputAmount) {
    (
      ,
      address _outputToken,
      ISwapRouter_Aerodrome swapRouter,
      address _unwrappedInput,
      address _unwrappedOutput,
      int24 _tickSpacing
    ) = abi.decode(strategyData, (address, address, ISwapRouter_Aerodrome, address, address, int24));
    if (_unwrappedOutput != address(0)) {
      outputToken = IERC20Upgradeable(_unwrappedOutput);
    } else {
      outputToken = IERC20Upgradeable(_outputToken);
    }

    if (_unwrappedInput != address(0)) {
      inputToken.approve(address(inputToken), inputAmount);
      inputAmount = IERC4626(address(inputToken)).redeem(inputAmount, address(this), address(this));
      inputToken = IERC20Upgradeable(_unwrappedInput);
    }

    inputToken.approve(address(swapRouter), inputAmount);

    outputAmount = swapRouter.exactInputSingle(
      ISwapRouter_Aerodrome.ExactInputSingleParams(
        address(inputToken),
        address(outputToken),
        _tickSpacing,
        address(this),
        block.timestamp,
        inputAmount,
        0,
        0
      )
    );

    if (_unwrappedOutput != address(0)) {
      IERC20Upgradeable(_unwrappedOutput).approve(address(_outputToken), outputAmount);
      IERC4626(_outputToken).deposit(outputAmount, address(this));
      outputAmount = IERC4626(_unwrappedOutput).balanceOf(address(this));
      outputToken = IERC20Upgradeable(_outputToken);
    }
  }

  function name() public pure virtual override returns (string memory) {
    return "AerodromeCLLiquidator";
  }
}

contracts/liquidators/AerodromeV2Liquida.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;
import "openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";

import { IRouter_Aerodrome } from "../external/aerodrome/IAerodromeRouter.sol";

/**
 * @title AerodromeV2Liquidator
 * @notice Exchanges seized token collateral for underlying tokens via a Aerodrome V2 router for use as a step in a liquidation.
 */
contract AerodromeV2Liquidator {
  function _swap(IRouter_Aerodrome router, uint256 inputAmount, IRouter_Aerodrome.Route[] memory swapPath) internal {
    router.swapExactTokensForTokens(inputAmount, 0, swapPath, address(this), block.timestamp);
  }

  function name() public pure virtual returns (string memory) {
    return "AerodromeV2Liquidator";
  }

  /**
   * @notice Redeems custom collateral `token` for an underlying token.
   * @param inputToken The input wrapped token to be redeemed for an underlying token.
   * @param inputAmount The amount of the input wrapped token to be redeemed for an underlying token.
   * @param strategyData The ABI-encoded data to be used in the redemption strategy logic.
   * @return outputToken The underlying ERC20 token outputted.
   * @return outputAmount The quantity of underlying tokens outputted.
   */
  function redeem(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    bytes memory strategyData
  ) external returns (IERC20Upgradeable outputToken, uint256 outputAmount) {
    return _convert(inputToken, inputAmount, strategyData);
  }

  function _convert(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    bytes memory strategyData
  ) internal returns (IERC20Upgradeable outputToken, uint256 outputAmount) {
    // Get Uniswap router and path
    (IRouter_Aerodrome router, IRouter_Aerodrome.Route[] memory swapPath) = abi.decode(
      strategyData,
      (IRouter_Aerodrome, IRouter_Aerodrome.Route[])
    );
    require(
      swapPath.length >= 1 && swapPath[0].from == address(inputToken),
      "Invalid AerodromeV2Liquidator swap path."
    );

    // Swap underlying tokens
    inputToken.approve(address(router), inputAmount);

    // call the relevant fn depending on the uni v2 fork specifics
    _swap(router, inputAmount, swapPath);

    // Get new collateral
    outputToken = IERC20Upgradeable(swapPath[swapPath.length - 1].to);
    outputAmount = outputToken.balanceOf(address(this));
  }
}

contracts/liquidators/AlgebraSwapLiquida.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "./IRedemptionStrategy.sol";
import "../external/algebra/ISwapRouter.sol";

/**
 * @title AlgebraSwapLiquidator
 * @notice Exchanges seized token collateral for underlying tokens via a Algebra router for use as a step in a liquidation.
 * @author Veliko Minkov <veliko@midascapital.xyz> (https://github.com/vminkov)
 */
contract AlgebraSwapLiquidator is IRedemptionStrategy {
  /**
   * @notice Redeems custom collateral `token` for an underlying token.
   * @param inputToken The input wrapped token to be redeemed for an underlying token.
   * @param inputAmount The amount of the input wrapped token to be redeemed for an underlying token.
   * @param strategyData The ABI-encoded data to be used in the redemption strategy logic.
   * @return outputToken The underlying ERC20 token outputted.
   * @return outputAmount The quantity of underlying tokens outputted.
   */
  function redeem(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    bytes memory strategyData
  ) external returns (IERC20Upgradeable outputToken, uint256 outputAmount) {
    (address _outputToken, IAlgebraSwapRouter swapRouter) = abi.decode(strategyData, (address, IAlgebraSwapRouter));
    outputToken = IERC20Upgradeable(_outputToken);

    inputToken.approve(address(swapRouter), inputAmount);

    IAlgebraSwapRouter.ExactInputSingleParams memory params = IAlgebraSwapRouter.ExactInputSingleParams(
      address(inputToken),
      _outputToken,
      address(this),
      block.timestamp,
      inputAmount,
      0, // amountOutMinimum
      0 // limitSqrtPrice
    );

    outputAmount = swapRouter.exactInputSingle(params);
  }

  function name() public pure returns (string memory) {
    return "AlgebraSwapLiquidator";
  }
}

contracts/liquidators/BaseUniswapV2Liqui.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";
import "openzeppelin-contracts-upgradeable/contracts/token/ERC20/utils/SafeERC20Upgradeable.sol";

import "../external/uniswap/IUniswapV2Router02.sol";

import "./IRedemptionStrategy.sol";

abstract contract BaseUniswapV2Liquidator is IRedemptionStrategy {
  using SafeERC20Upgradeable for IERC20Upgradeable;

  /**
   * @notice Redeems custom collateral `token` for an underlying token.
   * @param inputToken The input wrapped token to be redeemed for an underlying token.
   * @param inputAmount The amount of the input wrapped token to be redeemed for an underlying token.
   * @param strategyData The ABI-encoded data to be used in the redemption strategy logic.
   * @return outputToken The underlying ERC20 token outputted.
   * @return outputAmount The quantity of underlying tokens outputted.
   */
  function redeem(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    bytes memory strategyData
  ) external override returns (IERC20Upgradeable outputToken, uint256 outputAmount) {
    return _convert(inputToken, inputAmount, strategyData);
  }

  function _convert(
    IERC20Upgradeable inputToken,
    uint256 inputAmount,
    bytes memory strategyData
  ) internal returns (IERC20Upgradeable outputToken, uint256 outputAmount) {
    // Get Uniswap router and path
    (IUniswapV2Router02 uniswapV2Router, address[] memory swapPath) = abi.decode(
      strategyData,
      (IUniswapV2Router02, address[])
    );
    require(swapPath.length >= 2 && swapPath[0] == address(inputToken), "Invalid UniswapLiquidator swap path.");

    // Swap underlying tokens
    inputToken.approve(address(uniswapV2Router), inputAmount);

    // call the relevant fn depending on the uni v2 fork specifics
    _swap(uniswapV2Router, inputAmount, swapPath);

    // Get new collateral
    outputToken = IERC20Upgradeable(swapPath[swapPath.length - 1]);
    outputAmount = outputToken.balanceOf(address(this));
  }

  function _swap(
    IUniswapV2Router02 uniswapV2Router,
    uint256 inputAmount,
    address[] memory swapPath
  ) internal virtual;
}

