    inputAmount = amounts[0];
    inputToken = IERC20Upgradeable(swapPath[0]);
  }

  function name() public pure override(UniswapV2Liquidator, IRedemptionStrategy) returns (string memory) {
    return "UniswapV2LiquidatorFunder";
