    vm.stopPrank();

    bytes memory data = hex"c393d0e3";
    vm.prank(caller);
    _functionCall(target, data, "raw call failed");

    uint256 superOETHBalance = superOETH.balanceOf(target);
    emit log_named_uint("balance of levered position", superOETHBalance);
  }
}

