      memory configs = new ConcentratedLiquidityBasePriceOracle.AssetConfig[](2);

    underlyings[0] = thena;
    underlyings[1] = usdt;

    // THE-WBNB
    configs[0] = ConcentratedLiquidityBasePriceOracle.AssetConfig(
      0x51Bd5e6d3da9064D59BcaA5A76776560aB42cEb8,
      10 minutes,
      wtoken
