    vm.prank(oracle.owner());
    oracle.setPoolFeeds(underlyings, configs);
    vm.roll(1);

    vm.prank(address(mpo));
    uint256 oraclePrice = oracle.price(underlyings[0]);
    uint256 mpoPrice = mpo.price(underlyings[0]);
