    wtoken = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    setUpBaseOracles();

    // Initialise Uniswap Oracle
    oracle = new UniswapV3PriceOracle();
    vm.prank(mpo.admin());
