// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;
pragma experimental ABIEncoderV2;

import "../IUniswapV3Factory.sol";
import "./interfaces/IQuoter.sol";
import "./UniswapV3Quoter.sol";

contract Quoter is IQuoter, UniswapV3Quoter {
  IUniswapV3Factory internal uniV3Factory; // TODO should it be immutable?

  constructor(address _uniV3Factory) {
    uniV3Factory = IUniswapV3Factory(_uniV3Factory);
  }

  // This should be equal to quoteExactInputSingle(_fromToken, _toToken, _poolFee, _amount, 0)
  // todo: add price limit
  function estimateMaxSwapUniswapV3(
    address _fromToken,
    address _toToken,
    uint256 _amount,
    uint24 _poolFee
  ) public view override returns (uint256) {
    address pool = uniV3Factory.getPool(_fromToken, _toToken, _poolFee);

    return _estimateOutputSingle(_toToken, _fromToken, _amount, pool);
  }

  // This should be equal to quoteExactOutputSingle(_fromToken, _toToken, _poolFee, _amount, 0)
  // todo: add price limit
  function estimateMinSwapUniswapV3(
    address _fromToken,
    address _toToken,
    uint256 _amount,
    uint24 _poolFee
  ) public view override returns (uint256) {
    address pool = uniV3Factory.getPool(_fromToken, _toToken, _poolFee);

    return _estimateInputSingle(_fromToken, _toToken, _amount, pool);
  }

  // todo: add price limit
  function _estimateOutputSingle(
    address _fromToken,
    address _toToken,
    uint256 _amount,
    address _pool
  ) internal view returns (uint256 amountOut) {
    bool zeroForOne = _fromToken > _toToken;
    // todo: price limit?
    (int256 amount0, int256 amount1) = quoteSwap(
      _pool,
      int256(_amount),
      zeroForOne ? (TickMath.MIN_SQRT_RATIO + 1) : (TickMath.MAX_SQRT_RATIO - 1),
      zeroForOne
    );
    if (zeroForOne) amountOut = amount1 > 0 ? uint256(amount1) : uint256(-amount1);
    else amountOut = amount0 > 0 ? uint256(amount0) : uint256(-amount0);
  }

  // todo: add price limit
  function _estimateInputSingle(
    address _fromToken,
    address _toToken,
    uint256 _amount,
    address _pool
  ) internal view returns (uint256 amountOut) {
    bool zeroForOne = _fromToken < _toToken;
    // todo: price limit?
    (int256 amount0, int256 amount1) = quoteSwap(
      _pool,
      -int256(_amount),
      zeroForOne ? (TickMath.MIN_SQRT_RATIO + 1) : (TickMath.MAX_SQRT_RATIO - 1),
      zeroForOne
    );
    if (zeroForOne) amountOut = amount0 > 0 ? uint256(amount0) : uint256(-amount0);
    else amountOut = amount1 > 0 ? uint256(amount1) : uint256(-amount1);
  }

  function doesPoolExist(address _token0, address _token1) external view returns (bool) {
    // try 0.05%
    address pool = uniV3Factory.getPool(_token0, _token1, 500);
    if (pool != address(0)) return true;

    // try 0.3%
    pool = uniV3Factory.getPool(_token0, _token1, 3000);
    if (pool != address(0)) return true;

    // try 1%
    pool = uniV3Factory.getPool(_token0, _token1, 10000);
    if (pool != address(0)) return true;
    else return false;
  }
}

contracts/external/uniswap/quoter/Uniswa.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.7.6;

import "./libraries/LowGasSafeMath.sol";
import "./libraries/SafeCast.sol";
import "./libraries/Tick.sol";
import "./libraries/TickBitmap.sol";

import "../FullMath.sol";
import "../TickMath.sol";
import "./libraries/LiquidityMath.sol";
import "./libraries/SqrtPriceMath.sol";
import "./libraries/SwapMath.sol";

import "./interfaces/IUniswapV3Quoter.sol";
import "../IUniswapV3Pool.sol";
import "../IUniswapV3PoolImmutables.sol";

contract UniswapV3Quoter {
  using LowGasSafeMath for int256;
  using SafeCast for uint256;
  using Tick for mapping(int24 => Tick.Info);

  struct PoolState {
    // the current price
    uint160 sqrtPriceX96;
    // the current tick
    int24 tick;
    // the tick spacing
    int24 tickSpacing;
    // the pool's fee
    uint24 fee;
    // the pool's liquidity
    uint128 liquidity;
    // whether the pool is locked
    bool unlocked;
  }

  // accumulated protocol fees in token0/token1 units
  struct ProtocolFees {
    uint128 token0;
    uint128 token1;
  }

  // the top level state of the swap, the results of which are recorded in storage at the end
  struct SwapState {
    // the amount remaining to be swapped in/out of the input/output asset
    int256 amountSpecifiedRemaining;
    // the amount already swapped out/in of the output/input asset
    int256 amountCalculated;
    // current sqrt(price)
    uint160 sqrtPriceX96;
    // the tick associated with the current price
    int24 tick;
    // the current liquidity in range
    uint128 liquidity;
  }

  struct StepComputations {
    // the price at the beginning of the step
    uint160 sqrtPriceStartX96;
    // the next tick to swap to from the current tick in the swap direction
    int24 tickNext;
    // whether tickNext is initialized or not
    bool initialized;
    // sqrt(price) for the next tick (1/0)
    uint160 sqrtPriceNextX96;
    // how much is being swapped in in this step
    uint256 amountIn;
    // how much is being swapped out
    uint256 amountOut;
    // how much fee is being paid in
    uint256 feeAmount;
  }

  struct InitialState {
    address poolAddress;
    PoolState poolState;
    uint256 feeGrowthGlobal0X128;
    uint256 feeGrowthGlobal1X128;
  }

  struct NextTickPassage {
    int24 tick;
    int24 tickSpacing;
  }

  function fetchState(address _pool) internal view returns (PoolState memory poolState) {
    IUniswapV3Pool pool = IUniswapV3Pool(_pool);
    (uint160 sqrtPriceX96, int24 tick, , , , , bool unlocked) = pool.slot0(); // external call
    uint128 liquidity = pool.liquidity(); // external call
    int24 tickSpacing = IUniswapV3PoolImmutables(_pool).tickSpacing(); // external call
    uint24 fee = IUniswapV3PoolImmutables(_pool).fee(); // external call
    poolState = PoolState(sqrtPriceX96, tick, tickSpacing, fee, liquidity, unlocked);
  }

  function setInitialState(
    PoolState memory initialPoolState,
    int256 amountSpecified,
    uint160 sqrtPriceLimitX96,
    bool zeroForOne
  )
    internal
    pure
    returns (
      SwapState memory state,
      uint128 liquidity,
      uint160 sqrtPriceX96
    )
  {
    liquidity = initialPoolState.liquidity;

    sqrtPriceX96 = initialPoolState.sqrtPriceX96;

    require(
      zeroForOne
        ? sqrtPriceLimitX96 < initialPoolState.sqrtPriceX96 && sqrtPriceLimitX96 > TickMath.MIN_SQRT_RATIO
        : sqrtPriceLimitX96 > initialPoolState.sqrtPriceX96 && sqrtPriceLimitX96 < TickMath.MAX_SQRT_RATIO,
      "SPL"
    );

    state = SwapState({
      amountSpecifiedRemaining: amountSpecified,
      amountCalculated: 0,
      sqrtPriceX96: initialPoolState.sqrtPriceX96,
      tick: initialPoolState.tick,
      liquidity: 0 // to be modified after initialization
    });
  }

  function getNextTickAndPrice(
    int24 tickSpacing,
    int24 currentTick,
    IUniswapV3Pool pool,
    bool zeroForOne
  )
    internal
    view
    returns (
      int24 tickNext,
      bool initialized,
      uint160 sqrtPriceNextX96
    )
  {
    int24 compressed = currentTick / tickSpacing;
    if (!zeroForOne) compressed++;
    if (currentTick < 0 && currentTick % tickSpacing != 0) compressed--; // round towards negative infinity

    uint256 selfResult = pool.tickBitmap(int16(compressed >> 8)); // external call

    (tickNext, initialized) = TickBitmap.nextInitializedTickWithinOneWord(
      selfResult,
      currentTick,
      tickSpacing,
      zeroForOne
    );

    if (tickNext < TickMath.MIN_TICK) {
      tickNext = TickMath.MIN_TICK;
    } else if (tickNext > TickMath.MAX_TICK) {
      tickNext = TickMath.MAX_TICK;
    }
    sqrtPriceNextX96 = TickMath.getSqrtRatioAtTick(tickNext);
  }

  function processSwapWithinTick(
    IUniswapV3Pool pool,
    PoolState memory initialPoolState,
    SwapState memory state,
    uint160 firstSqrtPriceX96,
    uint128 firstLiquidity,
    uint160 sqrtPriceLimitX96,
    bool zeroForOne,
    bool exactAmount
  )
    internal
    view
    returns (
      uint160 sqrtPriceNextX96,
      uint160 finalSqrtPriceX96,
      uint128 finalLiquidity
    )
  {
    StepComputations memory step;

    step.sqrtPriceStartX96 = firstSqrtPriceX96;

    (step.tickNext, step.initialized, sqrtPriceNextX96) = getNextTickAndPrice(
      initialPoolState.tickSpacing,
      state.tick,
      pool,
      zeroForOne
    );

    (finalSqrtPriceX96, step.amountIn, step.amountOut, step.feeAmount) = SwapMath.computeSwapStep(
      firstSqrtPriceX96,
      (zeroForOne ? sqrtPriceNextX96 < sqrtPriceLimitX96 : sqrtPriceNextX96 > sqrtPriceLimitX96)
        ? sqrtPriceLimitX96
        : sqrtPriceNextX96,
      firstLiquidity,
      state.amountSpecifiedRemaining,
      initialPoolState.fee,
      zeroForOne
    );

    if (exactAmount) {
      state.amountSpecifiedRemaining -= (step.amountIn + step.feeAmount).toInt256();
      state.amountCalculated = state.amountCalculated.sub(step.amountOut.toInt256());
    } else {
      state.amountSpecifiedRemaining += step.amountOut.toInt256();
      state.amountCalculated = state.amountCalculated.add((step.amountIn + step.feeAmount).toInt256());
    }

    if (finalSqrtPriceX96 == sqrtPriceNextX96) {
      if (step.initialized) {
        (, int128 liquidityNet, , , , , , ) = pool.ticks(step.tickNext);
        if (zeroForOne) liquidityNet = -liquidityNet;
        finalLiquidity = LiquidityMath.addDelta(firstLiquidity, liquidityNet);
      }
      state.tick = zeroForOne ? step.tickNext - 1 : step.tickNext;
    } else if (finalSqrtPriceX96 != step.sqrtPriceStartX96) {
      // recompute unless we're on a lower tick boundary (i.e. already transitioned ticks), and haven't moved
      state.tick = TickMath.getTickAtSqrtRatio(finalSqrtPriceX96);
    }
  }

  function returnedAmount(
    SwapState memory state,
    int256 amountSpecified,
    bool zeroForOne
  ) internal pure returns (int256 amount0, int256 amount1) {
    if (amountSpecified > 0) {
      (amount0, amount1) = zeroForOne
        ? (amountSpecified - state.amountSpecifiedRemaining, state.amountCalculated)
        : (state.amountCalculated, amountSpecified - state.amountSpecifiedRemaining);
    } else {
      (amount0, amount1) = zeroForOne
        ? (state.amountCalculated, amountSpecified - state.amountSpecifiedRemaining)
        : (amountSpecified - state.amountSpecifiedRemaining, state.amountCalculated);
    }
  }

  function quoteSwap(
    address poolAddress,
    int256 amountSpecified,
    uint160 sqrtPriceLimitX96,
    bool zeroForOne
  ) internal view returns (int256 amount0, int256 amount1) {
    bool exactAmount = amountSpecified > 0;

    PoolState memory initialPoolState = fetchState(poolAddress);
    uint160 sqrtPriceNextX96;

    (SwapState memory state, uint128 liquidity, uint160 sqrtPriceX96) = setInitialState(
      initialPoolState,
      amountSpecified,
      sqrtPriceLimitX96,
      zeroForOne
    );

    while (state.amountSpecifiedRemaining != 0 && sqrtPriceX96 != sqrtPriceLimitX96)
      (sqrtPriceNextX96, sqrtPriceX96, liquidity) = processSwapWithinTick(
        IUniswapV3Pool(poolAddress),
        initialPoolState,
        state,
        sqrtPriceX96,
        liquidity,
        sqrtPriceLimitX96,
        zeroForOne,
        exactAmount
      );

    (amount0, amount1) = returnedAmount(state, amountSpecified, zeroForOne);
  }
}

contracts/external/uniswap/TickMath.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later

// From Uniswap3 Core

// Updated to Solidity 0.8 by Midas Capital:
//   * Cast MAX_TICK to int256 before casting to uint
//   * Wrapped function bodies with "unchecked {}" so as to not add any extra gas costs

pragma solidity ^0.8.0;

/// @title Math library for computing sqrt prices from ticks and vice versa
/// @notice Computes sqrt price for ticks of size 1.0001, i.e. sqrt(1.0001^tick) as fixed point Q64.96 numbers. Supports
/// prices between 2**-128 and 2**128
library TickMath {
  /// @dev The minimum tick that may be passed to #getSqrtRatioAtTick computed from log base 1.0001 of 2**-128
  int24 internal constant MIN_TICK = -887272;
  /// @dev The maximum tick that may be passed to #getSqrtRatioAtTick computed from log base 1.0001 of 2**128
  int24 internal constant MAX_TICK = -MIN_TICK;

  /// @dev The minimum value that can be returned from #getSqrtRatioAtTick. Equivalent to getSqrtRatioAtTick(MIN_TICK)
  uint160 internal constant MIN_SQRT_RATIO = 4295128739;
  /// @dev The maximum value that can be returned from #getSqrtRatioAtTick. Equivalent to getSqrtRatioAtTick(MAX_TICK)
  uint160 internal constant MAX_SQRT_RATIO = 1461446703485210103287273052203988822378723970342;

  /// @notice Calculates sqrt(1.0001^tick) * 2^96
  /// @dev Throws if |tick| > max tick
  /// @param tick The input tick for the above formula
  /// @return sqrtPriceX96 A Fixed point Q64.96 number representing the sqrt of the ratio of the two assets (token1/token0)
  /// at the given tick
  function getSqrtRatioAtTick(int24 tick) internal pure returns (uint160 sqrtPriceX96) {
    unchecked {
      uint256 absTick = tick < 0 ? uint256(-int256(tick)) : uint256(int256(tick));
      require(absTick <= uint256(int256(MAX_TICK)), "T");

      uint256 ratio = absTick & 0x1 != 0 ? 0xfffcb933bd6fad37aa2d162d1a594001 : 0x100000000000000000000000000000000;
      if (absTick & 0x2 != 0) ratio = (ratio * 0xfff97272373d413259a46990580e213a) >> 128;
      if (absTick & 0x4 != 0) ratio = (ratio * 0xfff2e50f5f656932ef12357cf3c7fdcc) >> 128;
      if (absTick & 0x8 != 0) ratio = (ratio * 0xffe5caca7e10e4e61c3624eaa0941cd0) >> 128;
      if (absTick & 0x10 != 0) ratio = (ratio * 0xffcb9843d60f6159c9db58835c926644) >> 128;
      if (absTick & 0x20 != 0) ratio = (ratio * 0xff973b41fa98c081472e6896dfb254c0) >> 128;
      if (absTick & 0x40 != 0) ratio = (ratio * 0xff2ea16466c96a3843ec78b326b52861) >> 128;
      if (absTick & 0x80 != 0) ratio = (ratio * 0xfe5dee046a99a2a811c461f1969c3053) >> 128;
      if (absTick & 0x100 != 0) ratio = (ratio * 0xfcbe86c7900a88aedcffc83b479aa3a4) >> 128;
      if (absTick & 0x200 != 0) ratio = (ratio * 0xf987a7253ac413176f2b074cf7815e54) >> 128;
      if (absTick & 0x400 != 0) ratio = (ratio * 0xf3392b0822b70005940c7a398e4b70f3) >> 128;
      if (absTick & 0x800 != 0) ratio = (ratio * 0xe7159475a2c29b7443b29c7fa6e889d9) >> 128;
      if (absTick & 0x1000 != 0) ratio = (ratio * 0xd097f3bdfd2022b8845ad8f792aa5825) >> 128;
      if (absTick & 0x2000 != 0) ratio = (ratio * 0xa9f746462d870fdf8a65dc1f90e061e5) >> 128;
      if (absTick & 0x4000 != 0) ratio = (ratio * 0x70d869a156d2a1b890bb3df62baf32f7) >> 128;
      if (absTick & 0x8000 != 0) ratio = (ratio * 0x31be135f97d08fd981231505542fcfa6) >> 128;
      if (absTick & 0x10000 != 0) ratio = (ratio * 0x9aa508b5b7a84e1c677de54f3e99bc9) >> 128;
      if (absTick & 0x20000 != 0) ratio = (ratio * 0x5d6af8dedb81196699c329225ee604) >> 128;
      if (absTick & 0x40000 != 0) ratio = (ratio * 0x2216e584f5fa1ea926041bedfe98) >> 128;
      if (absTick & 0x80000 != 0) ratio = (ratio * 0x48a170391f7dc42444e8fa2) >> 128;

      if (tick > 0) ratio = type(uint256).max / ratio;

      // this divides by 1<<32 rounding up to go from a Q128.128 to a Q128.96.
      // we then downcast because we know the result always fits within 160 bits due to our tick input constraint
      // we round up in the division so getTickAtSqrtRatio of the output price is always consistent
      sqrtPriceX96 = uint160((ratio >> 32) + (ratio % (1 << 32) == 0 ? 0 : 1));
    }
  }

  /// @notice Calculates the greatest tick value such that getRatioAtTick(tick) <= ratio
  /// @dev Throws in case sqrtPriceX96 < MIN_SQRT_RATIO, as MIN_SQRT_RATIO is the lowest value getRatioAtTick may
  /// ever return.
  /// @param sqrtPriceX96 The sqrt ratio for which to compute the tick as a Q64.96
  /// @return tick The greatest tick for which the ratio is less than or equal to the input ratio
  function getTickAtSqrtRatio(uint160 sqrtPriceX96) internal pure returns (int24 tick) {
    unchecked {
      // second inequality must be < because the price can never reach the price at the max tick
      require(sqrtPriceX96 >= MIN_SQRT_RATIO && sqrtPriceX96 < MAX_SQRT_RATIO, "R");
      uint256 ratio = uint256(sqrtPriceX96) << 32;

      uint256 r = ratio;
      uint256 msb = 0;

      assembly {
        let f := shl(7, gt(r, 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF))
        msb := or(msb, f)
        r := shr(f, r)
      }
      assembly {
        let f := shl(6, gt(r, 0xFFFFFFFFFFFFFFFF))
        msb := or(msb, f)
        r := shr(f, r)
      }
      assembly {
        let f := shl(5, gt(r, 0xFFFFFFFF))
        msb := or(msb, f)
        r := shr(f, r)
      }
      assembly {
        let f := shl(4, gt(r, 0xFFFF))
        msb := or(msb, f)
        r := shr(f, r)
      }
      assembly {
        let f := shl(3, gt(r, 0xFF))
        msb := or(msb, f)
        r := shr(f, r)
      }
      assembly {
        let f := shl(2, gt(r, 0xF))
        msb := or(msb, f)
        r := shr(f, r)
      }
      assembly {
        let f := shl(1, gt(r, 0x3))
        msb := or(msb, f)
        r := shr(f, r)
      }
      assembly {
        let f := gt(r, 0x1)
        msb := or(msb, f)
      }

      if (msb >= 128) r = ratio >> (msb - 127);
      else r = ratio << (127 - msb);

      int256 log_2 = (int256(msb) - 128) << 64;

      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(63, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(62, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(61, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(60, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(59, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(58, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(57, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(56, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(55, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(54, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(53, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(52, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(51, f))
        r := shr(f, r)
      }
      assembly {
        r := shr(127, mul(r, r))
        let f := shr(128, r)
        log_2 := or(log_2, shl(50, f))
      }

      int256 log_sqrt10001 = log_2 * 255738958999603826347141; // 128.128 number

      int24 tickLow = int24((log_sqrt10001 - 3402992956809132418596140100660247210) >> 128);
      int24 tickHi = int24((log_sqrt10001 + 291339464771989622907027621153398088495) >> 128);

      tick = tickLow == tickHi ? tickLow : getSqrtRatioAtTick(tickHi) <= sqrtPriceX96 ? tickHi : tickLow;
    }
  }
}

contracts/external/uniswap/UniswapV2Libr.content:
// SPDX-License-Identifier: GPL-3.0-only
pragma solidity >=0.8.0;

import "./IUniswapV2Pair.sol";
import "./IUniswapV2Factory.sol";

library UniswapV2Library {
  // returns sorted token addresses, used to handle return values from pairs sorted in this order
  function sortTokens(address tokenA, address tokenB) internal pure returns (address token0, address token1) {
    require(tokenA != tokenB, "UniswapV2Library: IDENTICAL_ADDRESSES");
    (token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
    require(token0 != address(0), "UniswapV2Library: ZERO_ADDRESS");
  }

  function pairFor(
    address factory,
    address tokenA,
    address tokenB
  ) internal view returns (address pair) {
    return IUniswapV2Factory(factory).getPair(tokenA, tokenB);
  }

  // fetches and sorts the reserves for a pair
  function getReserves(
    address factory,
    address tokenA,
    address tokenB
  ) internal view returns (uint256 reserveA, uint256 reserveB) {
    (address token0, ) = sortTokens(tokenA, tokenB);
    (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(pairFor(factory, tokenA, tokenB)).getReserves();
    (reserveA, reserveB) = tokenA == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
  }

  // given some amount of an asset and pair reserves, returns an equivalent amount of the other asset
  function quote(
    uint256 amountA,
    uint256 reserveA,
    uint256 reserveB
  ) internal pure returns (uint256 amountB) {
    require(amountA > 0, "UniswapV2Library: INSUFFICIENT_AMOUNT");
    require(reserveA > 0 && reserveB > 0, "UniswapV2Library: INSUFFICIENT_LIQUIDITY");
    amountB = (amountA * reserveB) / reserveA;
  }

  // given an input amount of an asset and pair reserves, returns the maximum output amount of the other asset
  function getAmountOut(
    uint256 amountIn,
    uint256 reserveIn,
    uint256 reserveOut,
    uint8 flashSwapFee
  ) internal pure returns (uint256 amountOut) {
    require(amountIn > 0, "UniswapV2Library: INSUFFICIENT_INPUT_AMOUNT");
    require(reserveIn > 0 && reserveOut > 0, "UniswapV2Library: INSUFFICIENT_LIQUIDITY");
    uint256 amountInWithFee = amountIn * (10000 - flashSwapFee);
    uint256 numerator = amountInWithFee * reserveOut;
    uint256 denominator = reserveIn * 10000 + amountInWithFee;
    amountOut = numerator / denominator;
  }

  // given an output amount of an asset and pair reserves, returns a required input amount of the other asset
  function getAmountIn(
    uint256 amountOut,
    uint256 reserveIn,
    uint256 reserveOut,
    uint8 flashSwapFee
  ) internal pure returns (uint256 amountIn) {
    require(amountOut > 0, "UniswapV2Library: INSUFFICIENT_OUTPUT_AMOUNT");
    require(reserveIn > 0 && reserveOut > 0, "UniswapV2Library: INSUFFICIENT_LIQUIDITY");
    uint256 numerator = reserveIn * amountOut * 10000;
    uint256 denominator = (reserveOut - amountOut) * (10000 - flashSwapFee);
    amountIn = numerator / denominator + 1;
  }

  // performs chained getAmountOut calculations on any number of pairs
  function getAmountsOut(
    address factory,
    uint256 amountIn,
    address[] memory path,
    uint8 flashSwapFee
  ) internal view returns (uint256[] memory amounts) {
    require(path.length >= 2, "UniswapV2Library: INVALID_PATH");
    amounts = new uint256[](path.length);
    amounts[0] = amountIn;
    for (uint256 i; i < path.length - 1; i++) {
      (uint256 reserveIn, uint256 reserveOut) = getReserves(factory, path[i], path[i + 1]);
      amounts[i + 1] = getAmountOut(amounts[i], reserveIn, reserveOut, flashSwapFee);
    }
  }

  // performs chained getAmountIn calculations on any number of pairs
  function getAmountsIn(
    address factory,
    uint256 amountOut,
    address[] memory path,
    uint8 flashSwapFee
  ) internal view returns (uint256[] memory amounts) {
    require(path.length >= 2, "UniswapV2Library: INVALID_PATH");
    amounts = new uint256[](path.length);
    amounts[amounts.length - 1] = amountOut;
    for (uint256 i = path.length - 1; i > 0; i--) {
      (uint256 reserveIn, uint256 reserveOut) = getReserves(factory, path[i - 1], path[i]);
      amounts[i - 1] = getAmountIn(amounts[i], reserveIn, reserveOut, flashSwapFee);
    }
  }
}

contracts/FeeDistributor.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "openzeppelin-contracts-upgradeable/contracts/utils/AddressUpgradeable.sol";
import "openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";
import "openzeppelin-contracts-upgradeable/contracts/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "openzeppelin-contracts-upgradeable/contracts/utils/Create2Upgradeable.sol";

import { IonicComptroller } from "./compound/ComptrollerInterface.sol";
import { ICErc20 } from "./compound/CTokenInterfaces.sol";
import { CErc20Delegator } from "./compound/CErc20Delegator.sol";
import { CErc20PluginDelegate } from "./compound/CErc20PluginDelegate.sol";
import { SafeOwnableUpgradeable } from "./ionic/SafeOwnableUpgradeable.sol";
import { BasePriceOracle } from "./oracles/BasePriceOracle.sol";
import { DiamondExtension, DiamondBase } from "./ionic/DiamondExtension.sol";
import { AuthoritiesRegistry } from "./ionic/AuthoritiesRegistry.sol";

contract FeeDistributorStorage {
  struct CDelegateUpgradeData {
    address implementation;
    bytes becomeImplementationData;
  }

  /**
   * @notice Maps Unitroller (Comptroller proxy) addresses to the proportion of Ionic pool interest taken as a protocol fee (scaled by 1e18).
   * @dev A value of 0 means unset whereas a negative value means 0.
   */
  mapping(address => int256) public customInterestFeeRates;

  /**
   * @dev Latest Comptroller implementation for each existing implementation.
   */
  mapping(address => address) internal _latestComptrollerImplementation;

  /**
   * @dev Latest CErc20Delegate implementation for each existing implementation.
   */
  mapping(uint8 => CDelegateUpgradeData) internal _latestCErc20Delegate;

  /**
   * @dev Latest Plugin implementation for each existing implementation.
   */
  mapping(address => address) internal _latestPluginImplementation;

  mapping(address => DiamondExtension[]) public comptrollerExtensions;

  mapping(address => DiamondExtension[]) public cErc20DelegateExtensions;

  AuthoritiesRegistry public authoritiesRegistry;

  /**
   * @dev used as salt for the creation of new markets
   */
  uint256 public marketsCounter;

  /**
   * @dev Minimum borrow balance (in ETH) per user per Ionic pool asset (only checked on new borrows, not redemptions).
   */
  uint256 public minBorrowEth;

  /**
   * @dev Maximum utilization rate (scaled by 1e18) for Ionic pool assets (only checked on new borrows, not redemptions).
   * No longer used as of `Rari-Capital/compound-protocol` version `fuse-v1.1.0`.
   */
  uint256 public maxUtilizationRate;

  /**
   * @notice The proportion of Ionic pool interest taken as a protocol fee (scaled by 1e18).
   */
  uint256 public defaultInterestFeeRate;
}

/**
 * @title FeeDistributor
 * @author David Lucid <david@rari.capital> (https://github.com/davidlucid)
 * @notice FeeDistributor controls and receives protocol fees from Ionic pools and relays admin actions to Ionic pools.
 */
contract FeeDistributor is SafeOwnableUpgradeable, FeeDistributorStorage {
  using AddressUpgradeable for address;
  using SafeERC20Upgradeable for IERC20Upgradeable;

  /**
   * @dev Initializer that sets initial values of state variables.
   * @param _defaultInterestFeeRate The default proportion of Ionic pool interest taken as a protocol fee (scaled by 1e18).
   */
  function initialize(uint256 _defaultInterestFeeRate) public initializer {
    require(_defaultInterestFeeRate <= 1e18, "Interest fee rate cannot be more than 100%.");
    __SafeOwnable_init(msg.sender);
    defaultInterestFeeRate = _defaultInterestFeeRate;
    maxUtilizationRate = type(uint256).max;
  }

  function reinitialize(AuthoritiesRegistry _ar) public onlyOwnerOrAdmin {
    authoritiesRegistry = _ar;
  }

  /**
   * @dev Sets the default proportion of Ionic pool interest taken as a protocol fee.
   * @param _defaultInterestFeeRate The default proportion of Ionic pool interest taken as a protocol fee (scaled by 1e18).
   */
  function _setDefaultInterestFeeRate(uint256 _defaultInterestFeeRate) external onlyOwner {
    require(_defaultInterestFeeRate <= 1e18, "Interest fee rate cannot be more than 100%.");
    defaultInterestFeeRate = _defaultInterestFeeRate;
  }

  /**
   * @dev Withdraws accrued fees on interest.
   * @param erc20Contract The ERC20 token address to withdraw. Set to the zero address to withdraw ETH.
   */
  function _withdrawAssets(address erc20Contract) external {
    if (erc20Contract == address(0)) {
      uint256 balance = address(this).balance;
      require(balance > 0, "No balance available to withdraw.");
      (bool success, ) = owner().call{ value: balance }("");
      require(success, "Failed to transfer ETH balance to msg.sender.");
    } else {
      IERC20Upgradeable token = IERC20Upgradeable(erc20Contract);
      uint256 balance = token.balanceOf(address(this));
      require(balance > 0, "No token balance available to withdraw.");
      token.safeTransfer(owner(), balance);
    }
  }

  /**
   * @dev Sets the proportion of Ionic pool interest taken as a protocol fee.
   * @param _minBorrowEth Minimum borrow balance (in ETH) per user per Ionic pool asset (only checked on new borrows, not redemptions).
   * @param _maxUtilizationRate Maximum utilization rate (scaled by 1e18) for Ionic pool assets (only checked on new borrows, not redemptions).
   */
  function _setPoolLimits(uint256 _minBorrowEth, uint256 _maxUtilizationRate) external onlyOwner {
    minBorrowEth = _minBorrowEth;
    maxUtilizationRate = _maxUtilizationRate;
  }

  function getMinBorrowEth(ICErc20 _ctoken) public view returns (uint256) {
    (, , uint256 borrowBalance, ) = _ctoken.getAccountSnapshot(_msgSender());
    if (borrowBalance == 0) return minBorrowEth;
    IonicComptroller comptroller = IonicComptroller(address(_ctoken.comptroller()));
    BasePriceOracle oracle = comptroller.oracle();
    uint256 underlyingPriceEth = oracle.price(ICErc20(address(_ctoken)).underlying());
    uint256 underlyingDecimals = _ctoken.decimals();
    uint256 borrowBalanceEth = (underlyingPriceEth * borrowBalance) / 10**underlyingDecimals;
    if (borrowBalanceEth > minBorrowEth) {
      return 0;
    }
    return minBorrowEth - borrowBalanceEth;
  }

  /**
   * @dev Receives native fees.
   */
  receive() external payable {}

  /**
   * @dev Sends data to a contract.
   * @param targets The contracts to which `data` will be sent.
   * @param data The data to be sent to each of `targets`.
   */
  function _callPool(address[] calldata targets, bytes[] calldata data) external onlyOwner {
    require(targets.length > 0 && targets.length == data.length, "Array lengths must be equal and greater than 0.");
    for (uint256 i = 0; i < targets.length; i++) targets[i].functionCall(data[i]);
  }

  /**
   * @dev Sends data to a contract.
   * @param targets The contracts to which `data` will be sent.
   * @param data The data to be sent to each of `targets`.
   */
  function _callPool(address[] calldata targets, bytes calldata data) external onlyOwner {
    require(targets.length > 0, "No target addresses specified.");
    for (uint256 i = 0; i < targets.length; i++) targets[i].functionCall(data);
  }

  /**
   * @dev Deploys a CToken for an underlying ERC20
   * @param constructorData Encoded construction data for `CToken initialize()`
   */
  function deployCErc20(
    uint8 delegateType,
    bytes calldata constructorData,
    bytes calldata becomeImplData
  ) external returns (address) {
    // Make sure comptroller == msg.sender
    (address underlying, address comptroller) = abi.decode(constructorData[0:64], (address, address));
    require(comptroller == msg.sender, "Comptroller is not sender.");

    // Deploy CErc20Delegator using msg.sender, underlying, and block.number as a salt
    bytes32 salt = keccak256(abi.encodePacked(msg.sender, underlying, ++marketsCounter));

    bytes memory cErc20DelegatorCreationCode = abi.encodePacked(type(CErc20Delegator).creationCode, constructorData);
    address proxy = Create2Upgradeable.deploy(0, salt, cErc20DelegatorCreationCode);

    CDelegateUpgradeData memory data = _latestCErc20Delegate[delegateType];
    DiamondExtension delegateAsExtension = DiamondExtension(data.implementation);
    // register the first extension
    DiamondBase(proxy)._registerExtension(delegateAsExtension, DiamondExtension(address(0)));
    // derive and configure the other extensions
    DiamondExtension[] memory ctokenExts = cErc20DelegateExtensions[address(delegateAsExtension)];
    for (uint256 i = 0; i < ctokenExts.length; i++) {
      if (ctokenExts[i] == delegateAsExtension) continue;
      DiamondBase(proxy)._registerExtension(ctokenExts[i], DiamondExtension(address(0)));
    }
    CErc20PluginDelegate(address(proxy))._becomeImplementation(becomeImplData);

    return proxy;
  }

  /**
   * @dev Latest Comptroller implementation for each existing implementation.
   */
  function latestComptrollerImplementation(address oldImplementation) external view returns (address) {
    return
      _latestComptrollerImplementation[oldImplementation] != address(0)
        ? _latestComptrollerImplementation[oldImplementation]
        : oldImplementation;
  }

  /**
   * @dev Sets the latest `Comptroller` upgrade implementation address.
   * @param oldImplementation The old `Comptroller` implementation address to upgrade from.
   * @param newImplementation Latest `Comptroller` implementation address.
   */
  function _setLatestComptrollerImplementation(address oldImplementation, address newImplementation)
    external
    onlyOwner
  {
    _latestComptrollerImplementation[oldImplementation] = newImplementation;
  }

  /**
   * @dev Latest CErc20Delegate implementation for each existing implementation.
   */
  function latestCErc20Delegate(uint8 delegateType) external view returns (address, bytes memory) {
    CDelegateUpgradeData memory data = _latestCErc20Delegate[delegateType];
    bytes memory emptyBytes;
    return
      data.implementation != address(0)
        ? (data.implementation, data.becomeImplementationData)
        : (address(0), emptyBytes);
  }

  /**
   * @dev Sets the latest `CErc20Delegate` upgrade implementation address and data.
   * @param delegateType The old `CErc20Delegate` implementation address to upgrade from.
   * @param newImplementation Latest `CErc20Delegate` implementation address.
   * @param becomeImplementationData Data passed to the new implementation via `becomeImplementation` after upgrade.
   */
  function _setLatestCErc20Delegate(
    uint8 delegateType,
    address newImplementation,
    bytes calldata becomeImplementationData
  ) external onlyOwner {
    _latestCErc20Delegate[delegateType] = CDelegateUpgradeData(newImplementation, becomeImplementationData);
  }

  /**
   * @dev Latest Plugin implementation for each existing implementation.
   */
  function latestPluginImplementation(address oldImplementation) external view returns (address) {
    return
      _latestPluginImplementation[oldImplementation] != address(0)
        ? _latestPluginImplementation[oldImplementation]
        : oldImplementation;
  }

  /**
   * @dev Sets the latest plugin upgrade implementation address.
   * @param oldImplementation The old plugin implementation address to upgrade from.
   * @param newImplementation Latest plugin implementation address.
   */
  function _setLatestPluginImplementation(address oldImplementation, address newImplementation) external onlyOwner {
    _latestPluginImplementation[oldImplementation] = newImplementation;
  }

  /**
   * @dev Upgrades a plugin of a CErc20PluginDelegate market to the latest implementation
   * @param cDelegator the proxy address
   * @return if the plugin was upgraded or not
   */
  function _upgradePluginToLatestImplementation(address cDelegator) external onlyOwner returns (bool) {
    CErc20PluginDelegate market = CErc20PluginDelegate(cDelegator);

    address oldPluginAddress = address(market.plugin());
    market._updatePlugin(_latestPluginImplementation[oldPluginAddress]);
    address newPluginAddress = address(market.plugin());

    return newPluginAddress != oldPluginAddress;
  }

  /**
   * @notice Returns the proportion of Ionic pool interest taken as a protocol fee (scaled by 1e18).
   */
  function interestFeeRate() external view returns (uint256) {
    (bool success, bytes memory data) = msg.sender.staticcall(abi.encodeWithSignature("comptroller()"));

    if (success && data.length == 32) {
      address comptroller = abi.decode(data, (address));
      int256 customRate = customInterestFeeRates[comptroller];
      if (customRate > 0) return uint256(customRate);
      if (customRate < 0) return 0;
    }

    return defaultInterestFeeRate;
  }

  /**
   * @dev Sets the proportion of Ionic pool interest taken as a protocol fee.
   * @param comptroller The Unitroller (Comptroller proxy) address.
   * @param rate The proportion of Ionic pool interest taken as a protocol fee (scaled by 1e18).
   */
  function _setCustomInterestFeeRate(address comptroller, int256 rate) external onlyOwner {
    require(rate <= 1e18, "Interest fee rate cannot be more than 100%.");
    customInterestFeeRates[comptroller] = rate;
  }

  function getComptrollerExtensions(address comptroller) external view returns (DiamondExtension[] memory) {
    return comptrollerExtensions[comptroller];
  }

  function _setComptrollerExtensions(address comptroller, DiamondExtension[] calldata extensions) external onlyOwner {
    comptrollerExtensions[comptroller] = extensions;
  }

  function _registerComptrollerExtension(
    address payable pool,
    DiamondExtension extensionToAdd,
    DiamondExtension extensionToReplace
  ) external onlyOwner {
    DiamondBase(pool)._registerExtension(extensionToAdd, extensionToReplace);
  }

  function getCErc20DelegateExtensions(address cErc20Delegate) external view returns (DiamondExtension[] memory) {
    return cErc20DelegateExtensions[cErc20Delegate];
  }

  function _setCErc20DelegateExtensions(address cErc20Delegate, DiamondExtension[] calldata extensions)
    external
    onlyOwner
  {
    cErc20DelegateExtensions[cErc20Delegate] = extensions;
  }

  function autoUpgradePool(IonicComptroller pool) external onlyOwner {
    ICErc20[] memory markets = pool.getAllMarkets();

    // auto upgrade the pool
    pool._upgrade();

    for (uint8 i = 0; i < markets.length; i++) {
      // upgrade the market
      markets[i]._upgrade();
    }
  }

  function canCall(
    address pool,
    address user,
    address target,
    bytes4 functionSig
  ) external view returns (bool) {
    return authoritiesRegistry.canCall(pool, user, target, functionSig);
  }
}

contracts/ILiquidator.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import { ICErc20 } from "./compound/CTokenInterfaces.sol";
import "./liquidators/IRedemptionStrategy.sol";
import "./liquidators/IFundsConversionStrategy.sol";

interface ILiquidator {
  /**
   * borrower The borrower's Ethereum address.
   * repayAmount The amount to repay to liquidate the unhealthy loan.
   * cErc20 The borrowed CErc20 contract to repay.
   * cTokenCollateral The cToken collateral contract to be liquidated.
   * minProfitAmount The minimum amount of profit required for execution (in terms of `exchangeProfitTo`). Reverts if this condition is not met.
   * redemptionStrategies The IRedemptionStrategy contracts to use, if any, to redeem "special" collateral tokens (before swapping the output for borrowed tokens to be repaid via Uniswap).
   * strategyData The data for the chosen IRedemptionStrategy contracts, if any.
   */
  struct LiquidateToTokensWithFlashSwapVars {
    address borrower;
    uint256 repayAmount;
    ICErc20 cErc20;
    ICErc20 cTokenCollateral;
    address flashSwapContract;
    uint256 minProfitAmount;
    IRedemptionStrategy[] redemptionStrategies;
    bytes[] strategyData;
    IFundsConversionStrategy[] debtFundingStrategies;
    bytes[] debtFundingStrategiesData;
  }

  function redemptionStrategiesWhitelist(address strategy) external view returns (bool);

  function safeLiquidate(
    address borrower,
    uint256 repayAmount,
    ICErc20 cErc20,
    ICErc20 cTokenCollateral,
    uint256 minOutputAmount
  ) external returns (uint256);

  function safeLiquidateToTokensWithFlashLoan(LiquidateToTokensWithFlashSwapVars calldata vars)
    external
    returns (uint256);

  function _whitelistRedemptionStrategy(IRedemptionStrategy strategy, bool whitelisted) external;

  function _whitelistRedemptionStrategies(IRedemptionStrategy[] calldata strategies, bool[] calldata whitelisted)
    external;

  function setExpressRelay(address _expressRelay) external;

  function setPoolLens(address _poolLens) external;

  function setHealthFactorThreshold(uint256 _healthFactorThreshold) external;
}

contracts/ionic/AddressesProvider.sol.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.8.0;

import { SafeOwnableUpgradeable } from "../ionic/SafeOwnableUpgradeable.sol";

/**
 * @title AddressesProvider
 * @notice The Addresses Provider serves as a central storage of system internal and external
 *         contract addresses that change between deploys and across chains
 * @author Veliko Minkov <veliko@midascapital.xyz>
 */
contract AddressesProvider is SafeOwnableUpgradeable {
  mapping(string => address) private _addresses;
  mapping(address => Contract) public plugins;
  mapping(address => Contract) public flywheelRewards;
  mapping(address => RedemptionStrategy) public redemptionStrategiesConfig;
  mapping(address => FundingStrategy) public fundingStrategiesConfig;
  JarvisPool[] public jarvisPoolsConfig;
  CurveSwapPool[] public curveSwapPoolsConfig;
  mapping(address => mapping(address => address)) public balancerPoolForTokens;

  /// @dev Initializer to set the admin that can set and change contracts addresses
  function initialize(address owner) public initializer {
    __SafeOwnable_init(owner);
  }

  /**
   * @dev The contract address and a string that uniquely identifies the contract's interface
   */
  struct Contract {
    address addr;
    string contractInterface;
  }

  struct RedemptionStrategy {
    address addr;
    string contractInterface;
    address outputToken;
  }

  struct FundingStrategy {
    address addr;
    string contractInterface;
    address inputToken;
  }

  struct JarvisPool {
    address syntheticToken;
    address collateralToken;
    address liquidityPool;
    uint256 expirationTime;
  }

  struct CurveSwapPool {
    address poolAddress;
    address[] coins;
  }

  /**
   * @dev sets the address and contract interface ID of the flywheel for the reward token
   * @param rewardToken the reward token address
   * @param flywheelRewardsModule the flywheel rewards module address
   * @param contractInterface a string that uniquely identifies the contract's interface
   */
  function setFlywheelRewards(
    address rewardToken,
    address flywheelRewardsModule,
    string calldata contractInterface
  ) public onlyOwner {
    flywheelRewards[rewardToken] = Contract(flywheelRewardsModule, contractInterface);
  }

  /**
   * @dev sets the address and contract interface ID of the ERC4626 plugin for the asset
   * @param asset the asset address
   * @param plugin the ERC4626 plugin address
   * @param contractInterface a string that uniquely identifies the contract's interface
   */
  function setPlugin(
    address asset,
    address plugin,
    string calldata contractInterface
  ) public onlyOwner {
    plugins[asset] = Contract(plugin, contractInterface);
  }

  /**
   * @dev sets the address and contract interface ID of the redemption strategy for the asset
   * @param asset the asset address
   * @param strategy redemption strategy address
   * @param contractInterface a string that uniquely identifies the contract's interface
   */
  function setRedemptionStrategy(
    address asset,
    address strategy,
    string calldata contractInterface,
    address outputToken
  ) public onlyOwner {
    redemptionStrategiesConfig[asset] = RedemptionStrategy(strategy, contractInterface, outputToken);
  }

  function getRedemptionStrategy(address asset) public view returns (RedemptionStrategy memory) {
    return redemptionStrategiesConfig[asset];
  }

  /**
   * @dev sets the address and contract interface ID of the funding strategy for the asset
   * @param asset the asset address
   * @param strategy funding strategy address
   * @param contractInterface a string that uniquely identifies the contract's interface
   */
  function setFundingStrategy(
    address asset,
    address strategy,
    string calldata contractInterface,
    address inputToken
  ) public onlyOwner {
    fundingStrategiesConfig[asset] = FundingStrategy(strategy, contractInterface, inputToken);
  }

  function getFundingStrategy(address asset) public view returns (FundingStrategy memory) {
    return fundingStrategiesConfig[asset];
  }

  /**
   * @dev configures the Jarvis pool of a Jarvis synthetic token
   * @param syntheticToken the synthetic token address
   * @param collateralToken the collateral token address
   * @param liquidityPool the liquidity pool address
   * @param expirationTime the operation expiration time
   */
  function setJarvisPool(
    address syntheticToken,
    address collateralToken,
    address liquidityPool,
    uint256 expirationTime
  ) public onlyOwner {
    jarvisPoolsConfig.push(JarvisPool(syntheticToken, collateralToken, liquidityPool, expirationTime));
  }

  function setCurveSwapPool(address poolAddress, address[] calldata coins) public onlyOwner {
    curveSwapPoolsConfig.push(CurveSwapPool(poolAddress, coins));
  }

  /**
   * @dev Sets an address for an id replacing the address saved in the addresses map
   * @param id The id
   * @param newAddress The address to set
   */
  function setAddress(string calldata id, address newAddress) external onlyOwner {
    _addresses[id] = newAddress;
  }

  /**
   * @dev Returns an address by id
   * @return The address
   */
  function getAddress(string calldata id) public view returns (address) {
    return _addresses[id];
  }

  function getCurveSwapPools() public view returns (CurveSwapPool[] memory) {
    return curveSwapPoolsConfig;
  }

  function getJarvisPools() public view returns (JarvisPool[] memory) {
    return jarvisPoolsConfig;
  }

  function setBalancerPoolForTokens(
    address inputToken,
    address outputToken,
    address pool
  ) external onlyOwner {
    balancerPoolForTokens[inputToken][outputToken] = pool;
  }

  function getBalancerPoolForTokens(address inputToken, address outputToken) external view returns (address) {
    return balancerPoolForTokens[inputToken][outputToken];
  }
}

contracts/ionic/AuthoritiesRegistry.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import { PoolRolesAuthority } from "../ionic/PoolRolesAuthority.sol";
import { SafeOwnableUpgradeable } from "../ionic/SafeOwnableUpgradeable.sol";
import { IonicComptroller } from "../compound/ComptrollerInterface.sol";

import { TransparentUpgradeableProxy } from "@openzeppelin/contracts/proxy/transparent/TransparentUpgradeableProxy.sol";

contract AuthoritiesRegistry is SafeOwnableUpgradeable {
  mapping(address => PoolRolesAuthority) public poolsAuthorities;
  PoolRolesAuthority public poolAuthLogic;
  address public leveredPositionsFactory;
  bool public noAuthRequired;

  function initialize(address _leveredPositionsFactory) public initializer {
    __SafeOwnable_init(msg.sender);
    leveredPositionsFactory = _leveredPositionsFactory;
    poolAuthLogic = new PoolRolesAuthority();
  }

  function reinitialize(address _leveredPositionsFactory) public onlyOwnerOrAdmin {
    leveredPositionsFactory = _leveredPositionsFactory;
    poolAuthLogic = new PoolRolesAuthority();
    // for Neon the auth is not required
    noAuthRequired = block.chainid == 245022934;
  }

  function createPoolAuthority(address pool) public onlyOwner returns (PoolRolesAuthority auth) {
    require(address(poolsAuthorities[pool]) == address(0), "already created");

    TransparentUpgradeableProxy proxy = new TransparentUpgradeableProxy(address(poolAuthLogic), _getProxyAdmin(), "");
    auth = PoolRolesAuthority(address(proxy));
    auth.initialize(address(this));
    poolsAuthorities[pool] = auth;

    auth.openPoolSupplierCapabilities(IonicComptroller(pool));
    auth.setUserRole(address(this), auth.REGISTRY_ROLE(), true);
    // sets the registry owner as the auth owner
    reconfigureAuthority(pool);
  }

  function reconfigureAuthority(address poolAddress) public {
    IonicComptroller pool = IonicComptroller(poolAddress);
    PoolRolesAuthority auth = poolsAuthorities[address(pool)];

    if (msg.sender != poolAddress || address(auth) != address(0)) {
      require(address(auth) != address(0), "no such authority");
      require(msg.sender == owner() || msg.sender == poolAddress, "not owner or pool");

      auth.configureRegistryCapabilities();
      auth.configurePoolSupplierCapabilities(pool);
      auth.configurePoolBorrowerCapabilities(pool);
      // everyone can be a liquidator
      auth.configureOpenPoolLiquidatorCapabilities(pool);
      auth.configureLeveredPositionCapabilities(pool);

      if (auth.owner() != owner()) {
        auth.setOwner(owner());
      }
    }
  }

  function canCall(
    address pool,
    address user,
    address target,
    bytes4 functionSig
  ) external view returns (bool) {
    PoolRolesAuthority authorityForPool = poolsAuthorities[pool];
    if (address(authorityForPool) == address(0)) {
      return noAuthRequired;
    } else {
      // allow only if an auth exists and it allows the action
      return authorityForPool.canCall(user, target, functionSig);
    }
  }

  function setUserRole(
    address pool,
    address user,
    uint8 role,
    bool enabled
  ) external {
    PoolRolesAuthority poolAuth = poolsAuthorities[pool];

    require(address(poolAuth) != address(0), "auth does not exist");
    require(msg.sender == owner() || msg.sender == leveredPositionsFactory, "not owner or factory");
    require(msg.sender != leveredPositionsFactory || role == poolAuth.LEVERED_POSITION_ROLE(), "only lev pos role");

    poolAuth.setUserRole(user, role, enabled);
  }
}

contracts/ionic/DiamondExtension.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

/**
 * @notice a base contract for logic extensions that use the diamond pattern storage
 * to map the functions when looking up the extension contract to delegate to.
 */
abstract contract DiamondExtension {
  /**
   * @return a list of all the function selectors that this logic extension exposes
   */
  function _getExtensionFunctions() external pure virtual returns (bytes4[] memory);
}

// When no function exists for function called
error FunctionNotFound(bytes4 _functionSelector);

// When no extension exists for function called
error ExtensionNotFound(bytes4 _functionSelector);

// When the function is already added
error FunctionAlreadyAdded(bytes4 _functionSelector, address _currentImpl);

abstract contract DiamondBase {
  /**
   * @dev register a logic extension
   * @param extensionToAdd the extension whose functions are to be added
   * @param extensionToReplace the extension whose functions are to be removed/replaced
   */
  function _registerExtension(DiamondExtension extensionToAdd, DiamondExtension extensionToReplace) external virtual;

  function _listExtensions() public view returns (address[] memory) {
    return LibDiamond.listExtensions();
  }

  fallback() external {
    address extension = LibDiamond.getExtensionForFunction(msg.sig);
    if (extension == address(0)) revert FunctionNotFound(msg.sig);
    // Execute external function from extension using delegatecall and return any value.
    assembly {
      // copy function selector and any arguments
      calldatacopy(0, 0, calldatasize())
      // execute function call using the extension
      let result := delegatecall(gas(), extension, 0, calldatasize(), 0, 0)
      // get any return value
      returndatacopy(0, 0, returndatasize())
      // return any return value or error back to the caller
      switch result
      case 0 {
        revert(0, returndatasize())
      }
      default {
        return(0, returndatasize())
      }
    }
  }
}

/**
 * @notice a library to use in a contract, whose logic is extended with diamond extension
 */
library LibDiamond {
  bytes32 constant DIAMOND_STORAGE_POSITION = keccak256("diamond.extensions.diamond.storage");

  struct Function {
    address extension;
    bytes4 selector;
  }

  struct LogicStorage {
    Function[] functions;
    address[] extensions;
  }

  function getExtensionForFunction(bytes4 msgSig) internal view returns (address) {
    return getExtensionForSelector(msgSig, diamondStorage());
  }

  function diamondStorage() internal pure returns (LogicStorage storage ds) {
    bytes32 position = DIAMOND_STORAGE_POSITION;
    assembly {
      ds.slot := position
    }
  }

  function listExtensions() internal view returns (address[] memory) {
    return diamondStorage().extensions;
  }

  function registerExtension(DiamondExtension extensionToAdd, DiamondExtension extensionToReplace) internal {
    if (address(extensionToReplace) != address(0)) {
      removeExtension(extensionToReplace);
    }
    addExtension(extensionToAdd);
  }

  function removeExtension(DiamondExtension extension) internal {
    LogicStorage storage ds = diamondStorage();
    // remove all functions of the extension to replace
    removeExtensionFunctions(extension);
    for (uint8 i = 0; i < ds.extensions.length; i++) {
      if (ds.extensions[i] == address(extension)) {
        ds.extensions[i] = ds.extensions[ds.extensions.length - 1];
        ds.extensions.pop();
      }
    }
  }

  function addExtension(DiamondExtension extension) internal {
    LogicStorage storage ds = diamondStorage();
    for (uint8 i = 0; i < ds.extensions.length; i++) {
      require(ds.extensions[i] != address(extension), "extension already added");
    }
    addExtensionFunctions(extension);
    ds.extensions.push(address(extension));
  }

  function removeExtensionFunctions(DiamondExtension extension) internal {
    bytes4[] memory fnsToRemove = extension._getExtensionFunctions();
    LogicStorage storage ds = diamondStorage();
    for (uint16 i = 0; i < fnsToRemove.length; i++) {
      bytes4 selectorToRemove = fnsToRemove[i];
      // must never fail
      assert(address(extension) == getExtensionForSelector(selectorToRemove, ds));
      // swap with the last element in the selectorAtIndex array and remove the last element
      uint16 indexToKeep = getIndexForSelector(selectorToRemove, ds);
      ds.functions[indexToKeep] = ds.functions[ds.functions.length - 1];
      ds.functions.pop();
    }
  }

  function addExtensionFunctions(DiamondExtension extension) internal {
    bytes4[] memory fnsToAdd = extension._getExtensionFunctions();
    LogicStorage storage ds = diamondStorage();
    uint16 functionsCount = uint16(ds.functions.length);
    for (uint256 functionsIndex = 0; functionsIndex < fnsToAdd.length; functionsIndex++) {
      bytes4 selector = fnsToAdd[functionsIndex];
      address oldImplementation = getExtensionForSelector(selector, ds);
      if (oldImplementation != address(0)) revert FunctionAlreadyAdded(selector, oldImplementation);
      ds.functions.push(Function(address(extension), selector));
      functionsCount++;
    }
  }

  function getExtensionForSelector(bytes4 selector, LogicStorage storage ds) internal view returns (address) {
    uint256 fnsLen = ds.functions.length;
    for (uint256 i = 0; i < fnsLen; i++) {
      if (ds.functions[i].selector == selector) return ds.functions[i].extension;
    }

    return address(0);
  }

  function getIndexForSelector(bytes4 selector, LogicStorage storage ds) internal view returns (uint16) {
    uint16 fnsLen = uint16(ds.functions.length);
    for (uint16 i = 0; i < fnsLen; i++) {
      if (ds.functions[i].selector == selector) return i;
    }

    return type(uint16).max;
  }
}

