","keccak256":"0xf1928dc8674efe6fb1a3d8480a84fa5891f80ae7d0c4b771815037b67098023b","license":"UNLICENSED"},"contracts/compound/ComptrollerInterface.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

abstract contract ComptrollerInterface {
  /// @notice Indicator that this is a Comptroller contract (for inspection)
  bool public constant isComptroller = true;

  /*** Assets You Are In ***/

  function enterMarkets(address[] calldata cTokens) external virtual returns (uint256[] memory);

  function exitMarket(address cToken) external virtual returns (uint256);

  /*** Policy Hooks ***/

  function mintAllowed(
    address cToken,
    address minter,
    uint256 mintAmount
  ) external virtual returns (uint256);

  function mintWithinLimits(
    address cToken,
    uint256 exchangeRateMantissa,
    uint256 accountTokens,
    uint256 mintAmount
  ) external virtual returns (uint256);

  function mintVerify(
    address cToken,
    address minter,
    uint256 mintAmount,
    uint256 mintTokens
  ) external virtual;

  function redeemAllowed(
    address cToken,
    address redeemer,
    uint256 redeemTokens
  ) external virtual returns (uint256);

  function redeemVerify(
    address cToken,
    address redeemer,
    uint256 redeemAmount,
    uint256 redeemTokens
  ) external virtual;

  function borrowAllowed(
    address cToken,
    address borrower,
    uint256 borrowAmount
  ) external virtual returns (uint256);

  function borrowWithinLimits(address cToken, uint256 accountBorrowsNew) external virtual returns (uint256);

  function borrowVerify(
    address cToken,
    address borrower,
    uint256 borrowAmount
  ) external virtual;

  function repayBorrowAllowed(
    address cToken,
    address payer,
    address borrower,
    uint256 repayAmount
  ) external virtual returns (uint256);

  function repayBorrowVerify(
    address cToken,
    address payer,
    address borrower,
    uint256 repayAmount,
    uint256 borrowerIndex
  ) external virtual;

  function liquidateBorrowAllowed(
    address cTokenBorrowed,
    address cTokenCollateral,
    address liquidator,
    address borrower,
    uint256 repayAmount
  ) external virtual returns (uint256);

  function liquidateBorrowVerify(
    address cTokenBorrowed,
    address cTokenCollateral,
    address liquidator,
    address borrower,
    uint256 repayAmount,
    uint256 seizeTokens
  ) external virtual;

  function seizeAllowed(
    address cTokenCollateral,
    address cTokenBorrowed,
    address liquidator,
    address borrower,
    uint256 seizeTokens
  ) external virtual returns (uint256);

  function seizeVerify(
    address cTokenCollateral,
    address cTokenBorrowed,
    address liquidator,
    address borrower,
    uint256 seizeTokens
  ) external virtual;

  function transferAllowed(
    address cToken,
    address src,
    address dst,
    uint256 transferTokens
  ) external virtual returns (uint256);

  function transferVerify(
    address cToken,
    address src,
    address dst,
    uint256 transferTokens
  ) external virtual;

  /*** Liquidity/Liquidation Calculations ***/

  function liquidateCalculateSeizeTokens(
    address cTokenBorrowed,
    address cTokenCollateral,
    uint256 repayAmount
  ) external view virtual returns (uint256, uint256);

  /*** Pool-Wide/Cross-Asset Reentrancy Prevention ***/

  function _beforeNonReentrant() external virtual;

  function _afterNonReentrant() external virtual;
}
